INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (321,'Snow with Occasional Lagombi','Schnee, vereinzelt Lagombi','Risque d''averse de Lagombi','Lagombi chubascosos','Neve mista Lagombi','','Hunt a Lagombi','Erjage einen Lagombi.','Chasser 1 Lagombi','Caza un Lagombi','Caccia un Lagombi','','Village','0','LR',0,0,3,3,50,200,1800,100,'Wound Lagombi''s head','Lagombi-Kopf verletzen','Blesser la tête du Lagombi','Hiere Lagombi en la cabeza','Ferisci la testa del Lagombi','','[REPORT BEGINS] Lagombi spotted in the Arctic Ridge [STOP] Requesting support from all qualified hunters [STOP] We are snowed in and cannot render assistance [REPORT ENDS]','[BERICHT] Lagombi im Schneegebirge gesichtet. [STOP] Erbitten Hilfe von allen qualifizierten Jägern. [STOP] Wir sind eingeschneit und können keine Hilfe leisten. [BERICHT ENDE]','[DÉBUT DU RAPPORT] Lagombi repéré sur Crête arctique. [STOP] Demandons aide de chasseurs qualifiés. [STOP] Aide impossible pour cause de neige. [FIN DU RAPPORT]','[INICIO INFORME] Lagombi avistado en Cimas Árticas. [STOP] Solicitamos apoyo de todos los cazadores. [STOP] Estamos aislados por la nieve y no podemos ayudar. [FIN INFORME]','[INIZIO MESSAGGIO] Lagombi avvistato sul monte artico [STOP] Si richiede supporto immediato [STOP] Siamo bloccati dalla neve e non possiamo prestare soccorso [FINE MESSAGGIO]','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (322,'Anteka Anticipation','In Erwartung der Anteka','L''eau à la bouche','Anticipación Anteka','Profumo d''Anteka','','Slay 8 Anteka','Erlege 8 Anteka.','Tuer 8 Anteka','Abate a 8 Anteka','Uccidi 8 Anteka','','Village','0','LR',1,0,3,103,50,100,900,80,'Deliver 1 Khezu Whelp','1 Khezu-Larve abliefern','Livrer 1 Bébé Khezu','Entrega 1 larva de Khezu','Consegna una larva Khezu','','I''ve gotten my paws on all kinds of monster meat before. You name it, I''ve grilled it. But I''ve never tried my paw at grilling up some Anteka. Help me out and we''ll have a barbeque for the ages!','Ich hab schon alle Arten Monsterfleisch in den Pfoten gehabt. Es gibt nichts, was ich noch nicht gegrillt hätte! Außer Anteka. Hilf mir, etwas Fleisch zu besorgen, und wir machen das Grillfest des Jahres!','J''ai vu passer toutes sortes de viande de monstre par le passé, mais je n''ai encore jamais tenté de préparer des grillades d''Anteka. Aidez-moi et on organisera un barbecue mémorable.','He puesto las patitas en todo tipo de carne de monstruo, pero nunca he cocinado Anteka. Ayúdame y disfrutaremos de una barbacoa épica.','Ho messo le zampe su ogni sorta di carne di mostro. Non c''è bestia che non abbia grigliato. Tranne l''Anteka! Aiutami a colmare la lacuna e prrreparerò la grigliata del secolo!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (323,'The Shadow in the Mountains','Der Schatten in den Bergen','L''ombre des montagnes','La sombra de las montañas','L''ombra nelle montagne','','Hunt a Khezu','Erjage einen Khezu.','Chasser 1 Khezu','Caza un Khezu','Caccia un Khezu','','Village','0','LR',0,0,3,3,50,300,2700,220,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','We were up in the Arctic Ridge -- doing a little research -- when all of a sudden, something really creepy and gross plopped down behind us. We managed to escape, but we can''t do any research with that thing up there!','Wir forschten im Bereich des Schneegebirges, als wir plötzlich hinter uns ein unheimliches Geräusch hörten. Wir konnten uns retten, aber das Biest muss verschwinden!','Nous menions des recherches sur la Crête arctique quand une créature répugnante a surgi derrière nous. Impossible de retourner là-haut tant qu''elle s''y trouve !','Estábamos investigando en las Cimas Árticas cuando de repente apareció algo muy asqueroso. Logramos huir, pero no podemos investigar mientras esa cosa siga ahí.','Eravamo sul monte artico a svolgere delle ricerche quando qualcosa di orribile ci è spuntato alle spalle. Siamo riusciti a fuggire, ma non possiamo più esplorare con quel mostro in giro!','',300,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (324,'No Go on the Popo','Herz in der Hose','Une présence furtive','Sensación de ansiedad','Richiamo profondo','','Deliver 3 Popo Tongues','Liefere 3 Pepe-Zungen ab.','Livrer 3 langues de Popo','Entrega 3 lenguas de Popo','Consegna 3 lingue di Popo','','Village','0','LR',3,0,3,103,50,100,900,80,'None','–','Rien','Ninguno','Nessuno','','Usually I can find Popo pretty easily, but the few I spotted today scampered up the mountain like they were spooked by something. I need some Popo meat, but I''m not going up there! Can you go and get me some?','Normalerweise kann ich Pepe leicht aufspüren, aber heute habe ich nur ein paar gesehen, die wie verschreckt oben in den Bergen hockten. Ich brauche Pepe-Fleisch, aber ich kann da nicht hoch! Kannst du gehen?','En général, ça grouille de Popo par ici, mais les rares que j''ai aperçus aujourd''hui ont détalé vers le sommet comme s''ils avaient peur. Or, j''ai besoin de viande. Vous pourriez m''en chercher ?','Normalmente encuentro Popo enseguida, pero los pocos que he visto hoy huyeron a la montaña como si algo los hubiese asustado. Necesito carne de Popo, ¿podrías ir tú y traerme?','Non è difficile trovare dei Popo, ma oggi sono fuggiti sulle montagne come se avessero visto un fantasma! La carne di Popo è gustosa, ma lassù non ci andrei mai! E se ci andassi tu?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (325,'No Go on the Popo','Herz in der Hose','Une présence furtive','Sensación de ansiedad','Richiamo profondo','','Deliver 3 Popo Tongues','Liefere 3 Pepe-Zungen ab.','Livrer 3 langues de Popo','Entrega 3 lenguas de Popo','Consegna 3 lingue di Popo','','Village','0','LR',3,0,3,103,50,100,900,80,'None','–','Rien','Ninguno','Nessuno','','Usually I can find Popo pretty easily, but the few I spotted today scampered up the mountain like they were spooked by something. I need some Popo meat, but I''m not going up there! Can you go and get me some?','Normalerweise kann ich Pepe leicht aufspüren, aber heute habe ich nur ein paar gesehen, die wie verschreckt oben in den Bergen hockten. Ich brauche Pepe-Fleisch, aber ich kann da nicht hoch! Kannst du gehen?','En général, ça grouille de Popo par ici, mais les rares que j''ai aperçus aujourd''hui ont détalé vers le sommet comme s''ils avaient peur. Or, j''ai besoin de viande. Vous pourriez m''en chercher ?','Normalmente encuentro Popo enseguida, pero los pocos que he visto hoy huyeron a la montaña como si algo los hubiese asustado. Necesito carne de Popo, ¿podrías ir tú y traerme?','Non è difficile trovare dei Popo, ma oggi sono fuggiti sulle montagne come se avessero visto un fantasma! La carne di Popo è gustosa, ma lassù non ci andrei mai! E se ci andassi tu?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (326,'Bully the Bullfango','Bullfango-Bedrängnis','Attaque de Bullfango','Acoso al Bullfango','Fango sui Bullfango','','Slay 20 Bullfango','Erlege 20 Bullfangos.','Tuer 20 Bullfango','Abate a 20 Bullfango','Uccidi 20 Bullfango','','Village','0','LR',1,0,3,104,50,300,2100,80,'Hunt a Bulldrome','Einen Bulldrome erjagen','Chasser 1 Bulldrome','Caza un Bulldrome','Caccia un Bulldrome','','Ugh! My backside''s seen better days. Blame the Bullfango! My mom sent me to the Misty Peaks to dig up mushrooms, see, and all of a sudden those beasts were behind me! Long story short, my bottom needs avenging!','Oh, meiner Rückseite ging es schon mal besser. Blöder Bullfango! Ich sollte für mei- ne Mutter Pilze in den Nebel- gipfeln sammeln, als sich die Bestien auf mich stürzten! Mein Hintern will Rache!','Aïe ! Mon popotin me fait souffrir. C''est à cause des Bullfango ! J''étais à la cueillette aux champignons dans les Pics brumeux, quand ces bêtes me sont tombées dessus ! En bref, vengez mon derrière !','¡Ag! Mi trasero ha vivido días mejores. ¡Y todo por los Bullfango! Mi madre me envió a los Picos Nublados a por setas, ¡y de repente me vi cercado por esas bestias! ¡Mi trasero clama venganza!','Il mio didietro ha vissuto giorni migliori. Tutta colpa dei Bullfango! Raccoglievo funghi, quando queste belve mi hanno attaccato alle spalle, all''improvviso! Il mio sedere grida vendetta!','',1200,80);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (327,'For Whom the Egg Yolks','Wer die Eier schlägt','Trafic d''œufs','Pon los huevos en la cesta','Tuorlo di sera...','','Deliver 3 Gargwa Eggs','Liefere 3 Gargwa-Eier ab.','Livrer 3 œufs de Gargwa','Entrega 3 huevos de Gargwa','Consegna 3 uova di Gargwa','','Village','0','LR',3,0,3,4,50,200,1800,120,'Deliver 3 Gargwa Guano','3 Gargwa-Guano abliefern','Livrer 3 guanos de Gargwa','Entrega 3 guanos de Gargwa','Consegna 3 guano di Gargwa','','I want...I mean, this TOWN could do with some Gargwa Eggs. They are a hot commodity right meow that people are willing to trade fur. Could you sneak me...er, US, definitely US, three Gargwa Eggs?','Ich will... Ich meine, das DORF möchte gerne einige Gargwa- Eier haben. Sie sind jetzt ja miaunzlich beliebt, sodass die Leute ihr letztes Fell dafür geben. Kannst du mir... ähm, UNS, drei Gargwa-Eier holen?','J''ai besoin... ce village a besoin d''œufs de Gargwa. Ils sont très demandés en ce moment, et les gens sont prêts à mettre le prix. Pouvez-vous me voler... nous ramener trois œufs ?','Yo... Es decir, el pueblo necesita huevos de Gargwa. Son un miauterial muy buscado y la gente quiere hacer trueques. ¿Podrías traerme... digo, traernos tres huevos de Gargwa?','Vorrei... Cioè... La gente del villaggio vorrebbe delle uova di Gargwa. Sai, sono una merce di scambio niente male. Puoi procurarmene, ehm... prrrocurarcene tre, miah?','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (328,'Royal Spit Take','Königlicher Wasserspucker','Sauvons notre navire','Arregla nuestro barco','S.O.S.','','Hunt a Royal Ludroth','Erjage einen Königs-Ludroth.','Chasser 1 Ludroth royal','Caza un Ludroth Real','Caccia un Ludroth reale','','Village','0','LR',0,0,3,4,50,300,2400,180,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','Ick! I got splattered with weird gunk that sapped my Stamina, so I couldn''t even run! Luckily, a Felyne came by and saved me, but now whenever I see anything yellow...urgh!','Igitt! Ich bin von diesem seltsamen Glibber bedeckt, der meine Ausdauer angreift. Ich kann nicht mal laufen! Zum Glück wurde ich durch eine Felyne gerettet. Aber die Farbe gelb? Grauenhaft!','Beurk ! Des éclaboussures bizarres ont drainé ma stamina et je ne pouvais plus courir ! Heureusement, un Felyne est venu m''aider. Maintenant, la seule vue du jaune me soulève le cœur !','¡Bah! Me han salpicado con algo mugriento que me robó la resistencia. ¡No podía ni correr! Por suerte, pasó un Felyne y me salvó, pero ahora, si veo algo amarillo... ¡Argh!','Ick! Mi hanno ricoperto di una schifezza che ha assorbito la mia resistenza, quindi non riuscivo a correre! Un Felyne mi ha salvato, ma se ora vedo qualcosa di giallo... Bleah!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (329,'A Forest Fracas','Aufruhr im Ur-Wald','Fracas forestier','Tumulto en el bosque','Rissa nei boschi','','Hunt a Bulldrome','Erjage den Bulldrome.','Chasser 1 Bulldrome','Caza un Bulldrome','Caccia un Bulldrome','','Village','0','LR',0,0,3,104,50,200,1200,80,'None','–','Rien','Ninguno','Nessuno','','Something''s afoot, er, ahoof in the forest, Hunter. A Bulldrome suddenly showed up and leveled our crops. Strange happenings ''round here. Can you check it out and take care of that Bulldrome while you''re at it?','In dem Wald geht, äh, trampelt etwas vor sich. Ein Bulldrome ist plötzlich aufgetaucht und hat unsere Ernte geplättet. Hier passiert Seltsames. Kannst du nachsehen und dich dabei auch um den Bulldrome kümmern?','Il se trame quelque chose dans la forêt. Un Bulldrome a dévasté nos récoltes et on est confrontés à des évènements étranges. Vous pouvez enquêter et nous débarrasser du Bullfango ?','Hay algo por el bosque, cazador. Ha aparecido un Bulldrome que destruye las cosechas. Pasan cosas raras. ¿Podrías ir a ver qué es, y de paso ocuparte de ese Bullfango?','Sta succedendo qualcosa nella foresta. Un Bulldrome è apparso all''improvviso, rovinando i nostri raccolti. Qualcosa non va. Potresti indagare e, già che ci sei, occuparti del Bullfango?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (330,'A Forest Fracas','Aufruhr im Ur-Wald','Fracas forestier','Tumulto en el bosque','Rissa nei boschi','','Hunt a Bulldrome','Erjage den Bulldrome.','Chasser 1 Bulldrome','Caza un Bulldrome','Caccia un Bulldrome','','Village','0','LR',0,0,3,104,50,200,1200,80,'None','–','Rien','Ninguno','Nessuno','','Something''s afoot, er, ahoof in the forest, Hunter. A Bulldrome suddenly showed up and leveled our crops. Strange happenings ''round here. Can you check it out and take care of that Bulldrome while you''re at it?','In dem Wald geht, äh, trampelt etwas vor sich. Ein Bulldrome ist plötzlich aufgetaucht und hat unsere Ernte geplättet. Hier passiert Seltsames. Kannst du nachsehen und dich dabei auch um den Bulldrome kümmern?','Il se trame quelque chose dans la forêt. Un Bulldrome a dévasté nos récoltes et on est confrontés à des évènements étranges. Vous pouvez enquêter et nous débarrasser du Bullfango ?','Hay algo por el bosque, cazador. Ha aparecido un Bulldrome que destruye las cosechas. Pasan cosas raras. ¿Podrías ir a ver qué es, y de paso ocuparte de ese Bullfango?','Sta succedendo qualcosa nella foresta. Un Bulldrome è apparso all''improvviso, rovinando i nostri raccolti. Qualcosa non va. Potresti indagare e, già che ci sei, occuparti del Bullfango?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (331,'Royal Honey Hunt','Jagd nach dem Königshonig','Secret du miel royal','A por jalea real','Caccia al miele reale','','Deliver 5 Royal Honeys','Liefere 5 Einheiten Königshonig ab.','Livrer 5 pots de miel royal','Entrega 5 jaleas reales','Consegna 5 mieli reali','','Village','0','LR',3,0,3,104,50,100,900,80,'Hunt an Arzuros','Einen Arzuros erjagen','Chasser 1 Arzuros','Caza un Arzuros','Caccia un Arzuros','','The name "Royal Honey" makes it sound like a divine treat that has to be tasted to be believed! I have to know how I can get my bees to make this "nectar of the gods."','Alleine der Name "Königs- honig" klingt schon so edel, den muss man probieren! Und ich will herausfinden, wie ich meine Bienen dazu bekomme, diesen "Nektar der Götter" herzustellen.','Le nom même de "miel royal" évoque une douceur à laquelle il faut goûter pour croire. Je veux savoir comment encourager mes abeilles à produire ce "nectar des dieux".','El nombre "jalea real" hace que suene como una delicia divina que hay que probar. Tengo que descubrir cómo hacer que mis abejas fabriquen ese néctar de los dioses.','Il "miele reale" meriterebbe di essere assaggiato anche solo per il nome! Voglio carpirne i segreti e indurre le mie api a produrre questo "nettare degli dei".','',1200,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (332,'Current Events','Nasse Neuigkeiten','Évènements actuels','Acontecimientos recientes','Bagnati fradici','','Hunt a Royal Ludroth','Erjage einen Königs-Ludroth.','Chasser 1 Ludroth royal','Caza un Ludroth Real','Caccia un Ludroth reale','','Village','1','LR',0,0,3,6,50,300,2400,180,'Slay 8 Ludroth','8 Ludrothe erlegen','Tuer 8 Ludroth','Abate a 8 Ludroth','Uccidi 8 Ludroth','','What a twist! People on land suddenly soaked to the bone! The soggy victims insist they saw something yellow before their drenching. What could it have been? Hunter, please, help me solve this riddle!','Merkwürdige Sache! Die Leute an Land wurden bis auf die Knochen nass! Die durchnässten Opfer haben von etwas Gelbem berichtet. Was kann das sein? Jäger, bitte hilf mir, dieses Rätsel zu lösen!','Les gens sont trempés jusqu''aux os ! Les victimes affirment avoir vu une chose jaune avant la douche. Je vous en prie, aidez-moi à résoudre cette énigme !','¡Qué misterio! Gente en tierra empapada hasta los huesos. Las víctimas dicen haber visto algo amarillo antes del remojón. ¿Qué habrá sido? ¡Ayúdame a resolver este acertijo!','Aiuto! La gente è di colpo inzuppata fino al midollo! Le vittime fradice hanno visto una creatura gialla prima di essere ricoperte d''acqua! Cacciatore, aiutami a fare luce su questa storia!','',1200,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (333,'The Crabbiest Catch','Krabben-Gedöns','La traque du crustacé','Deliciosos mejillones','Granchi rosa','','Capture a Daimyo Hermitaur','Fange einen Daimyo Hermitaur.','Capturer 1 Hermitaur Daimyo','Captura un Daimyo Hermitaur','Cattura un Daimyo Hermitaur','','Village','0','LR',2,0,3,6,50,400,3600,250,'Break Daimyo''s claws','D.Hermitaur-Klauen brechen','Briser les pinces du Daimyo','Rompe la garra del Hermitaur','Spezza la chela del D.Hermitaur','','You know, Hunter, Moga Mussels found on the Deserted Island are a world famous delicacy, but a Daimyo Hermitaur keeps messin'' with their breedin'' grounds. Could you capture that crabby critter for me?','Du musst wissen, Moga-Muscheln von der Ödnis-Insel sind eine weltbekannte Delikatesse. Aber ein Daimyo Hermitaur bringt immer ihren Brutplatz durch- einander. Kannst du diesen Griesgram für mich fangen?','Les moules Moga de l''Île déserte sont célèbres dans le monde entier, vous savez. Hélas, un Hermitaur Daimyo perturbe leur reproduction. Pourriez-vous aller me capturer ce gros crabe ?','¿Sabes qué, cazador? Los mejillones Moga de la Isla Desierta son mundialmente famosos, pero un Daimyo Hermitaur perturba su zona de cría. ¿Podrías capturar a esa alimaña enojosa?','I mitili di Moga dell''isola deserta sono una specialità nota in tutto il mondo, ma un Daimyo Hermitaur sta devastando l''area in cui crescono. Potresti catturare quella bestiaccia per me?','',300,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (334,'Pack Woes','Ein Rudel Sorgen','Une meute menaçante','Una manada problemática','Un branco di guai','','Slay a total of 20 Jaggi or Jaggia','Erlege insgesamt 20 Jaggi oder Jaggia.','Tuer un total de 20 Jaggi ou Jaggia','Abate a un total de 20 Jaggi o Jaggia','Uccidi 20 Jaggi o Jaggia in totale','','Village','0','LR',1,0,3,6,50,200,1800,80,'None','–','Rien','Ninguno','Nessuno','','Oh, dear me! I heard about a pack of Jaggi roaming closer and closer to the village lately. I''m worried my son might get mauled out there! Put my fears to rest and make sure it''s safe for my son to play.','Oh, Grundgütiger! Ich habe gehört, dass einige Jaggi nahe an unserem Dorf jagen. Ich mache mir solche Sorgen um meinen Sohn. Könntest du viel- leicht hingehen und nach ihm sehen, damit ich beruhigt bin?','Il paraît qu''une meute de Jaggi s''aventure toujours plus près du village et j''ai peur que mon fils se fasse tailler en pièces en jouant dehors. Faites le nécessaire pour me rassurer, par pitié !','Ay, ¡pobre de mí! Hay una manada de Jaggi cerca de la aldea. Temo que ataquen a mi hijo. Haz que me quede más tranquila; asegúrate de que mi hijo pueda jugar sin peligro.','Oh, povera me! Un branco di Jaggi si aggira nei pressi del villaggio. Santo cielo, e se assalissero mio figlio? Fammi stare tranquilla: assicurati che possa stare di nuovo in tranquillità!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (335,'The Fungus Among Us','Der Pilz und ich','En quête de champignons','Hongos entre nosotros','Fungo delle mie brame','','Deliver 5 Ripened Mushrooms','Liefere 5 Reife Pilze ab.','Livrer 5 champignons uniques','Entrega 5 setas maduras','Consegna 5 funghi maturi','','Village','0','LR',3,0,3,6,50,200,1200,100,'Deliver 10 Unique Mushrooms','10 Einmalige Pilze abliefern','Livrer 10 champignons spéciaux','Entrega 10 setas especiales','Consegna 10 funghi speciali','','I''ve got a real mushroom problem, Hunter! I need to get my hands on some Ripened Mushrooms for a cookout, but I''m not going anywhere near those creepy Altaroth! Could you get some for me? Pleeease!?','Ich habe ein echtes Pilz- problem, Jäger! Ich brauche dringend einige Reife Pilze für meine Party, aber da lauert so ein schrecklicher Altaroth! Kannst du mir nicht welche besorgen? Bitte? Bitte, bitte?','J''ai un sérieux problème ! Il me faut absolument des champignons uniques pour un pique-nique, mais je ne me vois pas me frotter à ces Altaroth. Pouvez-vous m''en chercher ? S''il vous plaît ?','Tengo un grave problema de setas, cazador. Necesito unas setas maduras para una receta, pero no pienso acercarme a esos Altaroth. Consígueme unas cuantas, por favor.','Questo è un bel problema! Ho bisogno di funghi maturi per un pranzo all''aperto, ma ho paura degli Altaroth! Potresti mica raccoglierne qualcuno per me? Ti preeego!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (336,'Moofah Must-haves','Muufah-Mode','Moofah à la mode','Pelusa de Moofah','Batuffoli di Moofah','','Deliver 5 pieces of Moofah Fur','Liefere 5 Einheiten Muufah-Fell ab.','Livrer 5 fourrures de Moofah','Entrega 5 uds. de pelo Moofah','Consegna 5 pellicce di Moofah','','Village','0','LR',3,0,3,6,50,100,900,100,'None','–','Rien','Ninguno','Nessuno','','We need to make warm clothing for our babies, but to make them, I need some Moofah wool from the Moofah on the Deserted Island. If you''ve got a Cutting-type weapon, it should be easy!','Wir müssen warme Kleidung für unsere Babys herstellen, aber dafür brauche ich Wolle von den Muufahs der Ödnis-Insel. Sollte aber mit einer Schnittwaffe nicht zu schwer zu besorgen sein.','Nos bébés ont besoin de pulls bien chauds en ce moment, mais il faut de la laine de Moofah de l''Île déserte pour en tricoter. En obtenir sera un jeu d''enfant si vous avez une arme tranchante.','Necesitamos ropa calentita para nuestros bebés, pero para hacerla necesito lana de Moofah de la Isla Desierta. Si tienes un arma cortante, te será fácil.','I maglioni caldi sono di moda ultimamente, ma per confezionarli ho bisogno della lana dei Moofah dell''isola deserta. Usando un''arma da taglio dovrebbe essere un gioco da ragazzi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (337,'Deserted Island Accounting','Buchhaltung: Ödnis-Insel','À point nommé : Île déserte','Objetivo: Isla Desierta','Punti sull''isola deserta','','Earn 1000 Wycademy Points','Sammle 1000 Wykademie-Punkte.','Obtenir 1000 points Wycadémie','Gana 1.000 puntos Wycademia','Ottieni 1000 punti Wyccademia','','Village','0','LR',3,0,3,6,50,300,2100,150,'Hunt a Tetsucabra','Einen Tetsucabra erjagen','Chasser 1 Tetsucabra','Caza un Tetsucabra','Caccia un Tetsucabra','','Young Hunter, your hands seem idle. Would you care to busy yourself collecting points for me on the Deserted Island? Any items are fine, as long as you meet the quota. Take care, now.','Dir scheint irgendwie total langweilig zu sein. Magst du vielleicht für mich auf der Ödnis-Insel Punkte sammeln gehen? Die Objekte sind ganz egal, solange die Punktzahl stimmt. Und sei vorsichtig!','Vous tombez bien ! Pourriez-vous livrer des objets bonus à l''Île déserte ? Peu importe les objets tant que vous atteignez le quota de points !','Se te ve ocioso, joven. ¿Querrías dedicarte a obtener puntos para mí en la Isla Desierta? Cualquier objeto me sirve, mientras alcances el objetivo. Ve con cuidado.','Ehi, giovine! Per caso ti stai annoiando? Se non hai altro da fare, perché non vai a caccia di punti sull''isola deserta? Ogni oggetto conta, l''importante è raggiungere la quota!','',2400,180);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (338,'Sunsnug Isle Rescue!','Sonnwohl-Insel-Rettung!','Sauvetage sur l''Île Soldoux !','¡Rescate en Isla Sunsnug!','Salvataggio sull''isola!','','Hunt an Iodrome','Erjage einen Iodrome.','Chasser 1 Iodrome','Caza un Iodrome','Caccia uno Iodrome','','Village','0','LR',0,0,3,7,50,200,1800,110,'Slay 12 Ioprey','12 Ioprey erlegen','Tuer 12 Ioprey','Abate a 12 Ioprey','Uccidi 12 Ioprey','','Oh, it''s a catastrophe! The Sunsnug Isle Meownster Hunters were in the Marshlands when an Iodrome kitty cornered them! Purrlease rescue them, Hunter!','Oh, es ist eine Katzastrophe! Die Miauster-Jäger der Sonnwohl-Insel waren im Marschland unterwegs, als ein Iodrome-Junges sie schnurrstracks in die Ecke trieb! Bitte rette sie, miau!','C''est la chatastrophe ! Les Chasseurs de miaonstres de l''Île Soldoux se sont fait piéger par un Iodrome dans les Marais. Chasseur, partez vite à leur rescousse !','¡Es una gatástrofe! ¡Los cazamiaunstruos de Isla Sunsnug han ido a la Ciénaga y, de repente, un Iodrome los ha acorralado! ¡Por favor, ayúdales!','Oh, che gattastrofe! I nostri cacciatori di miaostri sono stati sorpresi da uno Iodrome nella palude! Fai qualcosa! Salvali, ti prrrego!','',1200,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (339,'Robbed Blind','Komplett ausgeraubt','Chasse au voleur','El ladrón','Bottino luccicante','','Hunt a Gypceros','Erjage einen Gypceros.','Chasser 1 Gypceros','Caza un Gypceros','Caccia un Gypceros','','Village','1','LR',0,0,3,7,50,300,2100,170,'Wound Gypceros''s crest','Gypceros-Kamm verletzen','Briser la crête du Gypceros','Hiere Gypceros en la cresta','Ferisci la cresta del Gypceros','','I hear that Gypceros take a real shine to shiny objects. They''ve been known to swipe stuff from people! If we can hunt one down, I bet we''ll get our hands on some serious booty! Cha-ching!','Ich habe gehört, dass die Gypceros von glänzenden Objekten angezogen werden und sogar von Menschen klauen! Wenn wir einen jagen, könnten wir bestimmt haufenweise Schätze erbeuten! Zahltag, Baby!','On dit que les Gypceros aiment tant ce qui brille qu''ils n''hésitent pas à voler quand l''occasion se présente. En chasser nous permettrait sûrement de récupérer un sacré butin !','He oído que al Gypceros le gustan las cosas brillantes y que roban a la gente. Si podemos cazar a uno, apuesto a que nos haremos con un buen botín. ¡Dinero fácil!','Pare che i Gypceros siano attratti dagli oggetti luccicanti. Addirittura, li rubano alla gente! Se ne cacciassimo uno, scommetto che metteremmo le mani su un cospicuo bottino!','',300,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (340,'Pest Control','Schädlingsbekämpfung','Désinsectisation','Control de plagas','Concerto di Neopteron','','Slay a total of 20 Vespoid or Hornetaur','Erlege insgesamt 20 Vespoiden oder Hornetauren.','Tuer un total de 20 Vespoid ou Hornetaur','Abate a un total de 20 Vespoid o Hornetaur','Uccidi 20 Vespoid o Hornetaur in totale','','Village','0','LR',1,0,3,7,50,200,1200,80,'None','–','Rien','Ninguno','Nessuno','','Work is tough enough as it is, but I can''t even get a wink of sleep with all these bugs buzzing around! And now my boss is angry because my work is behind schedule! Make these bugs buzz off!','Die Arbeit an sich ist ja schon schlimm genug, aber jetzt bekomme ich nicht mal mehr Schlaf! Diese nervigen Käfer! Mein Boss wird schon wütend, weil ich nicht schnell genug arbeite, verjag die bitte!','Comme si le travail n''était pas assez dur comme ça, voilà que je n''arrive plus à fermer l''œil à cause de ces insectes ! Du coup, j''ai du retard et mon patron s''en plaint. Chassez-les !','El trabajo ya es duro de por sí, y encima no puedo pegar ojo. Esos malditos bichos son un incordio. Y mi jefe está enfadado porque me retraso. ¡Échalos de aquí!','Ormai non riesco più a chiudere occhio! Il ronzio di quegli insetti mi sta facendo impazzire! E ora il capo è su tutte le furie perché sono indietro con il lavoro! Mettili a tacere!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (341,'Travelers in Trouble','Wenn einer eine Reise tut...','Du rififi chez les touristes','Viajeros en peligro','Vacanza da urlo','','Slay 10 Ioprey','Erlege 10 Ioprey.','Tuer 10 Ioprey','Abate a 10 Ioprey','Uccidi 10 Ioprey','','Village','0','LR',1,0,3,7,50,200,1200,80,'Hunt an Iodrome','Einen Iodrome erjagen','Chasser 1 Iodrome','Caza un Iodrome','Caccia uno Iodrome','','Some travelers came all the way from Dundorma to visit Bherna, but on their way home they got attacked by Ioprey! This is terrible! They''ll probably give us terrible scores on the questionnaire!','Einige Reisende sind den gan- zen Weg von Dundorma nach Bherna gekommen, aber auf ihrem Weg nach Hause wurden sie von Ioprey angegriffen! Die kommen wohl eh nie wieder, rette sie aber bitte trotzdem!','Des touristes de Dundorma sont venus visiter Bherna, mais ils se sont fait attaquer par un Ioprey ! C''est une catastrophe ! On va perdre une étoile dans les guides de voyage !','Han venido algunos viajeros de Dundorma a visitar Bherna, ¡Pero unos Ioprey les han atacado de camino a casa! Probablemente no quieran volver a venir nunca, pero... ¡Ve a ayudarlos, por favor!','Alcuni turisti sono venuti da Dundorma per visitare Bherna, ma durante il loro tragitto sono stati assaliti da uno Ioprey! Mi sa che le loro vacanze sono già state rovinate, però tu vai ad aiutarli!','',1800,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (342,'Royal Rhinos Gone Wild','Königliche-Kabuto-Kracher','Rhinos royaux déchaînés','Campeonato Rinoceronte real','Kabuto reali fuori controllo','','Deliver 10 Royal Rhinos','Liefere 10 Königliche Kabutos ab.','Livrer 10 rhinos royaux','Entrega 10 rinocerontes reales','Consegna 10 kabuto reali','','Village','0','LR',3,0,3,7,50,200,1800,80,'Deliver 5 Auristone Pieces','5 Auristein-Stücke abliefern','Livrer 5 morceaux d''auripierre','Entrega 5 piezas aurirrocas','Consegna 5 pezzi d''auripietra','','I''ve had enough of being on the losing side of the Royal Rhino Royale! That''s all going to change this year, and that''s where you come in! Go out and get me ten. If I win, we can split the prize.','Ich will einfach nicht mehr beim Königlichen Kabuto- Spiel verlieren! Dieses Jahr wird alles anders, und du wirst mir dabei helfen! Geh und hol mir zehn, und wenn ich gewinne, teilen wir den Preis.','J''en ai assez de toujours perdre le concours des rhinos royaux. Mais ça va changer cette année, et grâce à vous. Rapportez-en dix, et si je gagne, on se partagera la récompense.','Ya me he cansado de perder siempre en la batalla campal de Rinocerontes reales. Este año eso va a cambiar, gracias a ti. Consígueme 10 y, si gano, dividimos el premio.','Non ne posso più di perdere tutti gli incontri tra kabuto reali! Quest''anno la musica cambierà, ma ho bisogno del tuo aiuto! Procuramene dieci, e se vinco dividiamo il premio!','',1200,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (343,'Marshlands Accounting','Buchhaltung: Marschland','À point nommé : Marais','Objetivo: Ciénaga','Punti nella palude','','Earn 1000 Wycademy Points','Sammle 1000 Wykademie-Punkte.','Obtenir 1000 points Wycadémie','Gana 1.000 puntos Wycademia','Ottieni 1000 punti Wyccademia','','Village','0','LR',3,0,3,7,50,300,2100,150,'Hunt a Yian Kut-Ku','Einen Yian Kut-Ku erjagen','Chasser 1 Yian Kut-Ku','Caza un Yian Kut-Ku','Caccia uno Yian Kut-Ku','','Young Hunter, your hands seem idle. Would you care to busy yourself collecting points for me in the Marsh- lands? Any items are fine, as long as you meet the quota. Take care, now.','Dir scheint irgendwie total langweilig zu sein. Magst du vielleicht für mich im Marschland Punkte sammeln gehen? Die Objekte sind ganz egal, solange die Punktzahl stimmt. Und sei vorsichtig!','Vous tombez bien ! Pourriez-vous livrer des objets bonus aux Marais ? Peu importe les objets tant que vous atteignez le quota de points !','Se te ve ocioso, joven. ¿Querrías dedicarte a obtener puntos para mí en la Ciénaga? Cualquier objeto me sirve, mientras alcances el objetivo. Ve con cuidado.','Ehi, giovine! Per caso ti stai annoiando? Se non hai altro da fare, perché non vai a caccia di punti nella palude? Ogni oggetto conta, l''importante è raggiungere la quota!','',1800,140);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (401,'Harvest Tour: Volcano','Erntetour: Vulkan','Visite : Volcan','Recolección: Volcán','Tour: Vulcano','','Survive until time expires or deliver a Paw Pass Ticket','Überlebe bis Zeitende oder gib einen Tatzenpass ab.','Temps expiré ou livrer un ticket Passechat','Aguanta hasta que acabe el tiempo o da un ticket zarpa','Sopravvivi fino allo scadere o consegna buono pass-zampa','','Village','0','LR',3,0,4,8,50,0,12,0,'None','–','Rien','Ninguno','Nessuno','','Interested in gathering? Then look no further than this Quest, brave Hunter. Gather as much as you like, then deliver a Paw Pass Ticket from the Supply Box to return at any time.','Magst du Sammeln? Dann habe ich genau die richtige Aufgabe für dich, mutiger Jäger! Nimm, was immer du magst. Wenn du fertig bist, gib einfach den Tatzenpass ab, den du in der Vorratsbox findest.','La collecte, ça vous dit ? J''ai justement la quête qu''il vous faut. Récoltez comme bon vous semble. Quand vous en avez assez, livrez un ticket Passechat de la boîte à fournitures.','¿Quieres ir de recogida? Tengo una misión ideal para ti, cazador. Recoge todo lo que quieras, entrega un ticket zarpa de la caja de suministros, y listos. ¿Vale?','La raccolta t''appassiona? Allora ho la missione per te! Raccogli come se non ci fosse un domani, consegna poi un buono pass-zampa nella cassa quando ne avrai abbastanza. D''accordo?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (402,'The Nocturnal Enchanter','Der Verzauberer der Nacht','L''enchanteur nocturne','El encantador nocturno','L''incantatore notturno','','Hunt a Malfestio','Erjage einen Malfestio.','Chasser 1 Malfestio','Caza un Malfestio','Caccia un Malfestio','','Village','2','LR',0,0,4,101,50,300,3000,200,'Wound Malfestio''s head','Malfestio-Kopf verletzen','Blesser la tête du Malfestio','Hiere Malfestio en la cabeza','Ferisci la testa del Malfestio','','The survey team ran into a Malfestio in the Jurassic Frontier. Before they knew it, everyone had either passed out or gotten so confused they don''t remember a thing. Be on your guard, Hunter!','Ein Wykademie-Untersuchungs- team hat im Jura-Grenzland einen Malfestio gefunden. Im Handumdrehen waren alle ohnmächtig oder verwirrt, niemand erinnert sich mehr an irgendwas. Sei also auf der Hut!','Les chercheurs de la Wycadémie qui sont tombés sur un Malfestio près de la Frontière jurassique ont tout oublié de cette rencontre. Étrange... Allez tirer cette affaire au clair.','El equipo de la Wycademia se encontró a un Malfestio en la Fronda Arcaica, y al poco tiempo estaban todos desmayados o tan confundidos que no saben nada. ¡Ve, pero cuidado!','La squadra di ricerca della Wyccademia si è imbattuta in un Malfestio nella frontiera giurassica. Sono tutti così confusi che non ricordano un accidenti dell''accaduto. Vai a dare un''occhiata!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (403,'Wyvern of the Wind','Wyvern des Windes','Wyverne du vent','Wyvern del viento','Wyvern del vento','','Hunt a Nargacuga','Erjage einen Nargacuga.','Chasser 1 Nargacuga','Caza un Nargacuga','Caccia un Nargacuga','','Village','0','LR',0,0,4,1,50,400,3600,250,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','The Nargacuga out on the Jurassic Frontier is scarily fast. Blink and you''ll miss it. But, Hunter, it''s gotta go! There''s nothing more important right now than hunting it, so get to it!','Die Nargacuga im Jura- Grenzland sind schrecklich schnell. Einmal blinzeln und alles verpasst. Aber, Jäger, jemand muss sich drum kümmern! Nichts ist wichtiger als die Jagd, also, los geht''s!','Le Nargacuga de la Frontière jurassique est si rapide qu''il suffit de cligner des yeux pour le perdre. Il faut nous en débarrasser, alors au travail ! Et vite !','El Nargacuga de la Fronda Arcaica es terriblemente rápido. Desaparece en un abrir y cerrar de ojos. No hay nada más importante que cazarlo, ¡así que manos a la obra!','Il Nargacuga apparso nella frontiera giurassica è paurosamente veloce. Si volatilizza in un batter d''occhio! Cacciarlo ha la priorità assoluta, quindi datti da fare!','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (404,'Jurassic Treasure: The Bug','Jura-Schätze: Der Käfer','Trésor jurassique : Insecte','Tesoro jurásico: bicho','Antico tesoro: Ediz. insetto','','Hunt a Volvidon','Erjage einen Volvidon.','Chasser 1 Volvidon','Caza un Volvidon','Caccia un Volvidon','','Village','0','LR',0,0,4,1,50,300,2400,120,'Slay 15 Bnahabra','15 Bnahabra erlegen','Tuer 15 Bnahabra','Abate a 15 Bnahabra','Uccidi 15 Bnahabra','','Gettin'' treasure trembles fer this Blossom Cricket I picked up in the Jurassic Frontier? Sorry to say, but if ye want it, ye gotta go work fer it! Earn it by ''untin'' me a Volvidon!','Kriegste Schatzzittern im Anblick der Blütengrille, die ich aus dem Jura- Grenzland mitgebracht habe? Gibt''s aber nicht umsonst! Jag mir einen Volvidon und verdien sie dir!','Vous voulez le grillon fleuri que j''ai capturé sur la Frontière jurassique ? Désolé de vous décevoir, mais vous allez devoir le mériter en allant chasser un Volvidon !','¿Quieres este grillo flor que he encontrado en la Fronda Arcaica? Lo siento, pero tendrás que ganártelo. Ve a cazar un Volvidon y te ganarás una recompensa.','Vorresti il grillo fiore che ho raccolto nella frontiera giurassica? Mi dispiace, ma dovrai guadagnartelo! Caccia un Volvidon e dimostrami di meritarlo!','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (405,'Unlock the Secret to...','Finde das Geheimnis von...','Percer le secret de...','Descubre el secreto de...','Segreto rivelato','','Deliver 2 Eternal Fossils','Liefere 2 Ewigkeitsfossile ab.','Livrer 2 fossiles éternels','Entrega 2 fósiles eternos','Consegna 2 fossili eterni','','Village','0','LR',3,0,4,101,50,400,3600,180,'Sever Rathian''s tail','Rathian-Schwanz abtrennen','Couper la queue de la Rathian','Corta la cola de la Rathian','Mozza la coda della Rathian','','The Eternal Fossil is a super rare type of fossil said to be the key to unlocking... Yes, blah blah blah...I know. You don''t care. But I do! I need to know if what they say is true! My research depends on it!','Das Ewigkeitsfossil ist ein extrem rares Fossil, von dem gesagt wird, dass es... Ja, ja, bla bla bla. Ist schon klar, dass dich das nicht sonderlich interessiert. Aber ich muss wissen, ob es stimmt!','Il paraît que les rarissimes fossiles éternels seraient la clé de... Oui, je sais, vous vous en fichez. Mais pas moi. Je dois savoir si ce qu''on dit est vrai ! Mes recherches en dépendent !','El fósil eterno es un fósil rarísimo del cual se dice que es clave para... Bla, bla, bla, no te importa. Pero a mí sí. ¡Tengo que saber si es cierto! ¡Es vital para mis estudios!','Il fossile eterno potrebbe essere la vera chiave per sbloccare... Bla bla bla. Oh, lo so, non t''interessa. Ma a me sì! Devo sapere se quello che si dice è vero! Ne va della mia ricerca!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (406,'A Unique Cuppa','Ein einmaliges Mahl','Une tasse pas ordinaire','Un té único','Il tè delle cinque','','Deliver 10 Unique Ferns or a Paw Pass Ticket','Liefere 10 Einmalige Farne oder gib einen Tatzenpass ab.','Livrer 10 fougères uniques ou 1 ticket Passechat','Entrega 10 helechos únicos o un ticket zarpa.','Consegna 10 felci speciali o consegna buono pass-zampa','','Village','0','LR',3,0,4,101,30,200,1800,150,'Wound Nargacuga''s wing','Nargacuga-Flügel verletzen','Blesser l''aile du Nargacuga','Hiere Nargacuga en el ala','Ferisci l''ala del Nargacuga','','Hi hi, Hunter-person! Have you ever heard of Bherna''s Fern Tea? It''s a tried and true recipe, but I think I can improve it with some Unique Ferns! That''s sure to make it a hit!','Hey, du, Jagdmensch! Hast du schon den berühmten Bherna- Farn-Tee probiert? Ein uraltes Rezept, aber ich kann es noch besser machen. Ich brauche dafür nur Einmaligen Farn. Erfolg ist damit garantiert!','Dites, vous connaissez le célèbre thé aux fougères de Bherna ? C''est une boisson fameuse, mais je pense pouvoir l''améliorer avec des fougères uniques ! Vous voulez bien m''en chercher ?','¡Eh, cazador! ¿Has oído hablar del famoso té de helecho de Bherna? Es una receta tradicional, pero puedo mejorarla con helechos únicos y ser famoso, je, je, je.','Ehilà! Hai mai sentito parlare del noto tè di felce di Bherna? Sì, è una ricetta collaudata, ma credo di poterla migliorare usando delle felci speciali! Così diventerà più famoso!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (407,'Hunt-a-thon: Gendrome','Jagd-Marathon: Gendrome','Chassathon : Gendrome','Cazamaratón: Gendrome','Cacciatona: Gendrome','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Gendrome oder gib einen Tatzenpass ab.','Chasser 2 Gendrome ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Gendrome o consegna buono pass-zampa','','Village','0','LR',4,0,4,5,50,300,2400,160,'None','–','Rien','Ninguno','Nessuno','','A merchant friend of mine got jumped by a bunch of Gendrome lurkin'' out in the desert. She''s not too worse for wear, but if y''ask me, we should make sure this sort of happenin'' is a one-time-only happenin''.','Eine Freundin von mir wurde in der Wüste von einigen Gendrome angegriffen. Sie ist nicht schlimm verletzt, aber wenn du mich fragst, sollten wir sicherstellen, dass so was nicht noch einmal passiert.','Une amie marchande a été attaquée dans le désert par une meute de Gendrome. Elle s''en est tirée, mais nous devrions faire en sorte que ce genre d''incident ne se reproduise plus.','Una mercader amiga mía fue asaltada por una manada de Gendrome en el desierto. No le hicieron daño, pero en mi opinión deberíamos asegurarnos de que esto no vuelva a pasar nunca.','Una mia amica mercante è stata assalita da un branco di Gendrome nel deserto. Lei non ne ha fatto un dramma, ma dovremmo assicurarci che episodi del genere non si ripetano.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (408,'Mining: The Final Frontier','Abbauen: Das letzte Abenteuer','Mineurs de l''extrême','Minería: la última frontera','Miniere, ultima frontiera','','Deliver 2 Meteor Chunks','Liefere 2 Meteoriten-Brocken ab.','Livrer 2 morceaux de météore','Entrega 2 frag. de meteorito','Consegna 2 blocchi meteoritici','','Village','0','LR',3,0,4,5,50,300,2700,180,'Hunt a Volvidon','Einen Volvidon erjagen','Chasser 1 Volvidon','Caza un Volvidon','Caccia un Volvidon','','Ore that falls from the sky is really rare. While we don''t know how to refine it yet, it''s still a valuable resource. As a forger, I''m keen to learn all that I can. Think you''re able to get some for me?','Erz fällt nur sehr selten vom Himmel. Wir wissen auch noch nicht, wie wir es verarbeiten, aber es ist trotzdem wertvoll. Als Schmied muss ich darüber lernen, so viel ich kann. Kannst du mir was davon bringen?','Bien qu''on ne sache pas encore le raffiner, le minerai céleste est aussi rare que précieux. En tant que forgeron, je suis très curieux à son sujet. Vous pourriez m''en trouver ?','Las rocas caídas del cielo son muy raras. Todavía no sabemos refinarlas, pero son un valioso recurso. Soy herrero y quiero aprender. ¿Podrías traerme unas cuantas?','I minerali che cadono dal cielo sono molto rari. Anche se non sappiamo come lavorarli, sono una risorsa preziosa. Mi piacerebbe saperne di più. Credi che potresti procurarmeli?','',2400,120);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (409,'Wrath of the Rathian','Der Zorn der Rathian','Le courroux de la Rathian','Ira de la Rathian','L''ira della Rathian','','Hunt a Rathian','Erjage eine Rathian.','Chasser 1 Rathian','Caza una Rathian','Caccia una Rathian','','Village','1','LR',0,0,4,2,50,400,3300,220,'Wound Rathian''s head','Rathian-Kopf verletzen','Blesser la tête de la Rathian','Hiere Rathian en la cabeza','Ferisci la testa della Rathian','','Dang Rathian! Ambushed my caravan and tore my business a new one! But I ain''t the gettin'' mad type, I''m the gettin'' even type! Time for some ice cold revenge, and I want you to dish it out!','Verdammte Rathian! Lauerte tatsächlich meiner Karawane auf und vermasselt mir das Geschäft! Aber da hat sie sich verrechnet! Ich will Rache und du sollst sie in meinem Namen ausführen!','Saleté de Rathian ! Elle a attaqué ma caravane et saccagé ma marchandise ! Au lieu de m''énerver, je vais lui rendre la pareille. La vengeance est un plat qui se mange froid !','¡Rathian Maldita! Nos tendió una emboscada y destrozó mi negocio. Pero voy a enfadarme, ¡voy a vengarme! La venganza es un plato que se sirve frío, y tú lo cocinarás.','Dannata Rathian! Ha sorpreso la mia carovana e mandato in rovina i miei affari! Ma io non sono il genere di leader che s''infuria... Potresti servirle una bella vendetta "fredda" per me?','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (410,'Noise Violation','Ruhestörung','Nuisance sonore','Serenata monstruosa','Caccia al Najarala','','Hunt a Najarala','Erjage einen Najarala.','Chasser 1 Najarala','Caza un Najarala','Caccia un Najarala','','Village','0','LR',0,0,4,2,50,400,3600,240,'Wound Najarala''s tail','Najarala-Schwanz verletzen','Briser la queue du Najarala','Hiere Najarala en la cola','Ferisci la coda del Najarala','','The Najarala around the village is making some pawful sounds! Does it think it''s some kind of mewsician? Hunter, make it shut up before it drives me up the wall!','Der Najarala beim Dorf macht ein Theater, dass mir das Fell zu Berge steht. Glaubt der, er sei ein Miausiker? Bitte, bring ihn schnell zum Schweigen, bevor ich noch die Wände hochgehe!','Le Najarala qui rôde autour du village fait des bruits horribles. Est-ce qu''il se prend pour un miaousicien ? Je vous en prie, faites-le taire avant que j''en perde mes moustaches !','El Najarala que se pasea cerca del pueblo hace unos ruidos horrimiables. ¿Se creerá que es música? Cazador, hazlo callar antes de que me vuelva loco.','Il Najarala nei pressi dal villaggio prrroduce dei rumori orribili! Pensa di essere un miausicista?! Ti prrrego, zittiscilo prima che mi faccia ammiaottire.','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (411,'Hunt-a-thon: Yian Kut-Ku','Jagd-Marathon: Yian Kut-Ku','Chassathon : Yian Kut-Ku','Cazamaratón: Yian Kut-Ku','Cacciatona: Yian Kut-Ku','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Yian Kut-Ku oder gib einen Tatzenpass ab.','Chasser 2 Yian Kut-Ku ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Yian Kut-Ku o consegna buono pass-zampa','','Village','0','LR',4,0,4,102,50,400,3300,250,'None','–','Rien','Ninguno','Nessuno','','Ugh, will those Yian Kut-Ku ever shut up!? They''re running around screaming all day, with those stupid faces and those big ears... Someone please hunt them down so I can finally get some sleep!','Argh, geben diese Yian Kut-Ku denn nie Ruhe? Sie rennen und schreien ständig, das scheint schon fast wie Absicht! Mir egal, wie, aber bitte jage so viele von ihnen wie nötig, damit hier Ruhe herrscht.','Grrr, ces Yian Kut-Ku vont-ils enfin la boucler ? J''ai l''impression qu''ils font exprès de courir en hurlant. Débrouillez-vous, mais tuez-en assez pour ramener le calme !','Argh, ¿cuándo se callarán esos Yian Kut-Ku? Corren y gritan tanto que casi es como si lo hicieran adrede. No me importa cómo, pero caza todos los que puedas y restaura la paz.','Argh! Perché gli Yian Kut-Ku non stanno mai zitti? Urlano al punto che inizio a credere che lo facciano di proposito per darmi fastidio! Cacciane più che puoi e riporta la pace.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (412,'Hunt-a-thon: Velocidrome','Jagd-Marathon: Velocidrome','Chassathon : Velocidrome','Cazamaratón: Velocidrome','Cacciatona: Velocidrome','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Velocidrome oder gib einen Tatzenpass ab.','Chasser 2 Velocidrome ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Velocidrome o consegna buono pass-zampa','','Village','0','LR',4,0,4,2,50,200,1800,130,'None','–','Rien','Ninguno','Nessuno','','Nyaaargh! My pointy little ears hurt from the yowling of those Velocidrome! I pounced on one, but more just followed! I dunno how many there are, but save me from their apawling screeches!','Mirrrr-jau! Meine spitzen Öhrchen schmerzen von Velo- cidrome-Gekreisch. Ich hab einen erwischt, keine Ahnung, wie viele da noch sind. Bitte rette meine armen Öhrchen vor dem entkätzlichen Lärm!','Aah ! Mes délicates oreilles pointues souffrent des hurlements des Velocidrome. j''ai bien tenté d''en taper un, mais ça n''a fait qu''en attirer d''autres. Pitié, sauvez-moi de ce vacarme !','¡Argh! Me duelen las orejas puntiagudas de tanto oír miaullidos de Velocidrome. Golpeé a uno, pero vinieron más. No sé cuántos hay, pero sálvame de sus miaullidos horribles.','Miaaao! Gli ululati dei Velocidrome stanno uccidendo le mie povere orecchie a punta! Ne ho aggredito uno, ma ne sono sbucati altri! Occupatene prrrsonalmente e salvami da questo strazio!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (413,'The New Tenant','Der neue Nachbar','Le roi des montagnes','El rey de las montañas','Il re dei monti','','Hunt a Blangonga','Erjage einen Blangonga.','Chasser 1 Blangonga','Caza un Blangonga','Caccia il Blangonga','','Village','0','LR',0,0,4,3,50,400,3300,240,'Slay 8 Blangos','8 Blangos erlegen','Tuer 8 Blango','Abate a 8 Blango','Uccidi 8 Blango','','A Blangonga has taken up residence in the Arctic Ridge, which spells trouble for us being able to gather firewood. Hurry and take that ugly ape out before we freeze to death!','Ein Blangonga hat sich im Schneegebirge niederge- lassen. Das behindert uns beim Holzsammeln. Beeil dich und erlege diesen ekligen Affen, bevor wir uns zu Tode frieren!','Un Blangonga s''est installé sur la Crête arctique, ce qui va nous compliquer la collecte de bois. Alors, dépêchez-vous d''éliminer ce singe hideux avant que nous ne mourions de froid !','Ha venido un Blangonga a las Cimas Árticas, lo cual nos dará problemas para ir a por leña. Date prisa y echa a ese mono feote antes de que muramos congelados.','Un Blangonga si è stabilito sul monte artico, e questo rende complicato raccogliere la legna per il fuoco. Caccia via quello scimmione, prima che congeliamo a morte!','',1200,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (414,'Lagombi Lair','Lagombi-Lager','Tanière de Lagombi','Guarida de Lagombi','Tana dei Lagombi','','Hunt 2 Lagombi','Erjage 2 Lagombi.','Chasser 2 Lagombi','Caza 2 Lagombi','Caccia 2 Lagombi','','Village','0','LR',0,0,4,103,50,400,3300,160,'Deliver 1 Beast Tear','1 Bestienträne abliefern','Livrer 1 larme de bête','Entrega 1 lágrima de bestia','Consegna una lacrima belva','','I''m reporting live from the Arctic Ridge where two Lagombi are making their way across the frozen landscape. I could stand here and watch these graceful creatures all day! ...Wait, are they coming this way!? RUN!','Ich berichte live aus dem Schneegebirge, wo sich zwei Lagombi ihren Weg durch die Eislandschaft mit der Eleganz von Eisballerinen bahnen. Ich könnte ihnen ewig zusehen. Oh, kommen sie her? WEG!','Je suis en direct de la toundra, où deux Lagombi traversent le paysage gelé comme des ballerines. Je passerais la journée à les regarder. Attention, ils arrivent ! FUYEZ !','Me encuentro en las Cimas Árticas, donde dos Lagombi se abren paso a través del gélido paisaje, elegantes como bailarines sobre hielo. ¡Me pasaría el día viéndolos! ... Esperad, ¿vienen? ¡Corred!','In diretta dal monte artico, due Lagombi avanzano con la grazia di pattinatrici sul ghiaccio. Potrei restare a guardare lo spettacolo per tutto il giorno! Ma... cosa? Vengono verso di me?!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (415,'Hungry Eyes','Hungrige Augen','Les yeux voraces','Ojos hambrientos','Occhi di fuoco','','Hunt a Nargacuga','Erjage einen Nargacuga.','Chasser 1 Nargacuga','Caza un Nargacuga','Caccia un Nargacuga','','Village','1','LR',0,0,4,104,50,400,3600,250,'Wound Nargacuga''s wing','Nargacuga-Flügel verletzen','Blesser l''aile du Nargacuga','Hiere Nargacuga en el ala','Ferisci l''ala del Nargacuga','','Night fishing is pure bliss, I tell ya. It''s cool...quiet. You can see the stars reflected in the water. Last night, I saw two red shooting stars streak overhead. Next thing I know, I''m bruised and battered.','Nachtfischen ist das reinste Vergnügen. Diese Ruhe. Die Sterne spiegeln sich im Wasser. Beim letzten Mal sah ich zwei rote Sternschnuppen. Dann weiß ich nur noch, dass ich verletzt am Boden lag...','J''adore pêcher la nuit. Il fait frais, tout est calme. Les étoiles se reflètent dans l''eau. La dernière fois, j''ai vu deux étoiles rouges, et je me suis retrouvé couvert de bleus.','Pescar de noche es una delicia, de verdad. Relajado. Tranquilo. Ves el reflejo de las estrellas en el agua. Hace poco vi pasar dos estrellas fugaces rojas. Acabé herido. ¿Una delicia?','Pescare di notte è bello! L''aria è fresca. Le stelle luccicano sull''acqua... Ma ieri due stelle cadenti rosse sono passate sopra la mia testa. Poi... quando ho ripreso i sensi, ero a pezzi!','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (416,'Royal Pains','Königliche Beschwerden','Douleurs royales','Dolores reales','Dolori reali','','Hunt 2 Royal Ludroth','Erjage 2 Königs-Ludrothe.','Chasser 2 Ludroth royaux','Caza 2 Ludroth Reales','Caccia 2 Ludroth reali','','Village','0','LR',0,0,4,4,50,500,4500,290,'Sever a Royal Ludroth''s tail','Königs-Ludroth-Schwanz abtrennen','Couper la queue du Ludroth royal','Corta la cola del Lud. Real','Mozza la coda del Ludroth reale','','You came at just the right time, Hunter! I''ve got two Royal Ludroth I''d love to see gone from the Misty Peaks. Can''t look for Super Sprouts with those spongy varmints sprayin'' me down!','Du kommst genau richtig, Jäger! Wir haben hier zwei Königs-Ludrothe bei den Nebelgipfeln, die dringend weg müssen. Ich komme nicht an die Supersprossen, solange die noch da sind!','Vous ne pouviez pas mieux tomber ! Deux Ludroth royaux m''empêchent de récolter des super pousses dans les Pics brumeux. Faites-moi disparaître cette vermine spongieuse !','Has venido en el momento justo, cazador. Hay dos Ludroth Reales a los que me gustaría ver fuera de los Picos Nublados. ¡Así no puedo ir a por brotes imperiales!','Sei al posto giusto nel momento giusto! Quei due Ludroth reali sulle cime nebbiose devono sparire! Non posso cercare germogli+ con quelle bestiacce che continuano a spruzzarmi!','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (417,'Peddling Practice','Hausierertraining','Apprentissage du négoce','Traficando un poco','Mercante in fiera','','Deliver 10 Balmstone Pieces','Liefere 10 Balsamstein-Stücke ab.','Livrer 10 morceaux d''odoripierre','Entrega 10 piezas ónix','Consegna 10 pezzi di balsamite','','Village','0','LR',3,0,4,104,50,300,2100,120,'None','–','Rien','Ninguno','Nessuno','','Heya, Hunter! Can we play merchant for a bit!? Um, let''s thee... I want you to dee-liver... Yeah! Deliver some Balm-thtone Pie-thes to me! If you do, I''ll give ya thomethin'' cool I found!','Hi, Jäger, wollen wir etwas "Kaufmann" schpielen? Hm, alscho... Du bischt jetzt der "Liefer-fant"! Genau! Liefer mir, äh ... einige Balscham-Schtück- Schteine! Danach bin ich dann dran und liefere dir was Cooles!','Vous voulez bien jouer à la marchande ? Je veux que vous m''apportiez des "moreseau d''audoripière" ! En échange, je vous donnerai un truc génial que j''ai trouvé.','¡Oye, cazador! ¿Quierez jugar un rato a mercaderez? Veamoz... Quiero que me traigaz unaz cuantaz piezaz ónix. Hazlo y te daré una coza guay que he encontrado.','Ehi! Che ne dishi di giocare al "mercante"? Uhm... Che ne dishi di una consegna? Scì! Consegna dei pezzi di balsamite per me! Se lo farai, ti donerò un oggetto sciuperfigo che ho trovato!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (418,'Island Menace','Inselschrecken','Menace insulaire','Amenaza en la isla','Malfestio molesto','','Capture a Malfestio','Fange einen Malfestio.','Capturer 1 Malfestio','Captura un Malfestio','Cattura un Malfestio','','Village','0','LR',2,0,4,6,50,500,4500,280,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Some kids came up to me and said they spotted something real odd-like on the Deserted Island. Could be one of those Malfestio. If that''s the case, capture that hootin'' hooligan before it hurts someone.','Ein paar von den Kindern haben mir von seltsamen Vorkommnissen auf der Ödnis- Insel erzählt. Könnte ein Mal- festio sein. Wenn das so ist, schnapp dir diesen heulenden Hooligan, ehe er jemand verletzt.','Des gamins sont venus me dire qu''ils ont repéré une créature bizarre sur l''Île déserte. C''est peut-être un Malfestio. Si oui, capturez-le vite avant qu''il n''arrive malheur.','Unos chicos vinieron a decirme que habían visto algo muy raro en la Isla Desierta. Podría ser un Malfestio. Si es así, captura a ese aullador asocial cuanto antes.','Alcuni bimbi hanno avvistato una creatura molto strana sull''isola deserta. Potrebbe trattarsi di un Malfestio. Forse dovresti catturare quel mascalzone prima che ferisca qualcuno.','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (419,'Deviled Legs','Teufelsschenkel','Cuisses sauce à la diable','Ancas asadas con picante','Zampe da intenditori','','Hunt 2 Tetsucabras','Erjage 2 Tetsucabras.','Chasser 2 Tetsucabra','Caza 2 Tetsucabra','Caccia 2 Tetsucabra','','Village','0','LR',0,0,4,6,50,500,4500,290,'Deliver 1 Toad Tear','1 Krötenträne abliefern','Livrer 1 larme de crapaud','Entrega 1 lágrima de sapo','Consegna una lacrima di rospo','','I''ve heard stories of frog legs bein'' an in-demand delicacy overseas. Imagine the feast we could cook up with those two Tetsucabras over on the Deserted Island. Spring to it Hunter, our beast feast awaits!','Ich habe Geschichten von Froschschenkeln gehört, die wohl die Delikatesse schlecht- hin sein sollen. Stell dir vor, wenn wir statt Fröschen die Tetsucabras von der Ödnis-Insel nehmen würden! Mach das mal!','Il paraît que les cuisses de grenouilles sont très prisées dans certaines contrées. Imaginez le festin que nous ferions avec les 2 Tetsucabra de l''Île déserte. À vous de jouer !','He oído decir que las ancas de rana son una delicia con mucha demanda en otros países. Imagínate el festín que supondrían los dos Tetsucabra de la Isla Desierta. ¡A por ellos!','Le zampe di rana sono una prelibatezza oltreoceano. Che banchetti potremmo allestire con quei due Tetsucabra sull''isola deserta! Mi lecco i baffi al solo pensiero!','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (420,'Hunt-a-thon: Daimyo Hermitaur','Jagd-Marathon: D. Hermitaur','Chassathon : Hermitaur D','Cazamaratón: D. Hermitaur','Cacciatona: D.Hermitaur','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Daimyo Hermitauren oder gib einen Tatzenpass ab.','Chasser 2 Hermitaur Daimyo ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 D.Hermitaur o consegna un buono pass-zampa','','Village','0','LR',4,0,4,6,50,500,4500,320,'None','–','Rien','Ninguno','Nessuno','','Goodness gravy! Whitescruff, bless his heart, has set off to drive away some Daimyo Hermitaurs that have popped up lately. I''m really worried about him. Be a doll and help him drive them away.','Oh mein Grundgütiger! Weißkragen, gesegnet sei er, hat sich aufgemacht, die Daimyo Hermitauren zu ver- treiben. Ich mache mir echt Sorgen um ihn. Sei doch bitte so gut und hilf ihm dabei, ja?','Bonté divine ! Blandin, qu''il soit béni, est parti chasser des Hermitaur Daimyo. Je me fais du souci pour lui. Auriez-vous l''amabilité d''aller l''aider ?','¡Madre mía! El Gallinita blanco, bendito sea, ha salido a echar a unos Daimyo Hermitaur que han aparecido últimamente. Me tiene preocupado. Ve a ayudarle, por favor.','Oh, santi sughi! Palla di Neve s''è messo in testa di cacciare quei Daimyo Hermitaur apparsi di recente. Sono preoccupato! Ti prego, aiutalo prima che sia troppo tardi.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (421,'Marshlands Mission','Herrscher des Marschlandes','Le roi incontesté du marais','El gran rey de la Ciénaga','Il signore della palude','','Hunt a Shogun Ceanataur','Erjage einen Shogun Ceanataur.','Chasser 1 Ceanataur Shogun','Caza un Shogun Ceanataur','Caccia uno Shogun Ceanataur','','Village','0','LR',0,0,4,7,50,400,3300,240,'Slay 8 Ceanataurs','8 Ceanatauren erlegen','Tuer 8 Ceanataur','Abate a 8 Ceanataur','Uccidi 8 Ceanataur','','New orders, priority one: Protect the villagers and increase our deployment options. We can''t do either until you hunt that Shogun Ceanataur. This mission is classified, understood?','Neue Befehle mit höchster Priorität: Wir müssen das Dorf beschützen und die Einsatz- möglichkeiten verbessern. Dafür muss diese Shogun Ceanataur verschwinden. Das ist streng geheim, verstanden?','Nos nouveaux ordres sont clairs : protéger les villageois et repérer des sites de déploiement. Mais vous devez d''abord chasser ce Ceanataur Shogun. Mission top secrète !','Nuevas órdenes. Prioridad absoluta: proteger a los aldeanos y mejorar nuestras opciones de despliegue. Pero no podemos hasta que caces a ese Shogun Ceanataur. Misión secreta.','Nuovi ordini, priorità assoluta: proteggere gli abitanti e incrementare le alternative di schieramento. Ma lo Shogun Ceanataur ci ostacola. La missione è classificata, intesi?','',1500,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (422,'Serpentine Samba','Serpentinen-Samba','Samba serpentine','Samba serpentina','Samba serpentina','','Hunt a Najarala','Erjage einen Najarala.','Chasser 1 Najarala','Caza un Najarala','Caccia un Najarala','','Village','1','LR',0,0,4,7,50,400,3600,240,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','My huntin'' is near as bad as my dancin'', but my little sis? Best moves in the tribe! I wanna make her a costume of serpent scales for the big festival. Could ya help me bag a big snake!?','Jagen und Tanzen sind nichts für mich, aber meine Schwester ist unsere beste Tänzerin! Für das Fest will ich ihr ein Kostüm aus Schlangenschuppen schneidern. Könntest du eine große Schlange dafür erlegen?','Je suis presque aussi nul en chasse qu''en danse, mais ma petite sœur est la star de la tribu ! Je veux lui faire un costume en écailles pour le festival. Vous m''attrapez un gros serpent ?','No sé qué hago peor, cazar o bailar, pero mi hermanita es la bailarina de la tribu. Quiero hacerle un traje de escamas de serpiente para el gran festival. ¿Puedes ir a cazar una serpiente grande?','A ballare faccio pena quasi quanto nella caccia, ma la mia sorellina è la migliore della tribù! Voglio cucirle un costume di scaglie di serpente per il festival. Me ne cacceresti uno?','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (423,'Lurkin'' in the Murk','Schrecken im Stinksumpf','Les marais de l''angoisse','Colas de la Ciénaga brumosa','Racconti della palude','','Hunt 2 Khezu','Erjage 2 Khezu.','Chasser 2 Khezu','Caza 2 Khezu','Caccia 2 Khezu','','Village','1','LR',0,0,4,7,50,600,5100,350,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','True horror lurks within the murky Marshlands... Two airborne wyverns, pallid hides gleaming in the dark skies, bodies sparking with electricity. Beware! They''re right behind you!','Im trüben Marschland lauert der reine Horror! Zwei fliegende Wyvern, mit bleichen Schuppen glitzernd vor dem dunklem Himmel, vor Elektrizität sprühend. Pass bloß auf! Sie sind genau hinter dir!','Les Marais abritent des créatures épouvantables : deux wyvernes volantes saturées d''électricité dont la peau livide étincelle dans le ciel nocturne. Attention ! Elles sont derrière vous !','Un auténtico horror acecha entre la bruma de la Ciénaga. Dos wyverns voladores, piel pálida reluciente, cuerpos chispeantes por la electricidad. ¡Cuidado! ¡Los tienes justo detrás!','Il vero orrore si annida nella torbida palude: due wyvern volanti, la pallida pelle che risplende nel cielo notturno, i corpi scintillanti di elettricità. Occhio! Sono dietro di te!','',600,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (424,'Crustacean Infestation','Ceanatauren-Bande','Une bande de Ceanataur','Banda de Ceanataur','Una banda di Ceanataur','','Slay 10 Ceanataurs','Erlege 10 Ceanatauren.','Tuer 10 Ceanataur','Abate a 10 Ceanataur','Uccidi 10 Ceanataur','','Village','0','LR',1,0,4,7,50,200,1800,120,'Break Shogun''s shell','S.Ceanataur-Panzer brechen','Briser la carapace du Shogun','Rompe caparazón del Ceanataur','Rompi corazza S.Ceanataur','','I was just mindin'' my own business, mining for some goodies, when a Ceanataur pops out of nowhere and breaks my Pickaxe! I would''ve yanked it out of its shell, but more of ''em showed up!','Ich hab hier so vor mich hin gebuddelt und an nichts Böses gedacht, als plötzlich ein Ceanataur meine Spitz- hacke mitgehen lässt! Ich hätte sie ihm ja aus dem Panzer ge- zogen, aber er war nicht allein!','Je creusais tranquillement dans mon coin quand un Ceanataur a surgi de nulle part pour briser ma pioche. Hélas, ses congénères l''ont rejoint avant que je puisse le tirer de sa carapace !','Estaba buscando materiales en la mina tan tranquilo, cuando de repente apareció un Ceanataur de la nada y me rompió el pico. Quería arrancarle el caparazón, pero vinieron más.','Ero alla ricerca di preziosi cimeli, quando un Ceanataur sbuca dal nulla e spezza il mio piccone. L''avrei preso a picconate volentieri, ma ne sono arrivati altri!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (425,'Tumbling Troublemaker','Felsrutsch','Chute de pierres','Desprendimiento de rocas','Caduta massi','','Hunt a Volvidon','Erjage einen Volvidon.','Chasser 1 Volvidon','Caza un Volvidon','Caccia un Volvidon','','Village','0','LR',0,0,4,8,50,300,2400,120,'None','–','Rien','Ninguno','Nessuno','','Holy moly! Have you seen one of those things before? I thought it was a giant boulder tumbling down the mountain, but no, it was a monster comin'' to eat me! I''m not going back there until you get rid of it!','Heiliger Strohsack! Hast du so etwas schon mal gesehen? Ich dachte, ein Fels rollt den Berg hinab, dabei war es ein Monster, das mich fressen wollte! Du musst es beseitigen!','Bon sang ! Vous avez vu ces bestioles ? J''ai cru qu''un gros rocher déboulait de la montagne, mais non ! C''était un monstre venu me dévorer. Je n''y retournerai pas tant que vous ne l''aurez pas tué !','¡Santo cielo! ¿Habías visto jamás algo así? Pensé que era una roca desprendida de la montaña, pero era un monstruo que me quería comer. ¡No pienso volver hasta que te libres de él!','Santo cielo! Avevi mai visto una di quelle belve? Pensavo fosse un macigno, invece era un mostro intenzionato a mangiarmi! Non tornerò lì finché non me l''avrai tolto dai piedi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (426,'Crustacean Infatuation','Verliebt in ein Krustentier','Passion pour les crustacés','Obsesión crustácea','Infatuazione crostacea','','Capture a Shogun Ceanataur','Fange einen Shogun Ceanataur.','Capturer 1 Ceanataur Shogun','Captura un Shogun Ceanataur','Cattura uno Shogun Ceanataur','','Village','1','LR',2,0,4,8,50,600,5100,340,'Deliver 3 Carapaceon Brains','3 Carapaceon-Hirne abliefern','Livrer 3 cerveaux Carapaceon','Entrega 3 sesos de carapaceon','Consegna 3 cervelli carapaceon','','I love Shogun Ceanataurs, don''t you!? Their carapaces have so many fine details, their claws are so long and beautiful... *ahem* I want to, er, study one up close, so could you capture one?','Ich liebe diese Shogun Ceanatauren, du etwa nicht? Die Details ihrer Panzer sind so fein, ihre Klauen so lang und elegant... *Ähm* Ich würde gerne einen von Nahem studieren, hilfst du mir?','J''aime les Ceanataur Shogun. Leurs carapaces sont de vraies œuvres d''art, leurs griffes sont si longues et belles. Vous pourriez en capturer un pour que je puisse l''étudier ?','Me encantan los Shogun Ceanataur, ¿y a ti? Sus corazas son tan intricadas, sus garras largas y bellas. Ejem, me gustaría... Eh... estudiar uno de cerca. ¿Podrías capturarme uno?','Adoro gli Shogun Ceanataur, e tu? Hanno un carapace così elaborato, artigli così lunghi e affilati... *ehm* Vorrei studiarne uno da vicino. Ne cattureresti un esemplare per me?','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (427,'Rocking the Uroktor','Mit dem Uroktor rocken','La raclée des Uroktor','Repaso al Uroktor','L''Uroktor nella roccia','','Slay 10 Uroktor','10 Uroktor erlegen','Tuer 10 Uroktor','Abate a 10 Uroktor','Uccidi 10 Uroktor','','Village','0','LR',1,0,4,8,50,300,2100,120,'None','–','Rien','Ninguno','Nessuno','','These Uroktor are fascinatin'' creatures! They burrow right through volcanic rock. How is that even possible? I wanna find out more about ''em, but they keep spittin'' gobs of fire at me. Help me unlock their mysteries!','Der Uruktor ist schon eine faszinierende Kreatur! Er gräbt sich direkt ins Vulkan- gestein. Wie ist so was über- haupt möglich? Ich würde das gerne erforschen, aber ich brauche Hilfe dabei!','J''aimerais savoir comment ces fascinants Uroktor font pour s''enfouir dans la roche volcanique, mais leurs crachats enflammés m''en empêchent. Aidez-moi à percer leurs mystères !','Los Uroktor son criaturas fascinantes. Agujerean la roca volcánica, ¿cómo es posible? Quiero saber más de ellos, pero no dejan de dispararme bolas de fuego. ¡Ayúdame a conocerlos más!','Gli Uroktor sono creature affascinanti. Scavano nella roccia vulcanica! Com''è possibile? Vorrei studiarli, ma se mi avvicinassi mi arrostirebbero! Aiutami a risolvere il mistero!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (428,'Great Ore Discovery','Die große Erzentdeckung','Du minerai à la pelle','Descubriendo minerales','Grande scoperta di minerali','','Deliver 15 pieces of Coal','Liefere 15 Einheiten Kohle ab.','Livrer 15 morceaux de charbon','Entrega 15 trozos de carbón','Consegna 15 pezzi di carbone','','Village','0','LR',3,0,4,8,50,300,2400,120,'Deliver 1 Powderstone','1 Pulverstein abliefern','Livrer 1 pierre explosive','Entrega 1 piedra pólvora','Consegna una pietra-polvere','','Great news! A new Coal bed has been discovered in the Volcano, and as much as I''d like to get in on that action, that place is Shogun Ceanataur turf. Care to go mining for me, Hunter?','Fantastische Neuigkeiten! Im Vulkan wurde ein neues Kohlenflöz entdeckt. Ich will da gerne auch abbauen, aber es wimmelt dort von Shogun Ceanatauren. Gehst du für mich abbauen?','Bonne nouvelle ! Un gisement de charbon a été découvert dans le Volcan. Hélas, pas moyen pour moi d''en profiter à cause des Ceanataur Shogun. Ça vous dirait de creuser pour mon compte ?','¡Buenas noticias! Hemos encontrado un filón de carbón en el Volcán, pero aunque me encantaría ir a por él, es territorio de Shogun Ceanataur. ¿Vas tú en mi lugar, cazador?','Che notizia! Una nuova vena di carbone è stata scoperta nel vulcano! Purtroppo, quel luogo è territorio dello Shogun Ceanataur. Che ne diresti di raccogliere il carbone al posto mio?','',3000,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (429,'Volcano Accounting','Buchhaltung: Vulkan','À point nommé : Volcan','Objetivo: Volcán','Punti al vulcano','','Earn 1000 Wycademy Points','Sammle 1000 Wykademie-Punkte.','Obtenir 1000 points Wycadémie','Gana 1.000 puntos Wycademia','Ottieni 1000 punti Wyccademia','','Village','0','LR',3,0,4,8,50,300,3000,200,'Hunt an Iodrome','Einen Iodrome erjagen','Chasser 1 Iodrome','Caza un Iodrome','Caccia uno Iodrome','','Young Hunter, your hands seem idle. Would you care to busy yourself collecting points for me in the Volcano? Any items are fine, as long as you meet the quota. Take care, now.','Dir scheint irgendwie total langweilig zu sein. Magst du vielleicht für mich im Vulkan Punkte sammeln gehen? Die Objekte sind ganz egal, solange die Punktzahl stimmt. Und sei vorsichtig!','Vous tombez bien ! Pourriez-vous livrer des objets bonus au Volcan ? Peu importe les objets tant que vous atteignez le quota de points !','Se te ve ocioso, joven. ¿Querrías dedicarte a obtener puntos para mí en el Volcán? Cualquier objeto me sirve, mientras alcances el objetivo. Ve con cuidado.','Ehi, giovine! Per caso ti stai annoiando? Se non hai altro da fare, perché non vai a caccia di punti al vulcano? Ogni oggetto conta, l''importante è raggiungere la quota!','',1800,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (501,'The Dark Age','Das Dunkle Zeitalter','L''âge sombre','La edad oscura','Tempi bui','','Hunt a Yian Garuga','Erjage einen Yian Garuga.','Chasser 1 Yian Garuga','Caza un Yian Garuga','Caccia uno Yian Garuga','','Village','2','LR',0,0,5,1,50,400,3900,250,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','Recently, a Yian Garuga has appeared in the Jurassic Frontier. Research in that area can''t continue until the threat has been dealt with. Good luck, Hunter.','Im Jura-Grenzland wurde ein Yian Garuga gesichtet. Die Forschung in der Gegend kann erst weitergehen, wenn jemand die Gefahr beseitigt hat. Daher beauftrage ich dich jetzt da- mit, das in Ordnung zu bringen.','Un Yian Garuga a été aperçu il y a peu dans la Frontière jurassique. Les recherches qui y sont menées ne continueront qu''une fois la menace écartée. Vous devez régler le problème.','Hace poco apareció un Yian Garuga en la Fronda Arcaica. No podemos retomar las investigaciones hasta que desaparezca la amenaza. Por favor, ocúpate, cazador.','Di recente, uno Yian Garuga è apparso nella frontiera giurassica. Le ricerche nell''area non possono continuare, se la minaccia non sarà eliminata. Devo chiederti di occupartene.','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (502,'Trifecta of Terror','Dämonische Dreifaltigkeit','Tiercé terrorisant','Apuesta triple de terror','Tris di terrore','','Hunt 3 Nibelsnarf','Erjage 3 Nibelsnarf.','Chasser 3 Nibelsnarf','Caza 3 Nibelsnarf','Caccia 3 Nibelsnarf','','Village','0','LR',4,0,5,5,50,600,5700,370,'None','–','Rien','Ninguno','Nessuno','','A trio of Nibelsnarf have been spotted in the Dunes. There''s no telling what kind of trouble this triad is up to. One thing is for sure: You need to tackle those three overgrown tadpoles!','In den Dünen wurde eine Gruppe von drei Nibelsnarf gesichtet. Das kann ganz schön Ärger bedeuten. Aber sicher ist, dass sich irgendjemand um diese drei übergroßen Kaulquappen kümmern muss.','Trois Nibelsnarf ont été repérés dans les Dunes. Allez savoir les dégâts qu''ils risquent de causer. Mais une chose est sûre : vous devez vous occuper de ces trois têtards géants.','Se ha visto a un trío de Nibelsnarf en las Dunas. Vete a saber qué pretenden esos tres. Pero una cosa es segura: tienes que vigilar de cerca a esos sapos gigantescos.','Tre Nibelsnarf sono stati avvistati tra le dune. Puoi immaginare quanti problemi stiano creando. La richiesta viene da sé: occupati di quei dannati girini troppo cresciuti!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (503,'Garuga of the Hills','Klagelied des einsamen Wolfs','Élégie pour un loup solitaire','Elegía por un lobo solitario','Elegia per un vecchio lupo','','Hunt a Yian Garuga','Erjage einen Yian Garuga.','Chasser 1 Yian Garuga','Caza un Yian Garuga','Caccia uno Yian Garuga','','Village','0','LR',0,0,5,102,50,400,3900,250,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','I''ll let you in on a secret of mine: I managed to track down where a Yian Garuga is hanging out. Don''t let anyone else know, but it''s in the Verdant Hills as we speak!','Ich verrate dir jetzt ein Geheimnis: Ich habe herausgefunden, wo ein Yian Garuga abhängt. Sag es nicht weiter, aber er hält sich jetzt gerade in den Grünen Hügeln auf!','Laissez-moi vous confier un secret : j''ai réussi à localiser un Yian Garuga. N''en parlez à personne, mais il se trouve en ce moment même dans les Collines verdoyantes !','Te contaré un secreto: he podido rastrear el paradero de un Yian Garuga. No se lo digas a nadie, pero en este mismo momento se encuentra en el Bosque Rocoso.','Ti confiderò un segreto: sono riuscito a scoprire il luogo in cui s''aggira uno Yian Garuga. In questo momento si trova sui colli verdi, ma non dirlo a nessuno!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (504,'The Thunderclaw Wyvern','Der Donnerklauen-Wyvern','Wyverne Griffe-tonnerre','El rebelde atronador','L''artiglio tonante','','Hunt an Astalos','Erjage einen Astalos.','Chasser 1 Astalos','Caza un Astalos','Caccia un Astalos','','Village','1','LR',0,0,5,2,50,600,5100,320,'Wound Astalos''s crest','Astalos-Kamm verletzen','Blesser la crête de l''Astalos','Hiere Astalos en la cresta','Ferisci la cresta dell''Astalos','','Listen here, kid! Things are lookin'' grim out in the Verdant Hills. There''s a mean, green, electrocutin'' machine out there messin'' things up. I need you to get out there and mess IT up!','Hör gut zu, junger Jäger! In den Grünen Hügeln sieht es düster aus. Da ist eine fiese, grüne Maschine, die alles und jedem Stromschläge ver- passt und alles durcheinander bringt. Erledige das!','Alerte rouge ! La situation dans les Collines verdoyantes est critique ! Une cruelle créature verte électrocute tous ceux qu''elle croise. Je veux que vous vous en chargiez.','Escúchame, joven cazador. Las cosas pintan mal en el Bosque Rocoso. Hay una máquina malvada verde que lo electrocuta todo. ¡Ve y estropea a ese monstruo!','Ascoltami! I colli verdi sono infestati da una malvagia creatura verde e folgorante. Vorrei che gli impartissi una fulminea lezione!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (505,'Fungus Fetch','Fungi finden','Cueillette aux champignons','A por setas','A caccia di funghi','','Deliver 10 Unique Mushrooms or a Paw Pass Ticket','Liefere 10 Einmalige Pilze oder gib einen Tatzenpass ab.','Livrer 10 champignons spéciaux ou 1 ticket Passechat','Entrega 10 setas especiales o un ticket zarpa.','Consegna 10 funghi speciali o consegna buono pass-zampa','','Village','0','LR',3,0,5,102,30,300,2700,180,'Sever Rathian''s tail','Rathian-Schwanz abtrennen','Couper la queue de la Rathian','Corta la cola de la Rathian','Mozza la coda della Rathian','','Thanks to you, business is soaring! So much, in fact, that I can''t keep up with demand! Hurry back to the Hills and gather me some more. But be careful, I heard there''s a Rathian about!','Dank dir, Jäger, läuft das Geschäft wieder großartig! So gut, dass ich kaum nach- komme. Also, geh schnell wieder in die Grünen Hügel und bring Nachschub! Pass aber auf, nicht dass du auch vergiftet wirst!','Les affaires marchent bien et c''est à vous que je le dois ! Je n''arrive même plus à répondre à la demande. Retournez vite dans les Collines verdoyantes pour me réapprovisionner !','Mis negocios van viento en popa gracias a ti, cazador. De hecho, ¡no consigo responder a la demanda! Vuelve al Bosque Rocoso y tráeme más. Pero cuidado, podrías volver envenenado.','Gli affari vanno a gonfie vele per merito tuo, al punto che non riesco a soddisfare la domanda! Raccogli altri funghi sui colli verdi. Ricorda, alcuni sono tornati avvelenati!','',900,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (506,'Two Roars in the Snow','Zweifaches Gebrüll im Schnee','Rugissements dans la neige','Dos rugidos en la nieve','Due ruggiti nella neve','','Hunt 2 Blangongas','Erjage 2 Blangongas.','Chasser 2 Blangonga','Caza 2 Blangonga','Caccia 2 Blangonga','','Village','0','LR',0,0,5,3,50,600,6000,380,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','It all happened so fast... These two Blangongas showed up and just wrecked all I had! I wanna rebuild my life, but there''s no way I''m going up there with those things! Help me get my life back!','Es ging alles so schnell... Die beiden Blangongas kamen und haben alles kaputt gemacht! Ich will nur mein Leben neu ordnen, aber ich kann da nicht wieder hin, solange die noch da sind! Hilf mir bitte!','Deux Blangonga ont détruit en un clin d''œil tout ce que je possédais. Pas question pour moi d''y retourner tant qu''ils y sont encore ! Je vous en prie, aidez-moi à reconstruire ma vie !','Todo ocurrió tan rápido... Aparecieron dos Blangonga y destrozaron todo lo que tenía. Quiero reconstruir mi vida, pero es imposible con ellos por aquí. ¡Ayúdame a recuperarme!','È accaduto tutto così in fretta... Due Blangonga sono comparsi dal nulla e hanno distrutto tutta la mia attrezzatura! Aiutami a disfarmi di quelle creature! Rivoglio la mia vita!','',900,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (507,'Hunt-a-thon: Khezu','Jagd-Marathon: Khezu','Chassathon : Khezu','Cazamaratón: Khezu','Cacciatona: Khezu','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Khezu oder gib einen Tatzenpass ab.','Chasser 2 Khezu ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Khezu o consegna buono pass-zampa','','Village','0','LR',4,0,5,103,50,600,5100,370,'None','–','Rien','Ninguno','Nessuno','','You look like you could go for a little adrenaline in your day! Well, you''re in luck! There''s heaps of monsters with electrifying personalities up in the Arctic Ridge! Have at it!','Du siehst aus, als ob du etwas Adrenalin gebrauchen könntest! Dann ist heute wohl dein Glückstag! Wir haben hier mehr als genug miese Monster im Schneegebirge, tob dich nur aus!','On dirait bien qu''un peu d''adrénaline ne vous ferait pas de mal ! Ça tombe bien, la Crête arctique abrite des tas de monstres, tout ce qu''il y a de plus stimulant. Éclatez-vous !','Parece que te vendría bien un poco de adrenalina. Pues tienes suerte. En las Cimas Árticas hay un montón de monstruos de carácter electrizante. ¡Todos tuyos!','Ti va di movimentare la giornata con una scarica di adrenalina? Beh, che fortuna! Si sono palesati vari mostri elettrici nel paesaggio ghiacciato. Buon divertimento!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (508,'The Unwavering Colossus','Der unnachgiebige Koloss','Le colosse inébranlable','El coloso inquebrantable','L''incrollabile colosso','','Hunt a Gammoth','Erjage einen Gammoth.','Chasser 1 Gammoth','Caza un Gammoth','Caccia un Gammoth','','Village','1','LR',0,0,5,3,50,600,5100,320,'Wound Gammoth''s legs','Gammoth-Beine verletzen','Blesser les pattes du Gammoth','Hiere Gammoth en las patas','Ferisci le zampe del Gammoth','','If we can tame the Gammoth, we''ll have an attraction to make us more famous than Bherna and Yukumo com- bined! You can''t tame monsters? Bah! Well, at least give it a try!','Wenn wir den Gammoth zähmen können, haben wir eine Attraktion, die uns bekannter machen wird, als Bherna und Yukumo zusammen! Man kann Monster nicht zähmen? Pah! Versuche es doch wenigstens!','Si nous pouvions dompter ce Gammoth, nous aurions une attraction qui nous rendrait plus célèbre que Bherna et Yukumo réunis ! Vous êtes chasseur, pas dompteur ? Essayez quand même !','¡Si domamos al Gammoth, tendremos una atracción para hacernos más famosos que Bherna y Yukumo juntas! ¿Que no sabes domar a monstruos? ¡Venga ya, al menos inténtalo!','Oh, povero me! Un Gammoth è su tutte le furie, qualcuno deve fermarlo! Quel mostro sta distruggendo le case e terrificando la popolazione. Ti prego, liberaci da questa colossale catastrofe!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (509,'Lagiacrus Fuss','Immer Ärger mit Lagiacrus','Un souci de Lagiacrus','Problemas con un Lagiacrus','Tutto sul Lagiacrus','','Hunt a Lagiacrus','Erjage einen Lagiacrus.','Chasser 1 Lagiacrus','Caza un Lagiacrus','Caccia un Lagiacrus','','Village','0','LR',0,0,5,104,50,500,4500,300,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','You came at just the right time, Hunter. See, I was on my way back from explorin'' the Misty Peaks when I ran into a dagnabbed Lagiacrus! Here''s hopin'' you make quick work of that varmint.','Du bist genau rechtzeitig gekommen, Jäger. Ich wollte gerade von den Nebelgipfeln nach Hause, als ich in diesen verdiddelten Lagiacrus lief! Ich hoffe doch schwer, dass du den leicht loswirst.','Vous tombez à pic ! J''étais sur le point de rentrer de mon expédition dans les Pics brumeux quand j''ai croisé un satané Lagiacrus ! Je compte sur vous pour vite m''en débarrasser.','Has venido en el momento justo, cazador. Volvía de explorar los Picos Nublados cuando me salió un maldito Lagiacrus. Espero que puedas ocuparte enseguida de esa alimaña.','Giusto in tempo, ti stavo aspettando. Ero di ritorno dalle cime nebbiose quando ho incrociato un maledetto Lagiacrus! Speravo che potessi occuparti di quel mascalzone.','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (510,'The Entrancing Water Dancer','Der bezaubernde Wassertänzer','Danseuse aquatique','Cautivador baile de agua','L''incantevole danzatore','','Hunt a Mizutsune','Erjage einen Mizutsune.','Chasser 1 Mizutsune','Caza un Mizutsune','Caccia un Mizutsune','','Village','1','LR',0,0,5,104,50,600,5100,320,'Wound Mizutsune''s head','Mizutsune-Kopf verletzen','Blesser la tête du Mizutsune','Hiere Mizutsune en la cabeza','Ferisci la testa del Mizutsune','','Heed my words, Hunter: It is said that there is a Mizutsune in the Misty Peaks. You will help rid Yukumo of this slippery devil, and your journey will be a safe one.','Achte auf meine Worte, Jäger, bei den Nebelgipfeln gibt es einen Mizutsune! Hilf Yukumo dabei, diesen schlüpfrigen Teufel loszu- werden, und mögen deine Schritte immer sicher sein!','Écoutez-moi attentivement. On raconte qu''une Mizutsune s''est installée dans les Pics brumeux. Aidez Yukumo à se débarrasser de ce monstre insaisissable. Je vous souhaite de réussir !','Escúchame, cazador. Dicen que hay un Mizutsune en los Picos Nublados. Ayúdanos a librar a Yukumo de este peligro tan resbaladizo. Te deseo un viaje seguro.','Ascoltami! Sembra che un Mizutsune si nasconda tra le cime nebbiose. Aiutaci a eliminare questa grave sventura. E che tu possa tornare per raccontarlo.','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (511,'The Fisherman''s Fiend','Fischers Feind','Le démon du pêcheur','El demonio del pescador','Il nemico del pescatore','','Hunt a Lagiacrus','Erjage einen Lagiacrus.','Chasser 1 Lagiacrus','Caza un Lagiacrus','Caccia un Lagiacrus','','Village','1','LR',0,0,5,6,50,500,4500,300,'Wound Lagiacrus''s head','Lagiacrus-Kopf verletzen','Blesser la tête du Lagiacrus','Hiere Lagiacrus en la cabeza','Ferisci la testa del Lagiacrus','','Yikes, this is horrible! We''ve just heard from our fishermen out in the sea that a Lagiacrus is here in this region! We need you to stop it before it does any serious damage!','Oh Schreck, wie furchtbar! Die Fischer auf dem Meer erzählen, dass ein Lagiacrus in der Nähe ist. Du musst die Bestie aufhalten, bevor jemand ernstlich in Gefahr gerät!','Catastrophe ! Nos pêcheurs en mer viennent de nous informer de la présence d''un Lagiacrus dans la région ! Il faut l''arrêter avant qu''il ne cause des dégâts irréparables !','¡Qué horror! ¡Acabamos de saber por los pescadores que hay un Lagiacrus por aquí cerca! Necesitamos que acabes con él antes de que pueda ocasionar daños graves.','Ah, ma è terribile! I nostri pescatori hanno avvistato un Lagiacrus nella regione! Ho bisogno che lo fermi, prima che causi danni seri!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (512,'Island Heat','Inselfieber','Chaleur tropicale','Calor en la isla','A spasso col Rathalos','','Hunt a Rathalos','Erjage einen Rathalos.','Chasser 1 Rathalos','Caza un Rathalos','Caccia un Rathalos','','Village','0','LR',0,0,5,6,50,500,4500,300,'None','–','Rien','Ninguno','Nessuno','','How''s it hangin'', Hunter dude? The Yukumo Gal hooked me up with a Quest request for you to house that Lame-o-los that''s lurkin'' on the Deserted Island. I''m just gonna chill here and think about these fluffy clouds.','Was geht, Jägerkumpel? Die Kleine aus Yukumo hat mir erzählt, dass du an dem Komplett-Rat(ha)los interessiert bist, der auf der Ödnis-Insel lauert. Ich entspanne hier und schau mir die Wolken an.','Salut, ça farte ? La Demoiselle Yukumo m''a parlé d''un Rathalos qui rôde sur l''Île déserte. Elle dit que ce serait top si vous pouviez le rajouter à vos trophées.','¿Qué tal, cazador? La chica de Yukumo me ha pedido que te diga que hay un Rathalos por la Isla Desierta, y que sería guay, o sea, que te lo cargaras.','Come va la caccia? La dama di Yukumo mi ha chiesto di riferirti che un Rathalos bazzica sull''isola deserta. Dice che sarebbe grandioso se potessi occuparti di quel seccatore!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (513,'Double Vision','Doppeltsehen','Les dangers de voir double','Visión doble','Doppia visione','','Hunt 2 Malfestio','Erjage 2 Malfestio.','Chasser 2 Malfestio','Caza 2 Malfestio','Caccia 2 Malfestio','','Village','0','LR',0,0,5,6,50,600,5400,320,'None','–','Rien','Ninguno','Nessuno','','Here''s an idea: What would happen if you ate the scales of two Malfestio? Would your brain go extra on the fritz or would the second batch cancel the first? Inquiring minds want to know!','Mal so gefragt: Was passiert, wenn man die Schuppen von zwei Malfestio isst? Würde das Gehirn komplett frittiert oder würde die zweite Ladung die erste ausgleichen? Es bringt mich noch um den Verstand!','Que se passe-t-il lorsqu''on consomme les écailles de deux Malfestio ? Le cerveau débloque-t-il encore plus ou la seconde fournée annule-t-elle la première ? Aidez-moi à le savoir !','Tengo una idea: ¿qué pasaría si comieses las escamas de dos Malfestio? ¿Te estropearían aún más el cerebro, o el segundo lote anularía el primero? ¡Quiero saberlo!','Domanda: cosa accadrebbe se mangiassi le scaglie di due Malfestio? Il cervello andrebbe in tilt oppure la seconda annullerebbe gli effetti della prima? La scienza deve sapere!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (514,'Hunt-a-thon: Gypceros','Jagd-Marathon: Gypceros','Gypceros en surnombre','Cazamaratón: Gypceros','Cacciatona: Gypceros','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Gypceros oder gib einen Tatzenpass ab.','Chasser 2 Gypceros ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Gypceros o consegna buono pass-zampa','','Village','0','LR',4,0,5,7,50,500,4200,290,'None','–','Rien','Ninguno','Nessuno','','My trade colleagues are in a bit of a jam after some Gypceros showed up on their route! Get rid of a few of them for me, and I''ll reward you with some grade-A Yukumo Riceflour!','Meine Handelskollegen sind ein wenig Panne, seit ein paar Gypceros auf deren Route aufgetaucht sind! Werde einige von denen los, dann belohne ich dich mit A-Klasse Yukumo-Reismehl!','Mes confrères sont soucieux depuis que des Gypceros sont apparus sur les routes ! Supprimez-les et je vous donnerai de la farine de riz Yukumo de première qualité !','¡Mis socios están en un aprieto desde que unos Gypceros aparecieron en nuestra ruta! ¡Deshazte de algunos para mí, y te daré harina de arroz de Yukumo extra como recompensa!','I miei colleghi sono un po'' in difficoltà dopo aver incontrato dei Gypceros sul loro tragitto. Occupati di essi e ti premierò con farina riso Yukumo!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (515,'Behind Closed Ores','Hinter verschlossenen Loren','Extraction discrète','En minas cerradas','Scavi top-secret','','Slay 10 before time expires or deliver a Paw Pass Ticket','Erlege 10 Bullfangos, bevor die Zeit abläuft. / Liefere einen Tatzenpass.','Tuer 10 Bullfango ou livrer un ticket Passechat','Abate a 10 Bullfango sin agotar el tiempo/entrega un ticket zarpa','Uccidi 10 Bullfango o consegna buono pass-zampa','','Village','0','LR',1,0,5,7,50,300,2700,180,'Hunt a Najarala','Einen Najarala erjagen','Chasser 1 Najarala','Caza un Najarala','Caccia un Najarala','','Psst! Hunter! I need you to take care of something for me. My best mining spot is infested with Bullfango. I need you to un-fest it, if you know what I mean. Thing is, the place is a trade secret, so be discreet!','Psst! Jäger! Du musst da was für mich erledigen. Mein bester Abbauplatz ist von Bullfangos besetzt worden. Kannst du die nicht vielleicht wieder verjagen? Die Sache ist nur, der Platz muss geheim bleiben, Top Secret!','Psst ! Ma mine favorite est infestée de Bullfango et j''ai besoin de vous pour procéder à son... nettoyage. Mais faites preuve de discrétion, car cet endroit doit rester secret.','¡Psst! ¡Cazador! Necesito que hagas algo por mí. Mi mina favorita está infestada por Bullfango. Soluciónalo; ya me entiendes. Pero con discreción; es un secreto.','Psst! Ehi! Ho bisogno di chiederti un favore. Uno dei migliori punti di scavo è infestato dai Bullfango. Vorrei che lo disinfestassi, ma con discrezione: la sua posizione è un segreto!','',3600,240);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (516,'A King, Robed in Smoke','Ein in Rauch gehüllter König','Un roi, entouré de fumée','Un rey, vestido de humo','Il re con vesti fumanti','','Hunt a Rathalos','Erjage einen Rathalos.','Chasser 1 Rathalos','Caza un Rathalos','Caccia un Rathalos','','Village','1','LR',0,0,5,8,50,500,4500,300,'Sever Rathalos''s tail','Rathalos-Schwanz abtrennen','Couper la queue du Rathalos','Corta la cola del Rathalos','Mozza la coda del Rathalos','','I fear the end times are upon us. A blood-colored demon has ascended from the depths to plague the land. I beg of thee, save the shrine that protects the Volcano and send that beast back to the abyss.','Ich fürchte, das Ende naht. Ein blutfarbener Dämon ist aus den Tiefen empor gekrochen, um das Land zu plagen. Bitte, rette den Schrein, der den Vulkan schützt, und zernichte das Biest!','C''est la fin des temps ! Un démon de sang a émergé des abysses pour dévaster le pays. Je vous en supplie, sauvez le temple du Volcan et renvoyez ce monstre d''où il vient !','Temo que ha llegado el fin de los tiempos. Un demonio color sangre ha ascendido de las profundidades. Por favor, salva el santuario que protege el Volcán y devuélvelo al abismo.','La fine del mondo è vicina. Un demone vermiglio è sorto dagli abissi per tormentare questa landa. Ti prego, salva il tempio del vulcano e convinci la bestia a tornare da dov''è venuta!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (517,'Fight or Uragaan!','Der Zorn des Vulkans','La fureur du volcan','La furia del volcán','La furia del vulcano','','Hunt an Uragaan','Erjage einen Uragaan.','Chasser 1 Uragaan','Caza un Uragaan','Caccia un Uragaan','','Village','1','LR',0,0,5,8,50,500,4200,250,'Wound Uragaan''s jaw','Uragaan-Kiefer verletzen','Briser la mâchoire de l''Uragaan','Hiere Uragaan en las fauces','Ferisci mascella Uragaan','','Don''t bother readin'' any further unless you''re a true hunter -- the type who loves that quiver in your guts when you''re livin'' on the razor''s edge. Still with me? Go to the Volcano. Somethin''s waitin''.','Nur für echte Jäger: Liebst du den Nervenkitzel, das Leben auf des Messers Schneide? Dann begib dich zum Vulkan. Dort wartet etwas auf dich.','Inutile de continuer si vous n''êtes pas de ces chasseurs qui aiment vivre sur le fil du rasoir. Vous voyez ce que je veux dire ? Allez donc jusqu''au Volcan. Quelque chose vous y attend.','No te molestes en seguir leyendo si no eres un cazador de los que se emocionan por dentro cuando corren peligro. ¿Sigues leyendo? Pues ve al volcán, y verás.','Non leggere oltre a meno che tu non sia un cacciatore che ama vivere pericolosamente! Sei ancora qui? Ok, allora recati al vulcano. C''è qualcosa che ti aspetta!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (518,'Great Knuckleballs o'' Fire!','Flammende Knöchel','Chauds comme la braise','Nudillos de fuego','Inferno di cristallo','','Hunt 2 Volvidons','Erjage 2 Volvidons.','Chasser 2 Volvidon','Caza 2 Volvidon','Caccia 2 Volvidon','','Village','0','LR',0,0,5,8,50,500,4500,190,'None','–','Rien','Ninguno','Nessuno','','I''m reporting live from the Volcano, where we''re observing two majestic Volvidons. If you look closely, you can see... you can see... them coming this way! Run for your lives!','Ich befinde mich hier live am Vulkan, wo wir zwei majestätische Volvidons bewundern können. Wenn man genau hinsieht, sieht man... dass sie direkt auf uns zu kommen! Rennt!','Je vous parle en direct du Volcan où nous observons deux majestueux Volvidon. Si vous regardez attentivement, vous pourrez constater... qu''ils viennent par ici ! Fuyez !','Informo en directo desde el Volcán, donde vemos a dos majestuosos Volvidon. Si miran atentamente, verán... ¡que vienen hacia aquí! ¡Corred! ¡Huid!','In diretta dal vulcano, siamo in presenza di due maestosi Volvidon. Se ci avvicinassimo, potremmo renderci conto che... vengono da questa parte! Scappate!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (519,'Hunt-a-thon: Iodrome','Jagd-Marathon: Iodrome','Chassathon : Iodrome','Cazamaratón: Iodrome','Cacciatona: Iodrome','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Iodrome oder gib einen Tatzenpass ab.','Chasser 2 Iodrome ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Iodrome o consegna buono pass-zampa','','Village','0','LR',4,0,5,8,50,400,3600,190,'Slay 20 Ioprey','20 Ioprey erlegen','Tuer 20 Ioprey','Abate a 20 Ioprey','Uccidi 20 Ioprey','','Some Iodrome are fighting for position as alpha, but their fighting''s going to leave the entire ecosystem poisoned! Please take out a couple to help mitigate the damage!','Einige Iodrome kämpfen um die Vorherrschaft als Alpha, aber wenn das so weitergeht, ist hier bald das komplette Ökosystem vergiftet! Kannst du ein paar davon erlegen, um den Schaden zu begrenzen?','Des Iodrome se battent pour prendre la tête de la meute. Leur présence menace d''empoisonner l''écosystème. Tuez-en au moins deux pour rétablir l''équilibre !','Un grupo de Iodrome está peleando para decidir cuál es el macho dominante, ¡pero van a dejar todo el ecosistema envenenado! ¡Por favor, cárgate a un par para reducir el daño!','Gli Iodrome sono in piena lotta per determinare il leader del branco. Di questo passo mineranno l''intero ecosistema! Potresti eliminarne almeno due, per limitare i danni?','',1800,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (520,'Envoy to Disaster','Vorboten des Unheils','Le messager du désastre','Mensajero de la desgracia','Messo del disastro','','Slay 10 Remobras','Erlege 10 Remobras.','Tuer 10 Remobra','Abate a 10 Remobra','Uccidi 10 Remobra','','Village','0','LR',1,0,5,8,50,300,3000,160,'None','–','Rien','Ninguno','Nessuno','','I sense an ill omen that could spell disaster for the Volcano, should it be left unchecked. Slay ten of the serpents to quell the danger before it''s too late!','Ich spüre ein düsteres Omen, das Schlimmes für den Vulkan verheißen kann, wenn sich niemand darum kümmert! Erschlage zehn Schlangen, ehe es zu spät ist!','Un mauvais présage annonce une catastrophe dans la région du Volcan. Il nous faut agir. Allez tuer 10 serpents pour juguler la menace avant qu''il ne soit trop tard !','Percibo un mal augurio que podría ser desastroso para el Volcán si nadie lo va a comprobar. Mata a 10 serpientes para atajar el peligro a tiempo.','Avverto che un possibile disastro sta per abbattersi sul vulcano. Uccidi dieci esemplari di quei serpenti per scongiurare il pericolo prima che sia troppo tardi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (521,'A Late Jurassic Excursion','Eine Reise ins späte Jura','Vadrouille jurassique','Viaje al jurásico tardío','Un''escursione giurassica','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,5,101,50,600,5400,290,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','I train the only way a warrior can -- with full abandon! Yet whenever I perform my kata, monsters gather around me. Honorable hunter! Winnow the beasts from this place so that I may hone my body properly!','Ich trainiere so, wie ein wahrer Krieger kämpft – mit voller Hingabe. Aber wenn ich meine Kata laufe, kommen von über- all Monster an! Werter Jäger, sei so gut und säubere diesen Ort, damit ich trainieren kann!','Je m''entraîne sans relâche, comme tous les vrais guerriers, mais mon kata attire les monstres. Chassez-les d''ici afin que mon corps puisse devenir l''arme qu''il doit être.','Entreno como tiene que entrenar un guerrero: ¡al máximo! Hago mi kata y los monstruos me rodean. Honorable cazador, dispérsalos para que pueda entrenar como es debido.','Un vero guerriero deve addestrarsi con assoluta dedizione! Purtroppo, ogni volta quei malefici mostri mi circondano. Scuoia quelle bestiacce, così che possa allenarmi in santa pace!','',2100,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (522,'A Sandy Battle Royale','Legendäre Sandschlacht','Ça chauffe dans le désert','Batalla campal en la arena','Battaglia all''ultima sabbia','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,5,5,50,600,5100,290,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','My ship was cruising over the sand when we suddenly ran aground, our cargo spilling hither and yon. Now, the local monsters are really keen on taking it, so I need you to dissuade them!','Mein Schiff flog geradezu über den Sand, als wir plötzlich auf Grund liefen. Unsere ganze Ladung ist jetzt verstreut und zieht Monster an! Kannst du die Viecher von der Fracht abhalten?','Mon navire survolait le désert quand il s''est échoué, répandant du même coup toute sa cargaison. Le hic, c''est que des monstres veulent s''en emparer. Vous devez les en dissuader.','Navegaba por las Dunas y de repente embarrancamos. Nuestra carga se desparramó y los monstruos la quieren, así que necesito que los disuadas.','La mia nave veleggiava sulla sabbia, quando si è arenata all''improvviso. Il carico ora è sparpagliato in ogni dove e i mostri non vedono l''ora d''impossessarsene. Ho bisogno che tu li dissuada!','',2100,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (523,'Scientific Method','Wissenschaftliche Methoden','Méthode scientifique','Método científico','Metodo scientifico','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,5,6,50,600,5400,320,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','This island is, like, sooo amazing, don''tcha think? It''s got unique evolutionary patterns you can''t find aaanywhere else! Sweet, huh? So, like, be a pal and hunt me a monster! For science!','Diese Insel ist echt sooooo faszinierend, oder? Oder? Die Muster der Evolution hier sind absolut fas-zi-nierend! Echt unglaublich! Also, mein Freund, sei so nett und jag mir ein Monster. Für die Wissenschaft!','Cette île est stupéfiante, pas vrai ? L''évolution y a suivi des voies tout à fait uniques. Génial, hein ? Alors, soyez sympa et allez chasser un monstre pour moi... et pour la science !','Esta isla es súper guay, ¿no crees? Tiene patrones evolutivos únicos que no están en ningún otro sitio. ¡Grande! Anda, sé legal, tráeme un monstruo.','Quest''isola è sorprendente, non credi? Qui è possibile osservare modelli evolutivi non presenti da nessun''altra parte! Bello, eh? Ora fammi felice e caccia un mostro. Per la scienza!','',2100,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (601,'The Scorching Blade','Die sengende Klinge','La lame ardente','El filo ardiente','La spada rovente','','Hunt a Glavenus','Erjage einen Glavenus.','Chasser 1 Glavenus','Caza un Glavenus','Caccia un Glavenus','','Village','2','LR',0,0,6,1,50,600,5100,320,'Sever Glavenus''s tail','Glavenus-Schwanz abtrennen','Couper la queue du Glavenus','Corta la cola del Glavenus','Mozza la coda del Glavenus','','That bedeviled Glavenus has reappeared in the Jurassic Frontier, and the Wycademy search team''s outmatched. There is no time to waste. It has to be brought down. I''m counting on you!','Der teuflische Glavenus ist wieder zurück im Jura-Grenzland. Wir haben hohe Verluste unter den Wykademie-Forschern zu beklagen. Keine Zeit für mehr Erklärungen, jemand muss was tun. Ich zähle auf dich.','Ce satané Glavenus refait des siennes sur la Frontière jurassique et les chercheurs de la Wycadémie ont subi de lourdes pertes. Assez tergiversé, cette bête doit disparaître. Et vite !','Ese fastidioso Glavenus vuelve a hacer de las suyas en la Fronda Arcaica, y los investigadores de la Wycademia han sufrido bajas. No hay tiempo que perder, cuento contigo.','Un maledetto Glavenus ha decimato la squadra di ricerca della Wyccademia nella frontiera giurassica. Non c''è tempo da perdere: la bestia deve morire! Vai, conto su di te!','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (602,'Dark Wings, Dark Work','Dunkle Flügel, dunkle Werke','Noir comme la nuit','Alas negras como la noche','Le ali dell''oscurità','','Hunt a Gore Magala','Erjage Gore Magala.','Chasser 1 Gore Magala','Caza un Gore Magala','Caccia un Gore Magala','','Village','1','LR',0,0,6,1,50,600,5100,310,'Wound Gore Magala''s feelers','Gore-Magala-Fühler verletzen','Briser les antennes du Gore Magala','Hiere Gore M. en las antenas','Ferisci i sensori del G.Magala','','Beneath the azure skies of the Jurassic Frontier lurks a mysterious monster cloaked in black. Writhing in pain, it glares from unseen eyes. The hunter or the hunted: Which will you be?','Unter dem blauen Himmel des Jura-Grenzlands lauert ein geheimnisvolles Monster, von Schwärze umgeben. Seine Augen unsichtbar, seine Qualen spürbar. Jäger oder Gejagter: Was bist du?','Sous le ciel azur de la Frontière jurassique rôde un monstre mystérieux drapé de noir. Rongé par la douleur, il observe sans être vu. Qui serez-vous : le chasseur ou la proie ?','Bajo los cielos azules de la Fronda Arcaica acecha un misterioso monstruo negro que se retuerce de dolor y nos espía sin que nadie lo vea. ¿Serás el cazador o la presa?','Nel cielo azzurro sopra la frontiera giurassica si nasconde un mostro tinto di nero. Si contorce dal dolore e si cela agli occhi della gente. Tu sarai la preda o il cacciatore?','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (603,'Tuff Turf','Hartes Terrain','Terre sauvage','Territorio volcánico','Territorio violato','','Hunt a Zinogre','Erjage einen Zinogre.','Chasser 1 Zinogre','Caza un Zinogre','Caccia uno Zinogre','','Village','1','LR',0,0,6,101,50,500,4800,300,'Wound Zinogre''s back','Zinogre-Rücken verletzen','Blesser le dos du Zinogre','Hiere Zinogre en el lomo','Ferisci il dorso dello Zinogre','','I was training my tail off in the Jurassic Frontier when, uh, I guess I kinda sorta riled up a Zinogre. My bad! No way I can handle that beast. Somebody beat it up for me!','Ich habe mir im Jura-Grenz- land die Pfoten abtrainiert, als ich, ähm, naja, einem Zinogre auf den Schwanz getreten bin, sozusagen. Echt blöd von mir! Und allein werd ich mit dem nicht fertig!','Je m''entraînais sérieusement du côté de la Frontière jurassique quand j''ai énervé un Zinogre et cette créature est vraiment trop coriace pour moi. Quelqu''un pourrait s''en charger à ma place ?','Estaba entrenando como un loco en la Fronda Arcaica y parece que enfurecí a un Zinogre. ¡Qué mala pata! Yo no puedo con esa bestia, necesito ayuda.','Mi stavo allenando nella frontiera giurassica quando credo di aver disturbato uno Zinogre. Colpa mia! Quella creatura è troppo potente per me. Qualcuno l''affronti al posto mio!','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (604,'From Beyond the Sands','Jenseits des Sands','De l''autre côté des Dunes','Más allá de las arenas','Il buio oltre le sabbie','','Hunt a Glavenus','Erjage einen Glavenus.','Chasser 1 Glavenus','Caza un Glavenus','Caccia un Glavenus','','Village','0','LR',0,0,6,5,50,600,5100,320,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','I was roaming the Dunes when I happened to see a Glavenus blocking one of the major trade routes! Normally I keep to myself, but I can''t live without my delivery of Moga curry!','In den Dünen bemerkte ich einen Glavenus, der eine der Handelsrouten blockierte. Ich bitte für gewöhnlich niemanden um Hilfe, aber ohne meine Ration Moga- Curry kann ich nicht sein!','J''arpentais les Dunes quand j''ai vu qu''un Glavenus bloquait les grands axes commerciaux ! J''évite de me mêler de ce genre de choses mais j''attends une livraison de Moga au curry !','Iba por las Dunas y de repente vi un Glavenus que bloqueaba una de las principales rutas de comercio. En general yo no me meto... ¡pero no puedo vivir sin curry Moga!','Vagavo per le dune, quando ho visto un Glavenus che bloccava una delle tratte commerciali! Cosa me ne importa, chiedi? Ehi, devo assolutamente ricevere il mio carico di curry Moga!','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (605,'Tigrex by the Tail','Tigrex am Schwanz packen','L''œil du Tigrex','La cola del Tigrex','Tigrex per la coda','','Hunt a Tigrex','Erjage einen Tigrex.','Chasser 1 Tigrex','Caza un Tigrex','Caccia un Tigrex','','Village','1','LR',0,0,6,5,50,600,5400,310,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','I''ve got a distress signal from a hunter in the Dunes who''s run into a Tigrex! Anyone who finds this message is requested to head there and take it down ASAP!','Ich habe einen Hilferuf von einem Jäger in den Dünen erhalten. Ein Tigrex bedroht ihn. Alle Finder dieser Nachricht werden gebeten, Hilfe zu leisten: Schnellstmöglich!','Un chasseur a signalé la présence d''un Tigrex près des Dunes ! Quiconque trouvera ce message est prié d''aller sur place au plus vite pour éliminer cette créature !','¡He recibido un mensaje de socorro de un cazador de las Dunas que ha visto un Tigrex! A quien lea este mensaje: ¡ve allí y acaba con el monstruo cuanto antes!','Ho ricevuto un segnale di emergenza da un cacciatore che si è imbattuto in un Tigrex nelle dune! È in pericolo! Chiunque trovi questo messaggio si rechi immediatamente a salvarlo!','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (606,'A Thousand Scales of Dread','Schuppen des Schreckens','Les écailles de terreur','Mil escamas de terror','Mille scaglie di paura','','Hunt a Seregios','Erjage einen Seregios.','Chasser 1 Seregios','Caza un Seregios','Caccia un Seregios','','Village','1','LR',0,0,6,5,50,600,5400,370,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','Breaking news from the Elder Hall: A Seregios has shown up in the Dunes! Any and all skilled hunters are asked to help hunt it as soon as possible. Beware its blades!','Dringende Nachricht aus der Ältestenhalle! In den Dünen wurde ein Seregios gesichtet! Alle fähigen Jäger sind dazu aufgerufen, sich an der Jagd zu beteiligen: Aber Vorsicht! Die Klingen sind gefährlich!','Le Hall des Anciens vient de nous signaler la présence d''un Seregios dans les Dunes. Tous les chasseurs aguerris sont priés de participer à sa traque. Méfiez-vous de ses lames !','Últimas noticias de la sala ancestral: hay un Seregios en las Dunas. Se pide a todos los cazadores hábiles que ayuden a darle caza cuanto antes. ¡Cuidado!','Ultime notizie dalla sala antica: un Seregios è apparso sulle dune! A tutti i cacciatori di talento è richiesto di cacciarlo il prima possibile. Attenti alle sue lame!','',600,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (607,'The Perilous Pair','Das gefährliche Duo','Une paire dangereuse','Una peligrosa pareja','Il diabolico duo','','Hunt a Rathalos and a Rathian','Erjage einen Rathalos und eine Rathian.','Chasser 1 Rathalos Chasser 1 Rathian','Caza un Rathalos y una Rathian','Caccia un Rathalos e una Rathian','','Village','0','LR',0,0,6,102,50,800,7200,420,'None','–','Rien','Ninguno','Nessuno','','Luggage left with Neko still no make it here. Maybe that because there is Rathalos and Rathian in the Verdant Hills? Iya... All this give me bad yokan. "Yokan" means feeling!','Gepäck mit Neko immer noch nicht hier sein. Vielleicht ist, weil Rathalos und Rathian sind in Grüne Hügel? Alles das mir geben sehr schlechtes Yokan. Yokan heißt "Gefühl"!','La cargaison confiée à Neko n''est toujours pas là. C''est peut-être à cause du Rathalos et de la Rathian apparus dans les Collines verdoyantes. J''ai un mauvais yokan... Ça veut dire pressentiment !','Equipaje que fue con Neko todavía no aquí. ¿Tal vez culpa de Rathalos y Rathian en el Bosque Rocoso? Tengo un mal yokan. "Yokan" significa "presentimiento".','Bagagli lasciati da Neko non sono arrivati. Può essere per presenza di un Rathalos e di una Rathian sui colli verdi? Ho pessima yokan... "Yokan" significa sensazione!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (608,'The Hero''s Journey','Die Reise des Helden','Le voyage du héros','El viaje del héroe','Il viaggio dell''eroe','','Hunt a Tigrex','Erjage einen Tigrex.','Chasser 1 Tigrex','Caza un Tigrex','Caccia un Tigrex','','Village','0','LR',0,0,6,103,50,600,5400,310,'Wound Tigrex''s head','Tigrex-Kopf verletzen','Blesser la tête du Tigrex','Hiere Tigrex en la cabeza','Ferisci la testa del Tigrex','','The Arctic Ridge has materials I need to remake the Hero''s Blade, but right now it''s home to a nasty Tigrex. Be a dear and clear it out for me so I can go about my work, won''t you?','Ich brauche Materialien aus dem Schneegebirge, um die Heldenklinge neu herzustellen. Aber jetzt lebt dort ein gefährlicher Tigrex. Du kannst doch bestimmt für mich hingehen, oder?','La Crête arctique abrite les matériaux pour reforger l''épée héroïque. Ainsi qu''un Tigrex ! Soyez assez aimable de l''éliminer afin que je puisse reprendre mon travail, voulez-vous ?','Para reconstruir la espada heroica necesito materiales de las Cimas Árticas, pero ahora hay un Tigrex por ahí. Por favor, elimínalo para que pueda seguir con mi tarea.','Il monte artico è ricco di materiali utili per forgiare la spada eroica, ma ora è infestato da un odioso Tigrex! Ti dispiacerebbe neutralizzarlo, così potrò completare l''arma?','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (609,'The Bellowing Zinogre','Der brüllende Zinogre','Le Zinogre hurlant','El Zinogre rugiente','Uno Zinogre urlante','','Hunt a Zinogre','Erjage einen Zinogre.','Chasser 1 Zinogre','Caza un Zinogre','Caccia uno Zinogre','','Village','0','LR',0,0,6,104,50,500,4800,300,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Eeek, that Zinogre''s at it again, howling away! I get so scared hearing it, I can''t move, even when I have customers to serve! Someone, anyone, make it stop so I can do my job!','Oh nein, dieser Zinogre brüllt schon wieder! Ich bekomme da jedes Mal solche Panik, dass ich komplett erstarre. Unter- nimm da bitte was, damit ich wieder arbeiten kann!','Rhaaa, voilà que ce Zinogre remet ça ! Ses terrifiants hurlements me paralysent, même quand j''ai des clients à servir ! Il faut qu''on le fasse taire pour que je puisse travailler en paix !','Ay, ¡ese Zinogre ya vuelve a aullar! Me asusto tanto cuando lo oigo que no me puedo mover aunque tenga clientes. Por favor, ¡que alguien acabe con él!','Argh! Quello Zinogre sta di nuovo urlando come un ossesso! Il suo strillo mi terrorizza. Non riesco neppure a servire i clienti! Qualcuno mi aiuti, il lavoro non ammette pause!','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (610,'Brachydios Mio!','Oh mein Brachydios!','Brachydios Mío !','¡Brachydios mío!','Brachydios mio!','','Hunt a Brachydios','Erjage einen Brachydios.','Chasser 1 Brachydios','Caza un Brachydios','Caccia un Brachydios','','Village','1','LR',0,0,6,6,50,600,5700,370,'Sever Brachydios''s tail','Brachydios-Schwanz abtrennen','Couper la queue du Brachydios','Corta la cola del Brachydios','Mozza la coda del Brachydios','','We recently came across a monster with a dangerous slime on its forelimbs... If any gets on you, it explodes. We''ve been holding out okay so far, but we could really do with more hands on deck.','Uns ist letztens ein Monster begegnet, dessen Vorder- beine von explosivem Schleim bedeckt waren. Bisher halten wir uns ganz gut, aber etwas Unterstützung könnte nicht schaden.','On est récemment tombés sur un monstre dont les pattes sont recouvertes d''une poisse qui explose au contact. On tient le coup pour l''instant, mais votre aide ne serait pas de refus.','Hace poco nos encontramos un monstruo con una baba peligrosa en las patas: si te toca, estalla. Por ahora nos las arreglamos, pero nos vendría bien tu ayuda.','Di recente ci siamo imbattuti in un mostro con una pericolosa melma sulle zampe anteriori. Esplode al contatto! Finora ce la siamo cavata, ma una mano non ci dispiacerebbe per nulla.','',1200,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (611,'Under the Gaze of Heaven','Unter Aufsicht des Himmels','Sous le regard du paradis','Bajo la mirada del cielo','Sotto lo sguardo del Cielo','','Hunt 2 Shogun Ceanataurs','Erjage 2 Shogun Ceanatauren.','Chasser 2 Ceanataur Shogun','Caza 2 Shogun Ceanataur','Caccia 2 Shogun Ceanataur','','Village','0','LR',0,0,6,7,50,600,6000,380,'None','–','Rien','Ninguno','Nessuno','','My granddad let it slip that he was gonna go watch a "battle of shoguns." He has no place being there, but he won''t listen! Look, I''ll help, too. I just don''t wanna do it alone!','Mein Opa hat gemurmelt, dass er sich einen "Kampf der Shogune" ansehen will. Das ist viel zu gefährlich, aber er hört einfach nicht! Ich will ihm helfen gehen, könntest du bitte mitkommen?','Mon grand-père a laissé entendre qu''il compte assister à un combat de Shogun. Il refuse d''entendre raison, j''aimerais donc que vous m''accompagniez, car je n''y arriverai pas seule !','Mi abuelo confesó que iba a ver una "batalla de shoguns". No es lugar para él, pero no me escucha. Oye, yo te ayudaré, pero no quiero hacerlo solo.','Il nonno ha detto che sarebbe andato ad assistere a una "battaglia di Shogun". È pericoloso, ma ha la testa di marmo! Ti dispiacerebbe aiutarmi? Da sola non credo di farcela!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (612,'Break the Brachydios','Analyse des Brachydios','Analyse du Brachydios','A cazar Brachydios','Cattura il Brachydios','','Capture a Brachydios','Fange einen Brachydios.','Capturer 1 Brachydios','Captura un Brachydios','Cattura un Brachydios','','Village','0','LR',2,0,6,8,50,900,8700,520,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','There''s still much to learn about Brachydios, especially given that it''s only just been recently discovered. I simply must know more, so I''d love it if you captured me a specimen to examine!','Es gibt noch so viel, was wir über die Brachydios lernen müssen, wir wissen ja noch kaum etwas! Vielleicht könntest du mir einen fangen, damit ich ihn in aller Ruhe erforschen kann?','Il reste tant à apprendre au sujet des Brachydios, d''autant qu''ils n''ont été découverts que récemment. Capturez-moi un spécimen pour mes études.','Hay mucho que no sabemos sobre el Brachydios, ya que lo hemos descubierto hace poco. Necesito saber más, me iría muy bien que capturases un espécimen para examinarlo.','C''è ancora molto da imparare sui Brachydios, considerando che sono stati scoperti solo di recente. Ma devo saperne di più! Per questo vorrei che ne catturassi un esemplare per me! Puoi?','',600,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (613,'Night Terrors','Nachtschrecken','Terreurs nocturnes','Terrores nocturnos','Spaventi notturni','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,6,102,50,600,5700,320,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','Some eerie cries have been getting closer to the village every night. It''s freaking out the villagers here, so would you mind going out hunting for whatever''s behind it?','Jede Nacht kommen diese schrecklichen Schreie ein Stück näher an unser Dorf. Die Dorfbewohner bekommen es so langsam mit der Angst zu tun, könntest du da vielleicht mal nachsehen?','Les gémissements mystérieux qui se rapprochent chaque nuit du village commencent à effrayer tout le monde ici. Vous pouvez nous débarrasser de la source de ces lamentations ?','Se oyen gritos inquietantes que cada noche suenan más cerca de la aldea. Todos los aldeanos están asustados, ¿podrías ir a cazar lo que los emite?','Lamenti angoscianti sempre più vicini turbano le notti del villaggio. Gli abitanti sono spaventati. Potresti occuparti di chiunque si nasconda dietro questi pianti strazianti?','',2400,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (614,'Bristly Fanged Beasts','Borstige Reißzahn-Bestien','Bêtes à cran','Bestias de colmillos','Ispide belve zannute','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,6,103,50,600,5700,290,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','There''re three diff''rent Fanged Beasts out where I''m stayin''. Right now it ain''t too big a worry, but even the tiniest spark can cause a big fire, so you''d best put it out soon.','Da, wo ich jetz'' wohn'', gibt es drei Art''n Reißzahn- Bestien. Sin'' zwar nich'' so schlimm, aber wenn die ma'' richtig losleg''n könnt'' das schon''n Problem werd''n. Mach besser gleich was.','Trois bêtes à crocs se sont installées près de l''endroit où j''habite. Elles sont calmes pour l''instant, mais une étincelle suffirait à mettre le feu aux poudres. Mieux vaut vous en occuper.','Hay tres bestias de colmillos cerca de mi casa. Por ahora no es muy grave, pero cualquier chispa puede provocar un fuego, así que más vale apagarlo pronto.','Tre belve zannute infestano l''area. Al momento non rappresentano un pericolo, ma da una piccola scintilla può divampare un enorme incendio! Meglio domarle prima possibile.','',2400,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (615,'Drawing the Waterline','Eine Wasserlinie ziehen','La goutte de trop','En la línea de costa','Acque perigliose','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,6,104,50,900,8400,460,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','It''s a catastrophe! Terrible things came splashing up from the river, and now everything''s soaked! Trenya, bring your big-nosed hunter lackey and come punish the purrpetrators right away!','Eine Katzastrophe! Schreckliche Dinge sind aus dem Fluss gekommen und jetzt ist alles triefend nass! Trenya, du gehst jetzt mit deinem großnasigen Jägerlakeien und krallst dir die Schuldigen!','C''est une catastrophe ! Des choses horribles ont jailli de la rivière et tout est trempé maintenant ! Trenya ! Va immédiatement punir les coupables avec ton laquais !','¡Qué catástrofe! El río ha traído cosas terribles, y ahora todo está empapado. Trenya, ¡tú y tu compañero narizón debéis castigar a los culpables enseguida!','È una gattastrofe! Dal fiume affiorano delle mostruosità e ora è tutto fradicio! Tremià, porta con te il tuo compagno e prrrendi subito a zampate i colpevoli!','',2400,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (616,'The Cankerous Quagmire','Der verschlingende Sumpf','Le marécage miasmatique','El lodazal ulceroso','Miasmi nella palude','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,6,7,50,700,6600,320,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','I can''t believe it -- two Iodrome in the same place? What do they want? What if they fight the local Najarala!? This is bad! If you don''t act fast, the Marshlands''ll be a miasma of Poison and Paralysis!','Ich kann es kaum glauben – zwei Iodrome am gleichen Ort? Was sie hier nur wollen? Und was, wenn sie mit den Najarala Streit anfangen? Das wäre echt übel! Verhindere bitte, dass unser Marschland verwüstet wird!','Incroyable ! Deux Iodrome au même endroit ? Que veulent-ils ? Imaginez s''ils s''en prennent aux Najarala ! Intervenez ou les Marais ne seront plus que poison et paralysie !','No puedo creerlo: ¿dos Iodrome en el mismo sitio? ¿Qué quieren? ¿Y si se enfrentan al Najarala? ¡Es terrible! Si no actúas, la Ciénaga será presa del veneno y la parálisis.','Com''è possibile, due Iodrome nello stesso posto? Cosa vogliono? E se attaccassero il Najarala?! Oh, sarebbe pessimo! Agisci in fretta, o la palude diverrà un luogo di veleno e paralisi!','',2400,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (617,'Volcano Triple Play','Dreifach-Vulkan','Violence volcanique','Triple juego en el Volcán','Fuoricampo nel vulcano','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,6,8,50,900,8100,340,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','I was practicing out by the Volcano when I saw some cool monsters bouncing and leaping around! If you show me how to hit something like that, it''ll help me be the world''s best slugger!','Als ich hinten beim Vulkan geübt habe, sind mir ein paar coole Monster aufge- fallen, die da rumhüpfen. Kannst du mir beibringen, wie ich die umhauen kann? Dann wäre ich endlich jemand!','J''ai vu des monstres impressionnants s''ébattre près du Volcan l''autre jour. Vous pouvez me montrer comment les affronter ? Ça m''aidera à devenir le plus grand des cogneurs !','Estaba practicando cerca del Volcán cuando vi a unos monstruos dando tumbos por ahí. Si me enseñas a acertar contra algo así, seré el mejor bateador.','Mi stavo allenando vicino al vulcano quando ho visto dei mostri andarsene in giro nei dintorni! Se mi mostri come colpire qualcosa del genere potrò diventare il miglior battitore del mondo!','',2400,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (618,'It''s Electric','Er ist elektrisch!','C''est électrique','Es eléctrico','Esperienza elettrizzante','','Slay a Kirin','Erlege einen Kirin.','Tuer le Kirin','Abate un Kirin','Uccidi un Kirin','','Village','0','LR',1,0,6,101,50,700,6600,350,'None','–','Rien','Ninguno','Nessuno','','Unexplained natural events have been occurring deep within the Jurassic Frontier. Intriguing, don''t you think? I believe it''s a sign of mythic beasts, but you should go see it with your own eyes.','Im Jura-Grenzland kam es zu unerklärlichen Natur- phänomenen. Faszinierend, nicht wahr? Ich denke, dass mythische Bestien dahinter- stecken. Am besten, du überzeugst dich selbst.','Des évènements mystérieux ont lieu à la Frontière jurassique. Troublant, non ? Il s''agit peut-être de bêtes mystiques. Vous devriez aller voir par vous-même.','En las profundidades de la Fronda Arcaica ocurren fenómenos inexplicables. Intrigante, ¿no te parece? Creo que es culpa de unas bestias míticas, pero deberías ir a verlo tú.','Nella frontiera giurassica si sono verificati alcuni eventi inspiegabili! Credo sia un segno della presenza di bestie mistiche. Perché non vai a verificare di persona?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (619,'The White Brute','Das weiße Untier','La brute immaculée','La bestia blanca','Il bruto bianco','','Slay a Kirin','Erlege einen Kirin.','Tuer le Kirin','Abate un Kirin','Uccidi un Kirin','','Village','0','LR',1,0,6,3,50,700,6600,350,'Place flag on mountain top','Gildenflagge auf Gipfel setzen','Placer le drapeau au sommet','Coloca la bandera en la cima','Pianta bandiera Gilda','','My friends and I sought out a phantom beast. When we came upon it, we had but a moment before it smote us with thunder, casting us asunder. Please help us, Hunter.','Meine Freunde und ich suchten nach einer Phantom- kreatur. Als wir sie fanden, blieb uns nur ein Moment, bevor sie uns mit Donner überzog und auseinander- riss. Bitte hilf uns, Jäger!','Mes amis et moi cherchions une bête spectrale. Lorsque nous l''avons enfin trouvée, elle nous a frappés avec sa foudre et nous a projetés au loin. S''il vous plaît, venez-nous en aide !','Mis amigos y yo fuimos a por una bestia fantasmal. Cuando dimos con ella, enseguida nos atacó con rayos y nos venció. Ayúdanos, por favor, cazador.','I miei amici e io cercavamo una bestia fantasma. Quando l''abbiamo trovata, tuttavia, ci ha colpito con un tuono, scaraventandoci via. Abbiamo bisogno del tuo intervento!','',1200,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (620,'Stop the Wheel','Das Rad anhalten','Arrêter la roue du destin','Detén la rueda','Ferma il ciclo di morte','','Slay a Shagaru Magala','Erlege Shagaru Magala.','Tuer le Shagaru Magala','Abate al Shagaru Magala','Uccidi uno Shagaru Magala','','Village','0','LR',1,0,6,16,50,800,7200,400,'Break Shagaru Magala''s horns','Shagaru-Magala-Hörner brechen','Briser les cornes du Shagaru Magala','Rompe cuernos del Shagaru M.','Spezza il corno dello S.Magala','','I''ve only known the gods from their legends -- never did I expect to see one in my lifetime. Dare you attempt to halt the cycle of death and etch your own name into legend, Hunter?','Ich kenne die Götter nur aus Legenden. Hätte nie gedacht, zu meinen Lebzeiten einen zu sehen. Wagst du es, den Kreislauf des Todes zu stoppen und deinen Namen zur Legende zu machen, Jäger?','Je ne connais les dieux que par les légendes, je ne pensais pas en rencontrer un. Oserez-vous tenter de stopper le cycle de la mort et faire entrer votre nom dans la légende ?','Solo conocía a los dioses a través de leyendas... ¡No esperaba encontrarme uno en mi vida! ¿Te atreves a intentar interrumpir el ciclo de muerte y forjar tu leyenda, cazador?','Ho sentito molte leggende sugli dei, ma non mi sarei mai aspettato di vederne uno in carne e ossa. Avrai il coraggio di fermare questo ciclo di morte e di scolpire il tuo nome nella leggenda?','',1200,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (621,'The Fated Four','Die schicksalsgebundenen Vier','Les quatre condamnés','Los cuatro predestinados','Quattro in pericolo','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,0,6,17,50,1900,18600,900,'None','–','Rien','Ninguno','Nessuno','','O brave, wise, courageous hunter, only you can rise to the challenge that I have set forth. It shall take both all your mind and body to overcome, but I ask you to venture forth.','Oh weiser, herrlicher Jäger, nur du kannst die Aufgabe meistern, die ich für dich stellte. Eine Tat steht an, die deinen Körper wie Geist fordern wird, also schreite voran, wenn du bereit bist!','Ô vous dont le courage n''est plus à démontrer, je vous conjure d''employer votre force et votre esprit à relever le défi que j''ai conçu et dont nul autre chasseur ne peut triompher.','Sabio y valiente cazador, solo tú puedes vencer en el desafío que he preparado. Necesitarás toda tu mente y tu cuerpo, pero te pido que lo intentes.','O nobile e prode asso della caccia, tu e solo tu puoi aspirare a compiere la sfida che ti si para innanzi! Prepara corpo e mente, e affronta il periglio con audacia.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (622,'Advanced: Fungus Fetchin''','Fortg.: Spezialitätensuche','Avancé : Cuisine des Pics','Avda.: cocina de Picos Nub.','Avanzata: Cucina gattesca','','Deliver 20 Unique Mushrooms','Liefere 20 Einmalige Pilze ab.','Livrer 20 champis spéciaux','Entrega 20 setas especiales','Consegna 20 funghi speciali','','Village','0','HR',3,0,6,4,50,300,3000,250,'None','–','Rien','Ninguno','Nessuno','','We''re prepurring a new menu at the Tanzinya Grill, and we think a Dish with mushrooms in it would be the cat''s meow. Purrhaps you could pick some up for us, Hunter?','Wir bereiten ein schnurrhaar- schlecker-leckeres Miaunü für den Tanzinya-Grill vor. Ein Pilzgericht wäre da bestimmt das Miau auf der Katze! Kannst du uns ein paarrr pflücken, Jäger?','Le Tanzinya Grill prépare sa nouvelle carte et nous sommes convaincus qu''un plat accompagné de champignons ferait un vrai tabac. Accepteriez-vous d''en ramasser pour nous ?','Estamos preparando un nuevo menú en el Asador Tanzyne. Y creemos que un plato con setas sería requetemiau. ¿Podrías recogernos unas cuantas, cazador?','Stiamo preparando un nuovo menu al Tanzinya Grill e un piatto con i funghi sarebbe assolutamente gattesco. Ti prrrego, ne raccoglieresti qualcuno per me?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (623,'Advanced: Jurassic Treasure','Fortgeschritten: Jura-Schätze','Avancé : Trésor jurassique','Avanzada: tesoro jurásico','Avanzata: Tesoro giurassico','','Hunt a Glavenus','Erjage einen Glavenus.','Chasser 1 Glavenus','Caza un Glavenus','Caccia un Glavenus','','Village','0','HR',0,0,6,101,50,800,7500,510,'None','–','Rien','Ninguno','Nessuno','','Gettin'' treasure trembles fer this ''ere Kingmeat I picked up in the Jurassic Frontier? Sorry to say, but if ye want it, ye gotta go work fer it! ''Unt me a Glavenus -- then we''ll talk!','Kriegste Schatzzittern im Anblick des Königsfleischs, das ich aus dem Jura- Grenzland mitgebracht habe? Gibt''s aber nicht umsonst! Jage mir einen Glavenus, dann sehen wir weiter!','Vous voulez de cette reine des viandes que j''ai rapportée de la Frontière jurassique ? Eh bien, il va falloir bosser ! Trouvez-vous un Glavenus et gagnez votre pitance !','¿Quieres la carne superior que recogí en la Fronda Arcaica? Lo siento, pero si la quieres, te costará. Ve a cazar un Glavenus y consigue un poco de tesoro.','Vuoi questa carne reale che ho trovato nella frontiera giurassica? Mi spiace dirlo, ma nel caso devi andare a prendertela! Vai a caccia di Glavenus e guadagnati la tua parte!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (624,'Advanced: Sly Swooper','Fortg.: Schlauberger','Av : Gare au Yian Garuga','Avda.: que viene el Garuga','Avanzata: Garuga dal nulla','','Hunt a Yian Garuga','Erjage einen Yian Garuga.','Chasser 1 Yian Garuga','Caza un Yian Garuga','Caccia uno Yian Garuga','','Village','0','HR',0,0,6,2,50,600,6000,400,'None','–','Rien','Ninguno','Nessuno','','I was out peddling a little while ago when I saw a Yian Garuga swoop down! I''m scared what''ll happen to my friends and everyone else if we leave it alone. Can you make it go away?','Als ich letztens unterwegs war, um meine Sachen zu verkaufen, habe ich einen Yian Garuga gesehen! Jetzt habe ich Angst um meine Freunde, wenn wir ihn nicht verjagen. Kannst du uns helfen?','Un Yian Garuga a failli m''avoir pendant ma tournée. Depuis, je me fais un sang d''encre pour mes amis et tous les autres habitants de la région. Pouvez-vous le faire déguerpir ?','Estaba comerciando hace un rato y he visto pasar un Yian Garuga. Temo lo que nos ocurrirá a todos si lo dejamos a sus anchas. ¿Podrías echarlo?','Stavo trafficando per gli affari miei, quando ho visto uno Yian Garuga piombarci addosso! Ho paura di ciò che potrebbe accadere agli altri se non ce ne liberiamo. Potresti mandarlo via?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (625,'Advanced: Invoking Rath','Fortg.: Guter Rath ist teuer','Avancé : Invocation de Rath','Avanzada: Rath invocadores','Avanzata: Rath Pack','','Hunt a Rathalos and a Rathian','Erjage einen Rathalos und eine Rathian.','Chasser 1 Rathalos Chasser 1 Rathian','Caza un Rathalos y una Rathian','Caccia un Rathalos e una Rathian','','Village','0','HR',0,0,6,2,50,1100,10800,660,'None','–','Rien','Ninguno','Nessuno','','You look like you''re in the mood to bump against some tough monsters! There''s a Rathalos and a Rathian on the loose in the Verdant Hills. Knowing you, I''m sure you can handle ''em!','Du siehst so aus, als ob du ein paar echt harte Monster vertragen kannst. In den Grünen Hügeln rennen ein Rathalos und eine Rathian frei rum. So wie ich dich kenne, wirst du damit fertig.','Envie de vous frotter à des monstres coriaces ? Tant mieux, car un Rathalos et une Rathian rôdent dans les Collines verdoyantes. Vous connaissant, je sais que vous en viendrez à bout !','Parece que tienes ganas de enfrentarte a monstruos difíciles. Hay un Rathalos y una Rathian en el Bosque Rocoso. Conociéndote, podrás con ellos.','Sembri dell''umore giusto per lanciarti contro qualche mostro bello grosso! Ci sono un Rathalos e una Rathian a spasso per i colli verdi. Conoscendoti, non dubito che trionferai!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (626,'Advanced: Seismic Scares','Fortg.: Schreckensseismik','Avancé : Frayeurs sismiques','Avda.: sustos sísmicos','Avanzata: Paura sismica','','Hunt 2 Tigrex','Erjage 2 Tigrexe.','Chasser 2 Tigrex','Caza 2 Tigrex','Caccia 2 Tigrex','','Village','0','HR',0,0,6,3,50,1500,14700,780,'None','–','Rien','Ninguno','Nessuno','','Hunter, I must implore you to take arms for me once again. Your target this time is a pair of Tigrex. Steel yourself, and be careful.','Jäger, ich muss dich erneut ersuchen, für mich zu den Waffen zu greifen. Das Ziel ist diesmal ein Paar Tigrexe. Aber bloß Vorsicht! Nicht, dass dir etwas passiert!','Chasseur, je me vois contraint de vous implorer de reprendre les armes. Votre cible sera cette fois un couple de Tigrex. Armez-vous d''une volonté d''acier et bon courage !','Cazador, debo rogarte que tomes las armas por mí una vez más. Tu objetivo esta vez son dos Tigrex. Ármate hasta los dientes y ve con mucho cuidado.','Gentile asso della caccia, chiedo venia se ti disturbo ancora, ma devo implorarti di combattere di nuovo per me. Questa volta il tuo obiettivo sono due Tigrex. Armati e sii prudente.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (627,'Advanced: Twinkle Toes','Fortg.: Durunterboros','Av : Face au Duramboros','Avda.: golpe de Duramboros','Avanzata: Duro Duramboros','','Hunt a Duramboros','Erjage einen Duramboros.','Chasser 1 Duramboros','Caza un Duramboros','Caccia un Duramboros','','Village','0','HR',0,0,6,4,50,900,8100,490,'None','–','Rien','Ninguno','Nessuno','','I''m no hunter, but the way I see it, yer only as good as the hardest blow ya can take. An'' the hardest of all are the ones Duramboros can deal! Take one down in the Misty Peaks, an'' prove your worth!','Ich bin ja kein Jäger, aber so wie ich es sehe, ist man immer nur so stark wie der stärkste Schlag, den man aus- hält. Und diese Duramboros in den Nebelgipfeln haben einen ziemlichen Schlag drauf!','Un chasseur, ça doit encaisser ! Moi, je connais aucune bestiole qui cogne plus dur que les Duramboros des Pics brumeux. Tuez-en un pour prouver que vous en avez dans le ventre.','No soy cazador, pero tal y como yo lo veo, uno es tan bueno como lo que puede soportar. Y lo más difícil de soportar son golpes de Duramboros. Ponte a prueba en los Picos Nublados.','Non sono un cacciatore, ma per quanto mi riguarda è forte solo chi colpisce più duro. E il più forte è chi può battere un Duramboros! Cacciane uno sulle cime nebbiose, mostra quanto vali!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (628,'Advanced: Seltas Queen','Fortg.: Seltas-Königin','Av : Écraser la Reine Seltas','Avda.: pisa la Seltas Reina','Avanzata: Schiaccia la Seltas','','Hunt a Seltas Queen','Erjage eine Seltas-Königin.','Chasser 1 Reine Seltas','Caza una Seltas Reina','Caccia una Seltas regina','','Village','0','HR',0,0,6,13,50,700,6600,480,'None','–','Rien','Ninguno','Nessuno','','Hey, there''s a Seltas Queen that''s giving all the other hunters around here a run for their money. Could you step up and give it a shot? Think of it as a real test of strength! Feh heh!','Hey, wir haben hier eine Seltas-Königin, mit der die anderen Jäger nicht fertig werden. Kannst du da nicht aushelfen? Sieh es einfach so: Du zeigst damit, was du wirklich drauf hast!','Une Reine Seltas donne du fil à retordre à tous les chasseurs du coin. Vous pourriez essayer de lui régler son compte ? Voyez ça comme un test en conditions réelles. Hé hé !','Oye, hay una Seltas Reina que está haciendo sudar a todos los demás cazadores. ¿Podrías intervenir y ver qué puedes hacer? Tómatelo como una prueba de fuerza.','Ehi, c''è una Seltas regina che sta mettendo alla prova parecchi cacciatori. Perché non ti fai avanti e ci provi anche tu? Considerala una vera prova di forza! Eh eh!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (629,'Advanced: The Goliaths','Fortgeschritten: Die Goliaths','Avancé : Les Goliaths','Avanzada: los colosos','Avanzata: Grossi colossi','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,1,50,2100,20100,1040,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','A Wycademy search team''s been attacked by some large monsters, and now they''re stranded. What''s more, some villagers were with them! I''d breathe easier if you''d hunt those beasts.','Ein Suchteam der Wykademie ist von einigen sehr großen Monstern angegriffen worden und kommt nicht mehr weg. Es sind auch Dorfbewohner bei ihnen! Geh diese Mons- ter jagen, um sie zu retten.','Des chercheurs de la Wycadémie et des villageois se sont retrouvés coincés suite à une attaque de grands monstres. Vous me soulageriez en acceptant de chasser ces créatures.','Un equipo de investigación de la Wycademia ha sido atacado por unos monstruos y no puede regresar. Hay aldeanos con ellos. Da caza a esas bestias y respiraré.','Una squadra di ricerca della Wyccademia è stata attaccata da mostri enormi e ora è braccata! In più, alcuni cittadini erano con loro! Sarei molto più tranquillo se cacciassi quelle bestie.','',3000,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (630,'Advanced: Barrage of Blue','Fortg.: Blaue Belästigung','Avancé : Sus aux bleus !','Avanzada: sabuesos azules','Avanzata: Fifa blu','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,9,50,1000,9900,550,'None','–','Rien','Ninguno','Nessuno','','Hunter, it looks like the Guild wants you at the Arena. A trio of blue monsters have shown up and is causing a fuss, but I''m sure you can handle it! At any rate, I won''t take no for an answer!','Jäger, die Gilde will, dass du in die Arena gehst. Eine Dreiergruppe blauer Monster ist aufgetaucht und macht Ärger. Ist aber nichts, was du nicht hinbekommst. Also, du machst das schon!','Écoutez, la Guilde exige votre présence dans l''Arène pour mettre au pas trois monstres bleus qui font des histoires. Inutile de tergiverser, vous n''avez pas le choix !','El gremio te quiere en la Arena. Ha aparecido un trío de monstruos azules que están armando un escándalo, pero seguro que puedes con ellos. ¡No digas que no!','Ehilà, sembra che la Gilda ti voglia nell''Arena. Tre mostri blu stanno causando scompiglio, ma non dubito che tu andrai alla grande! Non accetterò una risposta negativa, s''intende!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (631,'Advanced: Seeing Red','Fortgeschritten: Rot sehen','Avancé : Le péril rouge','Avanzada: visión roja','Avanzata: Vedo rosso!','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,9,50,1300,12300,670,'None','–','Rien','Ninguno','Nessuno','','Looks like another request from the Guild... They want some red monsters hunted down. You''ll do it for me and save these hands from getting dirty, of course? Wonderful. Good luck, now!','Scheint so, dass die Gilde wieder unsere Hilfe braucht. Einige rote Monster müssen gejagt werden. Das kannst du doch übernehmen, oder? Perfekt, viel Spaß, ich mach''s mir solange bequem.','La Guilde demande à présent que soient chassés des monstres rouges. Vous allez vous charger de cette besogne pour m''éviter de me salir les mains, pas vrai ? Parfait ! Bonne chance !','Parece otro encargo del gremio... Quieren cazar unos monstruos rojos. Hazlo por mí para que no tenga que ensuciarme, ¿sí? Maravilloso. ¡Buena suerte!','Sembra un altro messaggio dalla Gilda... Vogliono che un qualche mostro rosso sia abbattuto. Puoi pensarci al posto mio? Sai, non vorrei sporcarmi le mani... Splendido! Buona fortuna!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (632,'Advanced: Nothing is Sacred','Fortg.: Drachenrache','Av : Rage du dragon ancien','Avda.: dragón celestial','Avanzata: Il vecchio drago','','Slay a Shagaru Magala','Erlege Shagaru Magala.','Tuer le Shagaru Magala','Abate al Shagaru Magala','Uccidi uno Shagaru Magala','','Village','0','HR',1,0,6,16,50,1100,10800,640,'None','–','Rien','Ninguno','Nessuno','','Got word a Shagaru Magala''s turned up at the Sanctuary near Cathar. A thing like that''ll make short work of anythin'' in its path. Ain''t many that could face it, but I know you''re the one to get it done!','Hab gehört, ein Shagaru Magala ist im Heiligtum nahe Cathar aufgetaucht. Diese Dinger machen alles zu Kleinholz, was ihnen im Wege steht. Nur wenige Jäger trauen sich an so was ran. Aber ich hab Vertrauen in dich!','Un Shagaru Magala a été vu au Sanctuaire près de Cathar et je crains qu''il ne fasse un carnage. Vous êtes parmi les rares chasseurs à même de l''affronter, alors occupez-vous-en !','Dicen que se ha visto un Shagaru Magala en la Zona Sagrada cerca de Cathar. Esa cosa destruye todo lo que se cruza en su camino. ¡Pocos cazadores podrían con él, pero tú sí!','Uno Shagaru Magala si è palesato al Santuario vicino Cathar. Quell''enorme bestia abbatterà tutto quello che trova sul suo cammino. Non conosco molti cacciatori all''altezza, tranne te!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (633,'Advanced: Snowtal Recall','Fortg.: Spuren im Schnee','Avancé : Dragon des neiges','Avda.: desafío nevado','Avanzata: Soli nella neve','','Slay a Kushala Daora or repel it','Erlege einen Kushala Daora oder wehre ihn ab.','Tuer ou repousser le Kushala Daora','Abate al Kushala Daora o ahuyéntalo','Uccidi un Kushala Daora o mettilo in fuga','','Village','0','HR',1,0,6,103,35,1200,12000,800,'None','–','Rien','Ninguno','Nessuno','','The Kushala Daora strikes again up in the Arctic Ridge, eh? Now that''s a foe I''m all too familiar with: Ol'' Stormbringer. It''s gonna be cold there, so down some Hot Drinks!','Der Kushala Daora ist also wieder im Schneegebirge aktiv, hm? Dieser Feind ist mir nur zu vertraut, alter Sturmbringer. Wird kalt werden, da oben, nimm ein heißes Getränk mit!','Le Kushala Daora refait des siennes sur la Crête arctique ? Cette créature est une de mes vieilles connaissances... Pensez à boire des boissons chaudes, il fait froid là-haut !','Así que Kushala Daora está otra vez en las Cimas Árticas, ¿eh? Conozco a ese enemigo tormentoso. Va a hacer frío, no olvides tomarte unas bebidas calientes.','Il Kushala Daora è tornato a imperversare sul monte artico, eh? Già, conosco il mio avversario, quel vecchio scatenatempeste. Farà freddo lì fuori, quindi manda giù qualche bevanda calda.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (634,'Advanced: Invisible Thief','Fortg.: Zungenbrecher','Av : Opération caméléon','Avda.: ladrón invisible','Avanzata: Scioglilingua','','Slay a Chameleos or repel it','Erlege einen Chameleos oder wehre ihn ab.','Tuer ou repousser le Chameleos','Abate al Chameleos o ahuyéntalo','Uccidi un Chameleos o mettilo in fuga','','Village','0','HR',1,0,6,2,35,1200,12000,800,'None','–','Rien','Ninguno','Nessuno','','Urgent wire! "Chameleos approaching village [STOP] All available hunters report ASAP [END MESSAGE]" Please hurry, and mind it doesn''t steal your supplies with that blasted tongue!','Eilmitteilung! "Chameleos bei Dorf gesichtet [STOP] Alle verfügbaren Jäger sofort kommen [ENDE DER MITTEILUNG]" Beeil dich bitte, und pass auf, dass dir diese verdammte Zunge nichts klaut!','Un télégramme urgent ! "Chameleos approche village [STOP] Chasseurs priés de se présenter immédiatement [FIN DU MESSAGE]" Faites vite et protégez vos objets de sa fichue langue !','¡Telegrama urgente! "Un Chameleos se acerca a la aldea [STOP] Se llama a todos los cazadores disponibles [FIN MENSAJE]" Date prisa, y cuidado o te robará los suministros.','Telegramma urgente! "Chameleos verso il villaggio [STOP] Tutti i cacciatori disponibili a rapporto [FINE MESSAGGIO]" Vai! E non farti rubare le provviste dalla sua lingua!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (635,'Advanced: Teostra''s Return','Fortg.: Teostras Rückkehr','Avancé : Retour du Teostra','Avda.: retorno del Teostra','Avanzata: Toc toc, Teostra','','Slay a Teostra or repel it','Erlege einen Teostra oder wehre ihn ab.','Tuer ou repousser le Teostra','Abate al Teostra o ahuyéntalo','Uccidi un Teostra o mettilo in fuga','','Village','0','HR',1,0,6,8,35,1200,12000,800,'None','–','Rien','Ninguno','Nessuno','','A Teostra has appeared in the Volcano! Not only are folks in the area in danger, but dragons prowling willy-nilly means I can''t grow my world- famous Prismatic Dandelions! Ward it off, would you?','Ein Teostra ist beim Vulkan aufgetaucht! Das bringt nicht nur die Menschen der Gegend in Gefahr, sondern auch meine preisgekrönte Zucht Spektral- Hundslattich! Kannst du ihn nicht verjagen gehen?','Un Teostra est apparu dans le Volcan ! Ce dragon menace les gens du coin et il m''empêche de faire pousser mes célèbres pissenlits prismiques ! Chassez-le, voulez-vous ?','¡Ha aparecido un Teostra en el Volcán! No solo pone a la gente en peligro, sino que me impide cultivar mis famosos dientes de león prismáticos. ¡Échalo, por favor!','Il Teostra è tornato nel vulcano! Non solo ora le persone sono in pericolo, ma i draghi sono a caccia! Non riuscirò mai a finire il mio famoso terassaco prismatico! Cacciali via, ti prego!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (636,'Advanced: Might is Right','Fortg.: Macht macht Recht','Avancé : Force fait loi','Avanzada: más vale fuerza','Avanzata: Grosso è bello','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,9,50,3000,30000,1580,'None','–','Rien','Ninguno','Nessuno','','When you have it all, the Arena is the only thing that can excite you. But lately, it''s just been a bunch of weaklings! I need a real hunter to come in and show us how it''s done!','Wenn man im Leben alles erreicht hat, bringt nur die Arena noch Spannung. Aber auch da gibt es inzwischen nur noch Schwächlinge. Denen müsste mal ein echter Jäger zeigen, wo''s langgeht!','Quand on possède déjà tout, il ne reste plus que l''Arène pour se sentir vivant. Mais les derniers concurrents étaient des mauviettes. Il faut qu''un vrai chasseur fasse une démonstration !','Cuando lo tienes todo, solo te emociona la Arena. Pero últimamente son todos unos debiluchos. Necesito que vaya un buen cazador y les enseñe cómo se hace.','Quando hai tutto, l''unica cosa che può darti emozioni è l''Arena. Ultimamente, tuttavia, qui ci sono solo pappemolli! Vogliamo un vero asso della caccia che possa dimostrare quanto vale!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (637,'Advanced: State in Flux','Fortg.: Stetiger Wandel','Avancé : Évolution constante','Avda.: estados alterados','Avanzata: Cos''è status?!','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,9,50,3300,32400,1790,'None','–','Rien','Ninguno','Nessuno','','Today''s cast of monsters oughta keep you right busy! That''s because they come packed with attacks that''ll inflict all sorts of Abnormal Statuses! Hope you''re up for a wild ride!','Heute haben wir ein paar Monster für dich, die dich ordentlich beschäftigt halten sollten. Ihre Besonderheit ist, dass sie anormale Statuseffekte verursachen. Mach dich auf einiges gefasst!','Les monstres du jour vont vous résister un moment, notamment à cause des diverses afflictions que vous infligeront leurs attaques. Vous allez en voir de toutes les couleurs !','El elenco de monstruos de hoy debería de mantenerte ocupado. Traen ataques con muchos estados anormales. ¡Espero que te apetezca un rato de emociones fuertes!','Oggi sarà una giornata assai impegnativa per te. È giunto un gruppo di mostri in grado di infliggere ogni tipo di status alterato! Forza, non perdere tempo e lanciati nella mischia!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (638,'Advanced: The Field''s a Stage','Fortg.: Feld oder Bühne','Avancé : Spectacle martial','Avda.: el campo es la arena','Avanzata: Luci della ribalta','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,9,50,2700,27000,1430,'None','–','Rien','Ninguno','Nessuno','','Calling all actors for the role of a lifetime! Everyone''s dreamed of standing in the limelight, and the Arena''s the limest light of all! Are you the star we need to bring the house down at the finale?','An alle Jäger, die gerne im Rampenlicht stehen: Eine einmalige Gelegenheit wartet auf euch – in der Arena, dem strahlendsten aller Orte! Wirst du zum großen Held des Finales?','Vous rêvez de monter sur les planches pour le rôle de votre vie ? Allez à l''Arène, la plus prestigieuse des scènes, où vous deviendrez une star adulée du public !','¡Buscamos actores para un papel histórico! ¿Quién no quiere salir al escenario? Y la Arena es el mejor del mundo. ¿Eres tú la estrella que necesitamos para el gran final?','Siamo alla ricerca di attori per il ruolo della vita! Tutti sognano di salire sul palco, e l''Arena è il palco più importante! Sei tu la stella che può far venire giù il teatro?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (639,'Advanced: Wrath of Rath','Fortgeschritten: Zornes-Rath','Avancé : Colère des Rath','Avanzada: ira de Rath','Avanzata: L''ira del Rath','','Hunt a Silver Rathalos and a Gold Rathian','Erjage einen Silbernen Rathalos und eine Goldene Rathian.','Chasser 1 Rathalos d''argent Chasser 1 Rathian d''or','Caza un Rathalos Plateado y una Rathian Dorada','Caccia un Rathalos argentato e una Rathian dorata','','Village','0','HR',0,0,6,9,50,2300,22800,1180,'None','–','Rien','Ninguno','Nessuno','','You''ve become a fine hunter, so how ''bout a bigger challenge? Picture this: you, versus a Rathalos AND a Rathian! Whaddaya say? Think you can get the wyvern royalty to bow down to you?','Wie wäre es jetzt, wo du ein guter Jäger bist, mit einer größeren Herausfor- derung? Du gegen einen Rathalos UND eine Rathian! Was meinst du? Kannst du die Wyvern-Hoheiten schlagen?','Vous avez bien progressé. Que diriez-vous d''un défi plus corsé ? Imaginez : vous contre un Rathalos ET une Rathian ! Alors ? Vous pensez pouvoir soumettre la crème des wyvernes ?','Te has convertido en un buen cazador, ¿quieres un desafío más difícil? Imagínate: un Rathalos y una Rathian. ¿Qué dices? ¿Podrás someterlos?','Ormai hai maturato un''ottima esperienza di caccia. Cosa ne dici di una sfida più impegnativa? Immagina: tu contro un Rathalos e una Rathian insieme! Pensi di poter conquistare gli onori?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (640,'Advanced: The Golden Pair','Fortgeschritten: Goldenes Paar','Avancé : La paire dorée','Avanzada: el par dorado','Avanzata: La coppia d''oro','','Hunt 2 Rajang','Erjage 2 Rajang.','Chasser 2 Rajang','Caza 2 Rajang','Caccia 2 Rajang','','Village','0','HR',0,0,6,9,50,1900,18600,880,'None','–','Rien','Ninguno','Nessuno','','I''ve got something special whipped up for today: two Rajang sporting shiny gold coats! Hope you''re ready to give them all you''ve got, ''cause they don''t know how to go easy on anybody!','Heute steht etwas Besonderes auf dem Programm: zwei Rajang im goldenen Mantel! Hoffentlich bist du gut in Form, denn diese Burschen geben grundsätzlich Vollgas!','J''ai une mission spéciale à vous confier : traquer deux Rajang au pelage doré ! Et j''espère que vous êtes en forme, car ces bestioles ne sont pas connues pour faire dans la dentelle !','Tengo algo especial para hoy: ¡dos Rajang con abrigo dorado! Espero que estés listo para darlo todo, porque ellos no tienen compasión por nadie.','Oggi ho qualcosa di molto speciale per te: due Rajang con le loro scintillanti pellicce dorate! Spero tu sia in forma, perché loro non si risparmieranno!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (641,'Advanced: Out of the Fry Pan','Fortg.: Auf heißem Eisen','Av : De Charybde en Scylla','Avda.: salir de la sartén','Avanzata: Dalla padella...','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','HR',5,0,6,9,50,2200,21600,1010,'None','–','Rien','Ninguno','Nessuno','','You must be the hunter that nobody can shut up about! To hear them tell it, there ain''t no one better. But I''ll only believe it when I see it myself, so show me proof you''re hot stuff!','Du musst der Jäger sein, von dem sie alle erzählen! Einen besseren scheint''s ja kaum zu geben. Trotzdem: Ich glaube nur, was ich sehe. Lass sehen, was du auf dem Kasten hast!','Je parie que c''est de vous dont tout le monde parle ! D''après la rumeur, aucun chasseur ne vous arrive à la cheville, mais j''attends de voir. Prouvez que votre réputation est méritée !','Debes ser el cazador de quien todo el mundo habla. Según dicen, eres el mejor. Pero yo lo creeré cuando lo vea, así que a ver de qué eres capaz.','Tu devi essere quel fenomeno della caccia di cui tutti parlano! Ho sentito ogni genere di storiella, ma ci crederò solo quando lo vedrò di persona. Dimostrami che quel che si racconta è vero!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10101,'Harvest Tour: Jurassic Frontier','Erntetour: Jura-Grenzland','Visite : Frontière jurassique','Recolección: Fronda Arcaica','Tour: Frontiera giurassica','','Survive until time expires or deliver a Paw Pass Ticket','Überlebe bis Zeitende oder gib einen Tatzenpass ab.','Temps expiré ou livrer un ticket Passechat','Aguanta hasta que acabe el tiempo o da un ticket zarpa','Sopravvivi fino allo scadere o consegna buono pass-zampa','','Guild','0','LR',3,0,1,1,50,0,12,0,'None','–','Rien','Ninguno','Nessuno','','Interested in gathering in the Jurassic Frontier, young Hunter? Take this Quest and you can gather as much as you like! Just deliver the Paw Pass Ticket when you''re ready to come back.','Bist du vielleicht an einer Erntetour im Jura-Grenzland interessiert? Nimm einfach diese Quest an, um deine Vorräte aufzufüllen. Liefere dann deinen Tatzenpass ab, um zurückzukehren.','Que diriez-vous d''une expédition de collecte à la Frontière jurassique ? Acceptez et vous pourrez garder tous les objets ! Livrez le ticket Passechat pour rentrer.','¿Quieres ir a recoger a la Fronda Arcaica? Acepta esta misión y recoge todo lo que quieras. Solo tienes que entregar el ticket zarpa cuando quieras volver a casa.','Vuoi depredare la frontiera giurassica? Oh, accetta questa missione e potrai raccogliere tutto ciò che vuoi! Per tornare a casa dovrai solo consegnare il buono pass-zampa.','',0,0);
