INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10106,'Boot to the Head','Stiefel am Kinn','En pleine tête','Bota en la cabeza','Gran calci sul muso','','Hunt a Great Maccao','Erjage einen Groß-Maccao.','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','Guild','0','LR',0,0,1,101,50,200,1800,100,'Deliver 5 Abyssal Mushrooms','5 Unheil-Pilze abliefern','Livrer 5 champis abyssaux','Entrega 5 setas abismales','Consegna 5 funghi abissali','','Ever taken a jump kick from a Great Maccao? I have! It nailed me, but I got up and wrecked it hard! You jelly? If you think you can do the same, then let me see what you got!','Schon mal einen Sprungtritt von einem Groß-Maccao abbekommen? Hat mich glatt umgehauen, aber ich hab''s ihm heimgezahlt! Wenn du''s mir nachtun möchtest, lass sehen, was du kannst, okay?','Vous avez déjà encaissé un coup de pied sauté d''un Grand Maccao ? Moi, oui ! Je l''ai senti passer, mais je me suis relevé pour le dérouiller. Vous pouvez en faire autant ?','¿Alguna vez has recibido una patada de un Gran Maccao? ¡Yo sí! Y me levanté y le di una buena. Si crees que podrías hacer lo mismo, demuéstralo.','Hai mai incassato un calcio volante da un Gran Maccao? Ah, io sì! Mi ha beccato in pieno, ma poi gliele ho suonate! Pensi di poter fare di meglio? D''accordo, allora dimostramelo!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10107,'Tackling the Tetsucabra','Tetsucabra-Tackling','Tetsucabra coriace','Duelo con Tetsucabra','Un Tetsucabra da domare','','Hunt a Tetsucabra','Erjage einen Tetsucabra.','Chasser 1 Tetsucabra','Caza un Tetsucabra','Caccia un Tetsucabra','','Guild','1','LR',0,0,1,1,50,500,4200,250,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Rock hard! That just about sums up what it''s like to fight that thing and see your attacks bounce off those boulders it digs up... I give up! How about you, though? Up for a challenge?','Beinhart! So lässt sich ein Kampf gegen dieses Ding am besten beschreiben. Angriffe prallen ab von den Fels- brocken, die es ausgräbt. Ich gebe es auf. Und du? Suchst du Herausforderung?','Dur, dur ! Voilà l''effet que ça fait d''affronter cette bête et de voir sa lame ricocher sur les rochers qu''elle déterre ! Je jette l''éponge, mais vous pouvez tenter le coup. Chiche ?','¡Como una roca! Así es como te sientes al luchar contra esa cosa, y ver que todos tus ataques rebotan en esas rocas que excava. ¡Me rindo! Pero, ¿y tú? ¿Te apetece un desafío?','Contro un muro! È così che ci si sente a combattere contro quel mostro e vedere i tuoi attacchi rimbalzare sulle rocce che solleva... Beh, ci rinuncio! Tu che ne pensi? Ti va una sfida?','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10108,'Lost in the Jurassic Frontier','Verschollen im Jura-Grenzland','Parc jurassique','Perdido en Fronda Arcaica','Perso nella frontiera','','Hunt a Gypceros','Erjage einen Gypceros.','Chasser 1 Gypceros','Caza un Gypceros','Caccia un Gypceros','','Guild','1','LR',0,0,1,1,50,400,3600,240,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','My luck is so bad lately. First a Gypceros swipes my Book of Combos, and then a Melynx runs off with my BBQ Spit! At this rate, I''m gonna have to quit hunting! Can you help me out here!?','Ich habe solch ein Pech in letzter Zeit. Erst stiehlt ein Gypceros mein Kombi- Buch und dann stibitzt eine Melynx meinen Grillspieß. Wenn''s so weitergeht, war''s das für mich. Bitte hilf mir!','J''ai une de ces poisses ! Un Gypceros m''a volé mon tome d''alchimie et un Melynx m''a dérobé ma broche BBQ ! À ce rythme, je vais devoir abandonner la chasse ! Aidez-moi !','Últimamente tengo muy mala suerte. Primero un Gypceros me roba el Libro combos, y después un Melynx mi asador de barbacoa. A este paso, no podré cazar. ¿Podrías echarme una mano?','Bah, non è proprio periodo! Prima un Gypceros ha rubato il mio libro alchimia, poi un Melynx mi ha fregato lo spiedo barbecue! Se continua così dovrò smettere di cacciare! Puoi aiutarmi?','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10109,'Red-Faced Ruffians','Rotgesichtige Raufbolde','Voyous rougeauds','Matones de cara roja','Paonazzi in volto','','Slay 20 Maccao','Erlege 20 Maccaos.','Tuer 20 Maccao','Abate a 20 Maccao','Uccidi 20 Maccao','','Guild','0','LR',1,0,1,1,50,200,1800,90,'Hunt a Great Maccao','Einen Groß-Maccao erjagen','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','I was taking a walk when a herd of Maccao surrounded me! Those blood-red faces... I was left catatonic for days! I''m too afraid to go out on walks now. Could you purrlease help me, Hunter?','Beim Spaziergang hat mich plötzlich eine Herde Maccaos überfallen! Wenn ich nur an ihre roten Gesichter denke! Ich war tagelang starr vor Schreck! Kannst du mir b-b-bitte helfen?','Je suis resté prostré plusieurs jours après m''être fait encercler par des Maccao pendant une promenade et je n''ose plus sortir. Accepteriez- vous de m''aider ?','Estaba dando un paseo y una manada de Maccao me rodeó. Esos rostros rojos... ¡Me pasé días catatónico! Ahora tengo miauedo y no salgo. ¿Me ayudas, cazador?','Stavo gattonando quando un branco di Maccao mi ha circondato! Miah, quei musi rosso sangue! Sono rimasto gattatonico per giorni! Ho troppa paura per uscire, ora. Potresti aiutarmi?','',1800,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10110,'A Fern is Forever','Ewiger Farn','Fougère éternelle','Un helecho es para siempre','Una felce è per sempre','','Deliver 20 Unique Ferns','Liefere 20 Einmalige Farne ab.','Livrer 20 fougères uniques','Entrega 20 helechos únicos','Consegna 20 felci speciali','','Guild','0','LR',3,0,1,101,50,200,1200,90,'Slay 8 Jaggi','8 Jaggi erlegen','Tuer 8 Jaggi','Abate a 8 Jaggi','Uccidi 8 Jaggi','','A Wycademy kiddo found *hiccup* a boutique urn... I mean a *hiccup* Unique Fern! It''s bitter, yet sweet, which would go great with my nightly tipple. *hiccup* Be a pal and nab me some of them, won''t ya?','Einer von der Wykademie hat *hicks* einen Einmaligen Farn aufgetrieben! Schmeckt bitter und süß, ideal für einen kleinen *hicks* Schlummertrunk. Bring mir ein paar, ja?','Un gamin de la Wycadémie a trouvé *hips* une congère... Pardon, une *hips* fougère unique ! Son goût doux-amer irait bien avec mon verre du soir. *hips* Soyez sympa et allez m''en chercher !','Un chaval de la Wycademia ¡hic! encontró un hecho... ¡hic! digo helecho único. Es amargo y dulce, me iría muy bien para mi trago de buenas noches. ¡Hic! ¿Me vas a buscar algunos?','Un tizio della Wyccademia ha *hic* trovato una falce speziata... Cioè, una felce speciale! È amara e dolce, sarebbe ottima per i miei cicchetti notturni. *Hic* Me ne porteresti qualcuna?','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10111,'Gendrome Roadblock','Gendrome-Straßensperre','Barrage de Gendrome','Bloqueo Gendrome','Ingorgo di Gendrome','','Hunt a Gendrome','Erjage einen Gendrome.','Chasser 1 Gendrome','Caza un Gendrome','Caccia un Gendrome','','Guild','0','LR',0,0,1,5,50,300,2100,130,'Wound Gendrome''s head','Gendrome-Kopf verletzen','Blesser la tête du Gendrome','Hiere Gendrome en la cabeza','Ferisci la testa del Gendrome','','I''ve been ferrying goods for a big deal coming up, only packs of Genprey keep messing things up. I''d bet there''s an alpha behind it. Take ''em out so my deal won''t fall through!','Beim Warentransport für ein wichtiges Geschäft kamen mir Genprey in die Quere. Bestimmt steckt da so ein Alpha dahinter. Mach sie unschädlich, damit mein Geschäft nicht scheitert!','Je transporte de la marchandise pour un gros contrat, mais des Genprey m''attaquent sans cesse. Il doit y avoir un alpha dans la meute. Tuez-le ou mon contrat tombe à l''eau.','Llevo mercancías para un negocio importante, pero hay manadas de Genprey que me ponen las cosas difíciles. Apuesto a que hay un alfa detrás. ¡Salva mi negocio! ¡Mátalos!','Sto trasportando delle merci per un importante affare, ma un gruppo di Genprey mi tormenta. Scommetto che c''è un alfa dietro! Devi farli fuori, altrimenti il mio accordo salterà!','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10112,'In Pursuit of the Sand Wyvern','Verfolgung des Sandwyvern','Sus à la wyverne des sables','Caza al wyvern de las arenas','Wyvern della sabbia','','Hunt a Cephadrome','Erjage einen Cephadrome.','Chasser 1 Cephadrome','Caza un Cephadrome','Caccia un Cephadrome','','Guild','0','LR',0,0,1,5,50,300,2700,140,'Slay 5 Cephalos','5 Cephalos erlegen','Tuer 5 Cephalos','Abate a 5 Cephalos','Uccidi 5 Cephalos','','When a Cephadrome shows up somewhere, a bunch of Cephalos follow suit and wreak havoc on our trade routes! If you take out the Cephadrome, that should get rid of the lot of ''em!','Sobald ein Cephadrome auftaucht, folgen ihm viele Cephalos, die unsere Handelsroute stören. Wenn du ihn beseitigen könntest, wäre das Problem wahrscheinlich gelöst!','Un Cephadrome qui déboule quelque part est toujours suivi de Cephalos qui perturbent nos routes commerciales. Éliminez le premier et les seconds nous laisseront en paix !','Si aparece un Cephadrome en alguna parte, enseguida vienen Cephalos y arrasan nuestras rutas comerciales. Acaba con el Cephadrome, así se dispersarán.','Quando un Cephadrome giunge in zona, subito lo seguono dei Cephalos, che poi creano scompiglio sulle nostre rotte commerciali! Abbatti il Cephadrome e risolverai entrambi i problemi!','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10113,'Shells of Steel','Panzer aus Stahl','Carapaces d''acier','Caparazones de acero','Granchi giganti nemici','','Hunt a Daimyo Hermitaur','Erjage einen Daimyo Hermitaur.','Chasser 1 Hermitaur Daimyo','Caza un Daimyo Hermitaur','Caccia un Daimyo Hermitaur','','Guild','0','LR',0,0,1,5,50,500,4200,250,'Break Daimyo''s shell','D.-Hermitaur-Panzer brechen','Briser la carapace du Daimyo','Rompe caparazón del Hermitaur','Rompi corazza del D.Hermitaur','','I hate to ask hunters for help, but there''s a huge Hermitaur out in the Dunes that just won''t go down nicely. No matter what I do, I can''t hurt it. I need you to go after it for me.','Ich frage nicht gern einen Jäger um Hilfe, aber ein riesiger Hermitaur ist in den Dünen und lässt sich nicht vertreiben. Ich bin machtlos gegen ihn. Du musst dich um ihn kümmern.','Je déteste demander de l''aide aux chasseurs, mais je n''arrive pas à vaincre l''Hermitaur des Dunes. J''ai beau frapper, rien n''y fait. Il faudrait que vous vous en chargiez.','No me gusta pedir ayuda a los cazadores, pero hay un Hermitaur enorme en las Dunas y no puedo con él. No consigo herirlo. Necesito que vayas tú en mi lugar.','Detesto chiedere aiuto ai cacciatori, ma c''è un enorme Hermitaur sulle dune che proprio non vuole andare giù. Ho provato di tutto, ma non riesco a ferirlo. Mi serve che tu lo sconfigga.','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10114,'Hermitaur Party','Hermitaur-Party','La fête des Hermitaur','Grupo de Hermitaur','Hermitaur ovunque!','','Slay 20 Hermitaurs','Erlege 20 Hermitauren.','Tuer 20 Hermitaur','Abate a 20 Hermitaur','Uccidi 20 Hermitaur','','Guild','0','LR',1,0,1,5,50,300,2400,90,'Deliver 1 Crab Pearl','1 Krabbenperle abliefern','Livrer 1 perle de crabe','Entrega 1 perla de cangrejo','Consegna una perla granchio','','Hermitaurs are getting to be a real pain... They wait in the sand to ambush us traders, and we can''t get anything done! Help us out! Careful, though: A Daimyo Hermitaur''s been sighted, too!','Die Hermitauren werden eine echte Plage. Sie lauern im Sand den Händlern auf, sodass wir kaum unseren Geschäften nachkommen können. Aber sei vorsichtig! Auch ein Daimyo Hermitaur ist mit dabei!','Les Hermitaur enfouis dans le sable menacent de plus en plus nos activités commerciales. Aidez-nous ! Mais attention, on signale aussi la présence d''un Hermitaur Daimyo !','Los Hermitaur son una grave molestia... Se ocultan en la arena para emboscarnos, y no podemos hacer nada. ¡Ayúdanos! Pero cuidado... hay un Daimyo Hermitaur.','Gli Hermitaur stanno diventando un problema. attendono nella sabbia per sorprenderci e non possiamo fare nulla! Aiutaci! E occhi aperti, perché è stato visto anche un Daimyo Hermitaur!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10115,'Ingredient Hunter','Zutatenjäger','Chasseur d''ingrédients','Cazador de ingredientes','Caccia di ingredienti','','Deliver 20 Tropical Berries','Liefere 20 Tropische Beeren ab.','Livrer 20 baies tropicales','Entrega 20 bayas tropicales','Consegna 20 bacche tropicali','','Guild','0','LR',3,0,1,5,50,200,1200,90,'Deliver 3 Piscine Livers','3 Pisces-Lebern abliefern','Livrer 3 foies de wyverne des mers','Entrega 3 hígados de nadador','Consegna 3 fegati di Piscine','','I can see the desire in your eyes... The desire for new ingredients, miao! If you purrcure some Tropical Berries for me, I''ll paw over some of my special ingredients!','Ich kann das Verlangen in deinen Augen sehen... Das Verlangen nach neuen Zutaten! Wenn du mir einige Tropische Beeren besorgst, tatze ich dir einige meiner besonderen Zutaten in die Kralle!','Je vois la flamme dans votre regard... Le désir pour de nouveaux ingrédients ! Apportez-moi des baies tropicales, et je vous donnerai quelques-uns de mes ingrédients spéciaux !','Puedo ver el deseo en sus ojos... ¡El deseo de nuevos ingredientes, miao! Si puedes conseguirme algunas bayas tropicales, te daré alguno de mis ingredientes especiales, miao.','Leggo nei tuoi occhi la brama... brama di nuovi ingredienti! Potresti procurarmi dei dolcetti tropicali? Aiutami e verrai ricompensato con degli ingredienti speciali!','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10116,'Hunt Down the Velocidrome!','Jag den Velocidrome!','Pourchassez le Velocidrome !','¡Caza al Velocidrome!','Caccia il Velocidrome!','','Hunt a Velocidrome','Erjage einen Velocidrome.','Chasser 1 Velocidrome','Caza un Velocidrome','Caccia un Velocidrome','','Guild','0','LR',0,0,1,102,50,200,1800,100,'Wound Velocidrome''s head','Velocidrome-Kopf verletzen','Blesser la tête du Velocidrome','Hiere Velocidrome en la cabeza','Ferisci la testa del Velocidrome','','My wife was in the Verdant Hills, picking some of my favorite mushrooms, when she was almost attacked by some wyverns! Please, make the woods safe for her again!','Meine Frau wollte für mich in den Grünen Hügeln Pilze sammeln, als sie beinahe von Wyvern angegriffen wurde! Bitte sorge dafür, dass sie sich im Wald wieder sicher fühlt!','Ma femme a échappé de peu à des wyvernes alors qu''elle ramassait des champignons dans les Collines verdoyantes. Faites en sorte qu''elle puisse y retourner en toute sécurité.','Mi esposa estaba en el Bosque Rocoso recogiendo setas y casi la atacaron unos wyverns. Haz que los bosques vuelvan a ser seguros para ella.','Mia moglie è andata sui colli verdi a raccogliere i miei funghi preferiti, ma ha quasi rischiato di essere assalita dai wyvern! Sii gentile, rendi la foresta più sicura per lei!','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10117,'Local Threat','Lokale Bedrohung','Kut-ku c''est nous !','¡Caza al Yian Kut-Ku!','Minaccia locale','','Hunt a Yian Kut-Ku','Erjage einen Yian Kut-Ku.','Chasser 1 Yian Kut-Ku','Caza un Yian Kut-Ku','Caccia uno Yian Kut-Ku','','Guild','1','LR',0,0,1,102,50,300,3000,200,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Yian Kut-Ku have been going after people all over the place, and during my patrol earlier, I spotted one wandering close to town. That curious bugger must be after some Konchu. Can you get rid of it?','Überall greifen Yian Kut-Ku die Menschen an. Bei meinem Rundgang ist mir in der Nähe der Stadt einer von ihnen aufgefallen. Bestimmt hat er es auf die Konchu abgesehen. Kannst du ihn vertreiben?','Les Yian Kut-Ku menacent les gens du coin et j''en ai même vu un s''aventurer dans le village lors de ma dernière patrouille. Il devait chercher un Konchu. Vous pouvez le chasser d''ici ?','Los Yian Kut-Ku persiguen gente por todas partes. En mi patrulla vi a uno muy cerca de la ciudad. Debía de estar buscando Konchu. ¿Podrías librarte de él?','Gli Yian Kut-Ku attaccano di continuo le persone! Durante la mia ronda ne ho visto uno persino vicino alla città. Quel mostro deve essere a caccia di Konchu. Potresti liberarcene?','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10118,'Financial Focus on Fungus','Finanzieller Fungus-Fokus!','Ruée sur les champignons','¡La fiebre de la seta!','Tutti pazzi per i funghi!','','Deliver 20 Unique Mushrooms','Liefere 20 Einmalige Pilze ab.','Livrer 20 champis spéciaux','Entrega 20 setas especiales','Consegna 20 funghi speciali','','Guild','0','LR',3,0,1,2,50,200,1200,90,'Deliver 5 Royal Rhinos','5 Königliche Kabuto abliefern','Livrer 5 rhinos royaux','Entrega 5 rinocerontes reales','Consegna 5 kabuto reali','','Mushrooms from the Verdant Hills are suddenly in high demand. If you gather me 20, I''ll give you a cut of the profits. Oh, I heard that Gypceros hang around there, so stay sharp!','Pilze aus den Grünen Hügeln sind aktuell sehr begehrt. Wenn du mir 20 bringst, beteilige ich dich gerne am Gewinn. Ach ja, ich hörte von dem Gypceros in der Gegend. Bleib wachsam!','Les champignons des Collines verdoyantes sont très prisés en ce moment. Si vous m''en rapportez 20, on partagera les bénéfices. Et méfiez-vous des Gypceros, compris ?','Hay mucha demanda de setas del Bosque Rocoso. Si me consigues 20, te daré parte de los beneficios. Ah, y he oído que hay Gypceros por ahí, ¡así que atento!','Ultimamente c''è una forte richiesta di funghi dai colli verdi. Portamene venti e ti elargirò parte dei profitti. Oh, ho sentito che ci sono dei Gypceros in quella zona, quindi occhio!','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10119,'Farming Frustrations','Listige Angreifer','Des pillards rusés','Astutos atacantes','Astuti predoni','','Slay 20 Velociprey','Erlege 20 Velociprey.','Tuer 20 Velociprey','Abate a 20 Velociprey','Uccidi 20 Velociprey','','Guild','0','LR',1,0,1,2,50,200,1800,90,'Hunt a Velocidrome','Einen Velocidrome erjagen','Chasser 1 Velocidrome','Caza un Velocidrome','Caccia un Velocidrome','','The Velociprey that live up in them there Verdant Hills are a real annoyance, going after our livestock and just being downright ornery. Put a hurtin'' on 20 fer me, will ya? Just be careful of their boss!','Die Velociprey in den Grünen Hügeln sind eine echte Plage. Sie jagen unsere Tiere und machen viel Ärger. Ich möchte, dass du 20 von ihnen erlegst. Aber sei auf der Hut vor ihrem Anführer!','Les Velociprey des Collines verdoyantes sont une vraie plaie. Ils s''en prennent au bétail et nous rendent la vie impossible ! Allez en dérouiller 20, mais gare à leur chef !','Los Velociprey del Bosque Rocoso son un dolor de cabeza: atacan el ganado y son un grave problema. Quiero que caces a 20, pero cuidado con su jefe.','I Velociprey che vivono sui colli verdi sono una vera seccatura. Attaccano il bestiame e sono parecchio irascibili. Ne cacceresti venti per me? Ma guardati dal loro leader!','',1800,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10120,'Snow with Occasional Lagombi','Schnee, vereinzelt Lagombi','Risque d''averse de Lagombi','Lagombi chubascosos','Neve mista Lagombi','','Hunt a Lagombi','Erjage einen Lagombi.','Chasser 1 Lagombi','Caza un Lagombi','Caccia un Lagombi','','Guild','1','LR',0,0,1,103,50,300,3000,140,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','[REPORT BEGINS] Lagombi spotted in the Arctic Ridge [STOP] Requesting support from all qualified hunters [STOP] We are snowed in and cannot render assistance [REPORT ENDS]','[BERICHT] Lagombi im Schneegebirge gesichtet. [STOP] Erbitten Hilfe von allen qualifizierten Jägern. [STOP] Wir sind eingeschneit und können keine Hilfe leisten. [BERICHT ENDE]','[DÉBUT DU RAPPORT] Lagombi repéré sur Crête arctique. [STOP] Demandons aide de chasseurs qualifiés. [STOP] Aide impossible pour cause de neige. [FIN DU RAPPORT]','[INICIO INFORME] Lagombi avistado en Cimas Árticas. [STOP] Solicitamos apoyo de todos los cazadores. [STOP] Estamos aislados por la nieve y no podemos ayudar. [FIN INFORME]','[INIZIO MESSAGGIO] Lagombi avvistato sul monte artico [STOP] Si richiede supporto immediato [STOP] Siamo bloccati dalla neve e non possiamo prestare soccorso [FINE MESSAGGIO]','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10121,'Preying on Giaprey','Giaprey-Gier','Chasse aux Giaprey','¡A por la carne del carnívoro!','Giaprey come preda','','Slay 20 Giaprey','Erlege 20 Giaprey.','Tuer 20 Giaprey','Abate a 20 Giaprey','Uccidi 20 Giaprey','','Guild','0','LR',1,0,1,3,50,200,1800,90,'Deliver 2 White Livers','2 Weiße Lebern abliefern','Livrer 2 foies blancs','Entrega 2 hígados blancos','Consegna 2 fegati bianchi','','I''ve got bad news: A pack of Giaprey is going after our villagers and they need to be stopped! If some kind hunter is up for taking out 20 of the beasts, that''d be great!','Schlechte Nachrichten: Ein Rudel Giaprey macht Jagd auf die Dorfbewohner. Sie müssen gestoppt werden! Wenn ein Jäger 20 von ihnen erledigen könnte, wäre das großartig!','Mauvaise nouvelle ! Une meute de Giaprey s''en prend à nos villageois. Il faut à tout prix les arrêter ! Nous remercions d''avance tout chasseur qui acceptera d''en éliminer 20 !','Tengo malas noticias: una manada de Giaprey persigue a nuestros aldeanos. ¡Hay que detenerlos! Si algún cazador pudiese matar a 20, sería maravilloso.','Ho pessime notizie: un branco di Giaprey sta attaccando i nostri cittadini e deve essere fermato! Se un cacciatore potesse eliminarne venti sarebbe grandioso!','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10122,'Special Home Brew','Spezielles Heimbräu','Décoction maison','¡Busca hierbas de montaña!','Trova le erbe di montagna!','','Deliver 20 Mountain Herbs','Liefere 20 Gebirgskräuter ab.','Livrer 20 herbes de montagne','Entrega 20 hierbas montaña','Consegna 20 erbe di montagna','','Guild','0','LR',3,0,1,3,50,200,1200,90,'Deliver 1 Khezu Whelp','1 Khezu-Larve abliefern','Livrer 1 Bébé Khezu','Entrega 1 larva de Khezu','Consegna una larva Khezu','','My artisanal home brews steeped in Mountain Herbs are the epitome of liquid refreshment! I want to make more, but I''ve run out of herbs! I need a refill stat, so go pick me 20, okay?','Meine hausgemachten Berg- kräutertinkturen sind die Krönung aller Erfrischungs- getränke. Zu gern würde ich mehr davon herstellen, aber mir fehlen die Kräuter! Bitte bring mir 20, okay?','Ma bière artisanale aux herbes de montagne est la boisson rafraîchissante ultime. Hélas, je suis à court d''herbes. Puis-je compter sur vous pour m''en rapporter 20 ?','Mis caldos caseros hervidos con hierbas de montaña son el mejor refresco. Quiero hacer más, pero no me quedan hierbas. Ve a recoger 20, ¿quieres?','I miei intrugli con erbe di montagna sono un vero e proprio ristoro in forma liquida! Vorrei prepararne altri, ma ho finito le erbe! Me ne servono assolutamente altre: potresti portamene venti?','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10123,'Bulldrome of the Peaks','Gipfel-Bulldrome','Bulldrome des Pics','Bulldrome de los Picos','Quella cima del Bulldrome','','Hunt a Bulldrome','Erjage einen Bulldrome.','Chasser 1 Bulldrome','Caza un Bulldrome','Caccia un Bulldrome','','Guild','0','LR',0,0,1,4,50,300,2100,120,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Gah! I was on my way back from some nice hot springs when BAM! I''m surrounded by Bulldrome -- angry ones! Visiting the inside of a gullet wasn''t on my itinerary! Help get me outta here!','Pah! Ich war auf dem Rück- weg von einer heißen Quelle, als ich *BAM!* von Bulldrome umzingelt war. Im Schlund einer Bestie zu landen, ist bestimmt nicht Teil meines Kurprogramms! Rette mich!','Je rentrais des sources chaudes, quand des Bulldrome furieux me sont tombés dessus. Visiter un estomac n''était pas vraiment dans mes intentions. Aidez-moi à filer d''ici !','¡Bah! Volvía del balneario y de repente me rodearon Bulldrome. ¡Muy enfadados! Visitar un esófago no entraba en mis planes. ¡Ayúdame a salir de aquí!','Ah! Stavo tornando da una fonte termale quando BAM! Sono stato circondato da Bulldrome! E infuriati, persino! Essere mangiato non era nei miei piani! Aiutami a uscire di qui!','',300,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10124,'The Ingredient of Legend','Die Zutaten einer Legende','Ingrédient légendaire','El ingrediente legendario','Ingrediente leggendario','','Hunt an Arzuros','Erjage einen Arzuros.','Chasser 1 Arzuros','Caza un Arzuros','Caccia un Arzuros','','Guild','0','LR',0,0,1,104,50,300,2100,130,'Deliver 5 Royal Honeys','5 Einheiten Königshonig abliefern','Livrer 5 pots de miel royal','Entrega 5 jaleas reales','Consegna 5 mieli reali','','I have tracked down a legendary ingredient in ze Misty Peaks! Keep ze Arzuros busy, and I will purrcure ze ingredient! Then we will be un paw de plus to a legendary dish!','In den Nebelgipfeln befindet sich eine legendäre Zutat! Beschäftige den Arzuros, dann kralle ich mir die Zutat! Wir sind nur einen Prankenhieb von einem Gericht für die Kochbücher entfernt!','J''ai retrouvé la trace d''un ingrédient légendaire dans les Pics brumeux ! Occupez-vous de cet Arzuros, et je récolterai l''ingrédient ! Nous ferons une patte de plus vers la concotion d''un nouveau plat !','¡He divisado un ingrediente legendario en los Picos Nublados! ¡Mantén ocupado al Arzuros, y podré ir a recogerlo! Estaremos una patita más cerca de un plato legendario.','Ecco dove si nascondeva l''ingrediente leggendario... Sulle cime nebbiose! Tieni quel monstre occupato, così potrò procurarmi tutto il necessario per creare delle spécialité délicieux!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10125,'Royal Spit Take','Königlicher Wasserspucker','Sauvons notre navire','Arregla nuestro barco','S.O.S.','','Hunt a Royal Ludroth','Erjage einen Königs-Ludroth.','Chasser 1 Ludroth royal','Caza un Ludroth Real','Caccia un Ludroth reale','','Guild','1','LR',0,0,1,4,50,500,4200,250,'Wound R.Ludroth''s mane','Königs-Ludroth-Mähne verletzen','Blesser la crinière du Ludroth royal','Hiere Lud. Real en la crin','Ferisci criniera Ludroth reale','','Ick! I got splattered with weird gunk that sapped my Stamina, so I couldn''t even run! Luckily, a Felyne came by and saved me, but now whenever I see anything yellow...urgh!','Igitt! Ich bin von diesem seltsamen Glibber bedeckt, der meine Ausdauer angreift. Ich kann nicht mal laufen! Zum Glück wurde ich durch eine Felyne gerettet. Aber die Farbe gelb? Grauenhaft!','Beurk ! Des éclaboussures bizarres ont drainé ma stamina et je ne pouvais plus courir ! Heureusement, un Felyne est venu m''aider. Maintenant, la seule vue du jaune me soulève le cœur !','¡Bah! Me han salpicado con algo mugriento que me robó la resistencia. ¡No podía ni correr! Por suerte, pasó un Felyne y me salvó, pero ahora, si veo algo amarillo... ¡Argh!','Ick! Mi hanno ricoperto di una schifezza che ha assorbito la mia resistenza, quindi non riuscivo a correre! Un Felyne mi ha salvato, ma se ora vedo qualcosa di giallo... Bleah!','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10126,'A Mother''s Woes','Muttersorgen','Le chagrin d''une mère','Aflicción de madre','Il dolore di una madre','','Slay 10 Ludroth','Erlege 10 Ludroth.','Tuer 10 Ludroth','Abate a 10 Ludroth','Uccidi 10 Ludroth','','Guild','0','LR',1,0,1,4,50,200,1500,90,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','My son can''t stop wetting the bed! I figure I can at least stop having to dry his sheets every day if I can make a cover out of absorbent Ludroth parts... Please get some for me!','Mein Söhnchen macht immerzu ins Bett. Wenn ich Laken aus saugfähigen Ludroth-Teilen hätte, könnte ich mir das ständige Trock- nen sparen. Bitte bring mir welche, in Ordnung?','Mon fils fait encore pipi au lit ! Pour ne pas avoir à changer toute sa literie, je peux faire fabriquer des draps absorbants en Ludroth. Allez me chercher ce qu''il faut, je vous prie.','Mi hijo no para de mojar la cama. Si al menos tuviese un cubrecamas absorbente hecho de partes de Ludroth, me iría mejor. Consígueme unas cuantas, por favor.','Mio figlio non fa altro che bagnare il letto! Potrei almeno finirla di asciugare di continuo le lenzuola, se avessi le parti assorbenti del Ludroth... Per favore, portamene qualcuna!','',600,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10127,'Jaggi Junket','Jaggi-Quark','La fête des Jaggi','Cuajada de Jaggi','Il buffone dei Jaggi','','Slay 20 Jaggi','Erlege 20 Jaggi.','Tuer 20 Jaggi','Abate a 20 Jaggi','Uccidi 20 Jaggi','','Guild','0','LR',1,0,1,4,50,200,1800,90,'Slay 8 Jaggia','8 Jaggia erlegen','Tuer 8 Jaggia','Abate a 8 Jaggia','Uccidi 8 Jaggia','','Are you going to the Misty Peaks? How about we make it interesting, and see how many Jaggi you can slay? I like to make all my retainers compete that way. Come on, it''ll be fun!','Bist du unterwegs zu den Nebelgipfeln? Wie wär''s da mit einer Herausforderung? Sieh, wie viele Jaggi du erlegen kannst! Das verlange ich von allen in meinem Gefolge. Komm schon!','Vous partez pour les Pics brumeux ? Et si vous pimentiez votre voyage en tuant un maximum de Jaggi ? J''aime bien lancer ce défi à mes laquais. Vous verrez, c''est amusant !','¿Te diriges a los Picos Nublados? Hagámoslo más interesante: a ver cuántos Jaggi puedes matar. Me gusta que mis siervos compitan. ¡Será divertido!','Stai andando sulle cime nebbiose? Vogliamo vedere quanti Jaggi riesci a uccidere? Sai, adoro far competere i miei sudditi. Andiamo, sarà spassoso!','',1200,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10128,'The Next Best Thing','Nächstbeste Lösung','À défaut de mieux','La mejor alternativa','Sempre meglio di nulla','','Deliver 10 Balmstone Pieces','Liefere 10 Balsamstein-Stücke ab.','Livrer 10 morceaux d''odoripierre','Entrega 10 piezas ónix','Consegna 10 pezzi di balsamite','','Guild','0','LR',3,0,1,104,50,200,1800,90,'Deliver 5 Royal Rhinos','5 Königliche Kabuto abliefern','Livrer 5 rhinos royaux','Entrega 5 rinocerontes reales','Consegna 5 kabuto reali','','My sweetheart just adores flowers, so it came to me: Why not give him a Balm- stone? The scent of flowers in your pocket! You must find me one! But, I suppose pieces would do just as well.','Mein Schatz liebt Blumen. Deshalb möchte ich ihm Balsamstein schenken. Blumenduft, den man immer bei sich trägt! Du musst für mich einen finden! Oder ein paar Stückchen, ja?','Mon chéri adore les fleurs, alors j''ai eu l''idée de lui offrir une odoripierre. Le parfum des fleurs sans les pétales fanés ! Trouvez-en une ! Même des morceaux feront l''affaire.','A mi amada le encantan las flores, así que pensé: ¿por qué no le regalo una piedra ónix? ¡Aroma de flores en el bolsillo! Encuéntrame una... O piezas.','Il mio tesoro adora i fiori, così ho avuto una splendida idea: perché non donargli una balsamite? Il profumo dei fiori sempre in tasca! Trovamene una! Beh, o anche solo qualche pezzetto...','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10201,'Harvest Tour: Deserted Island','Erntetour: Ödnis-Insel','Visite : Île déserte','Recolección: Isla Desierta','Tour: Isola deserta','','Survive until time expires or deliver a Paw Pass Ticket','Überlebe bis Zeitende oder gib einen Tatzenpass ab.','Temps expiré ou livrer un ticket Passechat','Aguanta hasta que acabe el tiempo o da un ticket zarpa','Sopravvivi fino allo scadere o consegna buono pass-zampa','','Guild','0','LR',3,0,2,6,50,0,12,0,'None','–','Rien','Ninguno','Nessuno','','Interested in gathering at the Deserted Island? Take this Quest and you can gather as much as you like! Just deliver the Paw Pass Ticket when you''re ready to come home.','Bist du vielleicht an einer Erntetour auf der Ödnis-Insel interessiert? Nimm einfach diese Quest an, um deine Vorräte aufzufüllen. Liefere dann deinen Tatzenpass ab, um zurückzukehren.','Que diriez-vous d''une expédition de collecte sur l''Île déserte ? Acceptez et vous pourrez garder tous les objets ! Livrez le ticket Passechat pour rentrer.','¿Quieres ir a recoger a la Isla Desierta, querido? Acepta esta misión y ve a coger todo lo que quieras. Solo tienes que entregar el ticket zarpa cuando quieras volver a casa.','Vuoi depredare l''isola deserta? Oh, accetta questa missione e potrai raccogliere tutto ciò che vuoi! Per tornare a casa dovrai solo consegnare il buono pass-zampa.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10202,'Harvest Tour: Marshlands','Erntetour: Marschland','Visite : Marais','Recolección: Ciénaga','Tour: Palude','','Survive until time expires or deliver a Paw Pass Ticket','Überlebe bis Zeitende oder gib einen Tatzenpass ab.','Temps expiré ou livrer un ticket Passechat','Aguanta hasta que acabe el tiempo o da un ticket zarpa','Sopravvivi fino allo scadere o consegna buono pass-zampa','','Guild','0','LR',3,0,2,7,50,0,12,0,'None','–','Rien','Ninguno','Nessuno','','Interested in gathering in the Marshlands, young Hunter? Take this Quest and you can gather as much as you like! Just deliver the Paw Pass Ticket when you''re ready to come back.','Bist du vielleicht an einer Erntetour ins Marschland interessiert? Nimm einfach diese Quest an, um deine Vorräte aufzufüllen. Liefere dann deinen Tatzenpass ab, um zurückzukehren.','Que diriez-vous d''une expédition de collecte dans les Marais ? Acceptez et vous pourrez garder tous les objets ! Livrez le ticket Passechat pour rentrer.','¿Quieres ir a recoger a la Ciénaga, querido? Acepta esta misión y ve a coger todo lo que quieras. Solo tienes que entregar el ticket zarpa cuando quieras volver a casa.','Vuoi depredare la palude? Oh, accetta questa missione e potrai raccogliere tutto ciò che vuoi! Per tornare a casa dovrai solo consegnare il buono pass-zampa.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10203,'Insomnia? Meet Hypnotism','Schlaflosigkeit? Triff Hypnose','Problèmes d''insomnie ?','¿Insomnio? Hipnotismo','Ipnosi contro l''insonnia','','Capture a Malfestio','Fange einen Malfestio.','Capturer 1 Malfestio','Captura un Malfestio','Cattura un Malfestio','','Guild','0','LR',2,0,2,101,50,700,6300,340,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','My husband''s got insomnia. Over the years, I''ve tried knocking him out to get him to sleep, but it turned out that''s not a great idea... So I want you to capture a Malfestio to hypnotize him!','Mein Mann leidet an Schlaf- losigkeit. Selbst härteste Kinnhaken bringen keine Besserung! Nun suche ich nach einem Malfestio, um es mit Hypnose zu versuchen!','Mon mari est insomniaque. J''ai tout tenté, violence comprise, pour le faire dormir, mais rien n''y fait. Je veux donc que vous capturiez un Malfestio pour l''hypnotiser !','Mi marido tiene insomnio. He intentado dejarlo inconsciente para que duerma, pero no ha sido buena idea... ¡Captura un Malfestio que lo hipnotice!','Mio marito è insonne. In questi anni l''ho sempre spedito al tappeto per farlo dormire, ma... forse non era una grande idea. Voglio che catturi un Malfestio per ipnotizzarlo!','',600,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10204,'Walk ''n Roll','Volvidon im Visier','Roulades enroulées','Con mucha marcha','Volvidon a rotoli','','Hunt a Volvidon','Erjage einen Volvidon.','Chasser 1 Volvidon','Caza un Volvidon','Caccia un Volvidon','','Guild','0','LR',0,0,2,1,50,400,3600,170,'Wound Volvidon''s back','Volvidon-Rücken verletzen','Blesser le dos d''un Volvidon','Hiere Volvidon en el lomo','Ferisci la schiena del Volvidon','','At times a ball of crimson, at times a lumbering, four- legged beast -- one of the Frontier''s many terrors: Volvidon! Go now, young hunter. You know what to do!','Mal ein scharlachroter Ball, dann wieder ein schwerfälliger Vierbeiner. Einer der Schrecken des Grenzlandes: Volvidon! Nun geh, junger Jäger. Du weißt, was du zu tun hast!','Se présentant sous la forme d''une boule rouge ou d''un quadrupède pesant, le Volvidon est l''une des nombreuses horreurs de la Frontière. Allez, Chasseur. Nettoyez la région !','A veces es una bola roja, otras una enorme bestia de cuatro patas... Uno de los terrores de la Fronda: el Volvidon. Ve, cazador. Ya sabes qué hacer.','A volte è una palla cremisi, a volte una goffa creatura con quattro zampe. È uno dei pericoli della frontiera. È il Volvidon! Vai, giovane leggenda, e mostra a tutti quanto vali!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10205,'Rocking Rhenoplos','Rhenoplos rocken','Rhenoplos féroces','Caña al Rhenoplos','Rhenoplos roccioso','','Slay 10 before time expires or deliver a Paw Pass Ticket','Erlege 10 Rhenoplos, bevor die Zeit abläuft. / Liefere einen Tatzenpass.','Tuer 10 Rhenoplos ou livrer un ticket Passechat','Abate a 10 Rhenoplos sin agotar el tiempo/entrega un ticket zarpa','Uccidi 10 Rhenoplos o consegna buono pass-zampa','','Guild','0','LR',1,0,2,1,50,300,2100,150,'Hunt a Yian Kut-Ku','Einen Yian Kut-Ku erjagen','Chasser 1 Yian Kut-Ku','Caza un Yian Kut-Ku','Caccia uno Yian Kut-Ku','','The people here are gettin'' rammed by Rhenoplos whenever they leave the village. They told me to take care of them, but you look strong enough. Go hunt some for me!','Sobald die Leute das Dorf verlassen, werden sie von Rhenoplos gerammt. Mir hat man gesagt, ich solle mich darum kümmern, aber du siehst auch stark genug aus. Bitte jage welche für mich.','Les gens se font charger par les Rhenoplos dès qu''ils sortent du village. Je suis censé régler ça, mais je préfère vous laisser faire. Alors, éliminez-en quelques-uns à ma place !','Los Rhenoplos atacan a los aldeanos en cuanto salen. Me dijeron que me ocupara, pero tú pareces bastante fuerte. ¡Ve a cazar unos cuantos por mí!','La gente viene attaccata dai Rhenoplos appena mette piede fuori dal villaggio. Sono stato incaricato di risolvere la questione, ma tu mi sembri più forte! Cacciane qualcuno per me!','',3000,200);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10206,'The Desert Gourmand','Der Wüstengourmet','Le gourmet du désert','El glotón del desierto','Il ghiottone del deserto','','Hunt a Nibelsnarf','Erjage einen Nibelsnarf.','Chasser 1 Nibelsnarf','Caza un Nibelsnarf','Caccia un Nibelsnarf','','Guild','1','LR',0,0,2,5,50,500,4500,310,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','One minute I was rattlin'' along the Dunes in me barrel buggy, mindin'' me own. And then, RAAARRR! Cart goes flyin'' -- and me with it! What gives!? The barrel business is s''pposed t''be safe, by gum!','Ich war mit meinem Fass- Buggy in den Dünen und dachte an nichts Böses. Und dann: RAAARRR! In hohem Bogen heben wir ab: Bruch- landung! Dabei soll Fass- handel doch so sicher sein!','Je traversais les Dunes sur mon chariot à tonneaux sans déranger personne et VLAN ! Le chariot s''est mis à voler dans les airs ! Moi qui croyais que le commerce de tonneaux était tranquille...','Un minuto iba tan tranquilo por las Dunas en mi carro, sin meterme con nadie, y de repente... ¡BAM! El carro sale volando y yo dentro. Y eso que se supone que era un negocio seguro.','Ero lì a sferragliare tra le dune con la mia bottemobile quando ROAR!, un mostro mi scaglia in aria con baracca e burattini! Ma che diavolo? Quello del bottaio dovrebbe essere un lavoro tranquillo!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10207,'Tongue-Tied','Zungenschlag','Une langue bien pendue','Con la lengua atada','Lingua lunga','','Capture a Volvidon','Fange einen Volvidon.','Capturer 1 Volvidon','Captura un Volvidon','Cattura un Volvidon','','Guild','0','LR',2,0,2,5,50,500,4800,200,'Deliver 1 Beast Tear','1 Bestienträne abliefern','Livrer 1 larme de bête','Entrega 1 lágrima de bestia','Consegna una lacrima belva','','Yo, Hunter! Long tongues are weird, y''know what I''m sayin''? Especially Volvidon ones! But, y''know, it''d be interesting to see just how long they can get... I know! Go capture me one!','Lange Zungen sind total interessant! Besonders, wenn sie von einem Volvidon stammen. Ich möchte wissen, wie lang die werden können. Bitte fange einen für mich!','Je trouve ça trop bizarre, les langues. Surtout quand elles sont aussi longues que celles des Volvidon. J''aimerais trop voir combien elles mesurent en vrai. Vous m''en récupérez une ?','¡Oye, cazador! Las lenguas largas son raras, ¿sabes? Sobre todo las de Volvidon. Pero quiero saber lo largas que pueden ser... ¡Ya sé! ¡Ve a capturarme uno!','Ehilà! Le lingue lunghe sono strane, eh? Sai di cosa parlo, no? Come quelle del Volvidon! Sarebbe molto interessante sapere quanto possono allungarsi. Ci pensi tu a catturarne uno?','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10208,'The Poison-fanged Duo','Das Giftzahn-Duo','Le duo aux crocs venimeux','Dúo de colmillos venenosos','Il duo dalle zanne velenose','','Hunt 2 Gendrome','Erjage 2 Gendrome.','Chasser 2 Gendrome','Caza 2 Gendrome','Caccia 2 Gendrome','','Guild','0','LR',0,0,2,5,50,300,3000,210,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','Two new alphas joined the Genprey in the Dunes, and now the whole pack of ''em are acting up, and people are gettin'' attacked! Time to downsize these bosses -- go wipe the pair out!','Zwei neue Alphatiere sind zu den Genprey in den Dünen gestoßen. Nun ist die Meute aufgeregt und greift die Menschen an. Wird Zeit, die zwei zurechtzustutzen. Schalte die beiden aus!','L''arrivée de deux nouveaux alphas a décuplé la hargne des Genprey des Dunes et la meute attaque désormais sans crier gare. L''heure est venue pour vous d''éliminer ces deux gêneurs !','Dos nuevos alfas se han unido a los Genprey de las Dunas, y ahora toda la manada está como loca. Ya ha habido ataques. Hay que bajarles los humos.','Due nuovi alfa si sono uniti al Genprey sulle dune. E ora il gruppo attacca la popolazione! Dobbiamo far vedere loro chi comanda... Annienta quei due!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10209,'Konchu Collector','Konchu-Sammler','Collectionneur de Konchu','Coleccionista de Konchu','Collezionista di Konchu','','Slay 20 Konchu','Erlege 20 Konchu.','Tuer 20 Konchu','Abate a 20 Konchu','Uccidi 20 Konchu','','Guild','0','LR',1,0,2,5,50,200,1800,120,'Hunt a Nibelsnarf','Einen Nibelsnarf erjagen','Chasser 1 Nibelsnarf','Caza un Nibelsnarf','Caccia un Nibelsnarf','','The Konchu is a most fascinating creature -- a bug of many metamorphoses! Attack it and watch it switch into defensive mode for evasive action. I need more specimens for my research!','Der Konchu ist eine wahrlich faszinierende Kreatur. Ein extrem wandelbarer Käfer! Wird er angegriffen, wechselt er in den Abwehrmodus, um auszuweichen. Ich brauche mehr Exemplare für meine Forschung!','Le Konchu est une créature fascinante ! Si vous l''attaquez, il passera en mode défensif pour tenter une esquive. J''ai besoin de nouveaux spécimens pour mes recherches !','El Konchu es una criatura fascinante... ¡Un bicho con varias metamorfosis! Atácalo y verás cómo cambia a una forma defensiva para protegerse. ¡Necesito más ejemplares para investigar!','Il Konchu è una creatura affascinante, in grado di trasformarsi all''occorrenza! Attaccalo e assumerà una postura difensiva. Ho bisogno di più esemplari per le mie ricerche!','',4500,310);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10210,'Liver of Legend!','Legendenleber!','Foie légendaire !','¡Hígado de leyenda!','Fegato leggendario!','','Deliver 8 Piscine Livers','Liefere 8 Pisces-Lebern ab.','Livrer 8 foies de wyverne des mers','Entrega 8 hígados de nadador','Consegna 8 fegati di Piscine','','Guild','0','LR',3,0,2,5,50,300,2700,150,'Hunt a Cephadrome','Einen Cephadrome erjagen','Chasser 1 Cephadrome','Caza un Cephadrome','Caccia un Cephadrome','','While doing some business out in the Dunes, I heard about a route where livers go for a really high price. I can''t ignore the scent of zenny to be made, so hurry and get me eight livers.','Während ich in der Wüste meinen Geschäften nachging, hörte ich von einer Route, auf der Leber einen wirklich guten Preis bringt. Mich reizen die Zenny ja schon! Kannst du mir bitte 8 Lebern besorgen?','L''un de mes clients du Désert m''a parlé d''un marché où les foies se négocient à prix d''or. Ma foi, je ne résiste pas à l''appel des zenny... Cette fois, j''ai besoin de 8 foies au plus vite !','Estaba en el desierto por un negocio y oí hablar de una ruta en la que vender hígados a buen precio. Huelo dinero, así que date prisa y tráeme ocho hígados.','Mentre trattavo degli affari nel deserto, ho sentito di una rotta lungo la quale i fegati sono pagati a peso d''oro. Non senti anche tu profumo di zenny? Potresti procurarmi otto fegati?','',2700,140);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10211,'Noise Violation','Ruhestörung','Nuisance sonore','Serenata monstruosa','Caccia al Najarala','','Hunt a Najarala','Erjage einen Najarala.','Chasser 1 Najarala','Caza un Najarala','Caccia un Najarala','','Guild','1','LR',0,0,2,102,50,600,5700,330,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','The Najarala around the village is making some pawful sounds! Does it think it''s some kind of mewsician? Hunter, make it shut up before it drives me up the wall!','Der Najarala beim Dorf macht ein Theater, dass mir das Fell zu Berge steht. Glaubt der, er sei ein Miausiker? Bitte, bring ihn schnell zum Schweigen, bevor ich noch die Wände hochgehe!','Le Najarala qui rôde autour du village fait des bruits horribles. Est-ce qu''il se prend pour un miaousicien ? Je vous en prie, faites-le taire avant que j''en perde mes moustaches !','El Najarala que se pasea cerca del pueblo hace unos ruidos horrimiables. ¿Se creerá que es música? Cazador, hazlo callar antes de que me vuelva loco.','Il Najarala nei pressi dal villaggio prrroduce dei rumori orribili! Pensa di essere un miausicista?! Ti prrrego, zittiscilo prima che mi faccia ammiaottire.','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10212,'In Search of the Yian Garuga','Die Yian-Garuga-Suche','Recherche Yian Garuga','En busca del Yian Garuga','Alla ricerca dello Yian Garuga','','Hunt a Yian Garuga','Erjage einen Yian Garuga.','Chasser 1 Yian Garuga','Caza un Yian Garuga','Caccia uno Yian Garuga','','Guild','1','LR',0,0,2,2,50,700,6300,350,'Sever Yian Garuga''s tail','Yian-Garuga-Schwanz abtrennen','Couper la queue du Yian Garuga','Corta la cola del Y. Garuga','Mozza la coda dello Yian Garuga','','In researching the mysteries of the Garuga King, I''ve come to the theory that the Yian Garuga is the key to solving it all. Hunt me one, and your name will go down in history! Under mine, of course.','Bezüglich des geheimnis- vollen Garuga-Königs bin ich mittlerweile überzeugt, dass der Schlüssel zur Lösung bei den Yian Garuga liegt. Bitte bring mir einen! Das wäre der Durchbruch!','L''étude du roi Garuga pose bien des questions, et les Yian Garuga me semblent détenir la clé du mystère. Trouvez-m''en un et votre nom entrera dans l''histoire. Après le mien, bien sûr.','Al estudiar los misterios del rey Garuga he llegado a la teoría de que el Yian Garuga es la clave de todo. Cázame uno, y haremos historia... Bueno, yo más.','Mentre indagavo sui segreti del re Garuga, sono giunto alla conclusione che lo Yian Garuga è la chiave! Portami qui un esemplare e il tuo nome entrerà nella storia! Dopo il mio, ovviamente...','',900,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10213,'Finishing the Job','Zum Abschluss bringen','Finir le boulot','Acabar el trabajo','Un lavoro a metà','','Hunt 2 Velocidrome','Erjage 2 Velocidrome.','Chasser 2 Velocidrome','Caza 2 Velocidrome','Caccia 2 Velocidrome','','Guild','0','LR',0,0,2,2,50,300,2700,160,'None','–','Rien','Ninguno','Nessuno','','My appurrentice in Pokke Village is in fur a world of trouble! Can''t even beat a Velociprey, and she agreed to hunt Velocidrome -- two! To avoid catastrophe, can you go hunt them instead?','Mein Lehrling in Pokke hat entkätzliche Probleme. Sie wird nicht einmal mit einem Velociprey ferrrtig, dabei hat sie versprochen, zwei Velocidrome zu jagen! Kannst du das bitte übernehmen?','Mon apprentie de Pokke a accepté de traquer deux Velocidrome alors qu''elle est incapable de venir à bout d''un Velociprey ! Pouvez-vous l''aider et les abattre à sa place ?','Mi aprendimiau de la aldea de Pokke está en un grave lío. No puede ni cazar a un Velociprey... ¡y aceptó ir a por dos Velocidrome! Ve tú, así evitamos un drama.','Il miao apprrrendista del villaggio di Pokke è in grossi guai! Non riuscirebbe a battere un Velociprey, eppure vuole cacciare due Velocidrome! Puoi pensarci tu, per evitare una gattastrofe?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10214,'Fresh is Best','Frisches Biest','Viande fraîche','Cuanto más fresco, mejor','Taglio di prima qualità','','Slay 20 Bullfango','Erlege 20 Bullfangos.','Tuer 20 Bullfango','Abate a 20 Bullfango','Uccidi 20 Bullfango','','Guild','0','LR',1,0,2,102,50,300,2100,120,'None','–','Rien','Ninguno','Nessuno','','I need you to procure some groceries for me. To be specific, I want meat, and you can get plenty off some Bullfango. Think you can go to the Verdant Hills and hunt me 20 of them?','Du musst ein paar Einkäufe für mich erledigen. Ich brauche Fleisch, am besten vom Bullfango. Kannst du vielleicht in den Grünen Hügeln 20 von denen für mich erlegen?','J''ai besoin de vous pour me rapporter de quoi cuisiner. Plus précisément, il me faut de la viande de Bullfango. Vous pensez pouvoir en abattre 20 dans les Collines verdoyantes ?','Necesito que me traigas comida. En concreto, carne; se puede obtener de los Bullfango. ¿Podrías ir al Bosque Rocoso y cazar 20 Bullfango para mí?','Ho bisogno che mi procuri del cibo. Nello specifico, della carne. E come sai, puoi ottenerne molta dai Bullfango. Pensi di poter andare sui colli verdi a cacciarne venti?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10215,'The Shadow in the Mountains','Der Schatten in den Bergen','L''ombre des montagnes','La sombra de las montañas','L''ombra nelle montagne','','Hunt a Khezu','Erjage einen Khezu.','Chasser 1 Khezu','Caza un Khezu','Caccia un Khezu','','Guild','0','LR',0,0,2,103,50,500,4500,310,'Wound Khezu''s body','Khezu-Körper verwunden','Blesser le corps du Khezu','Hiere Khezu en el cuerpo','Ferisci il corpo del Khezu','','We were up in the Arctic Ridge -- doing a little research -- when all of a sudden, something really creepy and gross plopped down behind us. We managed to escape, but we can''t do any research with that thing up there!','Wir forschten im Bereich des Schneegebirges, als wir plötzlich hinter uns ein unheimliches Geräusch hörten. Wir konnten uns retten, aber das Biest muss verschwinden!','Nous menions des recherches sur la Crête arctique quand une créature répugnante a surgi derrière nous. Impossible de retourner là-haut tant qu''elle s''y trouve !','Estábamos investigando en las Cimas Árticas cuando de repente apareció algo muy asqueroso. Logramos huir, pero no podemos investigar mientras esa cosa siga ahí.','Eravamo sul monte artico a svolgere delle ricerche quando qualcosa di orribile ci è spuntato alle spalle. Siamo riusciti a fuggire, ma non possiamo più esplorare con quel mostro in giro!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10216,'The New Tenant','Der neue Nachbar','Le roi des montagnes','El rey de las montañas','Il re dei monti','','Hunt a Blangonga','Erjage einen Blangonga.','Chasser 1 Blangonga','Caza un Blangonga','Caccia il Blangonga','','Guild','2','LR',0,0,2,3,50,600,5400,330,'Wound Blangonga''s fangs','Blangonga-Reißzähne verletzen','Briser les crocs du Blangonga','Hiere Blangonga en colmillos','Ferisci le zanne del Blangonga','','A Blangonga has taken up residence in the Arctic Ridge, which spells trouble for us being able to gather firewood. Hurry and take that ugly ape out before we freeze to death!','Ein Blangonga hat sich im Schneegebirge niederge- lassen. Das behindert uns beim Holzsammeln. Beeil dich und erlege diesen ekligen Affen, bevor wir uns zu Tode frieren!','Un Blangonga s''est installé sur la Crête arctique, ce qui va nous compliquer la collecte de bois. Alors, dépêchez-vous d''éliminer ce singe hideux avant que nous ne mourions de froid !','Ha venido un Blangonga a las Cimas Árticas, lo cual nos dará problemas para ir a por leña. Date prisa y echa a ese mono feote antes de que muramos congelados.','Un Blangonga si è stabilito sul monte artico, e questo rende complicato raccogliere la legna per il fuoco. Caccia via quello scimmione, prima che congeliamo a morte!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10217,'Blango Slaying Tactics','Blango-Jagdtaktik','Safari au Blango','Tácticas Blango','Tattica di caccia del Blango','','Slay 20 Blangos','Erlege 20 Blangos.','Tuer 20 Blango','Abate a 20 Blango','Uccidi 20 Blango','','Guild','0','LR',1,0,2,3,50,300,2700,120,'Deliver 20 Mountain Herbs','20 Gebirgskräuter abliefern','Livrer 20 herbes de montagne','Entrega 20 hierbas montaña','Consegna 20 erbe di montagna','','Blango Pelts have so many great uses. In particular, their insulation properties keep you toasty in snow! So could you hunt me 20 Blangos? Thanks! Oh, and watch out for Bulldrome!','Blango-Pelze sind vielseitig einsetzbar. Sie isolieren gut die Wärme und halten auch bei Schnee warm. Jagst du 20 Blango für mich? Und hüte dich vor den Bulldrome!','Les toisons de Blango sont incroyablement utiles, en particulier pour se protéger du froid quand il neige. Alors, pourriez-vous m''en rapporter 20 ? Merci ! Et attention aux Bulldrome !','Las pieles de Blango tienen muchos usos. Son muy aislantes y te mantienen calentito en la nieve. ¿Podrías cazar 20 Blango? ¡Cuidado con el Bulldrome!','La pelle del Blango ha molti utilizzi. Tra l''altro, isola al punto da tenere al caldo anche sulla neve! Potresti cacciare venti Blango per me? Grazie! Oh, guardati le spalle dal Bulldrome!','',1200,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10218,'Operation: Secure Specimen','Operation: Exemplarsicherung','Capture de spécimen','Captura de espécimen','Esemplare registrato!','','Capture a Nargacuga','Fange einen Nargacuga.','Capturer un Nargacuga','Captura un Nargacuga','Cattura un Nargacuga','','Guild','1','LR',2,0,2,4,50,800,7500,420,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','My boss from hell is making me write up a report on Nargacuga eating habits! No way can I do that on my own! Can you go capture one for me, Hunter? I''ll... I''ll get the cage ready!','Mein teuflischer Chef lässt mich einen Bericht über das Nargacuga-Fressverhalten schreiben! Alleine schaffe ich das nie! Kannst du einen für mich fangen? Ich hol schon mal den Käfig, ja?','Mon chef attend un rapport sur les habitudes alimentaires des Nargacuga, mais il me faut pour cela un spécimen. Pouvez-vous en capturer un ? Je... prépare la cage en attendant !','Mi maldito jefe me ha mandado un informe sobre los hábitos alimentarios del Nargacuga... ¿Podrías capturar uno para mí? ¡Yo prepararé la jaula!','Il mio capo pretende un rapporto sulle abitudini alimentari del Nargacuga! Non ce la farò mai da solo! Puoi catturarne uno per me? Io... Ehm, terrò la gabbia aperta.','',600,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10219,'Royal Assassination','Königliches Attentat','Régicide','Asesinato real','Assassinio reale','','Hunt a Rathian','Erjage eine Rathian.','Chasser 1 Rathian','Caza una Rathian','Caccia una Rathian','','Guild','1','LR',0,0,2,4,50,600,5400,310,'Deliver a Wyvern Egg','1 Wyvern-Ei abliefern','Livrer 1 œuf de wyverne','Entrega 1 huevo de wyvern','Consegna un uovo di wyvern','','The Misty Peaks aren''t safe for normal travelers now that a Rathian''s built her nest there. Can you do something about her so people like me can pass through there in peace!?','Die Nebelgipfel sind nicht mehr sicher für Reisende, seit eine Rathian dort ihr Nest gebaut hat. Kannst du etwas unternehmen, damit Leute wie ich dort sicher durchreisen können?','Les Pics brumeux ne sont plus sûrs depuis qu''une Rathian y a fait son nid. Pouvez-vous intervenir pour que les gens comme moi puissent traverser la région en toute sécurité ?','Los Picos Nublados no son seguros para los viajeros normales ahora que una Rathian ha hecho ahí su nido. ¿Puedes hacer algo para que me deje pasar?','Le cime nebbiose sono assai pericolose per i viandanti da quando una Rathian ha nidificato in zona. Puoi risolvere la situazione per permettere a persone come me di passare senza problemi?','',1200,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10220,'Ahoy! Royal Ludroth!','Ahoy, Königs-Ludroth!','Ludroth royaux à bâbord','Lud. Real cerca del puerto','Ludroth reale a prua!','','Hunt 2 Royal Ludroth','Erjage 2 Königs-Ludrothe.','Chasser 2 Ludroth royaux','Caza 2 Ludroth Reales','Caccia 2 Ludroth reali','','Guild','0','LR',0,0,2,4,50,600,6000,400,'None','–','Rien','Ninguno','Nessuno','','Shimpai... I worried because no hear word from Felyne carrying Neko''s luggage! If it met Royal Ludroth, all that water would... Taihen! Please shuryo them! "Shuryo" means hunt!','Shimpai... Ich Sorgen weil nichts hören von Felyne und Nekos Gepäck! Wenn Königs- Ludroth kommt. Das viele Wasser würde... Taihen! Bitte shuryo sie! Shuryo heißt "jagen"!','Je suis inquiet, car sans nouvelles du Felyne portant ma cargaison ! S''il croise des Ludroth royaux, alors toute cette eau sera... Nom d''un sushi ! Il faut partir les chasser !','Shimpai... Yo preocupado porque no sé nada de Felyne que lleva equipaje de Neko. Ay si encuentra Ludroth Real. Taihen, ¡dale shuryo! "Shuryo" significa "caza".','Shimpai... Sono allarmato, non sento più Felyne con bagagli di Neko! Se ha incontrato qualche Ludroth reale, tutta quell''acqua... Taihen! Per favore, shuryo! "Shuryo" significa "caccia"!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10221,'Bye Bye Jaggia','Bye-bye, Jaggia!','Au revoir les Jaggia','Adiós Jaggia','Ciao ciao, Jaggia','','Slay 20 Jaggia','Erlege 20 Jaggia.','Tuer 20 Jaggia','Abate a 20 Jaggia','Uccidi 20 Jaggia','','Guild','0','LR',1,0,2,104,50,300,2100,120,'Slay 12 Jaggi','12 Jaggi erlegen','Tuer 12 Jaggi','Abate a 12 Jaggi','Uccidi 12 Jaggi','','Jaggi are awful, am I right? Well, Jaggia are even worse! And now a gang of ''em are terrorizing the Misty Peaks! Please, put a stop to them before they come ruin our crops!','Jaggi sind schrecklich, nicht wahr? Aber Jaggia sind noch schlimmer! Eine Bande von ihnen terrorisiert nun die Nebelgipfel! Bitte halte sie auf, bevor sie die Felder vernichten!','Les Jaggi vous semblent pénibles ? Eh bien, les Jaggia qui sévissent dans les Pics brumeux sont pires encore. De grâce, arrêtez-les avant qu''elles ne dévastent nos récoltes !','Los Jaggi son horribles... Pero las Jaggia, peores. Y hay una bandada que atemoriza los Picos Nublados. Por favor, acaba con ellas antes de que nos dejen sin cultivo.','I Jaggi sono orribili, vero? Beh, i Jaggia sono persino peggio, e ora imperversano sulle cime nebbiose! Devi fermarli prima che rovinino i nostri raccolti!','',1200,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10222,'Malfestio Festival','Malfestio-Festival','Festival des Malfestio','Festival Malfestio','Il festival del Malfestio','','Hunt a Malfestio','Erjage einen Malfestio.','Chasser 1 Malfestio','Caza un Malfestio','Caccia un Malfestio','','Guild','1','LR',0,0,2,6,50,500,4800,280,'Wound Malfestio''s tail','Malfestio-Schwanz verletzen','Blesser la queue du Malfestio','Hiere Malfestio en la cola','Ferisci la coda del Malfestio','','selacs s''oitseflaM !gnizamA lortnoc esol uoy ekam yllaer tsum ylpmis I !ydob ruoy fo ,esaelP !selpmas erom evah !em rof emos hctef ...Though the effects seem to be short-lived. *sigh*','!neppuhcS-oitseflaM ,nnisnhaW eid ttalg nam treilrev aD ehcuarb hcI !ellortnoK !neborP eretiew tgnidebnu !ehclew rim gnirb ettiB ... Auch wenn die Wirkung nicht lange anhält. Leider!','ed selliacé seL ! elbayorcnI tuot erdrep tnof oitseflaM ! sproc ertov ed elôrtnoc rehcrehc ne''m zellA ! sertua''d Hélas, leur effet est de courte durée. *soupir*','ed samacse saL !elbíercnI¡ sadreip euq necah oitseflaM .opreuc ut erbos lortnoc ...sartseum sám otiseceN Aunque parece que no dura mucho... Ay...','led emauqs eL !elibidercnI itraf onossop oitseflaM out led ollortnoc li eredrep !ertla enrednerp oveD !oproc !arocna enematrop ,ogerp iT ...Anche se gli effetti non durano molto. *sigh*','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10223,'Wyvern of the Wind','Wyvern des Windes','Wyverne du vent','Wyvern del viento','Wyvern del vento','','Hunt a Nargacuga','Erjage einen Nargacuga.','Chasser 1 Nargacuga','Caza un Nargacuga','Caccia un Nargacuga','','Guild','0','LR',0,0,2,6,50,600,5700,350,'None','–','Rien','Ninguno','Nessuno','','The Nargacuga out on the Deserted Island is scarily fast. Blink and you''ll miss it. But, Hunter, it''s gotta go! There''s nothing more important right now than hunting it, so get to it!','Der Nargacuga auf der Ödnis- Insel ist fürchterlich schnell. Man kann ihn leicht aus den Augen verlieren. Trotzdem muss das Biest weg! Das hat höchste Priorität. Bitte unternimm etwas!','Le Nargacuga de l''Île déserte est effroyablement rapide, au point qu''il suffit de cligner des yeux pour le perdre. Mais il faut nous en débarrasser toutes affaires cessantes, alors au travail !','El Nargacuga de la Isla Desierta es rapidísimo. Si parpadeas, lo pierdes. ¡Pero tienes que matarlo! Ahora mismo cazarlo es lo más importante.','Il Nargacuga sull''isola deserta è pericolosamente veloce: può sparire in un batter d''occhio! Ti prego, devi mandarlo via! Non c''è nulla di più importante ora che dargli la caccia!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10224,'Tetsucabra Bounty','Tetsucabra-Kopfgeld','Butin de Tetsucabra','Recompensa por Tetsucabra','Taglia sul Tetsucabra','','Hunt a Tetsucabra','Erjage einen Tetsucabra.','Chasser 1 Tetsucabra','Caza un Tetsucabra','Caccia un Tetsucabra','','Guild','0','LR',0,0,2,6,50,500,4200,250,'Wound Tetsucabra''s back','Tetsucabra-Rücken verletzen','Blesser le dos du Tetsucabra','Hiere Tetsucabra en el lomo','Ferisci il dorso del Tetsucabra','','Oh, the horror! Oh, the zenny! Apparently, a Tetsucabra is making a mess of some newly developed land. Big reward on its head, I hear. Help me and we''ll split it fifty-fifty!','Oh Graus! Oh Zenny! Offenbar verwüstet ein Tetsucabra ein neu erschlossenes Stück Land. Auf seinen Kopf wurde eine hohe Belohnung ausgesetzt, wie ich hörte! Hilf mir, dann können wir halbe-halbe machen!','Quelle horreur ! Il semblerait qu''un Tetsucabra ravage des territoires récemment développés. Celui qui l''éliminera recevra une grosse récompense. Aidez-moi, on partagera !','¡Qué horror! ¡Oh, los zenis! Al parecer, un Tetsucabra anda armando jaleo en una tierra recién cultivada. Hay una gran recompensa por su cabeza. ¡Ayúdame y podemos repartírnosla!','Oh, paura! Oh, zenny! Pare che un Tetsucabra stia seminando scompiglio in nuovi territori. C''è una grossa taglia sulla sua testa. Aiutami e faremo a metà!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10225,'Deserted Island Dustup','Ödnis-Insel-Keilerei','Rixe sur l''île déserte','Disputa en la Isla Desierta','Zuffa sull''Isola deserta','','Hunt 2 Arzuros','Erjage 2 Arzuros.','Chasser 2 Arzuros','Caza 2 Arzuros','Caccia 2 Arzuros','','Guild','0','LR',0,0,2,6,50,300,3000,210,'Deliver 1 Beast Tear','1 Bestienträne abliefern','Livrer 1 larme de bête','Entrega 1 lágrima de bestia','Consegna una lacrima belva','','I''m reporting live from the Deserted Island, where two Arzuros have decided to discuss their differences like civilized monsters. Oh, who am I kidding? Run for your lives! Women and reporters first!','Ich berichte live von der Ödnis-Insel, wo sich zwei Arzuros streiten wie zivili- sierte Monster. Oh, was sag ich? Lauft um euer Leben! Frauen und Reporter zuerst!','Je suis en direct de l''île déserte, où deux Arzuros sont en train de régler leurs différends en monstres civilisés. Non, je plaisante. Fuyez ! Les femmes et les reporters d''abord !','Informo desde Isla Desierta, donde dos Arzuros van a arreglar sus diferencias como monstruos civilizados. ¿A quién quiero engañar? ¡Pies en polvorosa! ¡Mujeres y reporteros primero!','In diretta dall''Isola deserta, due Arzuros hanno deciso di appianare le loro divergenze come due mostri civilizzati. Oh, chi prendo in giro? Scappate! Prima donne e reporter!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10226,'Bnahabra Bloodbath','Bnahabra-Blutbad','Tuerie de Bnahabra','Baño de sangre Bnahabra','Massacro di Bnahabra','','Slay 50 Bnahabra','Erlege 50 Bnahabra.','Tuer 50 Bnahabra','Abate a 50 Bnahabra','Uccidi 50 Bnahabra','','Guild','0','LR',1,0,2,6,50,400,3600,150,'Deliver 5 Silver Crickets','5 Silbergrillen abliefern','Livrer 5 grillons argentés','Entrega 5 grillos plateados','Consegna 5 grilli argentei','','What, you expect me to wear some old sad sack to the big soiree tonight? I wanna feel pretty! I need Bnahabra material to make that happen. So... Uh, hello? Help me make that happen already!','Was? Ich soll bei der großen Soiree heute Abend irgend- einen alten Sack tragen? Ich will mich schön fühlen! Und dafür brauche ich Bnahabra- Material. Ähm, hallo? Besorg mir gefälligst welches!','Vous croyez que je vais porter un vieux sac à la fête de ce soir ? Je veux être jolie ! Il me faut des parties de Bnahabra pour ça. Alors, vous allez m''aider ou quoi ?','¿Esperas que me ponga cualquier trapo para la fiesta de esta noche? ¡Quiero estar guapa! Y necesito materiales de Bnahabra para eso. ¡Eh! ¿Hola? ¡Ayúdame!','Non penserai mica che possa indossare questi stracci per il gran galà di stasera? Devo essere bellissima! Ma senza Bnahabra non ho speranze. Perché non mi aiuti a rinnovare il guardaroba?','',1200,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10227,'Poisonous Pest','Giftige Plage','Crocs venimeux','¡Colmillo del Iodrome!','Zanna di Iodrome!','','Hunt an Iodrome','Erjage einen Iodrome.','Chasser 1 Iodrome','Caza un Iodrome','Caccia uno Iodrome','','Guild','0','LR',0,0,2,7,50,300,3000,160,'Wound Iodrome''s head','Iodrome-Kopf verletzen','Blesser la tête de l''Iodrome','Hiere Iodrome en la cabeza','Ferisci la testa dello Iodrome','','Ioprey storming our village, we can handle. But their poison ruining our crops!? That''s our livelihood! Take out the big ugly one, and the rest of them should get the hint and leave!','Dass die Ioprey in die Dörfer kommen, damit können wir leben. Aber dass ihr Gift unsere Felder ruiniert? Das ist unsere Existenzgrundlage! Töte den Anführer, dann wird sich der Rest zerstreuen!','Que les Ioprey assaillent le village, passe encore. Mais empoisonner nos récoltes et menacer notre gagne-pain, ça non ! Tuez le plus gros et les autres devraient décamper !','Podemos aguantar que los Ioprey entren en nuestra aldea, pero su veneno nos arruina cultivos vitales. Cárgate al grandote feo, y los demás se irán.','Gli Ioprey che attaccano il nostro villaggio non sono un problema, ma il loro veleno sul nostro raccolto?! Quello è il nostro cibo! Elimina quello grosso e gli altri se ne andranno!','',600,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10228,'Pincer through the Sky','Himmelsschere','Pincer les cieux','Pinzas en el cielo','Una tenaglia nel cielo','','Hunt a Shogun Ceanataur','Erjage einen Shogun Ceanataur.','Chasser 1 Ceanataur Shogun','Caza un Shogun Ceanataur','Caccia uno Shogun Ceanataur','','Guild','0','LR',0,0,2,7,50,600,5400,330,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','A Shogun Ceanataur''s moved into the Marshlands recently and it''s attacking people and livestock alike. We''re at our wit''s end here! We need you to go hunt it down!','Ein Shogun Ceanataur hat sich seit Neuestem im Marschland niedergelassen und greift Menschen und Vieh an. Wir sind mit unserer Weisheit am Ende. Du musst ihn jagen!','Un Ceanataur Shogun a récemment élu domicile dans les Marais et attaque aussi bien les villageois que le bétail. Nous ne savons plus quoi faire ! Il faut nous en débarrasser !','Hace poco un Shogun Ceanataur se instaló en la Ciénaga y ataca a personas y ganado. ¡No sabemos qué más hacer! ¡Necesitamos que lo caces!','Uno Shogun Ceanataur si è da poco spostato nella palude e sta attaccando persone e bestiame. Non sappiamo più cosa fare! È necessario che tu lo abbatta per noi!','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10229,'Serpentine Samba','Serpentinen-Samba','Samba serpentine','Samba serpentina','Samba serpentina','','Hunt a Najarala','Erjage einen Najarala.','Chasser 1 Najarala','Caza un Najarala','Caccia un Najarala','','Guild','0','LR',0,0,2,7,50,600,5700,330,'Wound Najarala''s head','Najarala-Kopf verletzen','Blesser la tête du Najarala','Hiere Najarala en la cabeza','Ferisci la testa del Najarala','','My huntin'' is near as bad as my dancin'', but my little sis? Best moves in the tribe! I wanna make her a costume of serpent scales for the big festival. Could ya help me bag a big snake!?','Jagen und Tanzen sind nichts für mich, aber meine Schwester ist unsere beste Tänzerin! Für das Fest will ich ihr ein Kostüm aus Schlangenschuppen schneidern. Könntest du eine große Schlange dafür erlegen?','Je suis presque aussi nul en chasse qu''en danse, mais ma petite sœur est la star de la tribu ! Je veux lui faire un costume en écailles pour le festival. Vous m''attrapez un gros serpent ?','No sé qué hago peor, cazar o bailar, pero mi hermanita es la bailarina de la tribu. Quiero hacerle un traje de escamas de serpiente para el gran festival. ¿Puedes ir a cazar una serpiente grande?','A ballare faccio pena quasi quanto nella caccia, ma la mia sorellina è la migliore della tribù! Voglio cucirle un costume di scaglie di serpente per il festival. Me ne cacceresti uno?','',900,30);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10230,'Ioprey Hunting','Ioprey-Jagd','Chasse au Ioprey','Caza de Ioprey','Caccia allo Ioprey','','Slay 20 Ioprey','Erlege 20 Ioprey.','Tuer 20 Ioprey','Abate a 20 Ioprey','Uccidi 20 Ioprey','','Guild','0','LR',1,0,2,7,50,200,1800,120,'Deliver 1 Sootstone Ore','1 Rußsteinerz abliefern','Livrer 1 cristal de cendre','Entrega 1 min. piedra carbón','Consegna una pietra-fuliggine','','Got a second? I was out working in the Marshlands when a bunch of Ioprey popped up out of nowhere! If you could hunt 20 of them for me, I could get back to work!','Hast du kurz Zeit? Neulich ist mir im Marschland eine Gruppe Ioprey begegnet. Sie verhindern, dass ich weiterarbeiten kann. Kannst du 20 von ihnen erlegen? Das würde mir sehr helfen!','Vous avez une minute ? Des Ioprey ont surgi de nulle part pendant que je trimais dans les Marais. Si vous pouviez en éliminer 20, ça me permettrait de reprendre le travail !','¿Tienes un momento? Estaba en la Ciénaga y un grupo de Ioprey salió de la nada. Si pudieses cazar 20 por mí, podría volver a trabajar.','Hai un minuto? Vedi, stavo lavorando nella palude quando un gruppo di Ioprey è sbucato dal nulla! Se potessi cacciarne venti, potrei tornarmene al lavoro!','',900,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10231,'Attack of the Giant Bugs!','Angriff der Rieseninsekten!','L''assaut des insectes géants','Bichos gigantes al ataque','Attacco di insetti giganti!','','Slay 50 Vespoid','Erlege 50 Vespoiden.','Tuer 50 Vespoid','Abate a 50 Vespoid','Uccidi 50 Vespoid','','Guild','0','LR',1,0,2,7,50,400,3600,150,'Slay 20 Hornetaur','20 Hornetauren erlegen','Tuer 20 Hornetaur','Abate a 20 Hornetaur','Uccidi 20 Hornetaur','','My village is being plagued by giant bugs. If you could squash 50 of them, that''d help us a lot. But keep your guard up! Those bugs are tough in groups and a Najarala''s nearby, too!','Mein Dorf wird von riesigen Käfern heimgesucht. Wenn du 50 zerquetschen könntest, wäre uns sehr geholfen. Aber sei auf der Hut. Im Schwarm sind sie eine Gefahr und sogar ein Najarala ist dort!','Des insectes géants s''en prennent à mon village. Vous nous rendriez un fier service en acceptant d''en écraser 50. Mais restez sur vos gardes, car un Najarala rôde dans les parages.','Mi aldea está infestada de bichos gigantes. Si puedes cargarte a 50, sería de gran ayuda. ¡Pero no bajes la guardia! Van en grupo y también hay un Najarala.','Il mio villaggio è stato invaso da insetti giganti. Ci saresti d''aiuto se ne schiacciassi cinquanta. Ma sii prudente! Sono molto forti in gruppo e c''è un Najarala nelle vicinanze!','',1200,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10232,'Collect to Combine','Sammeln und Kombinieren','Collecter pour combiner','Recoge para combinar','Raccogli per combinare','','Deliver 20 Unique Mushrooms','Liefere 20 Einmalige Pilze ab.','Livrer 20 champis spéciaux','Entrega 20 setas especiales','Consegna 20 funghi speciali','','Guild','0','LR',3,0,2,7,50,200,1500,120,'Deliver 2 Goldenfish','2 Goldenfische abliefern','Livrer 2 poissons d''or','Entrega 2 peces dorados','Consegna 2 pesci-dorati','','Passion is important, but a true combiner knows that the key to quality combining is having fresh materials! So how ''bout you pick me, say, 20 Unique Mushrooms from the Marshlands? Sound good?','Motivation ist wichtig, aber das Wichtigste beim Kombi- nieren ist frisches Material! Wie wäre es also, wenn du mir genau 20 Einmalige Pilze aus dem Marschland bringen würdest? Abgemacht?','La passion est essentielle, mais un alchimiste sait que la fraîcheur des matériaux importe plus encore. Alors, que diriez-vous d''aller me chercher 20 champignons spéciaux des Marais ?','La pasión es importante, pero un buen combinador sabe que la clave de la calidad son los materiales frescos. ¿Podrías ir a la Ciénaga a por 20 setas especiales? ¿Suena bien?','La passione è importante, ma un vero combinatore sa che la chiave della qualità sta nei materiali freschi! Quindi, perché non raccogli, diciamo, venti funghi unici nella palude? Ti va?','',1200,10);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10301,'Harvest Tour: Volcano','Erntetour: Vulkan','Visite : Volcan','Recolección: Volcán','Tour: Vulcano','','Survive until time expires or deliver a Paw Pass Ticket','Überlebe bis Zeitende oder gib einen Tatzenpass ab.','Temps expiré ou livrer un ticket Passechat','Aguanta hasta que acabe el tiempo o da un ticket zarpa','Sopravvivi fino allo scadere o consegna buono pass-zampa','','Guild','0','LR',3,0,3,8,50,0,12,0,'None','–','Rien','Ninguno','Nessuno','','Interested in gathering in the Volcano, young Hunter? Take this Quest and you can gather as much as you like! Just deliver the Paw Pass Ticket when you''re ready to come back.','Bist du vielleicht an einer Erntetour am Vulkan interessiert? Nimm einfach diese Quest an, um deine Vorräte aufzufüllen. Liefere dann deinen Tatzenpass ab, um zurückzukehren.','Que diriez-vous d''une expédition de collecte au Volcan ? Acceptez et vous pourrez garder tous les objets ! Livrez le ticket Passechat pour rentrer.','¿Quieres ir a recoger al Volcán, querido? Acepta esta misión y ve a coger todo lo que quieras. Solo tienes que entregar el ticket zarpa cuando quieras volver a casa.','Vuoi depredare il vulcano? Oh, accetta questa missione e potrai raccogliere tutto ciò che vuoi! Per tornare a casa dovrai solo consegnare il buono pass-zampa.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10302,'Flames of Glavenus Passion','Glavenus in Flammen','Une passion dévorante','Llamas de pasión Glavenus','La passione ardente','','Hunt a Glavenus','Erjage einen Glavenus.','Chasser 1 Glavenus','Caza un Glavenus','Caccia un Glavenus','','Guild','0','LR',0,0,3,101,50,900,8700,450,'Wound Glavenus''s head','Glavenus-Kopf verletzen','Blesser la tête du Glavenus','Hiere Glavenus en la cabeza','Ferisci la testa del Glavenus','','People only care about the Glavenus''s tail, but me? I''m way more into that lava it breathes! How does its body make that stuff!? I want you to hunt it and find that out for me!','Am Glavenus interessieren sich die meisten für den Schwanz. Aber ich finde eher die Lavaatmung interessant! Wie erzeugt ein Körper diese Substanz? Um das herauszu- finden, musst du einen jagen!','Les gens ne s''intéressent qu''à la queue du Glavenus, mais moi, c''est la lave qu''il crache qui me passionne. Comment la produit-il ? Trouvez-en un et tirez la chose au clair !','A la gente solo le interesa la cola del Glavenus, pero yo prefiero la lava que emite. ¿Cómo la fabricará? Quiero que lo caces y lo descubras para mí.','Alla gente importa solo della coda del Glavenus, ma a me interessa molto di più la lava che sputa! Come ci riesce? Voglio che tu lo cacci per me e lo scopra!','',1200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10303,'Tigrex by the Tail','Tigrex am Schwanz packen','L''œil du Tigrex','La cola del Tigrex','Tigrex per la coda','','Hunt a Tigrex','Erjage einen Tigrex.','Chasser 1 Tigrex','Caza un Tigrex','Caccia un Tigrex','','Guild','0','LR',0,0,3,101,50,900,9000,430,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','I''ve got a distress signal from a hunter in the Jurassic Frontier who''s run into a Tigrex! Anyone who finds this message is requested to head there and take it down ASAP!','Ich habe einen Hilferuf von einem Jäger im Jura-Grenz- land erhalten. Ein Tigrex bedroht ihn. Alle Finder dieser Nachricht werden gebeten, Hilfe zu leisten. Schnellstmöglich!','Un chasseur a signalé la présence d''un Tigrex près de la Frontière jurassique. Quiconque trouvera ce message est prié d''aller sur place au plus vite pour éliminer cette créature !','Recibí un mensaje de socorro de un cazador de la Fronda Arcaica que había visto un Tigrex. Quien lea este mensaje debería ir y acabar con el monstruo cuanto antes.','Ho ricevuto un segnale di emergenza da un cacciatore che si è imbattuto in un Tigrex nella frontiera giurassica! Chiunque trovi questo messaggio si rechi immediatamente a salvarlo!','',600,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10304,'Dark Wings, Dark Work','Dunkle Flügel, dunkle Werke','Noir comme la nuit','Alas negras como la noche','Le ali dell''oscurità','','Hunt a Gore Magala','Erjage Gore Magala.','Chasser 1 Gore Magala','Caza un Gore Magala','Caccia un Gore Magala','','Guild','1','LR',0,0,3,1,50,900,8400,430,'None','–','Rien','Ninguno','Nessuno','','Beneath the azure skies of the Jurassic Frontier lurks a mysterious monster cloaked in black. Writhing in pain, it glares from unseen eyes. The hunter or the hunted: Which will you be?','Unter dem blauen Himmel des Jura-Grenzlands lauert ein geheimnisvolles Monster, von Schwärze umgeben. Seine Augen unsichtbar, seine Qualen spürbar. Jäger oder Gejagter: Was bist du?','Sous le ciel azur de la Frontière jurassique rôde un monstre mystérieux drapé de noir. Rongé par la douleur, il observe sans être vu. Qui serez-vous : le chasseur ou la proie ?','Bajo los cielos azules de la Fronda Arcaica acecha un misterioso monstruo negro que se retuerce de dolor y nos espía sin que nadie lo vea. ¿Serás el cazador o la presa?','Nel cielo azzurro sopra la frontiera giurassica si nasconde un mostro tinto di nero. Si contorce dal dolore e si cela agli occhi della gente. Tu sarai la preda o il cacciatore?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10305,'Fossilized Memories','Fossile Andenken','Souvenirs fossilisés','Recuerdos fosilizados','Memorie fossili','','Deliver 3 Eternal Fossils','Liefere 3 Ewigkeitsfossile ab.','Livrer 3 fossiles éternels','Entrega 3 fósiles eternos','Consegna 3 fossili eterni','','Guild','0','LR',3,0,3,1,50,600,6000,350,'None','–','Rien','Ninguno','Nessuno','','Could you help me gather some Eternal Fossils for a fellow merchant in the Jurassic Frontier? If you do, I''ll hook you up with a soft toy that can help you fall asleep!','Kannst du mir helfen, Ewigkeitsfossile für einen befreundeten Händler im Jura-Grenzland zu sammeln? Falls ja, kann ich dir zu einer kuschligen Einschlafhilfe verhelfen.','Accepteriez-vous de m''aider à chercher des fossiles éternels pour un marchand de la Frontière jurassique ? En échange, je vous donnerai une peluche qui vous aidera à dormir.','¿Podrías ayudarme a recoger algunos fósiles eternos para otro mercader en la Fronda Arcaica? A cambio te daré un juguetito local que te ayudará a dormir.','Mi aiuteresti a raccogliere dei fossili eterni nella frontiera giurassica per un altro mercante? Se lo farai, ti regalerò un oggetto che ti aiuterà a dormire!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10306,'A Thousand Scales of Dread','Schuppen des Schreckens','Les écailles de terreur','Mil escamas de terror','Mille scaglie di paura','','Hunt a Seregios','Erjage einen Seregios.','Chasser 1 Seregios','Caza un Seregios','Caccia un Seregios','','Guild','1','LR',0,0,3,5,50,900,9000,520,'Break Seregios''s horn','Seregios-Horn brechen','Briser la corne du Seregios','Rompe el cuerno del Seregios','Spezza il corno del Seregios','','Breaking news from the Elder Hall: A Seregios has shown up in the Dunes! Any and all skilled hunters are asked to help hunt it as soon as possible. Beware its blades!','Dringende Nachricht aus der Ältestenhalle! In den Dünen wurde ein Seregios gesichtet! Alle fähigen Jäger sind dazu aufgerufen, sich an der Jagd zu beteiligen: Aber Vorsicht! Die Klingen sind gefährlich!','Le Hall des Anciens vient de nous signaler la présence d''un Seregios dans les Dunes. Tous les chasseurs aguerris sont priés de participer à sa traque. Méfiez-vous de ses lames !','Últimas noticias de la sala ancestral: hay un Seregios en las Dunas. Se pide a todos los cazadores hábiles que ayuden a darle caza cuanto antes. ¡Cuidado!','Ultime notizie dalla sala antica: un Seregios è apparso sulle dune! A tutti i cacciatori di talento è richiesto di cacciarlo il prima possibile. Attenti alle sue lame!','',1200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10307,'A Shocking Scoundrel','Schockierender Schurke','Un intrus électrisant','Un canalla sorprendente','Un''esperienza elettrizzante','','Hunt an Astalos','Erjage einen Astalos.','Chasser 1 Astalos','Caza un Astalos','Caccia un Astalos','','Guild','2','LR',0,0,3,102,50,900,8700,450,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Sounds like an Astalos showed up in the Verdant Hills. It may be pretty for a monster, but don''t be fooled: its attacks are lethal, and it won''t hold back. Go prepared.','In den Grünen Hügeln ist wohl ein Astalos aufgetaucht. Von seinem ansprechenden Äußeren darf man sich aber keinesfalls täuschen lassen! Die Bestie ist tödlich und gnadenlos: Sei auf der Hut!','Il semble qu''un Astalos rôde dans les Collines verdoyantes. Ce monstre est aussi beau que mortel. Il ne fait pas de quartier. Abordez-le prudemment !','Parece que ha aparecido un Astalos en el Bosque Rocoso. Es bonito para ser un monstruo, pero no te fíes: sus ataques son letales y no tiene piedad. Ve con cuidado.','Sembra che un Astalos sia apparso sui colli verdi. Potrà anche sembrare carino, ma non farti fregare: i suoi attacchi sono letali e non si arrenderà senza colpo ferire. Sii prudente!','',1200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10308,'The King''s Domain','Domäne des Königs','Le domaine du roi','El dominio del rey','Il dominio reale','','Hunt a Rathalos','Erjage einen Rathalos.','Chasser 1 Rathalos','Caza un Rathalos','Caccia un Rathalos','','Guild','0','LR',0,0,3,2,50,800,7500,420,'None','–','Rien','Ninguno','Nessuno','','You hear that, Hunter? It''s the roar of a Rathalos, a wyvern that puts fear into even our fearless mercs. We need that thing gone, and it looks like you''re the best one for the job!','Hörst du das, Jäger? Das ist das Gebrüll eines Rathalos. So ein Wyvern- schrei schüchtert selbst die Mutigsten ein. Wir müssen das Biest loswerden. Und du darfst dich darum kümmern!','Vous entendez ça ? C''est le rugissement d''un Rathalos, une sorte de wyverne qui terrorise même nos mercenaires les plus courageux. Ça vous dirait de nous en débarrasser ?','¿Oyes eso, cazador? Es el rugido de un Rathalos, un wyvern que asusta hasta a los mercenarios más valientes. Necesitamos echarlo, y parece que tú eres el candidato ideal.','Riesci a sentirlo? È il ruggito di un Rathalos, un wyvern che incute timore anche al più impavido dei mercenari. Ci serve qualcuno che lo cacci, e sembra che tu ne sia all''altezza!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10309,'Fury Run Afoul','Fiese Furie','Le béhémoth furieux','Furia desencadenada','Corsa fuori controllo','','Hunt a Gammoth','Erjage einen Gammoth.','Chasser 1 Gammoth','Caza un Gammoth','Caccia un Gammoth','','Guild','1','LR',0,0,3,103,50,900,8700,450,'Deliver 1 Beast Tear','1 Bestienträne abliefern','Livrer 1 larme de bête','Entrega 1 lágrima de bestia','Consegna una lacrima belva','','A shortage of food has got a Gammoth goin'' crazy up on the ridge. No one can sleep at night, fearin'' it''ll come a-chargin'' through the walls! I beg ya, put it down so we can rest easy!','Ein Gammoth, der zu wenig zu fressen hat, terrorisiert die Umgegend. Niemand traut sich mehr zu schlafen vor Angst. Bitte erlege das Untier, damit wir wieder zur Ruhe kommen!','Un Gammoth visiblement affamé sème la zizanie sur la crête. Nous n''en dormons plus ! Nous craignons qu''il vienne enfoncer nos murs ! Je vous en supplie, tuez-le. Nous en serons soulagés !','Hay escasez de comida y un Gammoth anda como loco por las Cimas Árticas. Nadie puede dormir, por miedo a que derrumbe los muros y entre. Por favor, ¡acaba con él y tráenos paz!','La carenza di cibo ha fatto impazzire un Gammoth! Gli abitanti non riescono più a dormire la notte, per paura che carichi attraverso i muri. Ti prego, abbattilo, e finalmente potremo riposare!','',600,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10310,'Lagiacrus Lethargy','Lagiacrus-Lethargie','Lagiacrus léthargique','Letargo de Lagiacrus','Il letargo del Lagiacrus','','Hunt a Lagiacrus','Erjage einen Lagiacrus.','Chasser 1 Lagiacrus','Caza un Lagiacrus','Caccia un Lagiacrus','','Guild','0','LR',0,0,3,4,50,800,7500,420,'Sever Lagiacrus''s tail','Lagiacrus-Schwanz abtrennen','Couper la queue du Lagiacrus','Corta la cola del Lagiacrus','Mozza la coda del Lagiacrus','','The Lagiacrus may be king of the seas, but it''s just as scary on land. It was all I could do to distract the beast and hightail it out of there... Think you can fare any better?','Der Lagiacrus mag der König der Meere sein, aber auch an Land fürchte ich mich vor ihm. Mir blieb nur ein Täuschungsmanöver und dann die Flucht. Glaubst du, du schlägst dich besser?','Les Lagiacrus règnent sur les mers, mais ils sont tout aussi effrayants hors de l''eau. J''ai dû détourner l''attention de celui-là pour me carapater. Vous pensez pouvoir faire mieux ?','El Lagiacrus puede ser el rey de los mares, pero en tierra da el mismo miedo. Solo pude distraerlo y alejarlo de allí... ¿Crees que puedes hacerlo mejor?','Il Lagiacrus sarà anche il re dei mari, ma fa paura anche sulla terra. Io non ho potuto fare altro che... distrarlo e filarmela via! Tu pensi di poter fare di meglio?','',1200,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10311,'Moonlit Bubble Bath','Mondlicht-Blubberbad','Bain de bulles nocturne','Baño de burbujas nocturno','Bolle al chiaro di luna','','Hunt a Mizutsune','Erjage einen Mizutsune.','Chasser 1 Mizutsune','Caza un Mizutsune','Caccia un Mizutsune','','Guild','1','LR',0,0,3,104,50,900,8700,450,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','What a beautiful creature that Mizutsune is. And the way it uses bubbles to catch its prey... You gotta put a stop to it! What if people slip on those bubbles in the dark? Safety first!','Was für eine schöne Kreatur der Mizutsune ist! Und wie er Bläschen nutzt, um seine Beute zu fangen... das muss aufhören! Was ist, wenn Kinder damit spielen? Denkt denn keiner an die Kinder?','Cette Mizutsune est splendide, mais les bulles avec lesquelles elle attrape ses proies sont terribles... Quelqu''un pourrait glisser dessus dans le noir ! Mieux vaut prévenir que guérir !','Qué criatura tan bonita es ese Mizutsune. Y el modo en que usa las burbujas para cazar... ¡Eso no puede continuar! ¿Y si alguien se resbala? ¡La seguridad ante todo!','Il Mizutsune è una creatura meravigliosa. E come usa le bolle per catturare la sua preda... Ma devi fermarlo! Che succederebbe se qualcuno scivolasse su quelle bolle al buio? Prima la sicurezza!','',1200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10312,'Topple the Monarch','Stürze den Monarchen','Chasse au Chasseur','Derriba al monarca','Colpo di stato','','Hunt a Zinogre','Erjage einen Zinogre.','Chasser 1 Zinogre','Caza un Zinogre','Caccia uno Zinogre','','Guild','0','LR',0,0,3,4,50,900,8100,420,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','Many a poor soul has tried to hunt a Zinogre, only to end up being the one hunted instead. It''s truly worthy of its title, "The Hunter." Isn''t there anyone who can defy it?','Ich schätze, ich bin zu streng mit mir selbst. Ich bin bei Weitem nicht der einzige Jäger, der in den Nebelgipfeln durch den Zinogre zum Gejagten wurde. Warum sonst heißt er wohl "Der Jäger"? Trotzdem...','Je suis trop dur avec moi-même. Je ne suis pas le premier chasseur à devenir la proie du Zinogre des Pics Brumeux. Sinon, on ne l''appellerait pas "Le Chasseur", pas vrai ?','Supongo que soy muy duro conmigo mismo. No soy el único cazador cazado por el Zinogre de los Picos Nublados. ¿Por qué si no se conocería a ese monstruo como "el cazador"? Aun así...','Forse sono troppo duro con me stesso. Non sono il primo cacciatore che ha sfidato uno Zinogre sulle Cime nebbiose solo per diventare la sua preda. D''altra parte ci sarà un motivo se viene chiamato "Il cacciatore"...','',600,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10313,'Lagiacrushed','Lagiacrus-Havarie','Soupe de Lagiacrus','Muerte por Lagiacrus','Lagiacrisi','','Hunt a Lagiacrus','Erjage einen Lagiacrus.','Chasser 1 Lagiacrus','Caza un Lagiacrus','Caccia un Lagiacrus','','Guild','0','LR',0,0,3,6,50,800,7500,420,'None','–','Rien','Ninguno','Nessuno','','The mighty Lagiacrus is feared even by the most hardened men of the sea, and one has shown up where our villagers fish and work! Please, for their safety, hunt the Lagiacrus!','Der Lagiacrus ist der Schrecken der See. Sogar für die härtesten Seebären. Und nun ist einer bei den armen Fischern im Dorf auf- getaucht. Bitte jage ihn – für die Sicherheit des Dorfs!','Même les marins les plus endurcis craignent les Lagiacrus. Or, l''une de ces créatures hante en ce moment la zone de pêche de nos villageois ! Pour leur sécurité, éliminez-la !','Hasta los marineros más curtidos temen al poderoso Lagiacrus. ¡Pues ahora ha aparecido uno en el caladero de nuestra aldea! ¡Cázalo, por favor!','Il poderoso Lagiacrus è temuto persino dal più forte degli uomini di mare, e ora ne è giunto uno nel luogo in cui i cittadini pescano e lavorano! Devi cacciarlo, per la nostra sicurezza!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10314,'Tuff Turf','Hartes Terrain','Terre sauvage','Territorio volcánico','Territorio violato','','Capture a Zinogre','Fange einen Zinogre.','Capturer 1 Zinogre','Captura un Zinogre','Cattura uno Zinogre','','Guild','0','LR',2,0,3,6,50,1100,10800,500,'None','–','Rien','Ninguno','Nessuno','','I was training my tail off on the Deserted Island when, uh, I guess I kinda sorta riled up a Zinogre. My bad! No way I can handle that beast. Somebody beat it up for me!','Rückzug! Ein Zinogre ist auf der Ödnis-Insel! Ich muss ihn bei meinen Trainingsübungen verärgert haben. Das ist überhaupt nicht meine Liga. Würdest du ihn für mich erledigen?','Repli ! Un Zinogre sur l''île déserte ! J''ai dû l''énerver pendant mon entraînement. Je ne suis pas à la hauteur pour gérer ce problème. S''il vous plaît, aidez-moi !','¡Retirada! ¡Retirada! ¡Hay un Zinogre en la Isla Desierta! Debo haberlo cabreado con mis prácticas. Esto me queda grande. ¿Te encargas tú de derribarlo?','Ritirata! Ritirata! C''è uno Zinogre sull''Isola deserta! Forse l''ho disturbato mentre mi esercitavo. È troppo forte per me. Perché non te ne occupi tu?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10315,'Isle About that Bass','Wen kümmert denn die Baisse?','Basse ou baston ?','Una isla ruidosa','Il battito dell''isola','','Hunt a Brachydios','Erjage einen Brachydios.','Chasser 1 Brachydios','Caza un Brachydios','Caccia un Brachydios','','Guild','0','LR',0,0,3,6,50,900,9000,520,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','This time, I have a Quest for you. You hear that rumbling sound out of the Deserted Island? I want to know what''s causing it. If it seems to be something dangerous, then I want you to eliminate it!','Jetzt habe ich eine Aufgabe für dich: Hörst du das Rumpeln von der Ödnis- Insel? Ich möchte wissen, was dahintersteckt. Wenn es eine Gefahr ist, musst du sie ausschalten!','C''est moi qui ai une quête à vous confier cette fois. Vous entendez le bruit sourd provenant de l''Île déserte ? Je veux savoir d''où il sort. Et si la source vous semble dangereuse, éliminez-la !','Esta vez tengo una misión para ti. ¿Oyes ese rugido de la Isla Desierta? Quiero saber qué lo provoca. Y si resulta ser algo peligroso, quiero que lo elimines.','Questa volta ho una missione per te. Senti quello strano rumore che viene dall''isola deserta? Voglio scoprire cos''è che lo genera. Se è qualcosa di pericoloso... Beh, allora eliminalo!','',1200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10316,'Heaven and Earth','Himmel und Erde','Ciel et terre','Cielo y tierra','Paradiso in terra','','Hunt a Rathalos and a Rathian','Erjage einen Rathalos und eine Rathian.','Chasser 1 Rathalos Chasser 1 Rathian','Caza un Rathalos y una Rathian','Caccia un Rathalos e una Rathian','','Guild','1','LR',0,0,3,6,50,1000,9300,580,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','A Rathalos and a Rathian appeared on the Deserted Island. Do you know just how deadly they are when they''re together? And during mating season, it''s suicide! Still up for the hunt?','Ein Rathalos und eine Rathian sind auf der Ödnis-Insel aufgetaucht. Weißt du, wie tödlich die in Kombination miteinander sind? Absolut mörderisch zur Paarungszeit. Nun, bist du bereit zur Jagd?','Un Rathalos et une Rathian sont apparus sur l''Île déserte. Vous savez les dégâts qu''ils causent en pleine saison des amours ? C''est du suicide ! Alors, toujours envie d''y aller ?','Han aparecido un Rathalos y una Rathian en la Isla Desierta. ¿Sabes lo letales que son juntos? Y en época de cortejo, ¡vaya suicidio! ¿Todavía te interesa?','Un Rathalos e una Rathian sono giunti nell''isola deserta. Rathalos e Rathian sono letali, soprattutto nella stagione degli amori. Non dimenticarlo, se progetti di andare a caccia!','',600,60);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10317,'Fro Shizzle','Pro-Pro-Afro','Fulgurance capillaire','En serio','Parrucco all''ultimo grido','','Hunt an Astalos','Erjage einen Astalos.','Chasser 1 Astalos','Caza un Astalos','Caccia un Astalos','','Guild','0','LR',0,0,3,7,50,900,8700,450,'Deliver 1 Wyvern Tear','1 Wyvernträne abliefern','Livrer 1 larme de wyverne','Entrega 1 lágrima de wyvern','Consegna una lacrima wyvern','','Oh baby, I just had the grooviest idea: using an Astalos''s lightning to style the ultimate ''fro! That''s right, so hit the Marshlands and do your thang for me, baby!','Hör mal! Das ist die coolste Idee überhaupt: Astalos- Blitze für den absolut ultimativsten Afro! Das wird der Hammer, Dicker. Auf, los, ins Marschland mit dir! Zeig, was du kannst!','Je viens d''avoir une idée d''enfer. Utiliser la foudre d''un Astalos pour me faire la coupe afro ultime ! Cool, non ? Alors, allez dans les Marais et rapportez-moi de quoi me coiffer. Ouais !','Acabo de tener una idea total, chaval: usar el rayo de un Astalos para hacerme la afro más guay. Como lo oyes. Ve a la Ciénaga por mí y haz lo que sabes hacer.','Yo, doc, ho avuto un''idea mitica: usare il fulmine dell''Astalos per creare il parrucco definitivo! Esatto, quindi va'' nella palude e fallo fuori per me, doc!','',600,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10318,'Bladed Glavenus','Klingen-Glavenus','Glavenus coupant','Glavenus con filo','Il gladio del Glavenus','','Capture a Glavenus','Fange einen Glavenus.','Capturer 1 Glavenus','Captura un Glavenus','Cattura un Glavenus','','Guild','1','LR',2,0,3,8,50,1200,11400,540,'None','–','Rien','Ninguno','Nessuno','','I hear there are wyverns in this land that can wield katanas... Where I''m from, no such creatures exist. How do they do it? I''d really like to see one up close. Capture it for me.','Ich hörte, dass die Wyvern hier Schwerter verwenden. In meiner Heimat gibt es so etwas nicht. Wie machen sie das nur? Kannst du einen für mich fangen, damit ich mir das ansehen kann?','On dit que ce pays abrite des wyvernes maniant le katana. D''où je viens, de telles créatures n''existent pas. Comment font-elles ? J''aimerais en voir de près. Capturez-m''en une !','He oído que en este lugar hay wyverns capaces de blandir katanas... En mi país no hay nada así. ¿Cómo lo hacen? Querría verlo de cerca. Captúralo para mí.','Ho sentito che da queste parti ci sono wyvern che utilizzano katane. Da dove vengo io non esistono simili creature! Come fanno? Non mi dispiacerebbe vederne uno da vicino. Catturalo per me.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10319,'Fight or Uragaan!','Der Zorn des Vulkans','La fureur du volcan','La furia del volcán','La furia del vulcano','','Hunt an Uragaan','Erjage einen Uragaan.','Chasser 1 Uragaan','Caza un Uragaan','Caccia un Uragaan','','Guild','0','LR',0,0,3,8,50,700,6900,350,'Sever Uragaan''s tail','Uragaan-Schwanz abtrennen','Couper la queue de l''Uragaan','Corta la cola del Uragaan','Mozza la coda dell''Uragaan','','Don''t bother readin'' any further unless you''re a true hunter -- the type who loves that quiver in your guts when you''re livin'' on the razor''s edge. Still with me? Go to the Volcano. Somethin''s waitin''.','Nur für echte Jäger: Liebst du den Nervenkitzel, das Leben auf des Messers Schneide? Dann begib dich zum Vulkan. Dort wartet etwas auf dich.','Inutile de continuer si vous n''êtes pas de ces chasseurs qui aiment vivre sur le fil du rasoir. Vous voyez ce que je veux dire ? Allez donc jusqu''au Volcan. Quelque chose vous y attend.','No te molestes en seguir leyendo si no eres un cazador de los que se emocionan por dentro cuando corren peligro. ¿Sigues leyendo? Pues ve al volcán, y verás.','Non leggere oltre a meno che tu non sia un cacciatore che ama vivere pericolosamente! Sei ancora qui? Ok, allora recati al vulcano. C''è qualcosa che ti aspetta!','',1200,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10320,'The Shogun''s Encampment','Das Lager der Shogun','Le campement du Shogun','El campamento de Shogun','L’accampamento dello Shogun','','Hunt 2 Shogun Ceanataurs','Erjage 2 Shogun Ceanatauren.','Chasser 2 Ceanataur Shogun','Caza 2 Shogun Ceanataur','Caccia 2 Shogun Ceanataur','','Guild','0','LR',0,0,3,8,50,800,7800,530,'Deliver 1 Crab Pearl','1 Krabbenperle abliefern','Livrer 1 perle de crabe','Entrega 1 perla de cangrejo','Consegna una perla granchio','','Did you hear that two giant Carapaceon appeared at the Volcano!? This is where you hunters leap into action, yeah? If you really are a hunter, then you''ll be first in line to take out those beasts!','Zwei Carapaceon sind am Vulkan. Hast du davon gehört? Das ist doch ein Einsatzgebiet für euch Jäger, oder? Worauf wartest du? Sei der Erste, der diese Biester bekämpft!','Vous savez que deux Carapaceon géants ont élu domicile sur le Volcan ? C''est un boulot pour les chasseurs, ça. Prouvez votre valeur en les éliminant avant vos rivaux !','¿Has oído que han aparecido dos carapaceon gigantes en el Volcán? Es donde os toca intervenir, ¿no? Si eres realmente un cazador, te apuntarás a destruirlos.','Hai sentito? Nel vulcano sono apparsi due carapaceon giganti! A voi cacciatori non dispiace andare in zona, vero? In questo caso, non ti dispiacerà nemmeno abbattere quelle bestie!','',600,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10321,'Crustacean Infestation','Ceanatauren-Bande','Une bande de Ceanataur','Banda de Ceanataur','Una banda di Ceanataur','','Slay 20 Ceanataurs','Erlege 20 Ceanatauren.','Tuer 20 Ceanataur','Abate a 20 Ceanataur','Uccidi 20 Ceanataur','','Guild','0','LR',1,0,3,8,50,500,4500,200,'Wound Uragaan''s jaw','Uragaan-Kiefer verletzen','Briser la mâchoire de l''Uragaan','Hiere Uragaan en las fauces','Ferisci mascella Uragaan','','I was just minding my own business mining stuff when all of a sudden a Ceanataur shows up and bends my Iron Pickaxe clean in half! Rude! Go defeat 20 of them so I can resume my craft!','Ich war gerade am Schürfen, als ein Ceanataur auftauchte und meine eiserne Spitzhacke total verbog. Was für eine Unverschämtheit! Bitte besiege 20 von ihnen, sonst bin ich ohne Arbeit!','J''étais tranquillement en train de creuser quand un Ceanataur a surgi de nulle part pour plier ma pioche ! Quelle impudence ! Éliminez-en 20, que je puisse reprendre le travail !','Iba pensando en la mina cuando de repente salió un Ceanataur y me dobló el pico de hierro por el medio. ¡Qué grosería! Derrota a 20 de ellos para que pueda retomar lo mío.','Stavo scavando per affari miei quando un Ceanataur è spuntato fuori dal nulla e ha piegato il mio piccone di ferro! Bah, che cafone! Sconfiggine venti, così potrò tornare a scavare!','',1200,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10322,'Scavengers United','Vereinigte Plünderer','L''union des goinfres','Carroñeros unidos','Mangiatutto, uniti!','','Slay a total of 20 Remobra or Uroktor','Erlege insgesamt 20 Remobras oder Uroktor.','Tuer un total de 20 Remobra ou Uroktor','Abate a un total de 20 Remobra o Uroktor','Uccidi 20 Remobra o Uroktor in totale','','Guild','0','LR',1,0,3,8,50,600,5100,200,'Wound Brachydios''s arms','Brachydios-Vorderbein verletzen','Blesser les pattes avt. du Brachydios','Hiere Brach en patas del.','Ferisci zampe ant. Brachydios','','I was attacked by some monsters, but I played dead and fooled ''em all! All of ''em...except Uroktor and Remobras! Please, go out and slay them! My pride is at stake!','Ich wurde von Monstern angegriffen, doch ich konnte sie überlisten, indem ich mich totstellte. Nur Uroktor und Remobras durchschauten mich. Bitte töte sie. Sie könnten mich verraten!','Des monstres s''en sont pris à moi, mais j''ai réussi à tous les duper en faisant le mort. Enfin, sauf les Uroktor et les Remobras. Je vous en prie, tuez-les. Il en va de ma fierté !','Me atacaron unos monstruos, pero me hice el muerto y los engañé a todos... Menos a los Uroktor y Remobra. Por favor, ¡ve a matarlos! ¡Está en juego mi honor!','Sono stato attaccato da alcuni mostri, ma mi sono finto morto e li ho gabbati! Tutti quanti... Tranne gli Uroktor e i Remobra, ahimè! Ti prego, uccidili! Sai, ne va del mio orgoglio!','',1200,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10323,'More Coal Please','Mehr Kohle, bitte!','Toujours plus de charbon','Más carbón, por favor','Altro carbone, per favore!','','Deliver 20 pieces of Coal','Liefere 20 Einheiten Kohle ab.','Livrer 20 morceaux de charbon','Entrega 20 trozos de carbón','Consegna 20 pezzi di carbone','','Guild','0','LR',3,0,3,8,50,500,4200,200,'Deliver 1 Powderstone','1 Pulverstein abliefern','Livrer 1 pierre explosive','Entrega 1 piedra pólvora','Consegna una pietra-polvere','','The giant lance that protects our fortress runs on Coal, and I hear there''s plenty at the Volcano! Can you get some for us? If you break all your Pickaxes, you can always combine more!','Die Riesenlanze, die unsere Festung schützt, benötigt Kohle. Am Vulkan soll es viel davon geben. Kannst du uns welche holen? Falls die Spitzhacke bricht, kannst du weitere zusammensetzen.','L''épieu tueur de dragon a besoin de charbon. Nous aimerions que vous nous en rapportiez du Volcan. Sachez que vous pourrez fabriquer des pioches sur place au besoin.','La gran lanza que protege nuestra fortaleza necesita carbón, y he oído que en el Volcán hay mucho. ¿Puedes ir a buscarnos un poco? Y si rompes tus picos, puedes combinar más.','La lancia gigante che protegge la nostra fortezza è alimentata a carbone, e al vulcano ce n''è a profusione! Potresti procurarcene una scorta? Se rompi tutti i picconi, combinane altri!','',3000,20);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10324,'An Early Jurassic Excursion','Frühjura-Exkursion','Randonnée jurassique','Viaje al jurásico temprano','Un problema giurassico','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','LR',5,0,3,1,50,800,7500,390,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','Every time I try to focus my mind during training, some monster or other always attacks me. Noble Hunter! Clear these beasts from the Jurassic Frontier that I may meditate in peace!','Ich bin es leid! Ständig wird meine Konzentration beim Training durch Angriffe von Monstern gestört. Vertreibe diese Bestien aus dem Jura- Grenzland, damit ich in Frieden meditieren kann!','Des monstres ne cessent de m''importuner chaque fois que j''essaie de me concentrer. De grâce, chassez ces bêtes de la Frontière jurassique, que je puisse méditer en paix !','Cada vez que quiero centrar la mente, algún monstruo me ataca. Noble cazador, acaba con las bestias de la Fronda Arcaica para que pueda meditar en paz.','Ogni santa volta che provo ad allenarmi arriva un mostro che mi attacca. Non ne posso più! Uccidi quelle bestiacce dalla frontiera giurassica, ti prego, così potrò meditare in pace!','',1500,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10325,'Wyvern Sand Runners','Wyvern-Sandläufer','Course sur sable','Wyvern en la arena','Come wyvern sulla sabbia','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','LR',5,0,3,5,50,800,7800,460,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','On hunts, your footwork must be light, yet sturdy. That''s why I always train on the sand! I also look to bird wyverns and Seregios for inspiration. Fight a few so I can study their movements!','Meine Beinarbeit muss für die Jagd leichtfüßig und sicher sein, weshalb ich im Sand trainiere. Vogelwyvern und Seregios inspirieren mich – daher kämpfe ich zu Studienzwecken gegen sie.','À la chasse, il faut avoir le pas léger, mais assuré. Ainsi, je m''entraîne sur le sable. Je m''inspire aussi des wyvernes volantes et des Seregios. Affrontez-en, que je puisse les étudier !','Al ir de cacería, hay que colocar bien los pies. Por eso siempre entreno en la arena. Además, busco inspiración en wyverns y Seregios. ¡Lucha con ellos para que pueda estudiarlos!','A caccia il tuo passo deve essere leggero ma deciso. Per questo mi alleno sulla sabbia! Mi piacerebbe anche osservare Seregios e wyvern. Combattine qualcuno, e potrò studiare i loro movimenti!','',1500,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10326,'Zinogre, Bearly Fazed','Zinogre, leicht beunruhigt','L''impassible Zinogre','Un Zinogre anonadado','Non disturbare lo Zinogre','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','LR',5,0,3,6,50,800,7200,390,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','Did you see that!? An Arzuros totally just picked a fight with that Zinogre! Pretty reckless, I have to say... But whatever, now you should have the chance to get the jump on ''em both!','Hast du das gesehen? Ein Arzuros hat tatsächlich einen Kampf mit diesem Zinogre begonnen! Ziemlich leichtsinnig, wenn du mich fragst. Und eine gute Chance, beide zu erwischen!','Vous avez vu ? Un Arzuros vient de s''en prendre à ce Zinogre ! C''est courageux de sa part... Quoi qu''il en soit, c''est l''occasion ou jamais de chasser les deux !','¿Has visto? ¡Un Arzuros acaba de pelearse con ese Zinogre! Muy temerario, la verdad... Pero bueno, ¡ahora deberías poder ir a por los dos a la vez!','Hai visto?! Un Arzuros è andato a combattere contro quello Zinogre! Ah, ci vuole fegato, devo ammetterlo... Ma ora hai la possibilità di prenderli entrambi!','',1500,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10327,'Making it Safe for Swine','Schweine? Sicher!','Protéger le Mosswine','Un refugio seguro','Una specie in pericolo','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','LR',5,0,3,7,50,900,8400,410,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','Our studies show that the number of Mosswine in the Marshlands has declined due to large monsters in the area! Please, hunt down these beasts and protect the noble Mosswine!','Unsere Studien zeigen, dass die Zahl der Mosswine aufgrund größerer Monster in dieser Region sehr stark rückläufig ist. Bitte jagt diese Bestien und schützt die edlen Mosswine!','Nos études ont prouvé que le nombre de Mosswine dans les Marais a chuté en raison de la présence de monstres plus imposants ! Chassez ces bêtes pour sauver nos Mosswine !','Nuestros estudios indican que la presencia de grandes monstruos está reduciendo la población de Mosswine en la Ciénaga. Por favor, ¡caza esas bestias y protege al noble Mosswine!','I nostri studi mostrano che il numero di Mosswine nella palude sta calando a causa degli enormi mostri in zona! Collabora con la scienza! Abbatti quelle bestie e salva il nobile Mosswine!','',1500,40);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10328,'Rumble in the Volcano','Keilerei im Vulkan','Du rififi dans le Volcan','Estruendo en el Volcán','Tremori dal vulcano','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','LR',5,0,3,8,50,900,9000,480,'Earn 700 Wycademy Points','700 Wykademie-Punkte sammeln','Obtenir 700 points Wycadémie','Gana 700 puntos de Wycademia','Ottieni 700 punti Wyccademia','','I saw a Volvidon attacked by a Brachydios at the Volcano! It got away, and now it''s roaming dangerously close to my village! Can you hunt it for us? Keep an eye out for the Brachydios, too!','Ich sah, wie ein Volvidon von einem Brachydios angegriffen wurde. Ich konnte fliehen, doch nun ist er gefährlich nah am Dorf. Kannst du ihn jagen? Vergiss auch den Brachydios nicht!','Un Brachydios a attaqué un Volvidon au Volcan ! Celui-ci a pris la fuite, mais tourne à présent autour du village ! Pourriez-vous le chasser ? Et attention au Brachydios, aussi !','Vi un Brachydios atacar a un Volvidon en el Volcán. El Volvidon escapó, pero ahora se pasea muy cerca de la aldea. ¿Podrías cazarlo? Sin olvidarte del Brachydios, claro.','Ehi, ho visto un Volvidon attaccato da un Brachydios nel vulcano! È scappato, e ora si trova vicino al mio villaggio! Puoi cacciarlo per noi? Ma guardati le spalle anche dal Brachydios!','',1500,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10329,'It''s Electric','Er ist elektrisch!','C''est électrique','Es eléctrico','Esperienza elettrizzante','','Slay a Kirin','Erlege einen Kirin.','Tuer le Kirin','Abate un Kirin','Uccidi un Kirin','','Guild','0','LR',1,0,3,1,50,1100,10200,490,'None','–','Rien','Ninguno','Nessuno','','Unexplained natural events have been occurring deep within the Jurassic Frontier. Intriguing, don''t you think? I believe it''s a sign of mythic beasts, but you should go see it with your own eyes.','Im Jura-Grenzland kam es zu unerklärlichen Natur- phänomenen. Faszinierend, nicht wahr? Ich denke, dass mythische Bestien dahinter- stecken. Am besten, du überzeugst dich selbst.','Des évènements mystérieux ont lieu à la Frontière jurassique. Troublant, non ? Il s''agit peut-être de bêtes mystiques. Vous devriez aller voir par vous-même.','En las profundidades de la Fronda Arcaica ocurren fenómenos inexplicables. Intrigante, ¿no te parece? Creo que es culpa de unas bestias míticas, pero deberías ir a verlo tú.','Nella frontiera giurassica si sono verificati alcuni eventi inspiegabili! Credo sia un segno della presenza di bestie mistiche. Perché non vai a verificare di persona?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10330,'The White Brute','Das weiße Untier','La brute immaculée','La bestia blanca','Il bruto bianco','','Slay a Kirin','Erlege einen Kirin.','Tuer le Kirin','Abate un Kirin','Uccidi un Kirin','','Guild','0','LR',1,0,3,103,50,1100,10200,490,'Place flag on mountain top','Gildenflagge auf Gipfel setzen','Placer le drapeau au sommet','Coloca la bandera en la cima','Pianta bandiera Gilda','','My friends and I sought out a phantom beast. When we came upon it, we had but a moment before it smote us with thunder, casting us asunder. Please help us, Hunter.','Meine Freunde und ich suchten nach einer Phantom- kreatur. Als wir sie fanden, blieb uns nur ein Moment, bevor sie uns mit Donner überzog und auseinander- riss. Bitte hilf uns, Jäger!','Mes amis et moi cherchions une bête spectrale. Lorsque nous l''avons enfin trouvée, elle nous a frappés avec sa foudre et nous a projetés au loin. S''il vous plaît, venez-nous en aide !','Mis amigos y yo fuimos a por una bestia fantasmal. Cuando dimos con ella, enseguida nos atacó con rayos y nos venció. Ayúdanos, por favor, cazador.','I miei amici e io cercavamo una bestia fantasma. Quando l''abbiamo trovata, tuttavia, ci ha colpito con un tuono, scaraventandoci via. Abbiamo bisogno del tuo intervento!','',1200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10331,'The Frozen Dictator','Der gefrorene Diktator','Le dictateur glacial','El dictador glacial','Il dittatore ghiacciato','','Slay a Kushala Daora or repel it','Erlege einen Kushala Daora oder wehre ihn ab.','Tuer ou repousser le Kushala Daora','Abate al Kushala Daora o ahuyéntalo','Uccidi un Kushala Daora o mettilo in fuga','','Guild','0','LR',1,0,3,103,35,1300,12900,700,'Wound Kushala Daora''s wing','KushalaDaoraFlügel verletzen','Blesser les ailes du Kushala Daora','Hiere Kushala Daora en el ala','Ferisci l''ala del K.Daora','','I saw it! I know I did! A pair of black wings among a flurry of snow... Then a gust of wind nearly blew me off the mountain! Take my advice and don''t even go near that thing!','Ich habe sie gesehen. Ganz sicher. Zwei schwarze Flügel im Schneegestöber... Dann wurde ich fast vom Berg geweht. Hör auf meinen Rat und nähere dich diesem Ding erst gar nicht!','Je l''ai vu ! Je le sais ! Des ailes noires dans la neige... Et puis, un coup de vent a failli me renverser ! Un bon conseil, ne vous approchez pas de cette créature !','¡Lo vi! ¡Estoy seguro! Un par de alas negras en medio de la ráfaga de nieve. Luego, una ráfaga de viento casi se me llevó volando de la montaña. Hazme caso, ni te acerques a esa cosa.','L''ho visto! Non ho alcun dubbio! Due ali nere in un turbine di neve! E un attimo dopo una raffica di vento stava per scaraventarmi via dalla montagna! Credimi, quel mostro è inavvicinabile!','',1200,70);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10332,'The Elder Dragon of Mist','Drachenältester des Nebels','Dragon ancien de la brume','Anciano dragón de la niebla','Drago anziano della nebbia','','Slay a Chameleos or repel it','Erlege einen Chameleos oder wehre ihn ab.','Tuer ou repousser le Chameleos','Abate al Chameleos o ahuyéntalo','Uccidi un Chameleos o mettilo in fuga','','Guild','0','LR',1,0,3,2,35,1300,12900,700,'Sever Chameleos''s tail','Chameleos-Schwanz abtrennen','Couper la queue du Chameleos','Corta la cola del Chameleos','Mozza la coda del Chameleos','','I sensed a strong presence and turned around, but there was nobody there. There''s no way anything could have sneaked up on us from our vantage point, but still... Keep your guard up, Hunter!','Ich spürte etwas Starkes hinter mir und drehte mich um, aber da war niemand. An unseren Aussichtspunkt kann sich keiner heranschleichen, aber trotzdem... Bleib in Deckung, Jäger!','J''ai ressenti une forte présence. Mais, regardant derrière moi, je n''ai vu personne. Si on nous avait suivis, nous nous en serions aperçus... Gardez tout de même l''œil ouvert !','Noté una presencia poderosa, pero al girarme no había nadie. No es posible que alguien nos sorprendiese por la espalda desde donde estábamos y aun así... Ándate con ojo.','Ho percepito nettamente una presenza e mi sono voltato, ma non c''era nessuno. Nulla avrebbe potuto coglierci alle spalle dalla nostra posizione, eppure... Tieni gli occhi aperti!','',1200,70);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10333,'Emperor of Flame','Kaiser der Flammen','L''empereur de feu','Emperador de las llamas','Imperatore di fiamma','','Slay a Teostra or repel it','Erlege einen Teostra oder wehre ihn ab.','Tuer ou repousser le Teostra','Abate al Teostra o ahuyéntalo','Uccidi un Teostra o mettilo in fuga','','Guild','0','LR',1,0,3,8,35,1300,12900,700,'Wound Teostra''s head','Teostra-Kopf verletzen','Blesser la tête du Teostra','Hiere Teostra en la cabeza','Ferisci la testa del Teostra','','I beg of you, help me in carrying out my last task. I''ve chased a Teostra to the Volcano. Beware its flames, for they may roast you alive if you are careless. Godspeed, Hunter!','Bitte hilf mir dabei, meine letzte Aufgabe zu erfüllen. Ich habe einen Teostra bis an den Vulkan verfolgt. Hüte dich vor den Flammen, sie können leicht tödlich sein. Geh mit Gott, Jäger!','Aidez-moi à accomplir ma dernière tâche ! J''ai traqué un Teostra jusqu''au Volcan. Attention à ses flammes, qui peuvent vous calciner en un rien ! Bonne chance, Chasseur !','Te lo ruego, ayúdame a completar mi última tarea. He perseguido a un Teostra hasta el Volcán. Cuidado con sus llamas, pueden quemarte vivo si te descuidas. ¡Suerte, cazador!','Devo proprio chiedertelo: aiutami a portare a termine il mio ultimo incarico. Ho braccato un Teostra fino al vulcano. Occhio alle sue fiamme, però... o brucerai in un attimo. Buona fortuna!','',1200,70);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10334,'Stop the Wheel','Das Rad anhalten','Arrêter la roue du destin','Detén la rueda','Ferma il ciclo di morte','','Slay a Shagaru Magala','Erlege Shagaru Magala.','Tuer le Shagaru Magala','Abate al Shagaru Magala','Uccidi uno Shagaru Magala','','Guild','0','LR',1,0,3,16,50,1200,11100,560,'Wound S. Magala''s wingarms','Shagaru-Magala-Arm verletzen','Blesser le bras du Shagaru Magala','Hiere Shagaru M. en el brazo','Ferisci zampa Shagaru Magala','','I''ve only known the gods from their legends -- never did I expect to see one in my lifetime. Dare you attempt to halt the cycle of death and etch your own name into legend, Hunter?','Ich kenne die Götter nur aus Legenden. Hätte nie gedacht, zu meinen Lebzeiten einen zu sehen. Wagst du es, den Kreislauf des Todes zu stoppen und deinen Namen zur Legende zu machen, Jäger?','Je ne connais les dieux que par les légendes, je ne pensais pas en rencontrer un. Oserez-vous tenter de stopper le cycle de la mort et faire entrer votre nom dans la légende ?','Solo conocía a los dioses a través de leyendas... ¡No esperaba encontrarme uno en mi vida! ¿Te atreves a intentar interrumpir el ciclo de muerte y forjar tu leyenda, cazador?','Ho sentito molte leggende sugli dei, ma non mi sarei mai aspettato di vederne uno in carne e ossa. Avrai il coraggio di fermare questo ciclo di morte e di scolpire il tuo nome nella leggenda?','',1200,60);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10335,'Two-headed Carcass','Doppelkopfkadaver','Carcasse bicéphale','Cadáver bicéfalo','Carcassa a due teste','','Repel Nakarkos','Wehre Nakarkos ab.','Repousser le Nakarkos','Ahuyenta al Nakarkos','Metti in fuga il Nakarkos','','Guild','2','LR',1,0,3,21,50,1500,15000,840,'Break Nakarkos''s outer shell','Äußeren Nakarkos-Panzer brechen','Briser carapace ext. du Nakarkos','Rompe cpzón. externo Nakarkos','Rompi la corazza del Nakarkos','','Attention all hunters. Repelling Nakarkos is now the Wycademy''s top priority. Combine your efforts and bring this task to fruition. May luck be with you.','Jäger aufgepasst. Die Abwehr von Nakarkos hat höchste Priorität für die Wykademie. Bündelt eure Kräfte und seid erfolgreich! Viel Glück!','Chasseurs, votre attention, s''il vous plaît. La nouvelle priorité de la Wycadémie consiste à repousser le Nakarkos. Travaillez de concert pour mener à bien cette mission. Bonne chance.','Atención, cazadores. Ahora la prioridad de la Wycademia es echar al Nakarkos. Trabajad juntos para alcanzar el éxito. Que tengáis suerte.','Avviso a tutti i cacciatori. La nuova priorità della Wyccademia è ora quella di respingere il Nakarkos. Unite le forze e portate a termine la missione. Che la fortuna vi assista.','',1200,80);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (10401,'Harvest Tour: A. Steppe','Erntetour: Ahnensteppe','Visite : Steppe ancestrale','Recolección: Estepa Otoñal','Tour: Steppa ancestrale','','Survive until time expires or deliver a Paw Pass Ticket','Überlebe bis Zeitende oder gib einen Tatzenpass ab.','Temps expiré ou livrer un ticket Passechat','Aguanta hasta que acabe el tiempo o da un ticket zarpa','Sopravvivi fino allo scadere o consegna buono pass-zampa','','Guild','0','HR',3,0,4,11,50,0,12,0,'None','–','Rien','Ninguno','Nessuno','','Interested in gathering in the Ancestral Steppe, young Hunter? Take this Quest and you can gather as much as you like! Just deliver the Paw Pass Ticket when you''re ready to come back.','Bist du vielleicht an einer Erntetour in der Ahnensteppe interessiert? Nimm einfach diese Quest an, um deine Vorräte aufzufüllen. Liefere dann deinen Tatzenpass ab, um zurückzukehren.','Que diriez-vous d''une expédition de collecte dans la Steppe ancestrale ? Acceptez et vous pourrez garder tous les objets ! Livrez le ticket Passechat pour rentrer.','¿Quieres ir a recoger a la Estepa Otoñal? Acepta esta misión y ve a coger todo lo que quieras. Solo tienes que entregar el ticket zarpa cuando quieras volver a casa.','Vuoi depredare la steppa ancestrale? Oh, accetta questa missione e potrai raccogliere tutto ciò che vuoi! Per tornare a casa dovrai solo consegnare il buono pass-zampa.','',0,0);
