INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (40101,'Redhelm I: Hunt','Rothelm I: Jagd','Crâne-ardent I : Chasser','Ámbar I: Caza','Sanguis I: Caccia','','Hunt a Redhelm Arzuros','Erjage einen Rothelm- Arzuros.','Chasser 1 Arzuros Crâne-ardent','Caza un Arzuros Ámbar','Caccia un Sanguis Arzuros','','Permit','0','LR',0,0,1,6,50,600,5100,300,'None','–','Rien','Ninguno','Nessuno','','This Quest is for hunters with Special Permits. The target: a Redhelm Arzuros, a beast remarkable for its large frame and the threateningly crimson shade of its fur. This monster is very dangerous. Be careful.','Diese Quest ist für Jäger mit Spezialerlaubnis. Das Ziel: ein Rothelm-Arzuros, bekannt für seine große Statur und das bedrohlich rote Fell. Ein sehr gefähr- liches Monster, gib acht!','Quête pour les chasseurs ayant une licence spéciale. Votre cible : un Arzuros Crâne-ardent, connu pour sa carrure imposante et sa fourrure écarlate. Prenez garde, il est très dangereux.','Esta misión requiere licencia. El objetivo es un Arzuros Ámbar, una bestia famosa por su envergadura y su pelaje amenazante color carmín. Este monstruo es muy peligroso; ten cuidado.','Una missione per cacciatori con licenza. L''obiettivo è un Sanguis Arzuros, degno di nota per l''ampia ossatura e per la tonalità cremisi della sua pelliccia. È una creatura pericolosa, sii prudente!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (40102,'Redhelm II: Capture','Rothelm II: Einfangen','Crâne-ardent II : Capturer','Ámbar II: Captura','Sanguis II: Cattura','','Capture a Redhelm Arzuros','Fange einen Rothelm- Arzuros.','Capturer 1 Arzuros Crâne-ardent','Captura un Arzuros Ámbar','Cattura un Sanguis Arzuros','','Permit','0','LR',2,0,2,4,50,600,6000,360,'Hunt an Arzuros','Einen Arzuros erjagen','Chasser 1 Arzuros','Caza un Arzuros','Caccia un Arzuros','','This Quest is for hunters with Special Permits. A Redhelm Arzuros is in the Misty Peaks and the Wycademy wants it captured. Make sure you''re equipped to capture it before starting.','Diese Quest ist für Jäger mit Spezialerlaubnis. Fange einen Rothelm-Arzuros bei den Nebelgipfeln für die Wykademie ein. Stelle sicher, dass du alles zum Einfangen dabei hast.','Quête pour les chasseurs ayant une licence spéciale. Un Arzuros Crâne-ardent erre dans les Monts brumeux et la Wycadémie veut le capturer. Équipez-vous en conséquence avant d''y aller.','Esta misión requiere licencia. Hay un Arzuros Ámbar en los Picos Nublados, y la Wycademia quiere que lo captures. Asegúrate de llevar el equipo necesario cuando empieces.','Una missione per cacciatori con licenza. La Wyccademia vuole catturare un Sanguis Arzuros sulle cime nebbiose. Assicurati di avere l''occorrente per la cattura.','',2100,130);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (40103,'Redhelm III: Hunt','Rothelm III: Jagd','Crâne-ardent III : Chasser','Ámbar III: Caza','Sanguis III: Caccia','','Hunt 2 Redhelm Arzuros','Erjage 2 Rothelm-Arzuros.','Chasser 2 Arzuros Crâne-ardent','Caza 2 Arzuros Ámbar','Caccia 2 Sanguis Arzuros','','Permit','0','LR',4,0,3,6,50,800,7200,420,'None','–','Rien','Ninguno','Nessuno','','This Quest is for hunters with Special Permits. Two Redhelm Arzuros have been sighted on the Deserted Island and must be hunted. This is an extremely dangerous Quest, so stay sharp.','Diese Quest ist für Jäger mit Spezialerlaubnis. Zwei Rothelm-Arzuros wurden auf der Ödnis-Insel gesichtet. Geh sie jagen, aber sei vorsichtig. Diese Quest ist extrem schwer!','Quête pour les chasseurs ayant une licence spéciale. Deux Arzuros Crâne-ardent rôdent sur l''Île déserte. Chassez-les, mais gare, cette quête est très dangereuse. Ouvrez l''œil !','Esta misión requiere licencia. Se ha visto a dos Arzuros Ámbar en la Isla Desierta y hay que darles caza. Es una misión peligrosísima, así que ten cuidado.','Una missione per cacciatori con licenza. Due Sanguis Arzuros sono stati avvistati sull''isola deserta. Cacciali entrambi. È una missione molto pericolosa, stai in campana.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (40104,'Redhelm IV: Hunt','Rothelm IV: Jagd','Crâne-ardent IV : Chasser','Ámbar IV: Caza','Sanguis IV: Caccia','','Hunt a Redhelm Arzuros','Erjage einen Rothelm- Arzuros.','Chasser 1 Arzuros Crâne-ardent','Caza un Arzuros Ámbar','Caccia un Sanguis Arzuros','','Permit','0','HR',0,0,4,104,50,1100,10200,600,'Hunt a Royal Ludroth','Einen Königs-Ludroth erjagen','Chasser 1 Ludroth royal','Caza un Ludroth Real','Caccia un Ludroth reale','','This Quest is for hunters with Special Permits. We need you to hunt a Redhelm Arzuros in the Misty Peaks. Be warned: This specimen may be stronger than any you''ve faced thus far.','Diese Quest ist für Jäger mit Spezialerlaubnis. Jage einen Rothelm-Arzuros bei den Nebelgipfeln für uns. Sei aber gewarnt: Er ist vielleicht stärker als alles, was dir bisher begegnet ist.','Quête pour les chasseurs ayant une licence spéciale. Il faut chasser un Arzuros Crâne-ardent dans les Monts brumeux. Attention, il est sans doute plus dangereux que les autres.','Esta misión requiere licencia. Necesitamos que caces a un Arzuros Ámbar en los Picos Nublados. Pero vigila: este espécimen puede ser más fuerte que los que has visto hasta ahora.','Una missione per cacciatori con licenza. Dovresti occuparti di un Sanguis Arzuros sulle cime nebbiose. Fai attenzione: è più forte degli esemplari incontrati finora.','',6000,350);
