INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (41208,'Hellblade VIII: Capture','Zornklinge VIII: Einfangen','Lame-chaos VIII : Capturer','Inferno VIII: Captura','Ignis VIII: Cattura','','Capture a Hellblade Glavenus','Fange einen Zornklingen- Glavenus.','Capturer 1 Glavenus Lame-chaos','Captura un Glavenus Inferno','Cattura un Ignis Glavenus','','Permit','0','HR',2,0,8,12,50,3000,30000,2070,'None','–','Rien','Ninguno','Nessuno','','This Quest is for hunters with Special Permits. A Hellblade Glavenus is in the Volcanic Hollow and the Wycademy wants it captured. Supplies for capturing it will be provided. Use them wisely.','Diese Quest ist für Jäger mit Spezialerlaubnis. In der Vulkanmulde wurde ein Zornklingen-Glavenus gesichtet. Fang ihn für die Wykademie ein! Hilfsmittel zum Einfangen werden gestellt.','Quête pour les chasseurs ayant une licence spéciale. Un Glavenus Lame-chaos se trouve dans le Vallon volcanique et la Wycadémie le veut vivant. Utilisez les fournitures offertes.','Esta misión requiere licencia. Hay un Glavenus Inferno en el Hoyo Volcán y la Wycademia quiere que lo captures y te proporcionará materiales para hacerlo. Úsalos sabiamente.','Una missione per cacciatori con licenza. La Wyccademia vuole catturare un Ignis Glavenus nella fossa vulcanica. Ti forniremo le scorte per la cattura: sfruttale con intelligenza.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (41209,'Hellblade IX: Hunt','Zornklinge IX: Jagd','Lame-chaos IX : Chasser','Inferno IX: Caza','Ignis IX: Caccia','','Hunt 2 Hellblade Glavenus','Erjage 2 Zornklingen- Glavenus.','Chasser 2 Glavenus Lame-chaos','Caza 2 Glavenus Inferno','Caccia 2 Ignis Glavenus','','Permit','0','HR',4,0,9,101,50,3400,33300,2300,'None','–','Rien','Ninguno','Nessuno','','This Quest is for hunters with Special Permits. Two Hellblade Glavenus have been sighted in the Jurassic Frontier and must be hunted. This is an extremely dangerous Quest, so stay sharp.','Diese Quest ist für Jäger mit Spezialerlaubnis. Im Jura-Grenzland wurden zwei Zornklingen-Glavenus gesichtet. Geh sie jagen, aber sei vorsichtig. Diese Quest ist extrem schwer!','Quête pour les chasseurs ayant une licence spéciale. Deux Glavenus Lame-chaos ont été vus à la Frontière jurassique. Allez les chasser, mais gare, cette quête est très dangereuse. Ouvrez l''œil !','Esta misión requiere licencia. Se ha visto a dos Glavenus Inferno en la Fronda Arcaica y hay que darles caza. Es una misión extremadamente peligrosa, así que cuidado.','Una missione per cacciatori con licenza. Due Ignis Glavenus sono stati avvistati nella frontiera giurassica. Cacciali entrambi. È una missione molto pericolosa, stai in campana.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (41210,'Hellblade X: Hunt','Zornklinge X: Jagd','Lame-chaos X : Chasser','Inferno X: Caza','Ignis X: Caccia','','Hunt a Hellblade Glavenus and a Glavenus','Erjage einen Zornklingen- Glavenus und einen Glavenus.','Chasser 1 Glavenus Chasser 1 Glavenus Lame-chaos','Caza un Glavenus Inferno y un Glavenus','Caccia un Ignis Glavenus e un Glavenus','','Permit','0','HR',0,0,10,19,50,3700,36600,2530,'Wound Glavenus''s head & tail','Glavenus-Kopf+Schwanz verletzen','Blesser le Glavenus (tête & queue)','Hiere Glavenus cabeza/cola','Ferisci testa e coda Glavenus','','This Quest is for hunters with Special Permits. Both a Hellblade Glavenus and a Glavenus have been spotted on Ingle Isle, the former being especially strong. Be on your guard, Deputy.','Diese Quest ist für Jäger mit Spezialerlaubnis. Auf der Kamininsel wurden ein Zornklingen-Glavenus und ein Glavenus gesichtet. Vor allem Ersterer ist sehr stark, pass also auf dich auf!','Quête pour les chasseurs ayant une licence spéciale. Un Glavenus Lame-chaos et un Glavenus ont été vus sur l''Île d''Ingle. Le Lame-chaos est redoutable, alors, faites très attention.','Esta misión requiere licencia. Se ha visto a un Glavenus Inferno y a un Glavenus en Isla Ingle; el primero es especialmente fuerte. Ten cuidado, ayudante.','Una missione per cacciatori con licenza. Un Ignis Glavenus e un Glavenus sono stati avvistati sull''isola Focaia. Il primo è un esemplare molto temibile. Stai in guardia, vice.','',2400,250);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100201,'Prowler''s First Errand','Erste Pirscher-Besorgung','Première course','Primer encargo gatador','Commissioni da cacciamiao','','Deliver 5 Unique Ferns','Liefere 5 Einmalige Farne ab.','Livrer 5 fougères uniques','Entrega 5 helechos únicos','Consegna 5 felci speciali','','Village','0','LR',3,1,2,1,50,100,600,40,'None','–','Rien','Ninguno','Nessuno','','My! Why, aren''t you a cutie! Tough as any hunter, you say? I see... In that case, could I trouble you to bring me some Unique Ferns from the Jurassic Frontier? Good luck, dear!','Oh, du bist aber süß! Stark wie ein Jäger, sagst du? Na gut... Würdest du mir dann bitte ein paar Einmalige Farne aus dem Jura-Grenz- land bringen? Viel Glück, mein lieber Bote!','Regardez-moi cette bouille adorable ! Et aussi robuste qu''un Chasseur, vous dites ? Cela vous ennuierait de m''apporter des fougères uniques de la Frontière jurassique ? Bonne chance !','¡Vaya si no eres una preciosidad! ¿Tan duro como cualquier cazador, dices? Ya veo. En ese caso, ¿podrías traerme algunos helechos únicos de la Fronda Arcaica? ¡Buena suerte!','Oh, ma guarda che amore! Come dici? Un asso della caccia? Ah... In tal caso, potrei chiederti di portarmi delle felci speciali dalla frontiera giurassica? E buona fortuna, tesoro!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100202,'Jurassic Fungologist','Jura-Fungologe','Mycologue jurassique','Micólogo del Jurásico','Fungologo giurassico','','Deliver 8 Abyssal Mushrooms','Liefere 8 Unheil-Pilze ab.','Livrer 8 champignons abyssaux','Entrega 8 setas abismales','Consegna 8 funghi abissali','','Village','0','LR',3,1,2,1,50,200,1200,40,'None','–','Rien','Ninguno','Nessuno','','You like kinoko? Jumbo Village love kinoko! Villagers ask Neko to gather Abyssal Mushrooms from Jurassic Frontier. Saa! Do me this favor and kinoko trade get better, too! "Kinoko" means mushroom!','Magst du Kinoko? Jumbo-Dorf liebt Kinoko! Dorfbewohner wollen, dass Neko Unheil- Pilze im Jura-Grenzland sucht. Tu mir den Gefallen, dann floriert auch Kinoko- Handel. Kinoko heißt "Pilz".','Les villageois de Jumbo adorent les kinoko et veulent des champignons abyssaux de la Frontière jurassique. Aidez-moi à exporter des kinoko... Ça veut dire champignon !','¿Te gustan las kinokos? En Jumbo las adoran. Los aldeanos han pedido a Neko que coja setas abismales de la Fronda Arcaica. ¡Saa! Si me haces el favor, el comercio de setas mejorará.','A te piace kinoko? "Kinoko" significa fungo! A villaggio Jumbo piace kinoko! Abitanti chiedono a Neko di trovare funghi abissali da frontiera giurassica. Sa! Fai tu ciò, è bene per vendere kinoko!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100203,'Lucky Frontier Cat','Grenz-Glückskätzchen','Chat de frontière chanceux','Gato fronterizo afortunado','Gattofortuna di frontiera','','Earn 500 Wycademy Points','Sammle 500 Wykademie-Punkte.','Obtenir 500 points Wycadémie','Gana 500 puntos de Wycademia','Ottieni 500 punti Wyccademia','','Village','0','LR',3,1,2,101,50,100,900,50,'None','–','Rien','Ninguno','Nessuno','','Ah, a Prowler! You came at the right time! Would you be willing to collect me some points in the Jurassic Frontier? Deliver whatever items you like. Oughta be a breeze for you, eh?','Ah, ein Pirscher! Genau zur richtigen Zeit! Würdest du für mich im Jura-Grenzland ein paar Punkte sammeln? Liefere als Objekte ab, was du willst. Das ist doch ein Kinderspiel für dich, oder?','Un Miaroudeur ! Vous tombez à pic ! Accepteriez-vous de récupérer des points pour mon compte à la Frontière jurassique ? Livrez les objets de votre choix. Ça n''a rien de bien compliqué.','¡Un gatador! ¡Llegas justo a tiempo! ¿Quieres obtener puntos para mí en la Fronda Arcaica? Entrega lo que quieras. Seguro que te resulta sencillo.','Ah, cacciamiao! Capiti proprio a fagiolo! Avresti voglia di raccogliere dei punticini alla frontiera giurassica? Consegna pure gli oggetti che vuoi. Per te sarà una passeggiata, no?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100301,'Verdant Hills Bully','Tyrann der Grünen Hügel','Brute des Collines','Abusón del Bosque Rocoso','Il bullo dei colli verdi','','Slay 8 Melynxes','Erlege 8 Melynxe.','Tuer 8 Melynx','Abate a 8 Melynx','Uccidi 8 Melynx','','Village','0','LR',1,1,3,2,50,100,600,80,'None','–','Rien','Ninguno','Nessuno','','I refuse to stand by and see my precious Felynes bullied! Oh, I''m talking about those cursed Melynx running amok. If you can help me out, I''ll teach you how to easily employ more Felynes!','Ich sehe nicht zu, wie mei- ne lieben Felynen tyranni- siert werden. Ich spreche von den verdammten Melynxe. Wenn du mir hilfst, zeige ich dir, wie du problemlos mehr Felynen engagierst!','Je ne resterai pas les bras croisés sans rien faire alors que ces maudits Melynx persécutent mes Felynes. Aidez-moi et je vous aiderai à recruter aisément d''autres Felynes !','¡No voy a dejar que abusen de mis queridos Felyne! Me refiero a esos malditos Melynx que andan desbocados. Si me ayudas, te enseñaré a contratar fácilmente más Felyne.','Non posso stare a guardare mentre fanno i prepotenti con i miei preziosi Felyne! Oh, parlo di quei maledetti Melynx impazziti. Mi aiuti? Ti dirò come ingaggiare facilmente altri Felyne!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100302,'Lucky Hills Cat','Hügel-Glückskätzchen','Chat de colline chanceux','Gato de colina afortunado','Gattofortuna collinare','','Earn 800 Wycademy Points','Sammle 800 Wykademie-Punkte.','Obtenir 800 points Wycadémie','Gana 800 puntos de Wycademia','Ottieni 800 punti Wyccademia','','Village','0','LR',3,1,3,102,50,200,1500,100,'None','–','Rien','Ninguno','Nessuno','','Ah, my good Prowler friend! I''ve had nothing but good luck since our last little encounter. Now, I need you to collect points in the Verdant Hills. Deliver any items you like. Oughta be a breeze for you!','Ah, Pirscher, mein Freund! Seit unserem Treffen hatte ich nur Glück. Sammle jetzt in den Grünen Hügeln Punkte. Liefere als Objekte ab, was du willst. Das ist doch ein Kinderspiel für dich!','La chance me sourit depuis la dernière fois, Miaroudeur. Il faut maintenant que vous récupériez des points dans les Collines verdoyantes. Livrez ce que vous voulez. C''est un jeu d''enfant !','¡Mi buen gatador! He tenido buena suerte desde que nos vimos. Necesito obtener puntos en el Bosque Rocoso. Entrega lo que quieras. Te será sencillo, ya verás.','Ah, cacciamiao! La fortuna non smette di sorridermi, da quando ci siamo incontrati! Ora, voglio che tu raccolga punti sui colli verdi. Consegna gli oggetti che vuoi. Sarà una passeggiata!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100303,'The Mining Drag','Der Duft von Erz','L''odeur du minerai','Olor mineral','Un profumo prezioso','','Deliver 8 Balmstone Pieces','Liefere 8 Balsamstein-Stücke ab.','Livrer 8 morceaux d''odoripierre','Entrega 8 piezas ónix','Consegna 8 pezzi di balsamite','','Village','0','LR',3,1,3,4,50,200,1500,80,'None','–','Rien','Ninguno','Nessuno','','Girl, this Balmstone Piece smells something FIERCE. If I had more, I could carry them around as perfume and REALLY work it! Get some for me, would you? Good luck, and DON''T screw it up!','Oh Mann, dieses Balsamstein- Stück riecht INTENSIV! Wenn ich mehr davon hätte, könnte ich das Zeug als eine Art Parfum nutzen. Das wäre echt DUFTE! Besorge mir noch ein paar davon, ja?','Ah... Quelle odeur divine ! Un morceau d''odoripierre ! J''aimerais réaliser un collier. Mais il m''en faut plus ! Vous pourriez m''aider ? Quoi ? Une hache ? Les Miaroudeurs n''en ont pas besoin, enfin !','¡Ah! ¡Qué olor más divino! Se le llama pieza ónix. La conseguí en los Picos Nublados, pero ahora necesito más. Me harás ese favor, ¿no? ¿Un pico? Eres un gatador, no lo necesitas.','Ah... Questo profumo è così divino! Proviene da un pezzo di balsamite! Ne ho trovato uno sulle cime nebbiose, ma ne voglio di più! Ci pensi tu, vero? Eh? Un piccone? Ma per farci cosa, cacciamiao?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100304,'Felyne Fishing Weekly','Angelwoche für Felynen','Pêche hebdomadaire','Semanal de pesca Felyne','Tutto sulla pesca Felyne','','Deliver 3 Premium Sashimi','Liefere 3 Premium-Sashimi ab.','Livrer 3 sashimis Premium','Entrega 3 sashimi especiales','Consegna 3 sashimi premium','','Village','0','LR',3,1,3,2,50,100,900,90,'None','–','Rien','Ninguno','Nessuno','','We Felynes love fishing, but we need to purrform more studies if we want to discover the secrets of fishing and share them with the world! Those big pink Premium Sashimi should be purrfect for our research!','Wir Felynen lieben das Fi- schen, aber dessen Geheim- nissen kommen wir nur mit mehr Forschung und Publika- tionen auf die Spur. Diese großen rosa Premium-Sashimi sind dafür miausgezeichnet!','Les Felynes aiment la pêche, mais il faut approfondir nos recherches en la matière pour partager nos secrets avec le reste du monde ! Ces gros sashimis Premium roses seraient parfaits !','A los Felyne nos encanta pescar, pero tenemos que estudiar para descubrir los secretos de la pesca y compartirlos. Esos sashimi especiales rosas serían perfectos para el estudio.','Noi Felyne amiamo pescare, ma servono più ricerche se vogliamo scoprire i segreti della pesca e condividerli con il miaondo! Oh, quei bei sashimi premium sarebbero prrrfetti per i miei studi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100305,'Grooming the Moofahs','Muufah-Fellpflege','Toilettage de Moofah','Preparación de los Moofah','Toeletta per Moofah','','Deliver 5 pieces of Moofah Fur','Liefere 5 Einheiten Muufah-Fell ab.','Livrer 5 fourrures de Moofah','Entrega 5 uds. de pelo Moofah','Consegna 5 pellicce di Moofah','','Village','0','LR',3,1,3,6,50,200,1200,90,'None','–','Rien','Ninguno','Nessuno','','Okay, just a little off the...ack! Cutting Moofah fur is like trying to trim a hairball! Do me a solid and show me how you deal with it! ...You DO have a Cutting weapon, don''t you? Every good fur stylist needs one!','Okay, das war ein wenig... ups! Muufah-Fell schneiden ist wie ein Haarknäuel frisieren. Tu mir den Gefallen, zeig es mir... Eine Schnittwaffe hast du!? Ein Muss für Fellstylisten!','On coupe un peu sur... Oh, cette toison de Moofah est un vrai cauchemar ! Vous pourriez me montrer comment faire ? Vous avez une arme tranchante ? Un bon styliste ne sort jamais sans !','Un pequeño corte... ¡Grrr! Cortar pelo de Moofah es como intentar peinar una pelusa. ¡Anda, enséñame cómo se hace! Tienes algo que corte, ¿no? Todo buen estilista lo necesita.','Ok, ora un po'' qui... Miah! Tagliare il vello dei Moofah è come rifinire una palla di pelo! Ti prego, mostrami tu come si fa! Hai un''arma lacerante, no? Ogni buon acconciatore ne ha una!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100306,'Bug Reports','Fehlerbericht: Finde Bugs!','Invasion d''insectes','Informes de bichos','Tra mushi e moscerini','','Slay a total of 10 Vespoid or Hornetaur','Erlege insgesamt 10 Vespoiden oder Hornetauren.','Tuer un total de 10 Vespoid ou Hornetaur','Abate a un total de 10 Vespoid o Hornetaur','Uccidi 10 Vespoid o Hornetaur in totale','','Village','0','LR',1,1,3,7,50,100,600,80,'None','–','Rien','Ninguno','Nessuno','','Saikin, lately, Marshlands filled with giant insects. Ruining seinokei, ecosystem of Moga Village! Get rid of bugs, they ask. Come with Neko to turn the mushi into mush! "Mushi" means bug!','Saikin, das heißt unlängst, ist das Marschland voller Rieseninsekten. Sie zerstören Seinokei, das Ökosystem des Dorfes Moga. Werde mit Neko die Mushi-Plage los. Mushi heißt "Käfer"!','En ce moment, les Marais grouillent d''insectes géants. Ils ruinent l''écosystème du Village Moga ! Aidez-moi à tuer les mushi ! Mushi veut dire insecte.','Saikin, últimamente, la Ciénaga rebosa bichos gigantes que destrozan el ecosistema de Moga. Quieren deshacerse de ellos. Hagamos el encargo más mushi (significa bicho).','Saikin, di recente, palude è piena di mushi giganti. "Mushi" significa insetti! Così rovinano ecosistema di Villaggio di Moga! Manda via insetti, chiedono. Vieni con Neko a scacciare mushi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100401,'Great Maccao Headbreaker','Groß-Maccao-Kopfbrecher','Brise-tête de Grand Maccao','Partecabezas Gran Maccao','Spaccateste Gran Maccao','','Wound Great Maccao''s head','Verletze den Kopf des Groß-Maccao.','Blesser la tête d''un Grand Maccao','Hiere Gran Maccao en la cabeza','Ferisci la testa del Gran Maccao','','Village','0','LR',0,1,4,1,50,100,900,60,'None','–','Rien','Ninguno','Nessuno','','Hey, how about a friendly little competition? Let''s see if either of us can wound the head of one of those gaudy Great Maccao! If you succeed, then I''ll teach you how to hire more Palicoes!','Hey, was hältst du von einem kleinen Wettstreit? Ob wohl einer von uns den Kopf eines bunten Groß-Maccao verletzen kann? Wenn du gewinnst, gibt''s mehr Tipps zum Anheuern von Kameraden!','Un petit concours amical, ça vous dit ? Voyons si un d''entre nous peut briser la tête d''un Grand Maccao ! Si vous y parvenez, je vous expliquerai comment engager d''autres Palicos !','¿Os apetece una competición amistosa? A ver si podéis herir las cabezas de esos Gran Maccao fanfarrones. Si lo lográis, os enseñaré a contratar más camaradas.','Ehilà! Ti va una piccola sfida? Vediamo se uno di noi riesce a ferire la testa di uno di quei Gran Maccao dai colori sgargianti! Se vinci, ti insegnerò come ingaggiare altri compagni!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100402,'Dash It All','Spurt voraus','Ruée en sens inverse','Córrelo todo','Corse e rincorse storiche','','Slay 10 Bullfango','Erlege 10 Bullfangos.','Tuer 10 Bullfango','Abate a 10 Bullfango','Uccidi 10 Bullfango','','Village','0','LR',1,1,4,102,50,200,1200,120,'None','–','Rien','Ninguno','Nessuno','','No matter how many times a Bullfango charges me, I can never evade it. And I''m not lion, I''m trying real hard! I just turn around, and run straight! What am I doing wrong!?','Wie oft auch ein Bullfango mich angreift, ich entgehe ihm nie. Das ist nicht an den Schnurrhaaren herbeige- zogen. Umdrehen, weglaufen, was mache ich falsch?!','J''ai beau essayer, chaque fois qu''un Bullfango charge sur moi, je n''arrive pas à l''éviter. Pourtant, je fais de mon mieux ! Je fais volte-face et je cours tout droit. Je m''y prends mal ?','Da igual cuántas veces me embista un Bullfango, nunca consigo esquivarlo. Y no será porque no lo intento, de veras. Me doy la vuelta y corro en línea recta. ¿Qué es lo que hago mal?','Oh, i Bullfango mi caricano sempre, ma non c''è una volta che mi riesca di schivarli. Non sarò un leone, eppure mi impegno! Mi giro e scappo tutta a dritta! Dov''è che sbaglio?!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100403,'An Offer You Can''t Refuse','Ein bestechendes Angebot','À bon chat bon œuf','Oferta irrechazable','Un''offerta irrifiutabile','','Deliver 3 Gargwa Eggs','Liefere 3 Gargwa-Eier ab.','Livrer 3 œufs de Gargwa','Entrega 3 huevos de Gargwa','Consegna 3 uova di Gargwa','','Village','0','LR',3,1,4,4,50,200,1500,130,'None','–','Rien','Ninguno','Nessuno','','Hey, Prowler. You''ll help a kit in need, right? I need a Gargwa Egg. Why? ''Claws I said so! Less talkin'', more egg-straction! Here''s a tip: poke it from behind, and maybe it''ll leave an egg for ya. I''m countin'' on you!','Hey, Pirscher. Du hilfst doch einer Katze in Not?! Ich brauche ein Gargwa-Ei. Warum? Kann dir ganz Ei-gal sein. Ein Tipp: Ein Stoß von hinten sichert dir mit Glück ein Ei. Also los!','Aidez un matou en détresse. Il me faut un œuf de Gargwa. Pourquoi ? Mêlez-vous de vos chatons. Un conseil : piquez-le par-derrière. Qui sait, il pondra peut-être un œuf. Je compte sur vous !','¡Eh, gatador! ¿Me ayudas? Necesito un huevo de Gargwa. ¿Que por qué? ¡Porque sí! Venga, déjate de rollos. Si le golpeas por detrás, tal vez suelte un huevo. Cuento contigo.','Ehi, cacciamiao. I mici si vedono nel miaomento del bisogno, sì? Ebbene, a me serve un uovo di Gargwa! Perché? Perché l''uovoglio! Ricorda: colpendo da dietro, magari ti sgancia un ovetto!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100404,'The Secret Ingredient','Die geheime Zutat','L''ingrédient secret','El ingrediente secreto','L''ingrediente segreto','','Deliver 4 Carapaceon Brains','Liefere 4 Carapaceon-Hirne ab.','Livrer 4 cerveaux de Carapaceon','Entrega 4 sesos de carapaceon','Consegna 4 cervelli carapaceon','','Village','0','LR',3,1,4,8,50,200,1200,120,'None','–','Rien','Ninguno','Nessuno','','The secret to cooking is to waste as little time as possible and blast everything at high heat! Then, just top it with my secret ingredient... What? I''m out of stock!? Please, get me some more!','Das Geheimnis beim Kochen ist absolute Schnelligkeit und starkes Erhitzen! Dann fehlt nur noch meine geheime Zutat... Was? Die ist mir ausgegangen?! Hol mir bitte Nachschub!','Le secret de la cuisine, c''est de faire au plus vite et de tout cuire à feu vif ! Il suffit ensuite d''ajouter mon ingrédient secret et... Quoi ? Je n''en ai plus ? Vous pourriez en chercher ?','El secreto de la cocina es tardar lo menos posible y cocinar con fuego alto. Solo hay que añadir mi ingrediente secreto y... ¿¡Qué!? ¡Se me ha acabado! Consígueme más, por favor.','Il miao segreto in cucina? Spreco meno tempo possibile e cuocio tutto a fiamma alta! Poi aggiungo un pizzico del mio ingrediente segreto e... Miah! È finito?! Ti prrrego, portamene ancora!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100405,'Marshlands Fishing?','Fischen im Marschland?','Pêche dans les marais ?','¿Pesca en la Ciénaga?','Pesca di palude?','','Deliver 3 Goldenfish','Liefere 3 Goldenfische ab.','Livrer 3 poissons d''or','Entrega 3 peces dorados','Consegna 3 pesci-dorati','','Village','0','LR',3,1,4,7,50,200,1500,120,'None','–','Rien','Ninguno','Nessuno','','Tanzia Market have freshest sakana. "Sakana" means fish! Shikashi! But! If you scout out good fishing spots, we ensure good supply! Sure to make trade for oishii fish improve, too! "Oishii" means tasty!','Der Tanzia-Markt bietet frischste Sakana. Sakana heißt "Fische". Shikashi! Aber! Finde du gute Angel- plätze, wir sichere Versor- gung. Für mehr oishi Fisch! Oishi heißt "lecker".','Le Marché de Tanzia vend du sakana très frais. Sakana veut dire poisson ! Trouvez de bons coins de pêche pour nous aider à assurer l''approvisionnement en bon poisson !','El mercado de Tanzia tiene el sakana (pescado) más fresco. ¡Shikashi! Pero si encuentras buenas zonas de pesca, nos aseguramos el suministro. El pescado rico mejorará las ventas.','Mercato di Tanzia ha sakana freschissimo. "Sakana" vuol dire pesce! Shikashi! Ma! Se trovi buone zone di pesca, otteniamo buone scorte! Per vendere pesce oishii! "Oishii" significa gustoso!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100406,'Arctic Point Expedition','Arktische Punktexpedition','Expédition en Arctique','Expedición al Punto ártico','Spedizione al punto artico','','Earn 1000 Wycademy Points','Sammle 1000 Wykademie-Punkte.','Obtenir 1000 points Wycadémie','Gana 1.000 puntos Wycademia','Ottieni 1000 punti Wyccademia','','Village','0','LR',3,1,4,103,50,200,1800,150,'None','–','Rien','Ninguno','Nessuno','','I found the cutest Felynes, dear! But first they want some points, I fear. If you can help with Point collection then we''ll have better Scout selection!','Ich habe ein supersüßes kleines Schätzchen gefun- den, aber es redete nur vom Punkte sammeln. Mit deiner Hilfe könnte ich noch mehr Lieblinge ausfindig machen!','J''ai trouvé une adorable créature, mais elle me rebat les oreilles pour que je lui récupère des points. Si vous m''aidez, je pourrai peut-être en trouver d''autres comme elle !','Me he encontrado a un pequeño encantador, pero no para de pedirme que obtenga puntos para él. Si me ayudas, podré encontrar aún más preciosidades.','Ho trovato un amorino carino carino, ma quando gli ho parlato mi ha proibito di raccogliere punti per lui... Se mi darai una manina tu, però, potrò andare a scovare altri tesorini tenerissimi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100501,'Blood Red Marshlands','Blutrotes Marschland','Les marais pourpres','Ciénaga rojo sangriento','Una palude rosso sangue','','Slay 10 Ioprey','Erlege 10 Ioprey.','Tuer 10 Ioprey','Abate a 10 Ioprey','Uccidi 10 Ioprey','','Village','0','LR',1,1,5,7,50,100,900,160,'None','–','Rien','Ninguno','Nessuno','','Prowler, heed my request! There''s an Iodrome in the Marshlands I''ve been hunting, but a gang of Ioprey keep getting in the way! Do you think you could lend me a hand out there?','Pirscher, hör mir zu! Ich habe im Marschland einen Iodrome gejagt, aber eine Gruppe Ioprey ist mir immerzu im Weg! Kannst du mir vielleicht helfen da draußen?','Miaroudeur, j''ai besoin de vous ! Je chasse un Iodrome dans les marais, mais une bande d''Ioprey me met des bâtons dans les roues ! Vous pourriez me donner un petit coup de main ?','¡Escucha mi petición, gatador! Estoy intentando cazar un Iodrome en la Ciénaga, pero una banda de Ioprey se interpone en mi camino. ¿Podrías echarme una mano?','Cacciamiao, ascolta la mia richiesta! Nella palude si aggira uno Iodrome a cui sto dando la caccia, ma un branco di Ioprey continua a intralciarmi! Potresti darmi una mano?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100502,'An Offer You Can''t Relieve','Ein unmoralisches Angebot','Qui vole un œuf','Una oferta irreemplazable','Un''offerta indeclinabile','','Deliver a Wyvern Egg','Liefere ein Wyvern-Ei ab.','Livrer 1 œuf de wyverne','Entrega 1 huevo de wyvern','Consegna un uovo di wyvern','','Village','0','LR',3,1,5,102,50,200,1200,170,'None','–','Rien','Ninguno','Nessuno','','Hey, Prowler! You''ll help a kit in need, right? I need a Wyvern Egg! Little dangerous, but that''s purr for the course for you, nya? That egg should be nutritious enough to... Uh, nevermind! Just get that egg!','Hey, Pirscher! Du hilfst doch einer Katze in Not?! Ich brauche ein Wyvern-Ei! Das ist natürlich nicht ungefährlich. Das Ei ist so nahrhaft... Ach, vergiss es! Hol einfach das Ei!','Vous aideriez un matou en détresse ? Il me faut un œuf de wyverne ! C''est difficile, mais vous retomberez sur vos pattes ! Cet œuf sera assez gros pour... Oubliez ! Apportez-le moi !','¡Eh, gatador! ¿Ayudarías a un minino necesitado? Necesito un huevo de wyvern. Tiene cierto peligro, pero eso no te para, ¿no? Es nutritivo y... Da igual. Anda, tráemelo.','Ehi, cacciamiao! I veri mici si vedono nel miaomento del bisogno, sì? Ecco, a me serve un uovo di wyvern! Ok, è rischioso, ma sai quanto è gustoso? E nutriente! E... Insomma, prendi quell''uovo!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100503,'Uroktor Hide ''n Seek','Uroktor-Versteckspiel','Partie de cache-cache','Escondite con Uroktor','Nascondino con l''Uroktor','','Slay 10 Uroktor','10 Uroktor erlegen','Tuer 10 Uroktor','Abate a 10 Uroktor','Uccidi 10 Uroktor','','Village','0','LR',1,1,5,8,50,300,2100,160,'None','–','Rien','Ninguno','Nessuno','','I was playing hide-and-seek at the Volcano when Uroktor burst from the ground -- talk about a surpurrise! Luckily, I burrowed before they could see me. If you run into trouble, be sure to hide as soon as possible!','Ich hab am Vulkan Verstecken gespielt, da kommt ein Uroktor aus dem Boden geschossen und lässt mir die Schnurrhaare zu Berge stehen! Zum Glück hat er mich nicht gesehen. Bei Ärger versteck dich bloß sofort!','Je jouais à cache-cache au Volcan quand un Uroktor a surgi du sol. Heureusement, j''ai réussi à m''enfouir avant qu''il ne me voie. En cas de problème, cachez-vous rapidement !','Estaba jugando al escondite en Volcán cuando ha salido un Uroktor de un matorral. ¡Menuda sorpresa! Por suerte, me he enterrado y no me ha visto. Si te ves en apuros, escóndete rápido.','Giocavo a nascondino nel vulcano quando è spuntato dal terreno un Uroktor... Chiamala sorprrresa! Fortuna che me la sono data a zampe! Nascondersi è la parola d''ordine quando vedi guai!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100504,'Into the Volcano','In den Vulkan','Au cœur du Volcan','Dentro del Volcán','Dentro il vulcano','','Slay 8 Remobras','Erlege 8 Remobras.','Tuer 8 Remobras','Abate a 8 Remobra','Uccidi 8 Remobra','','Village','0','LR',1,1,5,8,50,300,2400,160,'None','–','Rien','Ninguno','Nessuno','','Ugh... I took this job, but I''m having a little trouble finishing it. Help me out? Remobras hate loud noises, so use that to your advantage. Also... They''re ravenous carnivores. Good luck!','Pfui Teufel... Ich habe diesen Job übernommen, aber er macht mir Probleme. Hilfst du mir? Remobras hassen Lärm, also nutz das aus. Sie sind auch gierige Fleischfresser. Viel Glück!','Pff, j''ai accepté une tâche, mais je ne m''en sors pas. Vous pourriez m''aider ? Les Remobra détestent le bruit, alors, faites du boucan. Au fait, ces carnivores sont affamés. Bonne chance !','Uf, acepté un encargo, pero me está costando acabarlo. ¿Me ayudas? Usa el hecho de que los Remobra odian el ruido. Y también que son carnívoros voraces. ¡Buena suerte!','Uff... Ho accettato questo incarico, ma portarlo a termine è complicato. Puoi darmi una mano? I Remobra detestano i rumori molesti: approfittane! Ah, e... sono carnivori famelici. Auguri!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100505,'Kut-Ku Earbreaker','Kut-Ku-Ohrbrecher','Briseur d''oreille de Kut-Ku','Rompeorejas de Kut-Ku','Spaccaorecchie Kut-Ku','','Wound Yian Kut-Ku''s head','Verletze den Kopf des Yian Kut-Ku.','Blesser la tête du Yian Kut-Ku','Hiere Yian Kut-Ku en la cabeza','Ferisci testa Yian Kut-Ku','','Village','0','LR',0,1,5,101,50,200,1500,120,'None','–','Rien','Ninguno','Nessuno','','Was delivering new trade cart when spotted by Yian Kut-Ku! Prowler, find dishonorable beast and attack its big mimi! "Mimi" means ears! Then Neko can finally deliver new cart to help with trade! Yakusoku!','Ich liefere neuen Handels- karren aus, da sieht mich Yian Kut-Ku. Pirscher, such ehrlose Bestie und geh ihre großen Mimi an! Mimi heißt "Ohren". Dann kann Neko endlich liefern! Yakusoku!','Un Yian Kut-Ku m''a repéré lors d''une livraison ! Miaroudeur, chassez-le coûte que coûte pour que je puisse reprendre mes affaires ! Attaquez ses grandes oreilles !','Iba a entregar un carro de mercancías cuando me ha visto un Yian Kut-Ku. Busca a esa bestia y ataca en sus mimi (significa orejas). Neko entregará el carro de hacer negocios. ¡Yakusoku!','Neko consegnava carro quando avvistato da Yian Kut-Ku! Tu trova bestia disonorevole e attacca sue mimi! "Mimi" significa orecchie! Poi Neko dà carro per tuo aiuto! Yakusoku!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100601,'The Right Cattitude','Die richtige Einstellung','La bonne chattitude','La gactitud adecuada','La gattitudine giusta','','Hunt an Arzuros','Erjage einen Arzuros.','Chasser 1 Arzuros','Caza un Arzuros','Caccia un Arzuros','','Village','0','LR',0,1,6,4,50,200,1800,210,'None','–','Rien','Ninguno','Nessuno','','Wow! That talent! It''s hard to believe you''re a Felyne like me! Maybe you could handle an Arzuros, too? Nah... No way just a Felyne could take one of those on... Huh? You''ll try it!? My hero!','Wow! Was für ein Talent... Bist du wirklich eine Felyne wie ich? Kannst du etwa auch einen Arzuros erledi- gen? Für eine Felyne ist das doch unmöglich... Was? Du versuchst''s? M-Mein Held!','Miaou ! Quel talent ! C''est fou que vous soyez un Felyne ! Si ça se trouve, vous pourriez vaincre un Arzuros ! Non, aucun Felyne n''y arriverait... Vous tenteriez le coup ? Waouh !','¡Vaya! Cuesta creer que seas un Felyne como yo. Quizá también serías capaz de vértelas con un Arzuros. No, es imposible que un Felyne ganase a... ¿¡Vas a intentarlo!? ¡Eres mi héroe!','Miaow, che talento! Da non credere che tu sia Felyne come me! E affronteresti anche un Arzuros? Naaah, un Felyne contro un bestione così non potrebbe mai! Eh? Ci prrroverai?! Grandioso!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100602,'D. Hermitaur Shellbreaker','D.-Hermitaur-Panzerbrecher','Brise-carapace Hermitaur D','Rompecapar. D. Hermitaur','Spaccacorazze D.Hermitaur','','Break Daimyo''s shell','D.-Hermitaur-Panzer brechen','Briser la carapace du Daimyo','Rompe caparazón del Hermitaur','Rompi corazza del D.Hermitaur','','Village','0','LR',0,1,6,5,50,200,1800,150,'None','–','Rien','Ninguno','Nessuno','','Hey, how about a friendly little competition? Let''s see if either of us can smash the shell of a Daimyo Hermitaur! I''ll even give you a tip: try mounting it first! If you win, I''ll give you some more tips!','Hey, was hältst du von ein- nem kleinen Wettstreit? Ob wohl einer von uns den Pan- zer eines Daimyo Hermitaur brechen kann? Ein Tipp: Spring zuerst auf! Wenn du gewinnst, gibt''s mehr Tipps!','Un petit concours amical ? Voyons si l''un de nous peut briser la carapace d''un Hermitaur Daimyo ! Un conseil, montez dessus ! Si vous gagnez, je vous donnerai d''autres tuyaux !','¿Os apetece una competición amistosa? A ver si podéis partir el caparazón de un Daimyo Hermitaur. Os daré una pista: intentad montarlo antes. Si ganáis, os daré más consejos.','Ehilà! Ti va una piccola sfida? Vediamo se uno di noi riesce a spaccare la corazza di un Daimyo Hermitaur! Ti concedo anche una dritta: prima saltagli in groppa! Se vinci, avrai altri consigli!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100603,'Surrounded by Blue and Green','Inmitten von Blau und Grün','Tout de bleu et de vert','Rodeado de azul y verde','In mezzo al verde e al blu','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Village','0','LR',5,1,6,9,50,300,2100,230,'None','–','Rien','Ninguno','Nessuno','','Shimatta... Was delivering new cart when fell victim to cowardly kougeki. "Kougeki" means attack! Prowler, get rid of dishonorable beasts and Neko give beloved cart for kind help! Yakusoku!','Shimatta... Ich liefere neuen Karren aus und dann – feiger Kougeki. Kougeki heißt "Angriff". Pirscher, werd die ehrlosen Bestien los! Neko bedankt sich mit geliebtem Karren. Yakusoku!','Des lâches m''ont attaqué alors que je livrais un nouveau chariot ! C''est contraire au Bushido ! Miaroudeur, éliminez ces bêtes sans honneur et je vous donnerai mon chariot !','Shimatta... Iba a entregar un carro nuevo cuando sufrí un vil kougeki (que significa ataque). Deshazte de esas bestias sin honor y Neko te dará un carro a cambio de tu ayuda. ¡Yakusoku!','Shimatta... Neko consegnava nuovo carro e a improvviso subisce kougeki vigliacco. "Kougeki" significa attacco! Cacciamiao, elimina bestie disonorevoli e Neko dà carro per tuo aiuto! Yakusoku!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (100604,'Poison and Fangs','Gift und Reißzähne','Du poison et des crocs','Veneno y colmillos','Zanne e veleno','','Hunt a Bulldrome and an Iodrome','Erjage einen Bulldrome und einen Iodrome.','Chasser 1 Bulldrome et 1 Iodrome','Caza un Bulldrome y un Iodrome','Caccia un Bulldrome e uno Iodrome','','Village','0','LR',0,1,6,7,50,300,3000,260,'None','–','Rien','Ninguno','Nessuno','','I found some Felynes strong and wise. I''m sure they''d make for great allies! Go forth to aid them without pause and maybe then they''ll join our cause!','Ich habe im Marschland ein ganz süßes kleines Ding ge- funden. Aber überall lauern Monster... Hilfst du mir? Dann könnte ich noch mehr dieser guten Seelen finden!','J''ai découvert une adorable petite créature dans les Marais, mais il y a tant de monstres qui y rôdent... Si vous m''aidiez, je pourrais dénicher d''autres de ces jolies petites bêtes !','He encontrado una monada en la Ciénaga, pero al parecer está plagada de monstruos. ¿Me echas una mano? Si lo logras, puede que encuentre aún más almas prometedoras.','Nella palude ho trovato degli amorini carini carini, ma a quanto pare ci sono mostri ovunque... Mi daresti una manina? Se ci riesci, potrei anche trovare altre piccole anime innocenti!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110101,'Dance: The Final Frontier','Tanz: Die letzte Grenze','Une dernière danse','Baile: la frontera final','Danza, ultima frontiera','','Slay 15 Maccao','Erlege 15 Maccaos.','Tuer 15 Maccao','Abate a 15 Maccao','Uccidi 15 Maccao','','Guild','0','LR',1,1,1,1,50,200,1200,90,'None','–','Rien','Ninguno','Nessuno','','I wish to hold a ball in the Jurassic Frontier, but some ruffians have crashed the venue, and their clothes are simply atrocious... Ooh, I can''t stand it! Would you be a darling and drive them off?','Ich möchte einen Ball im Jura-Grenzland geben, aber einige Rüpel haben den Ort demoliert. Und ihre scheuß- liche Kleidung... einfach unerträglich! Bist du so nett und vertreibst sie?','Je compte organiser un bal à la Frontière jurassique, mais des malotrus ont saccagé la salle et leur plumage est tout simplement hideux. C''est inacceptable ! Pourriez-vous les chasser ?','Quiero celebrar un baile en la Fronda Arcaica, pero unos rufianes se han colado en el recinto. Llevan un atuendo infame. No puedo soportarlo. ¿Tendrías la gentileza de echarlos?','Gradirei dare un ballo nella frontiera giurassica, ma dei bricconi sono giunti senza invito e i loro soprabiti sono proprio vili... Oh, non posso sopportarlo! Sarebbe così gentile da cacciarli?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110102,'Taste the Mountains','So schmecken die Berge','Le goût de la montagne','Sabor de montaña','Un assaggio di montagna','','Deliver 10 Mountain Herbs','Liefere 10 Gebirgskräuter ab.','Livrer 10 herbes de montagne','Entrega 10 hierbas de montaña','Consegna 10 erbe di montagna','','Guild','0','LR',3,1,1,3,50,100,900,90,'None','–','Rien','Ninguno','Nessuno','','For today''s menu, Mountain Herbal Soup! Highly nourishing and purromotes good blood circulation. Now, it''s quite easy to make, but I do need lots of Mountain Herbs. Meow get moving!','Heute steht Gebirgskräuter- suppe auf dem Speiseplan! Sehr nahrhaft und gut für den Kreislauf. Das Rezept ist einfach, aber ich brau- che viele Gebirgskräuter. Miau, los geht''s!','Le plat du jour, c''est soupe d''herbes de montagne ! Elle est nutritive et fouette les sangs. Facile à préparer, elle nécessite beaucoup d''herbes de montagne. Mettez la patte à la pâte !','Para hoy tenemos sopa de hierbas de montaña. Es muy nutritiva y buena para la circulación. Es fácil de preparar, pero necesito muchas hierbas de montaña. ¡Miau, ponte en marcha!','Sul menu di oggi, zuppa alle erbe montane! Favorisce la circolazione del sangue ed è miaolto nutriente! Non è complicato prrrepararla, ma mi servono parecchie erbe di montagna. Prrrocuramele tu!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110103,'Lucky Mountain Cat','Berg-Glückskätzchen','Chat de montagne chanceux','Gato montés afortunado','Gattofortuna montano','','Earn 1500 Wycademy Points','1500 Wykademie-Punkte sammeln','Obtenir 1500 points Wycadémie','Gana 1.500 puntos Wycademia','Ottieni 1500 punti Wyccademia','','Guild','0','LR',3,1,1,104,50,200,1800,120,'None','–','Rien','Ninguno','Nessuno','','Ah, my good Prowler friend! It must be fate that brought us together again! This time, I want you to collect points in the Misty Peaks. Deliver whatever items you like. Oughta be a breeze for you, eh?','Ah, Pirscher, mein Freund! Das muss Schicksal sein. Diesmal sammle in den Nebelgipfeln Punkte für mich. Liefere als Objekte ab, was du willst. Das ist doch ein Kinderspiel für dich, oder?','Ah, mon bon Miaroudeur ! Le destin nous réunit encore ! Il faudrait cette fois que vous récupériez des points aux Pics brumeux. Livrez ce qui vous chante. Ça n''a rien de compliqué, si ?','¡Mi buen gatador! ¡Debe de ser el destino el que nos reúne! Me gustaría que obtuvieses puntos en las Picos Nublados. Entrega lo que quieras. Te resultará sencillo, ya verás.','Ah, il destino ci riunisce nuovamente, cacciamiao! Stavolta voglio che tu raccolga punti sulle cime nebbiose. Consegna pure gli oggetti che vuoi. Per te sarà una passeggiata, no?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110201,'Running with the Night','Durch die Nacht','Course de nuit','Correr con la noche','Corsa notturna','','Slay 15 Rhenoplos','Erlege 15 Rhenoplos.','Tuer 15 Rhenoplos','Abate a 15 Rhenoplos','Uccidi 15 Rhenoplos','','Guild','0','LR',1,1,2,101,50,200,1500,120,'None','–','Rien','Ninguno','Nessuno','','I''m studying the nighttime ecology of the Jurassic Frontier, but some Rhenoplos keep getting in my way. Can you help me? They like to charge foes, so try luring them into walls to stun them!','Ich studiere die nächtliche Ökologie des Jura-Grenzlands, aber einige Rhenoplos sind mir immer im Weg. Hilfst du mir? Sie greifen Feinde gerne an, also versuch, sie im Gemäuer zu überrumpeln!','J''étudie la vie nocturne de la Frontière jurassique, mais les Rhenoplos m''interrompent sans cesse. Pourriez-vous m''aider ? Ils aiment charger, alors attirez-les vers des murs pour les étourdir !','Estoy estudiando la ecología nocturna de la Fronda Arcaica, pero no paran de molestarme unos Rhenoplos. ¿Me ayudas? Como les gusta embestir, si los atraes hacia un muro los aturdirás.','Sto studiando l''ecosistema notturno della frontiera giurassica, ma dei Rhenoplos mi interrompono sempre. Puoi aiutarmi? Spesso caricano i nemici, ma se sbattono alle pareti... puoi stordirli!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110202,'A Cricket to Love','Eine Grille zum Verlieben','Amour de grillon','Un grillo que adorar','Un grillo per la testa','','Deliver 5 Silver Crickets','Liefere 5 Silbergrillen ab.','Livrer 5 grillons argentés','Entrega 5 grillos plateados','Consegna 5 grilli argentei','','Guild','0','LR',3,1,2,6,50,200,1200,120,'None','–','Rien','Ninguno','Nessuno','','Hi there, Prowler! Seen any Silver Crickets around here? No? Aww... I hear there are tons of them on the Deserted Island, but my parents won''t let me go on my own... Can you go and get some for me!?','Hey, du, Pirscher! Hast du hier Silbergrillen gesehen? Nein? Mh... Angeblich gibt''s viele auf der Ödnis-Insel, aber da lassen meine Eltern mich nicht hin. Kannst du mir von da welche holen?!','Bonjour, Miaroudeur ! Vous n''auriez pas vu des Grillons argentés ? Non ? Zut... Il paraît que l''Île déserte en regorge, mais mes parents m''interdisent d''y aller. Vous pourriez m''en rapporter ?','¡Hola! ¿Has visto por aquí grillos plateados? ¿No? Jo, he oído que hay un montón en la Isla Desierta, pero mis padres no me dejan ir solo... ¿Puedes traerme unos cuantos?','Ehi, cacciamiao! Hai visto dei grilli argentei qui in giro? No, vero? Uff... Sull''isola deserta ce ne sono a volontà, ma i miei non mi mandano da solo! Me ne prenderesti qualcuno tu?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110203,'A Resplendent Dance','Ein prächtiger Tanz','Une magnifique danse','Una danza refulgente','Una danza splendente','','Hunt a Great Maccao','Erjage einen Groß-Maccao.','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','Guild','0','LR',0,1,2,1,50,300,2100,160,'None','–','Rien','Ninguno','Nessuno','','We''ve secured the venue for our ball, but alas, I have yet to find my partner... Ah, you there! Would you join me for a dance? My, what large feathers yo-- a Great Maccao!? Help!','Den Ort für unseren Ball haben wir gefunden, aber leider fehlt mir noch ein Partner... Ah, du da! Tanzt du mit mir? Oh, was hast du nur für große Federn – ein Groß-Maccao?! Zu Hilfe!','Nous avons enfin trouvé une salle de bal, mais je n''ai toujours pas de partenaire. Vous, là ! Voudriez-vous danser avec moi ? Quelles belles plumes vous... Un Grand Maccao ? À l''aide !','Hemos reservado el sitio para el baile, pero aún no he encontrado pareja... ¡Eh, tú! ¿Quieres bailar conmigo? ¡Vaya, qué plumas más grandes tie...! ¡Un Gran Maccao! ¡Socorro!','Il luogo per il ballo è prenotato, ma ahimè, non ho ancora un compagno... Oh, proprio lei! Le andrebbe di unirsi a me per una danza? Ma che piume grandi che... Un Gran Maccao?! Aiuto!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110301,'Hot Scales & Sharp Pincers','Heiße Schuppen, scharfe Scheren','Pinces et écailles, aïe','Escamas y pinzas afiladas','Chele aguzze, scaglie calde','','Slay a total of 15 Ceanataurs or Uroktor','Erlege insgesamt 15 Ceanatauren oder Uroktor.','Tuer un total de 15 Ceanataur ou Uroktor','Abate a un total de 15 Ceanataur o Uroktor','Uccidi 15 Ceanataur o Uroktor in totale','','Guild','0','LR',1,1,3,8,50,300,3000,200,'None','–','Rien','Ninguno','Nessuno','','I took a request to take out some Ceanataurs, but once I got there, the place was full of Uroktor as well! I don''t really care which, but can you help me thin out their numbers a bit? Please! I need your help!','Es hieß, es ginge nur um Ceanatauren, aber hier sind genauso viele Uroktor. Mir ist egal, welche von den beiden, aber kannst du ein paar übernehmen? Ich brau- che wirklich deine Hilfe!','On m''a demandé d''éliminer des Ceanataur, mais l''endroit grouillait d''Uroktor ! Si vous pouviez en tuer quelques-uns, peu importe lesquels, vous me rendriez un fier service !','Acepté el encargo de matar algunos Ceanataur, pero al llegar allí, también había un montón de Uroktor. ¿Me ayudas a reducir su número? Los que prefieras, me da igual. Necesito ayuda.','Ho accettato di abbattere dei Ceanataur, ma una volta arrivata in zona ho trovato anche parecchi Uroktor! Potresti aiutarmi? Puoi eliminare chi vuoi, devi solo sfoltirne il numero!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110302,'There''s Always Room for Fish','Fisch geht immer','Comme une envie de poisson','Hueco para el pescado','Hasta el pesce siemprrre','','Deliver 4 Goldenfish','Liefere 4 Goldenfische ab.','Livrer 4 poissons d''or','Entrega 4 peces dorados','Consegna 4 pesci-dorati','','Guild','0','LR',3,1,3,7,50,300,3000,220,'None','–','Rien','Ninguno','Nessuno','','Oh... I''m so hungry I could eat a Plesioth! But I''d be satisfied with some smaller fish... Could you bring me some? Preferably Goldenfish! Their golden scales are so purrty, I bet they taste pawsitively delightful!','Oh... Ich könnte vor lauter Hunger einen Plesioth verspeisen! Kleinere Fische wären auch schon was. Holst du mir welche? Goldenfische mit ihren goldenen Schuppen schmecken sicher köstlich!','J''ai faim ! Je pourrais dévorer un Plesioth ! Mais des petits poissons d''or, avec leurs jolies écailles, feraient aussi l''affaire ! Vous m''en apporteriez ? J''en chalive d''avance !','¡Tengo tanta hambre que me comería un Plesioth! Pero me conformaría con peces pequeños. ¿Podrías traeme unos pocos peces dorados? ¡Sus escamas son preciosas! Seguro que son deliciosas.','Oh, che fame! Mi mangerei un Plesioth! In realtà anche qualche pesce più piccolino sarebbe sufficiente. Me ne porteresti qualcuno? Magari dei pesci-dorati! Le scaglie dorate sono prrregiate!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110303,'Out of the Frying Pan','Vom Regen in die Traufe','De mal en pis','Salido de la sartén','Dalla padella...','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','LR',5,1,3,9,50,400,3600,250,'None','–','Rien','Ninguno','Nessuno','','Aaand the challenger... A Prowler!? Cute! Daring! Not afraid of a Bulldrome, eh? How about going toe to toe with an Arzuros right after? No?','Uuund der Herausforderer... Ein Pirscher?! Süß! Tollkühn! Keine Angst vor einem Bulldrome, was? Wie wäre danach ein Kampf mit einem Arzuros? Nicht gut?','Et voici le challengeur... Un Miaroudeur ? Adorable ! Téméraire ! Alors, comme ça, on n''a pas peur d''un Bulldrome ? Ça vous dirait d''enchaîner avec un Arzuros ? Non ?','Y el aspirante... ¿¡Un gatador!? ¡Qué mono! ¡Qué valor! No te da miedo un Bulldrome, ¿eh? ¿Qué te parece vértelas mano a mano con un Arzuros justo después? ¿No?','E lo sfidante è... Cosa? Un cacciamiao!? Che dolce! Che audace! Non hai paura di un Bulldrome, vero? E che ne diresti di un incontro ravvicinato con un Arzuros, subito dopo? No?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110401,'The Grandmeowster''s Trials','Prüfungen vom Großmiauster','Défis du Grand-miaonstre','Pruebas del Granmiauestro','Le prove del granmiaestro','','Slay 15 Konchu','Erlege 15 Konchu.','Tuer 15 Konchu','Abate a 15 Konchu','Uccidi 15 Konchu','','Guild','0','HR',1,1,4,11,50,200,1500,120,'None','–','Rien','Ninguno','Nessuno','','As Meowster of Moves, the time has come for me to give you the ultimate test. Muster all your strength! Show no weakness! Now go, and do me purroud!','Als Miauster der Aktionen stelle ich dir hiermit die ultimative Prüfung. Sammle all deine Kraft! Zeige keine Schwäche! Jetzt schnurr ab und mach mich stolz!','En tant que miaonstre des habiletés, le temps est venu pour moi de vous soumettre au test ultime. Dépassez-vous ! Ne faiblissez pas ! Rendez-moi fière de vous !','Como miauestro de movimientos, ha llegado la hora de hacerte la prueba definitiva. Reúne todas tus fuerzas. No muestres debilidad. ¡Haz que me sienta orgulloso!','In qualità di miaestro dei movimenti, è giunta l''ora di sottoporti alla prrrova definitiva. Raccogli le forze! Non mostrare alcuna debolezza! E ora vai, non miaollare mai!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110402,'We Cat All Be Heroes','Jeder Katz ein Held sein!','Héros à moustaches','Todos podemos ser héroes','Gatti, non parole!','','Hunt a Seltas','Erjage einen Seltas.','Chasser 1 Seltas','Caza un Seltas','Caccia un Seltas','','Guild','0','HR',0,1,4,11,50,300,3000,200,'None','–','Rien','Ninguno','Nessuno','','To a Palico, there''s nothing cooler than a Seltas. Just seeing one gives me the chills! You think you''re tougher than a Seltas? Purr-lease! If you really think so, go purrove it!','Für einen Kameraden ist ein Seltas das absolut Coolste. Der Anblick genügt schon. Du denkst, du bist einem Seltas überlegen? Miau, Mann – dann beweis es gefälligst auch!','Il n''y a rien de plus génial pour un Palico qu''un Seltas. J''en ai la queue qui frétille rien que d''y penser ! Vous vous croyez plus coriace qu''un Seltas ? Prouvez-le donc !','Para un camarada, no hay nada mejor que un Seltas. Solo de ver uno me dan escalofríos. ¿Así que te crees que eres más duro que un Seltas? ¡Anda ya! Pues demuéstramelo.','Per un compagno, niente è più gattesco di un Seltas. Mi vengono i brividi solo a pensarci! Tu credi di essere più forte di un Seltas? Ah! Prrrego, allora! Dimostralo! Voglio prrroprio vederti!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110403,'Fois Gras Faux Pas','Fois-Gras-Fauxpas','Gastronomiaou','Foie gras, paso en falso','Gaffe di fois gras','','Deliver 12 Piscine Livers','Liefere 12 Pisces-Lebern ab.','Livrer 12 foies de wyverne des mers','Entrega 12 hígados de nadador','Consegna 12 fegati di Piscine','','Guild','0','HR',3,1,4,5,50,400,3600,150,'None','–','Rien','Ninguno','Nessuno','','You can''t cook a meal without the proper ingredients! And the ones we need today come from sand monsters... What are we making with the ingredients? No idea! I''ll figure that out once you get them!','Du kannst ein Gericht nur mit den richtigen Zutaten kochen! Für uns kommen die heute von Sandmonstern... Was wir daraus machen? Gute Frage! Ich überleg''s mir, wenn du die Zutaten hast.','Impossible de préparer un plat sans ingrédients dignes de ce nom ! Et aujourd''hui, il m''en faut un provenant de monstres des sables. Ce que je vais cuisiner ? J''improviserai quand vous me l''apporterez !','No se puede cocinar sin los ingredientes apropiados. Y el que necesitamos proviene de monstruos de las arenas. ¿Qué vamos a preparar con los ingredientes? No sé, ya veremos cuando los traigas.','Non si può cucinare senza i giusti ingredienti! E quelli che ci servono oggi vengono dai mostri delle sabbie. Cosa prepareremo con gli ingredienti? Boh! Questo lo saprò quando me li porterai!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110404,'Splash in the Moonlight','Spritzer im Mondlicht','Baignade au clair de lune','Baño a la luz de la luna','Tuffo al chiaro di luna','','Slay 15 Ludroth','Erlege 15 Ludrothe.','Tuer 15 Ludroth','Abate a 15 Ludroth','Uccidi 15 Ludroth','','Guild','0','HR',1,1,4,4,50,400,3600,120,'None','–','Rien','Ninguno','Nessuno','','Ahh, perfect weather for a romantic evening with my beloved, just the two of... Huh!? Where did these Ludroth come from!? Don''t worry darling, I''ll bring back help!','Hach, perfektes Wetter für einen romantischen Abend zu zweit nur mit meinem Liebli... Was?! Wo kommen denn diese Ludrothe her?! Keine Angst, Schatz, ich hole Hilfe!','Ah, il fait un temps idéal pour une soirée romantique en tête-à-tête avec ma douce... Mais ? D''où sortent ces Ludroth ? Ne t''en fais pas, ma chérie, je vais trouver de l''aide !','Ah, un tiempo perfecto para una velada romántica con mi amada, los dos solos... ¿Eh? ¿De dónde han salido esos Ludroth? No te preocupes, cariño, voy a buscar ayuda y vuelvo.','Ahhh, che serata perfetta per un''uscita romantica con la mia adorata. Finalmente saremo soli, e... Uh?! E questi Ludroth da dove sbucano?! Non temere, amore, corro a chiamare aiuto!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110501,'Calm, Cool, and Collected','Ruhig, gelassen und gefasst','Calme et détendu','Calma, frialdad y control','Calma e sangue freddo','','Slay 15 Zamite','15 Zamiten erlegen','Tuer 15 Zamite','Abate a 15 Zamite','Uccidi 15 Zamite','','Guild','0','HR',1,1,5,14,50,500,4500,180,'None','–','Rien','Ninguno','Nessuno','','Purrhaps they don''t scare hunters much, but we find them apawling! There''re bigger beasts out there, but the sight of them pouncing on poor little Felynes is just pawful! Please, get rid of them for us!','Vielleicht lassen sie Jäger recht kalt, aber wir finden sie schnurrhaarsträubend! Nicht besonders groß, aber wenn kleine Felynen ihnen in die Pfoten fallen... Werde sie bitte für uns los!','Les chasseurs n''en ont peut-être pas peur, mais nous, ils nous hérissent le poil ! C''est terrible de les voir s''attaquer à de pauvres Felynes ! Je vous en supplie, éliminez-les !','Quizás no os asusten a los cazadores, pero nos parecen temibles. Hay bestias más grandes ahí fuera, pero verlos abalanzarse sobre los pobres Felyne es horrible. Líbranos de ellos.','I cacciatori forse non li temono, ma perrr noi sono un incubo! È vero, esistono bestie molto più grosse, ma vederli piombare sui poveri Felyne è straziante! Puoi toglierceli dalle zampe?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110502,'Kecha Wacha Earbreaker','Kecha-Wacha-Ohrbrecher','Brise-oreille Kecha Wacha','Rompeorejas Kecha Wacha','Spaccaorecchie Kecha Wacha','','Wound Kecha Wacha''s ears','Verletze die Ohren des Kecha Wacha.','Blesser les oreilles du Kecha Wacha','Hiere Kecha Wacha en las orejas','Ferisci le orecchie del Kecha Wacha','','Guild','0','HR',0,1,5,11,50,300,2400,200,'None','–','Rien','Ninguno','Nessuno','','Sure, you''re decent at targeting body parts, but you could still learn a thing or two from "the Partbreaker"! For practice, try smashing some Kecha Wacha ears! ''Course, you can''t reach ''em while it''s hanging on vines!','Du zielst wirklich recht gut auf Körperteile, aber kannst vom "Teilebrecher" noch viel lernen. Zertrümmere zur Übung ein paar Kecha- Wacha-Ohren! Tipp: An den Ranken ist er zu weit weg.','OK, tu sais viser, mais tu n''arrives pas à la cheville du destructeur ! Entraîne-toi à détruire des oreilles de Kecha Wacha ! N''oublie pas que tu ne peux pas les avoir s''ils s''accrochent aux lianes !','Sí, las extremidades no se te dan mal, pero aún puedes aprender algunas cosas del "Destructor". Para practicar, intenta chafar las orejas del Kecha Wacha. No llegarás si están en la enredadera.','Ok, hai una mira niente male, ma lo "spaccamostri" qualcosina può insegnartelo! Se vuoi allenarti, sfonda qualche orecchio di Kecha Wacha! Ricorda, non lo tangi quando sta sui rampicanti!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110503,'Fish On!','Fisch schön weiter!','La pêche au gros','¡Buena pesca!','Che lenza!','','Deliver 3 Silverfish','Liefere 3 Silberfische ab.','Livrer 3 poissons d''argent','Entrega 3 peces de plata','Consegna 3 pesci-argento','','Guild','0','HR',3,1,5,6,50,500,4500,200,'None','–','Rien','Ninguno','Nessuno','','Whoa! I finally caught the big ones -- two whole Silverfish! Hm? Why ya looking at me like that? You may like fish, but you''ll never beat my record. Let''s see if you can catch three of them at the Deserted Island!','Ja! Endlich ein großer Fang! Zwei ganze Silberfische... Hm? Warum guckst du so? Du kannst Fisch noch so mögen, meinen Rekord erreichst du doch nie! Mal sehen, ob du 3 an der Ödnnis-Insel fängst!','Ah, ça mord enfin ! Deux gros poissons d''argent ! Pourquoi vous me regardez comme ça ? Vous pensez pouvoir faire mieux ? On va bien voir ! Allez donc en pêcher 3 sur l''Île déserte !','¡Vaya! Al final he pescado peces gordos. Dos peces de plata enteros. ¿Por qué me miras así? Nunca podrás superar mi récord. A ver si puedes coger tres en la Isla Desierta.','Wow, finalmente ho preso i pesci grossi! Di preciso, due pesci-argento! Perché quella faccia? Vuoi battere il mio record? D''accordo, vediamo se riesci a pescarne tre sull''isola deserta!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110504,'Lucky Volcano Cat','Vulkan-Glückskätzchen','Chat du Volcan chanceux','Gato volcán afortunado','Gattofortuna vulcanico','','Earn 1800 Wycademy Points','Sammle 1800 Wykademie-Punkte.','Obtenir 1800 points Wycadémie','Gana 1.800 puntos Wycademia','Ottieni 1800 punti Wyccademia','','Guild','0','HR',3,1,5,12,50,400,3900,250,'None','–','Rien','Ninguno','Nessuno','','Ah, my favorite Prowler! Fate brings us together once more! I want to ask you to collect points at the Volcanic Hollow. Deliver whatever items you like. Oughta be a breeze for you, eh?','Ah, mein liebster Pirscher! Das muss Schicksal sein! Sammle für mich Punkte an der Vulkanmulde. Liefere als Objekte ab, was du willst. Das ist doch ein Kinderspiel für dich, oder?','Comme on se retrouve ! Si ce n''est pas mon Miaroudeur préféré ! Je voudrais que vous récupériez des points au Vallon volcanique. Livrez les objets de votre choix. Un vrai jeu d''enfant, non ?','¡Mi gatador favorito! ¡El destino nos vuelve a unir! Te quería pedir que obtengas puntos para mí en el Hoyo Volcán. Entrega lo que quieras. Seguro que te resulta sencillo.','Ah, il destino ci riunisce nuovamente, cacciamiao! Vorrei chiederti di raccogliere punti alla fossa vulcanica. Consegna pure gli oggetti che vuoi. Per te sarà una passeggiata, no?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110505,'Under the Blazing Sun','Unter sengender Sonne','Sous le soleil de l''été','Bajo un sol de justicia','Sotto un sole cocente','','Hunt a Gendrome','Erjage einen Gendrome.','Chasser 1 Gendrome','Caza un Gendrome','Caccia un Gendrome','','Guild','0','HR',0,1,5,5,50,400,3600,230,'None','–','Rien','Ninguno','Nessuno','','The Dunes are home to Felynes bold, yet proud and fierce, if truth be told. Aiding them with this hunt, I expect, will be more than enough to earn their respect!','Ich habe supersüße kleine Schätzchen in den Dünen gefunden. Aber es gibt dort viele Monster. Mit deiner Hilfe könnte ich noch mehr Lieblinge ausfindig machen!','J''ai trouvé d''adorables petites créatures dans les dunes, mais il y a tant de monstres qui y rôdent... Si vous m''aidiez, je pourrais dénicher d''autres de ces petits amours !','He encontrado preciosidades monísimas en las Dunas, pero al parecer hay muchos monstruos rondando por allí. Si me echas una mano, podré buscar aún más pequeñas maravillas encantadoras.','Nel mezzo delle dune ho trovato degli amorini carini carini, ma da quelle parti girano troppi mostri, sigh! Sai, se mi dessi una zampa potrei andare a scovare altri tesorini tenerissimi!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110601,'Everywhere You Look','Wohin du auch siehst','La grande invasion','Por todas partes','Fin dove arriva lo sguardo','','Hunt 2 Iodrome','Erjage 2 Iodrome.','Chasser 2 Iodrome','Caza 2 Iodrome','Caccia 2 Iodrome','','Guild','0','HR',0,1,6,13,50,600,6000,350,'None','–','Rien','Ninguno','Nessuno','','Yikes! What are those things!? Twins? I thought I was seeing double! Even if you can catch them on their own, they''ll call the little ones and purrolong the fight. At any rate, don''t get in over your whiskers!','Huch! Was ist das denn?! Zwillinge? Ich dachte erst, ich sehe doppelt! Auf sich allein gestellt rufen sie die Kleinen, der Kampf wird zäh! Lass dir nicht auf die Schnurrhaare treten!','Mais qu''est-ce que c''est que chat ? Des jumeaux ? J''ai cru voir double ! Si on en chasse un, il crie au secours et d''autres arrivent en renfort. Attention à ne pas vous faire submerger !','¡Ostras! ¿¡Qué es eso!? ¿Gemelos? Creía que veía doble. Aunque los pilles solos, llaman a los pequeños y alargan el combate. Así que ten cuidado con dónde metes los bigotes.','Miargh! Cosa sono quelli?! Gemelli? Credevo di vederci doppio! Anche se li prrrendi da soli, richiameranno i piccoli, prrrolungando lo scontro. Vedi di non fare il passo più lungo della zampa!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110602,'Malfestio Headbreaker','Malfestio-Kopfbrecher','Briseur de crâne Malfestio','Partecabezas de Malfestio','Spaccateste Malfestio','','Wound Malfestio''s head','Malfestio-Kopf verletzen','Blesser la tête du Malfestio','Hiere Malfestio en la cabeza','Ferisci la testa del Malfestio','','Guild','0','HR',0,1,6,6,50,300,3000,240,'None','–','Rien','Ninguno','Nessuno','','Hey, brah! Smashin'' tons of parts lately? See those scales everywhere? Guess no one told Malfestio not to litter. So smash it in the head ''til it gets the message! If you can''t get close, use your ''rang, brah!','Hey, Kumpel! Viele Teile gebrochen in letzter Zeit? Malfestio hat wohl einfach überall Schuppen verteilt. Schmetter ihm die Botschaft an den Kopf, bis sie ankommt. Zur Not mit dem Bumerang!','Tu t''éclates ? Vise un peu toutes ces écailles ! Ce Malfestio est d''un sans- gêne ! Quelques coups sur le crâne lui remettront les idées en place. Utilise ton boomerang si tu es trop loin !','¿Qué, has partido muchas extremidades últimamente? No le han dicho al Malfestio que no se debe ensuciar. Házselo entender a golpes en la cabeza. Si no puedes acercarte, usa el bumerán.','Bella, oh! Com''è, si spacca di brutto? Ma le vedi tutte queste scaglie? Mi sa che va spiegato al Malfestio di non sporcare. Ficcaglielo in testa! E se non riesci ad avvicinarti, usa il ''rang!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110603,'The Snow Must Go On','Die Schneeschmelze','Tombe la neige','Que nieva, que nieva','La lepre e la neve','','Hunt a Lagombi','Erjage einen Lagombi.','Chasser 1 Lagombi','Caza un Lagombi','Caccia un Lagombi','','Guild','0','HR',0,1,6,14,50,500,4500,380,'None','–','Rien','Ninguno','Nessuno','','I went to the Frozen Seaway in search of some flowers, but when I found them, a giant bunny kept tossing snowballs at me! The flowers are too delicate for such violence! Put a stop to it!','Ich habe am Frostseeweg ein paar Blumen gesucht, aber dann hat ein riesiger Hase Schneebälle nach mir gewor- fen! Für solche Grobheiten sind die Blümchen viel zu zart – Schluss damit!','Je suis allée cueillir des fleurs à la Mer de glace et un lapin géant m''a lancé des boules de neige ! Ces fleurs sont trop fragiles pour ce genre de jeu. Empêchez-le de recommencer !','Fui al Canal Helado a buscar flores, pero cuando las encontré, un conejo gigante se puso a lanzarme bolas de nieve. Las flores no aguantan tanta violencia. Haz que pare.','Ero al canale ghiacciato a cercare dei fiori, ma quando li ho trovati un leprotto gigante mi ha presa a palle di neve! I fiori sono troppo delicati per questa sciocca violenza! Devi fermarlo!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110604,'Big Bro''s Benevolence','Gunst des großen Bruders','La B.A. du grand frère','Benevol. del gran hermano','Il buon fratellone','','Deliver 2 Meteor Chunks','Liefere 2 Meteoriten-Brocken ab.','Livrer 2 morceaux de météore','Entrega 2 frag. de meteorito','Consegna 2 blocchi meteoritici','','Guild','0','HR',3,1,6,5,50,400,3900,300,'None','–','Rien','Ninguno','Nessuno','','Hey, just the Prowler I need! That egg you got me did the trick! My adorable lil'' sis... she''s all better! Now that she''s Felyne fine, I wanna give her rare stones as a purresent. Only the best for my sis!','Hey, Pirscher, warte mal! Das Ei von dir war Gold wert. Meiner lieben kleinen Schwester geht''s wieder gut. Ich will ihr jetzt seltene Steine schenken. Für sie nur das Beste!','Ah, voilà le Miaroudeur qu''il me faut ! Votre œuf a remis ma petite sœur sur pieds ! Et je voudrais lui offrir des pierres rares à présent qu''elle va mieux. Elle le mérite bien !','¡El gatador que buscaba! El huevo que me diste funcionó. Mi hermanita está feliz como una perdiz, así que quiero regalarle unas gemas raras. Para mi hermana, solo lo mejor.','Toh! Prrroprio tu! Sai che il tuo uovo ha funzionato? La mia dolce sorellina... si è riprrresa! E ora che sta felino, vorrei regalarle delle pietre prrreziose. Per lei, solo il meglio!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110701,'The Ball''s in Your Court','Du bist am Ball','La balle dans votre camp','La pelota está en tu tejado','A te la palla','','Hunt a Volvidon','Erjage einen Volvidon.','Chasser 1 Volvidon','Caza un Volvidon','Caccia un Volvidon','','Guild','0','HR',0,1,7,101,50,600,5400,420,'None','–','Rien','Ninguno','Nessuno','','Hey, Prowler! I''ve got the perfect job for you. All you''ve got to do is play with this ball. It''s not yarn, but I promise you it''s just as fun! I''ve made all the preparations. Now, go enjoy yourself!','Hey, Pirscher! Ich hab genau das Richtige für dich. Du musst nur mit diesem Ball spielen. Der macht so viel Spaß wie ein Wollknäuel. Ich hab schon alles vor- bereitet. Viel Spaß damit!','J''ai un travail parfait pour un Miaroudeur. Il suffit de jouer avec cette balle. Ce n''est pas une pelote, mais c''est tout aussi amusant ! Tout est prêt, il n''y a plus qu''à !','¡Eh, gatador! Tengo un trabajo ideal para ti. Solo tienes que jugar con esta bola. Sin lucha, pero te aseguro que es divertido. Lo tengo todo listo. ¡Que te diviertas!','Ehi, cacciamiao! Ho qui un lavoretto perfetto per te. Devi solo giocare con questa palla. Non sarà un gomitolo, ma ti assicuro che lo spasso è identico! Vai, è già tutto pronto. E buon divertimento!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110702,'Uragaan Shellbreaker','Uragaan-Panzerbrecher','Brise-carapace Uragaan','Rompecaparazones Uragaan','Spaccacorazze Uragaan','','Wound Uragaan''s jaw','Verletze den Kiefer des Uragaan.','Briser la mâchoire de l''Uragaan','Hiere Uragaan en las fauces','Ferisci la mascella dell''Uragaan','','Guild','0','HR',0,1,7,12,50,400,3600,300,'None','–','Rien','Ninguno','Nessuno','','Hey, brah! Smashin'' tons of parts lately? You must be itchin'' for a real challenge, so try rockin'' a rock-solid Uragaan head! Tough even for pro bros like me, but try bustin'' out some bombs!','Hey, Kumpel! Viele Teile gebrochen in letzter Zeit? Ein steinharter Uragaan- Kopf ist eine Herausfor- derung – selbst für einen Profi wie mich schwierig! Bomben hochjagen erlaubt!','Salut ! Ça boume ? Si t''as envie d''un défi, va donc exploser un crâne d''Uragaan ! Pas fastoche même pour les pros comme moi, mais avec tes bombes, tu devrais t''en tirer !','¿Qué, has partido muchas extremidades últimamente? ¿A que te apetece un reto de verdad? Intenta abrirle la cabeza a un Uragaan. Es complicado, pero prueba a lanzarle unas bombas.','Bella, oh! Com''è, si spacca di brutto? Scommetto che sei in cerca di una sfida, eh! Allora spaccami una testa di Uragaan! È tosta anche per i pro come me, ma magari con qualche bomba puoi farcela!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110703,'Poison, Poison, Go Away','Gift lass nach','Averse de poison','Sigue tu camino, veneno','Veleno variabile','','Slay 20 Remobras','Erlege 20 Remobras.','Tuer 20 Remobra','Abate a 20 Remobra','Uccidi 20 Remobra','','Guild','0','HR',1,1,7,14,50,500,4200,300,'None','–','Rien','Ninguno','Nessuno','','And now for the weather in the Frozen Seaway. The fair skies take a turn for the worse as Remobras cloud the skies and bring a chance of poison showers. Don''t forget your umbrella!','Und jetzt zum Wetter am Frostseeweg. Es zieht sich zu und wird düster, da Remobras den Himmel verdunkeln. Dabei können Giftschauer auftreten. Vergiss deinen Schirm nicht!','Passons à la météo de la Mer de glace. Le temps se couvre avec l''arrivée du nuage de Remobra et des ondées empoisonnées sont à prévoir. N''oubliez pas vos parapluies !','A continuación, el parte del Canal Helado. Los cielos despejados se cubrirán con una nube de Remobra, con posibilidad de lluvia de veneno. No te olvides el paraguas.','E ora le previsioni per il canale ghiacciato. Brusco peggioramento all''arrivo di un''ondata di Remobra, con possibili rovesci velenosi sparsi su tutta la zona. Non dimenticate l''ombrello!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (110704,'Meownster Hunter','Miaunster-Jäger','Chasseur de miaonstres','Cazamiaunstruo','Cacciatori di miaostri','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Guild','0','HR',5,1,7,9,50,800,7200,500,'None','–','Rien','Ninguno','Nessuno','','The time has come to test your skills. Remember what I have taught you, commit your body and soul to this challenge, and overcome your limits. Now go, and do me purr-oud!','Es ist Zeit, deine Fähig- keiten zu testen. Denk daran, was ich dich gelehrt habe. Sei mit Leib und Seele bei der Sache und wachs über dich hinaus. Schnurr ab und mach mich stolz!','Le moment est venu de tester vos compétences. Souvenez-vous de mes enseignements, mettez-y votre corps et votre âme, et dépassez vos limites. Faites des étincelles !','Ha llegado la hora de poner a prueba tus habilidades. Recuerda lo que te he enseñado, concéntrate en este reto y desafía tus límites. ¡Haz que me sienta orgulloso!','È giunta l''ora di mettere alla prrrova le tue abilità. Ricorda i miei insegnamenti, concentrati sulla sfida con la mente e con il corpo, e supera i tuoi limiti. Ora va'', e non miaollare mai!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (120001,'Grudge Match: Great Maccao','Zweikampf: Groß-Maccao','Revanche : Grand Maccao','Arena: Gran Maccao','Arena: Gran Maccao','','Slay a Great Maccao','Erlege einen Groß-Maccao.','Tuer 1 Grand Maccao','Abate un Gran Maccao','Uccidi un Gran Maccao','','Arena','0','LR',1,1,2,9,50,0,900,0,'None','–','Rien','Ninguno','Nessuno','','To think, lil'' Greenhorn taking on a Great Maccao! I''m moved... Just like YOU should do when a Great Maccao tries to jump-kick you in the face! Support: Fighting, Protection, Healing, Bombing, Gathering','Ein Grünschnabel gegen einen Groß-Maccao? Rührend. Und rühren solltest du dich, wenn der Groß-Maccao zum Sprung ansetzt, um dir den Kopf abzureißen! Unterstützung: Kämpfen, Schutz, Heilen, Bombardieren, Sammeln','Dire que vous allez vous mesurer à un Grand Maccao, vous, Blanc-bec ! Restez bien mobile, surtout ! Classe : Soldat, Soigneur, Protecteur, Bombardeur, Chapardeur','Y pensar que este novato se va a enfrentar a un Gran Maccao... Conmovedor. Y moverte es lo que deberías hacer cuando vaya a por ti. Apoyo: combate, protección, curación, bombardero, recogida.','L''incapace contro il Gran Maccao! Sono colpito... Come lo sarai tu se non eviterai i suoi calci volanti! Supporto: Combattimento, Protezione, Cura, Bombardiere, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (120002,'Grudge Match: Gypceros','Zweikampf: Gypceros','Revanche : Gypceros','Arena: Gypceros','Arena: Gypceros','','Slay a Gypceros','Erlege einen Gypceros.','Tuer 1 Gypceros','Abate un Gypceros','Uccidi un Gypceros','','Arena','0','LR',1,1,3,9,50,0,900,0,'None','–','Rien','Ninguno','Nessuno','','This time, you''re up against a Gypceros! Be careful: They''re clever, and will sometimes play dead. Don''t end up as monster chow! That''s an order! Support: Charisma, Fighting, Protection, Healing, Gathering','Diesmal geht es gegen einen Gypceros! Sei vorsichtig: Sie sind schlau und stellen sich manchmal tot. Ende nicht als Monsterhappen. Unter- stützung: Charisma, Kämpfen, Schutz, Heilen, Sammeln','Vous allez combattre un Gypceros. Ils sont futés. Parfois, ils font le mort. Ne vous faites pas croquer. Classe : Leader, Soldat, Protecteur, Soigneur, Chapardeur','Esta vez te enfrentarás a un Gypceros. Ten cuidado: son listos y a veces se hacen los muertos. ¡No acabes en su barriga! ¡Es una orden! Apoyo: carisma, combate, protección, curación, recogida.','Stavolta dovrai affrontare un Gypceros! Ricorda, a volte si fingono morti. E non farti mangiare, è un ordine! Supporto: Comando, Cura, Combattimento, Protezione, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (120003,'Grudge Match: Bird Wyverns','Zweikampf: Vogelwyvern','Revanche : Wyverne rapace','Arena: wyverns pájaro','Arena: Wyvern rapaci','','Slay a Gendrome and an Iodrome','Erlege einen Gendrome und einen Iodrome.','Tuer 1 Gendrome Tuer 1 Iodrome','Abate un Gendrome y a un Iodrome','Uccidi un Gendrome e uno Iodrome','','Arena','0','HR',1,1,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','All right, Greenhorn! You''re up against not one, but TWO Bird Wyverns that use Poison and Paralysis! Shaking in your boots? If so, bring a friend! Support: Charisma, Fighting, Healing, Bombing, Gathering','Also, Grünschnabel. Diesmal geht''s gleich gegen ZWEI Wyvern, die Gift und Lähmung verwenden. Zitterst du schon? Falls ja, dann bring einen Freund mit! Unterstützung: Charisma, Kämpfen, Heilen, Bombardieren, Sammeln','Vous allez affronter non pas une, mais deux wyvernes rapaces avec Poison et Paralysie. Faites appel à un allié ! Classe : Leader, Soldat, Soigneur, Bombardeur, Chapardeur','Vale, novato. Te toca contra no uno, sino dos wyverns pájaro que usan veneno y parálisis. ¿Asustado? ¡Pues tráete a un amigo! Apoyo: carisma, combate, curación, bombardero, recogida.','Incapace! Dovrai affrontare due wyvern rapaci in grado di avvelenare a paralizzare! Hai paura? Porta un alleato con te! Supporto: Comando, Combattimento, Cura, Bombardiere, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130301,'Prowler Training: The Basics','Pirscherausbildung: Grundlagen','Leçon 1 : Bases du Miaroudeur','Práctica gatador: básico','Add. cacciamiao: Fondamentali','','Deliver 1 piece of Raw Meat','Liefere 1 Stück Rohes Fleisch ab.','Livrer 1 morceau de viande crue','Entrega 1 ud. de carne cruda','Consegna un pezzo di carne cruda','','Village','0','LR',3,1,0,1,50,0,150,0,'None','–','Rien','Ninguno','Nessuno','','Now that you understand the basics, Prowler, it''s time to prowl! For starters, take down a Larinoth, carve some Raw Meat from it, then deliver it to base camp! It might sound easy, but don''t get careless!','Da du nun die Grundlagen ver- standen hast, kannst du nun jagen gehen. Kannst du einen Larinoth erlegen, Rohes Fleisch von ihm erbeuten und es ins Basislager bringen? Klingt einfach, ist es aber nicht!','Les bases sont acquises, il est temps de partir en miaroude ! D''abord, tuez un Larinoth et découpez-en un morceau de viande crue pour le camp de base. Attention quand même !','Ahora que entiendes las nociones básicas, gatador, es hora de gatar. Empieza por un Larinoth, corta carne cruda y entrégala en el campamento base. ¡Suena fácil, pero ten cuidado!','Ora che conosci le basi, è il momento di applicarle! Per iniziare, abbatti un Larinoth e raccogline la carne cruda, quindi consegnala al campo base! Può sembrare una passeggiata, ma non lo è!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130302,'Prowler Training: Combining','Pirscherausbildung: Kombinieren','Leçon 2 : Création','Práctica gatador: combinar','Add. cacciamiao: Combinazioni','','Deliver 1 Mega Potion','Liefere 1 Megatrank ab.','Livrer 1 méga potion','Entrega 1 megapoción','Consegna una mega-pozione','','Village','0','LR',3,1,0,1,50,0,150,0,'None','–','Rien','Ninguno','Nessuno','','Hope you''re feeling creative! I want you to collect some items and then combine them to make something new! It''s not just fun, but an important skill you''ll need to survive in the field!','Ich hoffe, du bist in kreativer Stimmung! Ich möchte, dass du etwas sammelst und es zu etwas Neuem kombinierst! Das macht Spaß und ist eine wichtige Fähigkeit, die dir beim Überleben dort draußen helfen wird!','J''espère que vous êtes d''humeur créative ! Allez donc me chercher des objets et combinez-les pour créer quelque chose de nouveau ! C''est un talent dont vous aurez besoin pour survivre !','¡Espero que te sientas creativo! Quiero que cojas varios objetos y los combines en algo nuevo. Es divertido y, además, es una habilidad muy necesaria.','Ok, spero tu abbia molta fantasia! Voglio che tu raccolga alcuni materiali e li combini per creare qualcosa di nuovo! Non è solo un esercizio divertente, ma è anche utile per sopravvivere!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130303,'Prowler Training: Gathering','Pirscherausbildung: Sammeln','Leçon 3 : Collecte','Práctica gatador: recogida','Add. cacciamiao: Raccolta','','Deliver 1 Deodorant','Liefere 1 Deodorant ab.','Livrer 1 déodorant','Entrega 1 desodorante','Consegna un deodorante','','Village','0','LR',3,1,0,1,50,0,150,0,'None','–','Rien','Ninguno','Nessuno','','Hey, Prowler! Do your favorite instructor a solid -- gather some materials to combine into some Deodorant for me! You''ll learn a valuable lesson...and my BO won''t knock out my date!','Hey, Gefährte! Tu mir einen Gefallen und sammle Materialien, die zu einem Deodorant kombi- niert werden können. Dabei kannst du etwas Wichtiges lernen. Und mein Date wird nicht in Ohnmacht fallen wegen meines Geruchs.','Miaroudeur ! Rendez donc service à votre instructeur préféré. Rassemblez des composants pour fabriquer un déodorant ! Ça vous fera une bonne leçon. Et je ne me prendrai pas un râteau !','Eh, gatador, haz un favor a tu instructor: ¡consígue materiales para hacerme un desodorante! Aprenderás una valiosa lección... Y yo no apestaré tanto.','Ehi, cacciamiao! Vuoi fare un favore al tuo insegnante preferito? Raccogli alcuni materiali e combinali per creare un deodorante! Ne Ricaverai un''utile lezione... e sollievo per il tuo naso!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130304,'Prowler Training: Fishing','Pirscherausbildung: Angeln','Leçon 4 : Pêche','Práctica gatador: pesca','Add. cacciamiao: Pesca','','Deliver 1 Glutton Tuna','Liefere 1 Schlemmthunfisch ab.','Livrer 1 thon glouton','Entrega 1 atún glotón','Consegna un tonno-ghiottone','','Village','0','LR',3,1,0,1,50,0,150,0,'Deliver 1 Sushifish','1 Sushifisch abliefern','Livrer 1 sushipoisson','Entrega 1 pez sushi','Consegna un pesce-sushi','','Okay, Prowler! The next step of your training is to fish up and deliver a Glutton Tuna! But don''t you worry your whiskers -- the Master of Style will be there to teach you everything you need to know about fishing!','Okay, Pirscher! Der nächste Schritt deines Trainings ist das Angeln und Abliefern eines Schlemmthunfischs! Der Jagdstil-Meister wird bei dir sein und dir alles Nötige über das Angeln beibringen!','OK, Miaroudeur ! Prochaine étape : pêcher un thon glouton et le ramener ! Mais ne vous en faites pas, je suis là pour vous apprendre tout ce que vous avez besoin de savoir.','Muy bien, gatador. El próximo paso es pescar y entregar un atún glotón. Tú tranquilo, el gran maestro está aquí para enseñarte todo lo que hay que saber sobre la pesca.','Ok, cacciamiao! Il prossimo passo del tuo addestramento è pescare un tonno-ghiottone e consegnarlo! Ma non strapparti i baffi, il maestro di stile è qui per insegnarti tutto sulla pesca!','',50,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130305,'Prowler Training: Transporting','Pirscherausbildung: Transport','Leçon 5 : Transport','Práctica gatador: transporte','Add. cacciamiao: Trasporto','','Deliver 1 Carnivore Egg','Liefere 1 Fleischfresser-Ei ab.','Livrer 1 œuf de carnivore','Entrega 1 huevo de carnívoro','Consegna un uovo di carnivoro','','Village','0','LR',3,1,0,1,50,0,150,0,'None','–','Rien','Ninguno','Nessuno','','Hey, Prowler! Do you know how to transport Carnivore Eggs without breaking them? You will after this! Remember, they''re delicate. So take out any Maccao before snatching any eggs!','Hey, Pirscher! Weißt du, wie man Fleischfresser-Eier transpor- tiert, ohne sie zu zerbrechen? Gleich wirst du es wissen! Denk daran, sie sind empfindlich! Mach also alle Maccaos unschädlich, ehe du dir die Eier schnappst!','Hé, Miaroudeur ! Vous savez comment transporter des œufs carnivores sans les casser ? Après ceci, vous le saurez ! Ils sont fragiles. Tuez les Maccao avant de vous emparer des œufs !','Oye, gatador, ¿sabes cómo transportar huevos de carnívoro sin romperlos? Son delicados, así que cárgate a todos los Maccao antes de pillar los huevos.','Ehi, cacciamiao! Sai come trasportare uova di carnivoro senza romperle? Dopo il mio addestramento sarà uno scherzo! Sono molto delicate, quindi liberati di tutti i Maccao prima di raccoglierle!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130306,'Prowler Training: Capturing','Pirscherausbildung: Fangen','Leçon 7 : Capture','Práctica gatador: captura','Add. cacciamiao: Cattura','','Hunt a Great Maccao','Erjage einen Groß-Maccao.','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','Village','0','LR',0,1,0,1,50,0,150,0,'None','–','Rien','Ninguno','Nessuno','','Guess who!? This time, we''re focusing on how to capture monsters as a Prowler. It''s different from how hunters do it, so pay attention! We''ll cover some useful Support Moves, too!','Na, wer ist denn da? Diesmal konzentrieren wir uns darauf, wie man als Pirscher ein Monster fängt. Das ist anders als bei Jägern, also pass auf! Wir behandeln auch ein paar nütz- liche Unterstützungsaktionen!','Devinez quoi ? Cette fois- ci, on va se concentrer sur la capture des monstres en tant que Miaroudeur. C''est très différent des technique de chasseurs ! On abordera aussi les habiletés.','¿Sabes quién soy? Esta vez aprenderemos cómo capturar monstruos como gatador. El método es distinto, así que atento. ¡Y también aprenderás movimientos de apoyo útiles!','Indovina chi sono? Stavolta ti insegnerò a catturare un mostro come cacciamiao. La tecnica è diversa da quella dei cacciatori, quindi fai attenzione! Ti mostrerò anche utili mosse di supporto!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (130307,'Prowler Training: Slaying','Pirscherausbildung: Erlegen','Leçon 6 : Élimination','Práctica gatador: batida','Add. cacciamiao: Uccisione','','Hunt a Great Maccao','Erjage einen Groß-Maccao.','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','Village','0','LR',0,1,0,9,50,0,150,0,'None','–','Rien','Ninguno','Nessuno','','It''s me, the best instructor in the world! Time to learn how a Prowler pounces on big monsters, and how to "purrfect" your Support Moves! See what I did there?','Ich bin''s, der beste Lehrmeister der Welt. Du musst lernen, wie ein Pirscher die großen Monster anspringt und wie du deine Unterstützungsaktionen perfektionierst!','Il n''y a pas de meilleur instructeur que moi ! Il est temps d''apprendre comment un Miaroudeur bondit sur les monstres et comment perfectionner vos habiletés !','¡Soy yo, el mejor instructor del mundo! Aprenderemos a atacar monstruos grandes y perfecciomiauremos tus movimientos de apoyo.','Sono io, il miglior istruttore del mondo! È ora di apprendere come i veri cacciamiao affrontano i mostri di grandi dimensioni e come perrrfezionare le tue mosse di supporto! Chiaro?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010001,'Flames of Savagery at USJ','USJ: Flammen der Wildheit','USJ - Flammes du chaos','Llamas salvajes en USJ','USJ: Fiamme selvagge','','Hunt a Glavenus','Erjage einen Glavenus.','Chasser 1 Glavenus','Caza un Glavenus','Caccia un Glavenus','','Event','0','LR',0,0,3,1,50,1000,9600,500,'Hunt a Great Maccao','Einen Groß-Maccao erjagen','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','Join us at USJ for a once-in- a-lifetime hunter show, "Flames of Savagery," and come face-to-face with the blazing speed and power of the mighty Glavenus!','Werde in den USJ Teil der einzigartige Jagdshow "Flammen der Wildheit" und gehe auf Tuchfühlung mit dem blitzschnellen, kräftigen und mächtigen Glavenus!','Ne ratez pas le show exceptionnel "Flammes du chaos" ! Prenez votre courage à deux mains pour combattre le légendaire Glavenus ! Sortirez-vous vivant de cette aventure ?','Ven a USJ a vivir con nosotros un espectáculo de caza sin precedentes: "Llamas salvajes", ¡una oportunidad única de ver con tus propios ojos la fuerza y la velocidad del poderosísimo Glavenus!','Visita gli USJ e vivi l''emozione di "Fiamme selvagge", uno spettacolo che ti metterà faccia a faccia con la velocità e la forza del grande Glavenus!','',1800,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010004,'A Season for Lava','Lava-Saison','Éruption volcanique','Una temporada de lava','La stagione della lava','','Hunt 2 before time expires or deliver a Paw Pass Ticket','Erjage 2 Uragaan oder gib einen Tatzenpass ab.','Chasser 2 Uragaan ou livrer un ticket Passechat','Caza 2 antes del tiempo o entrega un ticket zarpa','Caccia 2 Uragaan o consegna buono pass-zampa','','Event','0','LR',4,0,3,8,50,1200,11700,630,'Deliver 2 Wyvern Tears','2 Wyverntränen abliefern','Livrer 2 larmes de wyverne','Entrega 2 lágrimas de wyvern','Consegna 2 lacrime wyvern','','Hehehe! This is quite the haul! A group of Uragaan''ve shown up near the Volcano! We can pick ''em off at our leisure! Although, there''re a few too many for me to face alone. Think ya can give me a hand?','Hehehe! Ein hübscher Fang! Am Vulkan wurde eine Gruppe Uragaan gesichtet. Leichte Beute durch und durch! Aber für mich allein ein paar zu viele, packst du mit an?','Hé hé ! Cette fois, c''est le gros lot ! Un groupe d''Uragaan est apparu près du Volcan ! Bref, y''a plus qu''à, mais ils sont trop nombreux pour moi. Vous me donnez un coup de main ?','¡Ja, ja! ¡Menudo botín! Un grupo de Uragaan ha aparecido en el Volcán. ¡Los tenemos en nuestras manos! Aunque quizá sean muchos para ir solo. ¿Me echas una mano?','Eh eh eh! Che occasione! Vicino al vulcano è comparso un branco di Uragaan! Io dico di fare man bassa! D''altra parte, non posso sconfiggerli tutti da solo. Perché non mi dai una mano tu?','',1200,60);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010101,'Crows: The Black Crows','Crows: Die schwarzen Krähen','Crows : les corbeaux noirs','Crows: cuervos negros','Crows: I corvi neri','','Hunt 2 Yian Garuga','Erjage 2 Yian Garuga.','Chasser 2 Yian Garuga','Caza 2 Yian Garuga','Caccia 2 Yian Garuga','','Event','0','HR',0,0,7,101,50,1300,12600,800,'Deliver 1 Large Wyvern Tear','1 Große Wyvernträne abliefern','Livrer 1 grosse larme wyverne','Entrega 1 lágrima de wyvern G','Consegna una g.lacrima wyvern','','There''s some weirdos makin'' trouble in this area. I don''t care why they''re here, they just need to get gone! Think you can do somethin'' ''bout it? If you do, I might give you somethin'' good.','Ein paar schräge Vögel machen die Gegend unsicher. Mir ganz egal, was die hier machen, aber sie müssen verschwinden! Meinst du, du kannst da was tun? Wenn ja, kriegst du was Gutes von mir.','Des types bizarres font des leurs dans la région. Je ne veux pas savoir pourquoi, ils doivent s''en aller, point final ! Vous pourriez intervenir ? Je saurai me montrer généreux !','Unos tipos raros están causando problemas. No sé qué hacen aquí, pero quiero que se vayan. ¿Crees que puedes encargarte de ello? Si lo consigues, puede que tenga algo a cambio...','Un gruppo di stramboidi sta seminando scompiglio in zona. Non so perché siano qui, ma devono andarsene subito! Credi di potermi aiutare? Se accetti, ti offrirò una degna ricompensa...','',2700,80);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010102,'Yowamushi Pedal: Princess','Yowamushi Pedal: Prinzessin','En selle, Sakamichi','Yowamushi Pedal: princesa','Yowamushi Pedal: Principessa','','Hunt 3 Rathians','Erjage 3 Rathian.','Chasser 3 Rathian','Caza 3 Rathian','Caccia 3 Rathian','','Event','0','HR',4,0,5,5,50,1700,16200,710,'Deliver 10 Piscine Livers','10 Pisces-Lebern abliefern','Livrer 10 foies de nageur','Entrega 10 hígados de nadador','Consegna 10 Fegati di Piscine','','I can''t deliver this jersey to its destination with these large beasts in my way! Please, Hunter! Take care of them and clear a path for me so I can get moving again! I beg of you!','Ich kann dieses Trikot nicht ausliefern, solange mir diese Viecher den Weg versperren! Bitte, Jäger, kümmere dich um sie, damit ich wieder auf die Piste komme! Ich bitte dich!','Avec tous les monstres qui rôdent par ici, impossible de livrer ce maillot à son destinataire ! Chasseur, occupez-vous d''eux que je puisse reprendre ma course. Je vous en prie !','No puedo llevar este jersey a su destino con tantas bestias en el camino. Por favor, cazador, ocúpate de ellas para que pueda seguir avanzando. ¡Te lo ruego!','Non posso consegnare questo maglione se la strada è infestata di bestie enormi! Per favore! Liberati di quei mostri e sgombra la via. Solo così potrò riprendere il mio viaggio! Ti scongiuro!','',3000,70);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010103,'Baki: Biggest vs. Strongest','Baki: Größe gegen Stärke','Baki : Taille contre force','Baki: más grande, más fuerte','Baki: Contro il più grande','','Hunt a Gammoth','Erjage einen Gammoth.','Chasser 1 Gammoth','Caza un Gammoth','Caccia un Gammoth','','Event','0','HR',0,0,7,9,50,2100,20400,1100,'Wound Gammoth''s head & trunk','Gammoth-Kopf+Rüssel verletzen','Blesser le Gammoth (tête & tronc)','Hiere Gammoth cabeza/trompa','Ferisci testa e prob. Gammoth','','In the red corner, we have the Immovable Mountain: the one and only Gammoth! And from the blue corner we have the brave hunter who refuses to lose! Our winner will receive a fabulous prize, so good luck!','In der roten Ecke haben wir den unerschütterlichen Berg: den einzigartigen Gammoth! Und in der blauen Ecke: der tapfere Jäger, der keine Niederlage akzeptiert. Den Sieger erwartet ein toller Preis. Viel Glück!','Dans le coin rouge, nous avons l''Immuable montagne, j''ai nommé le seul, l''unique... Gammoth ! En face de lui, un chasseur déterminé à triompher ! Que le meilleur gagne !','En la esquina roja, tenemos a la montaña inamovible: ¡el Gammoth! Y en la esquina azul, ¡tenemos al valiente cazador que se niega a perder! El ganador recibirá un premio fabuloso. ¡Suerte!','Nell''angolo rosso, la montagna vivente: l''unico e solo Gammoth! Nell''angolo blu, l''ardita stella della caccia! Al vincitore spetta un premio favoloso, quindi... Buona fortuna a entrambi!','',2700,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010104,'CoroCoro: Rolling Bowlers','CoroCoro: Immer rundherum','CoroCoro : Roulé-boulé','CoroCoro: monstruos rodantes','CoroCoro: Sfere rotolanti','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,6,9,50,1600,15300,760,'None','–','Rien','Ninguno','Nessuno','','It''s so fascinating to see these monsters rolling about. And just as I was thinking how grand it would be to see three of them at once, here they are! If you''re able to hunt these monsters, I''ll give you a special present.','Es ist faszinierend, diese Monster herumrollen zu sehen. Und gerade als ich dachte, ich würde gerne drei gleichzeitig sehen, sind sie auch schon da! Wenn du sie jagst, gebe ich dir ein besonderes Geschenk.','Fascinant spectacle que celui de ces monstres qui tournent et roulent sans jamais s''arrêter ! Puisque vous êtes là, chassez-en 3 et je vous remettrai un cadeau très spécial.','Es fascinante ver a estos monstruos rodando. Y justo cuando pensaba lo chulo que sería ver a tres de ellos juntos, ¡aquí están! Si los cazas, te haré un regalo especial.','È magnifico osservare questi mostri che se ne vanno in giro. E proprio quando ho pensato che avrei voluto vederne tre... Sono saltati fuori! Se riesci a cacciarli, ti offrirò un dono speciale.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010105,'Ushio & Tora: White Mizutsune','Ushio & Tora: Weißer Mizutsune','Ushio & Tora : Mizutsune blanc','Ushio y Tora: Mizuts. Blanco','Ushio & Tora: Mizutsune bianco','','Hunt a Mizutsune','Erjage einen Mizutsune.','Chasser 1 Mizutsune','Caza un Mizutsune','Caccia un Mizutsune','','Event','0','HR',0,0,7,104,50,1900,18600,1000,'Wound Mizutsune''s head & tail','Mizutsune-Kopf+Schwanz verletzen','Blesser le Mizutsune (tête & queue)','Hiere Mizutsune en cabeza/cola','Ferisci testa e coda Mizutsune','','I''m smelling the worst smell from somewhere... It doesn''t seem to be coming from a monster, but it stinks nonetheless. Think you could go deal with it for me? Oh, and if you could remove this spear for me, that''d be great.','Ich hab einen ganz üblen Geruch in der Nase. Es scheint kein Monster zu sein, aber es stinkt. Könntest du dich vielleicht darum kümmern? Oh, und es wäre toll, wenn du diesen Speer für mich entfernen würdest.','Quelle odeur écœurante ! Je doute qu''elle provienne d''un monstre, mais quand même... ça fouette ! Faites quelque chose ! Et pendant que vous y êtes, si vous pouviez ôter cette lance...','Huele fatal por aquí... No parece que provenga de un monstruo, pero apesta. ¿Puedes encargarte de ello? Ah, y si me quitas esta lanza, me harás un favor.','Avverto un odore nauseabondo. Non sembra legato a un mostro, ma il tanfo è insopportabile. Potresti occupartene tu? Oh, e se riuscissi a rimuovere questa lancia sarebbe grandioso.','',2700,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010106,'Magi: Rescue from the Fall','Magi: Rettung vor dem Sturz','Magi : Sus à la corruption','Magi: rescate del mal','Magi: Abisso e salvezza','','Hunt a Gore Magala','Erjage Gore Magala.','Chasser 1 Gore Magala','Caza un Gore Magala','Caccia un Gore Magala','','Event','0','HR',0,0,7,17,50,1800,17100,880,'Wound G.Magala''s feeler & wing','G.Magala-Fühler+Flügel verletzen','Blesser le G. Mag. (antenne & aile)','Hiere G. Magala en sensor/ala','Ferisci sensori e ala G.Magala','','There''s an evil force about... The darkness is encroaching upon him. But it''s okay! With your help, we''ll be able to keep him from harm. Now, let us work together to dispel the darkness!','Es ist eine üble Macht am Werk... Die Finsternis bedroht ihn. Aber es ist okay! Mit deiner Hilfe können wir ihn vor Schaden bewahren. Lass uns zusammenarbeiten, um die Finsternis zu vertreiben!','Une force maléfique sévit dans les parages... Elle s''empare peu à peu de lui, mais avec votre aide, nous le protégerons. Unissons nos efforts pour dissiper les ténèbres !','Hay una fuerza malvada... La oscuridad le invade. Pero no pasa nada: con tu ayuda, evitaremos que le haga daño. ¡Unamos fuerzas para combatir la oscuridad!','Una forza malvagia si avvicina. L''oscurità incombe su di lui. Ma non importa! Con il tuo aiuto, riusciremo a proteggerlo. Combattiamo insieme per dissipare le tenebre!','',2700,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010107,'The Exploding Flame King','König der Explosivflamme','Le Roi flamboyant explosif','El rey explosivo','Il re fiammante','','Slay a Teostra','Erlege einen Teostra.','Tuer 1 Teostra','Abate un Teostra','Uccidi un Teostra','','Event','0','HR',1,0,7,8,50,2200,21600,1200,'Wound Teostra''s head & wingtalon','Teostra-Kopf+Flügelkralle verletzen','Blesser le Teostra (tête & serre)','Hiere Teostra en cabeza/espolón','Ferisci testa e sperone Teostra','','This Quest should really fire you up! Teostra: cloaked in flame, it burns everything in its path... Phew! If you can beat the heat, I''ll reward you with an item that''ll let you forge some special gear!','Diese Quest wird dir einheizen! Teostra: In Flammen gehüllt, brennt er alles vor sich nieder. Widerstehst du der Hitze, bekommst du ein Objekt, mit dem du Spezialausrüstung schmieden kannst.','Lorsqu''on joue avec le feu, on finit par se brûler ! Le Teostra est l''empereur du feu, il réduit tout en cendres. Complétez cette quête et je vous donnerai un objet pour forger un équipement spécial !','¡Una misión bien calentita! Enfréntate al Teostra y a su manto de llamas, que lo abrasa todo a su paso. ¡Si superas esos calores, te lo agradeceré con un objeto con el que podrás forjar equipo especial!','Una missione infuocata! Teostra, avvolto da fiamme, incenerisce qualsiasi cosa gli si ponga davanti... Sopporta il fuoco e ottieni un oggetto per forgiare equipaggiamento speciale!','',2400,120);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010108,'Yu-Gi-Oh: A Beautiful Wyvern','Yu-Gi-Oh: Ein bildschöner Wyvern','Yu-Gi-Oh : Belle wyverne','Yu-Gi-Oh: un wyvern bonito','Yu-Gi-Oh: Splendido wyvern','','Slay a Shagaru Magala','Erlege Shagaru Magala.','Tuer le Shagaru Magala','Abate al Shagaru Magala','Uccidi uno Shagaru Magala','','Event','0','HR',1,0,7,16,50,2100,20700,1060,'Wound S.Magala''s horn & wing','S.Magala-Horn+Flügel verletzen','Blesser le S. Mag. (corne & aile)','Hiere S. Magala en cuerno/ala','Ferisci corno e ala S.Magala','','It''s time for the final match... This dragon is quite strong, but you''ve got the unbreakable bonds of friendship on your side! Now, get ready... It''s time to d-d-d-duel!','Zeit für den finalen Kampf... Dieser Drache ist ziemlich stark, aber du hast die unzerreißbaren Bande der Freundschaft auf deiner Seite! Jetzt mach dich bereit... Zeit fürs D-D-Duell!','L''heure de l''ultime combat a sonné... Ce dragon est puissant, mais les liens de l''amitié jouent en votre faveur ! En garde, Chasseur ! Le temps est venu d''en découdre !','Llega la partida final... Este dragón es muy fuerte, pero tú tienes los lazos irrompibles de la amistad de tu lado. Prepárate: ¡llega el duelo!','La battaglia finale è alle porte... Questo drago è potente, ma tu puoi contare sui legami indissolubili dell''amicizia! Preparati, adesso... Gioca la partita e vinci la fatica!','',2400,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010109,'Jump: Boiling Magma','Jump: Kochendes Magma','Jump : Magma en fusion','Jump: magma ardiente','Jump: Magma bollente','','Hunt a Lavasioth','Erjage einen Lavasioth.','Chasser 1 Lavasioth','Caza un Lavasioth','Caccia il Lavasioth','','Event','0','HR',0,0,7,8,50,1200,12000,750,'Wound Lavasioth''s head & top fin','Lavasioth-Kopf+Finne verletzen','Blesser le Lava. (tête & nag. dorsale)','Hiere Lavasioth en cabeza/aleta','Ferisci testa e pinna Lavasioth','','A Hyper Lavasioth is a worthy opponent indeed. We pirates tend to be at our best when we face a powerful foe. If you pass our little test, we''ll give you a ticket for making some legendary pirate weapons!','Ein Ferus-Lavasioth ist ein würdiger Gegner. Bei starken Feinden laufen wir Piraten zu Hochform auf. Bestehst du unseren kleinen Test, bekommst du einen Schein für ein paar legendäre Piratenwaffen!','L''hyper Lavasioth est un noble adversaire. Rien de tel pour se surpasser, foi de pirate ! Passez le test et nous vous donnerons un ticket pour fabriquer des armes pirates légendaires !','Un Híper Lavasioth es un rival digno. Los piratas solemos dar lo mejor cuando luchamos. Si pasas esta prueba, te daremos un ticket para crear armas piratas legendarias.','I Lavasioth iper sono nemici ostici. Noi pirati diamo sempre il meglio di fronte agli avversari più potenti. Se supererai questa prova, ti offriremo un buono per creare leggendarie armi piratesche!','',2700,80);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010110,'Famitsu: Mizutsune Feature','Famitsu: Mizutsune-Sonderausgabe','Famitsu : Mizutsune en une','Famitsu: el show del Mizutsune','Famitsu: Mizutsune chi?','','Hunt 3 Mizutsunes','Erjage 3 Mizutsune.','Chasser 3 Mizutsune','Caza 3 Mizutsune','Caccia 3 Mizutsune','','Event','0','HR',4,0,7,13,50,2700,26100,1210,'Deliver 2 Large Wyvern Tears','2 Große Wyverntränen abliefern','Livrer 2 grosses larmes de wyverne','Entrega 2 lágrimas wyvern G','Consegna 2 g.lacrime wyvern','','The Editorial Department here heard about a new monster resembling a fox, and we''d like for you to get some info on it. Oh, you''ll need to hunt it too, naturally! We''ll reward you with materials for new weapons!','Die Redaktion hat von einem neuen, fuchsähnlichen Monster gehört und möchte, dass du Infos darüber beschaffst. Oh, und natürlich musst du es auch jagen! Wir belohnen dich mit Material für neue Waffen!','La rédac'' a entendu parler d''un nouveau monstre qui ressemble à un renard et nous aimerions en savoir plus. Chassez-le et nous vous offrirons des composants d''armes !','El departamento editorial ha oído de un monstruo que parece un zorro y queremos que recojas info sobre él. Naturalmente, tendrás que cazarlo. Te recompensaremos con materiales para armas.','I nostri redattori hanno sentito parlare di un mostro simile a una volpe. Vorremmo che tu ci procurassi delle informazioni. E dovrai anche cacciarlo! Ti ricompenseremo con materiali per nuove armi!','',5100,120);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010111,'Dengeki: Turbulent Seas','Dengeki: Unruhige Gewässer','Dengeki : Mers agitées','Dengeki: mares turbulentos','Dengeki: Mari turbolenti','','Hunt a Lagiacrus','Erjage einen Lagiacrus.','Chasser 1 Lagiacrus','Caza un Lagiacrus','Caccia un Lagiacrus','','Event','0','HR',0,0,7,6,50,1600,15300,850,'Wound Lagiacrus''s chest & tail','Lagiacrus-Brust+Schwanz verletzen','Blesser le Lagiacrus (buste & queue)','Hiere Lagiacrus en pecho/cola','Ferisci torace e coda Lagiacrus','','Here''s a little challenge from the Dengeki editors! The target: a Hyper Lagiacrus, in all of its electric fury! If you manage to avoid a shocking defeat, you''ll receive a ticket good for making a brand new weapon!','Hier eine kleine Aufgabe der Dengeki-Redakteure! Das Ziel: ein Ferus-Lagiacrus in all seiner elektrischen Pracht! Vermeidest du den Schock einer Niederlage, bekommst du einen Schein für eine brandneue Waffe!','Voici un petit défi de la rédac'' de Dengeki ! Votre cible : un hyper Lagiacrus. Évitez de finir électrocuté afin de remporter un ticket permettant de fabriquer une arme flambant neuve !','Toma, un desafío de los editores de Dengeki. Tu objetivo: Híper Lagiacrus y su furia eléctrica. Si evitas sus descargas, te daré un ticket para crear un arma nueva.','Una sfida dai redattori di Dengeki! Il bersaglio è un Lagiacrus iper, celebre per la sua furia elettrica! Evita una sconfitta fulminante e otterrai un buono utile a creare una nuova arma!','',2700,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010112,'Uniqlo Shock Fest','Uniqlo-Schockfest','Défilé électrique Uniqlo','Desfile eléctrico Uniqlo','Festa elettrica Uniqlo','','Hunt 2 Astalos','Erjage 2 Astalos.','Chasser 2 Astalos','Caza 2 Astalos','Caccia 2 Astalos','','Event','0','HR',0,0,7,2,50,2100,20400,1210,'Deliver 1 Large Wyvern Tear','1 Große Wyvernträne abliefern','Livrer 1 grosse larme wyverne','Entrega 1 lágrima de wyvern G','Consegna una g.lacrima wyvern','','I''ve come up with the perfect idea for a look that''s stylish AND casual! Getting the materials may be a bit of a challenge, but if you can help me, we''ll shock the fashion world!','Ich habe die perfekte Design- idee für einen Look, der stil- voll und zwanglos aussieht! Es wird eine Herausforderung, die Materialien dafür zu bekommen, aber dafür werden wir die Modewelt schocken!','J''ai conçu le design idéal pour un look à la fois élégant et décontracté ! Mais obtenir les matériaux nécessaires sera un vrai défi. Si vous m''aidez, nous allons révolutionner la mode !','¡He tenido una idea perfecta para tener un aspecto con estilo y desenfadado! Puede que cueste un poco conseguir los materiales... ¡Pero si me ayudas, podemos dar un giro al mundo de la moda!','Ho trovato il design ideale per un oggetto stiloso ma casual al tempo stesso! Trovare i materiali sarà un po'' difficile, ma forse tu potrai aiutarmi!','',2700,120);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010113,'Dance of Dreams at USJ','USJ: Tanz der Träume','USJ - Danse des rêves','Baile de sueños en USJ','USJ: Danza dei sogni','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,1800,17400,960,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Don''t miss your chance to join us at USJ for our marvelous show, "Dance of Dreams"! Our two exquisite dancers will leave you so mesmerized you''ll swear it was all just a dream!','Verpasse nicht die Gelegen- heit, in den USJ an der sagen- haften Show "Tanz der Träume" teilzunehmen! Unsere zwei dramatischen Tänzer werden das Erlebnis wie einen Traum erscheinen lassen.','Vivez des moments magiques avec le dernier spectacle d''USJ, "La danse des rêves" ! Plongez-vous dans cette parade féérique et laissez-vous emporter vers le monde des illusions !','¡No pierdas la ocasión de acudir a USJ a ver el fantástico espectáculo "Baile de sueños"! La exquisita danza de estos dos bailarines te hará pensar que todo a tu alrededor no ha sido más que un bonito sueño.','Visita gli USJ e vivi l''emozione di "Danza dei sogni", uno spettacolo dove i nostri due danzatori d''eccezione ti incanteranno e ti faranno sognare a occhi aperti!','',2400,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010114,'Blazing Thunder at USJ','USJ: Rollender Donner','USJ - Tonnerre ardent','Rayo atronador en USJ','USJ: Tuono fiammante','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,2500,24300,1270,'Mount & topple monster 3 times','3x Monster besteigen + umwerfen','Monter & renverser un monstre 3 fois','Monta y derriba 3 veces al mons.','Cavalca e rovescia mostro x3','','Prepare yourself for the show of your life with "Blazing Thunder"! These two Hyper brutes will astound you with their display of raging flames and dazzling lightning! So, what''re you waiting for!?','Bereite dich auf die Show deines Lebens vor, "Rollender Donner"! Diese beiden Ferus- Monster werden dich mit wütenden Flammen und gleißenden Blitzen an deine Grenzen bringen.','Préparez-vous à un combat explosif avec "Tonnerre ardent" ! Ces deux hyper monstres vous frapperont de plein fouet avec des tempêtes de feu et des éclairs éblouissants !','¡Preparaos todos para el mayor espectáculo de vuestras vidas: "Rayo atronador"! ¡Dos brutales Híper monstruos te dejarán anonadado con sus llamas y rayos cegadores! ¿A qué estás esperando?','Preparati ad assistere a uno spettacolo senza pari con "Tuono fiammante"! Due brutali iper ti stupiranno con fiamme e lampi! Andiamo, che cosa aspetti?','',2700,130);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010115,'Frost and Fire at USJ','USJ: Frost und Feuer','USJ - Feu et glace','Fuego y hielo en USJ','USJ: Ghiaccio e fuoco','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,2100,20400,1060,'Mount & topple monster 3 times','3x Monster besteigen + umwerfen','Monter & renverser un monstre 3 fois','Monta y derriba 3 veces al mons.','Cavalca e rovescia mostro x3','','Look no further than USJ for the best in entertainment, like our icy hot show "Frost and Fire"! You''ll be sure to feel the burn at both extremes!','"Frost und Feuer", das ist die heißeste Eis-Show der ganzen Unterhaltungsindustrie, ex- klusiv in den USJ! Bereite dich sowohl auf Erfrierungen als auch Verbrennungen vor!','Assistez au plus grand spectacle du monde avec "Feu et glace" ! Expérimentez le monde des deux extrêmes !','¡En USJ encontrarás el mejor entretenimiento, como nuestro frío y cálido espectáculo "Fuego y hielo"! ¡Prepárate para experimentar los dos extremos!','Se vuoi divertirti, fermati agli USJ! "Ghiaccio e fuoco", uno show dalle temperature estreme, ti garantirà il meglio dell''intrattenimento!','',2400,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010118,'Star Fox: Battle at Arctic Ridge','Star Fox: Kampf im Schneegebirge','Star Fox : Combat arctique','Star Fox: Cimas Árticas','Star Fox: Battaglia artica','','Hunt a Blangonga','Erjage einen Blangonga.','Chasser 1 Blangonga','Caza un Blangonga','Caccia il Blangonga','','Event','0','HR',0,0,7,3,50,1100,10800,680,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','This is Star Fox! I''ve spotted a Hyper Monster near the Arctic Ridge! However, this sector is unstable and I''m unable to land. I need you to get in there and neutralize it. You''ll be properly rewarded! Good luck!','Hier spricht Star Fox! Ich habe im Schneegebirge ein Ferus- Monster entdeckt! Das Terrain eignet sich jedoch nicht zum Landen. Mach dich auf den Weg und neutralisiere es. Du wirst angemessen entlohnt.','Ici Star Fox ! J''ai aperçu un hyper monstre près de la Crête arctique ! Le secteur est instable, impossible de me poser. Je compte sur vous pour le neutraliser. Récompense à la clé !','¡Aquí Star Fox! He visto un Híper monstruo cerca de las Cimas Árticas. Sin embargo, la inestabilidad me impide aterrizar. Neutralízalo y te recompensaré bien. ¡Buena suerte!','Qui Star Fox! Ho individuato un mostro iper vicino al monte artico, ma il settore è instabile! Io non posso atterrare, quindi dovrai pensarci tu. Ti ricompenserò a dovere! E buona fortuna!','',2700,70);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010119,'The Three Virtues','Mut, Weisheit, Kraft','Force, Sagesse, Courage','Tres virtudes','Le tre virtù','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,2700,26700,1490,'Mount and topple monster 4 times','4x Monster besteigen + umwerfen','Monter & renverser un monstre 4 fois','Monta y derriba 4 veces al mons.','Cavalca e rovescia mostro x4','','A large number of big baddies have come crawlin'' out and are causin'' quite the commotion! We need yer wisdom, strength, and courage to drive off these scoundrels! We''ll make it worth yer while with a special item!','Ein Haufen großer Halunken sorgt für jede Menge Aufruhr! Wir brauchen deine Weisheit, Stärke und Courage, um dieses Pack zu vertreiben! Dafür belohnen wir dich auch mit einem besonderen Objekt!','Une horde de pendards est sortie de son repaire pour semer la panique ! Force, sagesse et courage vous permettront d''en venir à bout. En retour, vous recevrez un objet spécial.','Un montón de malotes se han reunido y están causando una gran conmoción. Necesitamos tu sabiduría, poder y valor para dispersar a estos bribones. Te daremos un objeto especial...','Un branco di cattivoni è comparso dal nulla e ha scatenato un gran trambusto! Prestaci la tua forza, la tua saggezza e il tuo coraggio! In cambio, possiamo offrirti un oggetto speciale!','',2400,150);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010120,'Metroid: Special Mission','Metroid: Spezialeinsatz','Metroid : Mission spéciale','Metroid: misión especial','Metroid: Missione speciale','','Hunt a Yian Garuga','Erjage einen Yian Garuga.','Chasser 1 Yian Garuga','Caza un Yian Garuga','Caccia uno Yian Garuga','','Event','0','HR',0,0,7,12,50,1500,14400,800,'Wound Yian Garuga''s back & tail','Y.Garuga-Rücken+Schwanz verletzen','Blesser le Yian Garuga (dos & queue)','Hiere Yian Garuga lomo/cola','Ferisci dorso e coda Yian Garuga','','Something is plotting to steal the galaxy''s most powerful weapon! Scanning... That beak, that ominous purple skin... Could it be!? Please, Hunter! Hurry to the Volcanic Hollow before it''s too late!','Irgendjemand plant, die mächtigste Waffe der Welt zu stehlen! Scanne... Dieser Schnabel, die unheilvolle lila Haut... Kann das wahr sein? Bitte, Jäger! Eile zur Vulkan- mulde, bevor es zu spät ist!','Une créature s''apprête à voler l''arme la plus puissante de la galaxie ! Analyse en cours... Un bec, une peau violacée... Bon sang ! Du nerf, Chasseur, au Vallon volcanique !','Algo está tramando robar el arma más potente de la galaxia. Ese pico y esa piel púrpura. ¿Es posible? ¡Por favor, cazador! Ve al Hoyo Volcán antes de que sea demasiado tarde.','Una creatura sta tentando di rubare l''arma più potente della galassia! Scansione... Quel becco, quella sinistra pelle viola... Possibile?! Corri alla fossa vulcanica, prima che sia troppo tardi!','',2700,80);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010121,'The Steel Vanguard','Die stählerne Vorhut','L''avant-garde d''acier','Vanguardia de acero','Guardia d''acciaio','','Slay a Kushala Daora','Erlege einen Kushala Daora.','Tuer le Kushala Daora','Abate un Kushala Daora','Uccidi un Kushala Daora','','Event','0','HR',1,0,7,103,50,2200,21600,1200,'Wound K.Daora''s head & wing','K.Daora-Kopf+Flügel verletzen','Blesser le Kushala Daora (tête & aile)','Hiere K. Daora en cabeza/alas','Ferisci testa e ali K.Daora','','Can you hear me...? A force bent on tearing the world asunder is closing in. Stand tall against this deliverer of chaos, and grant this great land eternal peace.','Kannst du mich hören? Es nähert sich eine Macht mit der Absicht, die Welt in Stücke zu reißen. Stelle dich dem Chaos entgegen und gewähre diesem großartigen Land ewigen Frieden.','Pouvez-vous m''entendre... ? Une force inconnue s''apprête à déchirer le monde. Affrontez l''émissaire du chaos, et apportez la paix éternelle au royaume.','¿Me oyes...? Se acerca un poder empeñado en hacer pedazos al mundo entero. Mantente firme ante este agente del caos, y protege la infinita paz de esta tierra.','Puoi sentirmi? Una forza che minaccia di lacerare il mondo si sta avvicinando. Sfida la bestia portatrice di caos e dona la pace eterna a queste lande.','',2400,120);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010122,'The Voracious King','Der gefräßige König','Le roi vorace','El rey voraz','Il re vorace','','Hunt a Deviljho','Erjage einen Dämonjho.','Chasser 1 Deviljho','Caza un Deviljho','Caccia un Deviljho','','Event','0','HR',0,0,7,101,50,2200,21600,980,'Wound Deviljho''s head','Dämonjho-Kopf verletzen','Blesser la tête du Deviljho','Hiere Deviljho en la cabeza','Ferisci la testa del Deviljho','','Can you hear me...? An evil has awakened, threatening to plunge the world into darkness. Stand tall against this devourer of light, and grant this great land eternal glory.','Kannst du mich hören? Eine böse Macht ist erwacht und droht, die Welt in Dunkelheit zu stürzen. Gebiete dem Lichtverschlinger Einhalt und bringe dem Land ewigen Ruhm.','Pouvez-vous m''entendre ? Une force maléfique s''apprête à plonger le monde dans les ténèbres. Affrontez le dévoreur de lumière, et apportez la gloire éternelle au royaume.','¿Me oyes...? Una fuerza maligna se ha despertado y amenaza con sumir al mundo en tinieblas. No te dejes amedrentar por ese devorador de luz: ¡protege la gloria de esta tierra!','Puoi sentirmi? Una forza che minaccia di oscurare il mondo si sta avvicinando. Sfida la bestia divoratrice di luce e dona la pace eterna a queste lande.','',2700,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010123,'DMC: The Seltas Queen''s Cry','DMC: Schrei der Seltas-Königin','DMC : La Reine Seltas','DMC: llanto de la Seltas Reina','DMC: Il grido della regina','','Hunt a Seltas Queen','Erjage eine Seltas-Königin.','Chasser 1 Reine Seltas','Caza una Seltas Reina','Caccia una Seltas regina','','Event','0','HR',0,0,7,13,50,1700,16800,940,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','Hey, can you hear that? One of the monsters fled that way. Well, I guess it''s not really much of a monster, more of a bug, actually. Anyways, if you can take care of it for me, I''ll be sure to make it worth your while.','Hey, kannst du das hören? Ein Monster ist in diese Richtung geflohen. Also, kein richtiges Monster, mehr eine Art Käfer. Wie auch immer, wenn du das Ding für mich erledigst, soll es sich für dich lohnen.','Hé, vous entendez ? L''un des monstres a filé par là. Il ressemble plutôt à un insecte, d''ailleurs. Enfin bref, occupez-vous de son cas et vous n''aurez pas à le regretter.','Eh, ¿oyes eso? Uno de los monstruos ha escapado. Supongo que no es realmente un monstruo, sino más bien un bicho. Bueno, si te ocupas de él, te compensaré como es debido.','Ehi, lo senti? Un mostro è fuggito da quella parte. Beh, non è proprio un mostro. Sembra più un insetto, in realtà. Comunque... Se lo togli di mezzo, sono pronto a offrirti un degno compenso.','',2700,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010124,'Mega Man: Yellow Devil','Mega Man: Der gelbe Dämon','Mega Man : Démon jaune','Mega Man: demonio amarillo','Mega Man: Demone giallo','','Hunt a Royal Ludroth','Erjage einen Königs-Ludroth.','Chasser 1 Ludroth royal','Caza un Ludroth Real','Caccia un Ludroth reale','','Event','0','HR',0,0,7,15,50,900,8700,500,'Slay 18 Ludroth','18 Ludrothe erlegen','Tuer 18 Ludroth','Abate a 18 Ludroth','Uccidi 18 Ludroth','','The Royal Ludroth is a royal pain. Every time a hunter gets rid of one, it seems another pops up in its place. Reminds me of a certain evil scientist. No matter how many times he''s defeated, he keeps coming back!','Der Königs-Ludroth geht mir königlich auf die Nerven. Kaum hat ein Jäger einen erlegt, taucht schon der nächste auf. Erinnert mich an einen gewissen bösen Wissenschaftler. Der kommt auch immer wieder!','Le Ludroth royal est une vraie plaie ! On a beau les éliminer, on n''en voit jamais le bout. Ça me rappelle ce savant fou... Lui aussi, il ne cesse de revenir quoi qu''on fasse !','El Ludroth Real es realmente molesto. Cada vez que un cazador se carga uno, otro aparece en su lugar. Es como un científico malvado que conozco. Aunque le derrotes, ¡siempre acaba volviendo!','I Ludroth reali sono una tortura. Quando un cacciatore ne elimina uno, subito ne compare un altro. Uhm, mi ricordano un certo scienziato pazzo... Sempre sconfitto e sempre pronto a tornare!','',4200,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010125,'Capcom: Brilliant Orchestra','Capcom: Brillantes Orchester','Capcom : Chef d''orchestre','Capcom: orquesta magnífica','Capcom: Magnifica orchestra','','Hunt a Najarala','Erjage einen Najarala.','Chasser 1 Najarala','Caza un Najarala','Caccia un Najarala','','Event','0','HR',0,0,7,2,50,1300,12900,750,'Wound Najarala''s head & back','Najarala-Kopf+Rücken verletzen','Blesser le Najarala (tête & dos)','Hiere Najarala en cabeza/lomo','Ferisci testa e dorso Najarala','','I have THE WORST writer''s block right now. I want to compose a song that depicts the emotion of a hunt, but I have zero inspi- ration. I bet if I saw you in action, the notes would come pouring out of my pen.','Ich habe die SCHLIMMSTE Komponierblockade aller Zeiten. Ich will die Emotionen der Jagd in einem Lied festhalten, aber mir fehlt die Inspiration. Wenn ich dich in Aktion sähe, würden die Noten sicher nur so purzeln.','Je souhaite composer une mélodie évoquant ce que l''on ressent à la chasse, mais je suis en panne d''inspiration. Vous voir à l''œuvre stimulerait sans doute ma créativité.','Tengo un bloqueo mental muy grande. Quiero componer una canción que describa la emoción de una caza, pero no estoy inspirado. Si te viera en acción, las notas brotarían de mi pluma.','Sono in crisi nera. Vorrei comporre una melodia che rievochi le emozioni della caccia, ma non sono ispirato. Se ti vedessi in azione, però, le note tornerebbero a sgorgare dalla mia penna.','',2700,80);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010127,'Felyne Village: The Tourney','Felynen-Dorf: Das Turnier','Village Felyne : Tournoi','Aldea Felyne: Gran torneo','Villaggio Felyne: Torneo','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,10,50,2000,19200,1040,'None','–','Rien','Ninguno','Nessuno','','Nya, nya, nya, nya, nya! I''m here to give you a super cool deluxe purresent! Although, nothing comes for furree! You need to win the little hunting tournament we''re purrlanning! Now, let''s get started, nya!','Miau! Miaumiaumiau! Ich bin hier, um dir ein ganz fant-tatz- tisches Geschenk zu machen! Obwohl – alles hat seinen Preis! Du musst schnurrerst das kleine Jagdturnier gewinnen, das wir planen. Na, dann los, miau!','Miaou, miaou, miaou ! On m''envoie vous remettre un superbe chadeau ! Cela dit, rien n''est gratuit. Il vous faudra d''abord gagner notre tournoi de chasse. C''est parti, miaou !','¡Miau, miau, miau! He venido a darte un regalo de lujo superchulo. Pero todo tiene un precio: debes ganar el torneo de caza que hemos preparado. ¡Venga, a por ello!','Mià, mià, mià, mia! Sono qui per offrirti un regalo prrrodigioso! Ma niente è gratis! Prrrima devi vincere il torneo di caccia che stiamo organizzando! Allora, che ne dici? Iniziamo, mià!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010128,'Fan Club: Great All Stars','Fanclub: All-Stars','Fan Club : Les vedettes','Club de fans: All Stars','Fan club: Grandi campioni','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,1100,10500,530,'Wound Great Maccao''s head','Groß-Maccao-Kopf verletzen','Blesser la tête d''un Grand Maccao','Hiere Gran Maccao en la cabeza','Ferisci la testa del Gran Maccao','','Hey, buddy! There''s nothing like takin'' on the best of the best in a little tussle! Better yet, you''ll get an item that lets you make special weapons and armor! I''m so glad I joined the MH Fan Club! Best decision I ever made!','Hallo, Kumpel! Was könnte besser sein, als sich mit den Besten der Besten zu messen? Und dann bekommst du noch ein Objekt, mit dem du Spezialwaffen und -Rüstungen machen kannst! Der MH-Fanclub ist echt spitze!','Salut ! Rien de tel qu''une empoignade avec les meilleurs ! Mieux encore, vous gagnerez un objet pour fabriquer des armes et armures spéciales ! Ce fan club est vraiment au top !','¡Eh, amigo! No hay nada como enfrentarse a los mejores en una pelea. Además, obtendrás un objeto para crear armas y armaduras especiales. ¡Estoy tan contento de estar en el club de fans de MH!','Ehilà! Non c''è niente di meglio che azzuffarsi con i più grandi mostri del globo! E puoi vincere un oggetto che permette di creare armi e armature speciali! Il fan club MH è fenomenale!','',1800,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010129,'Fan Club: Remobra Removal','Fanclub: Remobra-Rauswurf','Fan Club : À bas les Remobra','Club fans: remobra fuera','Fan club: Eccidio Remobra','','Slay 12 Remobras','Erlege 12 Remobras.','Tuer 12 Remobras','Abate a 12 Remobra','Uccidi 12 Remobra','','Event','0','HR',1,0,7,9,50,600,6000,300,'Hunt a Malfestio','Einen Malfestio erjagen','Chasser 1 Malfestio','Caza un Malfestio','Caccia un Malfestio','','Hello, everybody! Our foe this time around is a large pack of Remobras! Once you''ve taken ''em out, I''ll give you an item that you can trade for some special MH Fan Club armor! Good hunting!','Hallo allerseits! Dieses Mal haben wir es mit einem großen Rudel Remobras zu tun! Wenn du sie erledigt hast, bekommst du von mir ein Objekt, das du für eine besondere MH-Fanclub- Rüstung eintauschen kannst!','Salut à tous ! Cette fois, nous sommes aux prises avec une meute de Remobra ! Éliminez-les et je vous donnerai un objet à échanger contre une armure spéciale du MH Fan Club !','¡Hola a todos! Esta vez, debemos enfrentarnos a una manada de Remobra. Cuando acabes con ella, te daré un objeto que podrás cambiar por armadura del club de fans de MH. ¡Buena caza!','Salve a tutti! Quest''oggi dovrete affrontare un enorme branco di Remobra! Il premio che vi attende è un oggetto che può essere scambiato con un''armatura speciale del fan club MH! Buona caccia!','',6900,400);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010130,'Capcom: White & Black','Capcom: Weiß und Schwarz','Capcom : Blanc & noir','Capcom: blanco y negro','Capcom: Bianco e nero','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,104,50,2100,20400,1140,'Mount & topple monster 3 times','3x Monster besteigen + umwerfen','Monter & renverser un monstre 3 fois','Monta y derriba 3 veces al mons.','Cavalca e rovescia mostro x3','','A group posing as us showed up in the Misty Peaks and totally wrecked the place! What''ll we do if people think it''s us!? We''re begging you, Hunter! Help us out and we''ll give you a special ticket as a present!','Eine Gruppe, die wie wir aussah, hat die Nebelgipfel vollkommen verwüstet! Jetzt denken die Leute, dass wir das waren! Bitte hilf uns, Jäger, dann schenken wir dir zum Dank einen besonderen Schein.','Un groupe d''usurpateurs fait des siennes sur les Pics brumeux ! Ils se font passer pour nous et notre réputation est compromise. Aidez-nous et vous gagnerez un ticket spécial.','Un grupo se ha hecho pasar por nosotros y ha destrozado los Picos Nublados. ¡La gente creerá que es cosa nuestra! Te lo ruego, cazador: ayúdanos y te daremos un ticket especial de regalo.','Una banda di furfanti è apparsa sulle cime nebbiose e ha devastato tutto spacciandosi per noi! Ora la gente ci odierà! Ti prego, aiutaci! In cambio ti offriremo un buono speciale!','',2700,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010131,'Capcom: The Ruler''s Rage','Capcom: Der Zorn des Regenten','Capcom : Fureur royale','Capcom: la rabia del rey','Capcom: L''ira del sovrano','','Slay an Akantor','Erlege einen Akantor.','Tuer l''Akantor','Abate un Akantor','Uccidi un Akantor','','Event','0','HR',1,0,7,19,50,2400,23100,1200,'None','–','Rien','Ninguno','Nessuno','','A wyvern that once attacked my kingdom has returned, causing the land to tremble beneath it as it strikes without warning. I need your strength, brave hunter. Please slay this beast and restore peace to the land!','Ein Wyvern, der einst mein Reich angriff, ist zurückgekehrt und schlägt ohne Vorwarnung zu. Das Land erzittert vor ihm. Ich brauche deine starke Hand, Jäger. Erschlage die Bestie und stelle den Frieden wieder her.','La wyverne qui a attaqué mon royaume autrefois est de retour. Elle frappe au hasard, sans crier gare. Tuez la bête, Chasseur, et ramenez la paix sur mes terres !','Un wyvern que un día atacó mi reino ha vuelto y ha hecho temblar la tierra con sus golpes. Necesito tu fuerza, noble cazador. Abate a esta bestia y devuelve la paz a la tierra.','Il wyvern che attaccò il mio regno è tornato, colpendo senza preavviso e scuotendo la terra stessa. Ho bisogno del tuo aiuto. Ti prego, uccidi quel mostro e riporta la pace in questa regione!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010132,'Strider: Frozen Mission','Strider: Frostige Mission','Strider : mission polaire','Strider: misión gélida','Strider: Missione glaciale','','Hunt a Deviljho','Erjage einen Dämonjho.','Chasser 1 Deviljho','Caza un Deviljho','Caccia un Deviljho','','Event','0','HR',0,0,7,14,50,2200,21600,1080,'None','–','Rien','Ninguno','Nessuno','','This is headquarters. Your mission: hunt a Deviljho. Your reward, should you prove successful: some sweet duds. Don''t forget: it''s either hunt or be hunted. Now, get to work.','Hier spricht das Hauptquartier. Deine Mission: Erjage einen Dämonjho. Deine Belohnung bei Erfolg: ein paar nette Klamotten. Vergiss nicht: Es heißt jagen oder gejagt werden. Und jetzt an die Arbeit!','Ici le QG. Votre mission si vous l''acceptez : chasser un Deviljho. Votre prime si vous réussissez : des fringues super classe. N''oubliez pas : c''est vous ou lui. Au boulot !','Aquí el cuartel general. Misión: cazar un Deviljho. Recompensa (¡si es que lo consigues!): ropa molona. No te olvides, ¡hay que cazar o ser cazado! ¡A trabajar!','Qui quartier generale. La tua missione: cacciare un Deviljho. Premio: nuovo equipaggiamento. Sta a te decidere se essere cacciatore o preda. Pronto per la missione?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010133,'Moonlit Tryst','Mondlicht-Stelldichein','Rendez-vous au clair de lune','Encuentro bajo la luna','Incontro al chiaro di luna','','Hunt a Silver Rathalos and a Gold Rathian','Erjage einen Silbernen Rathalos und eine Goldene Rathian.','Chasser 1 Rathalos d''argent Chasser 1 Rathian d''or','Caza un Rathalos Plateado y una Rathian Dorada','Caccia un Rathalos argentato e una Rathian dorata','','Event','0','HR',0,0,7,104,50,3200,31800,1770,'None','–','Rien','Ninguno','Nessuno','','The time is ripe! We are having a large banquet tonight! Come forth, you reckless hunters! The moon demands fresh blood! Do what you can to survive this night of madness and revelry!','Die Zeit ist gekommen! Heute Abend halten wir ein großes Bankett ab. Herbei, ihr furcht- losen Jäger! Der Mond verlangt nach frischem Blut! Gebt euer Bestes, um diese Nacht des Wahnsinns zu überleben!','L''heure a sonné ! Ce soir, nous organisons un grand banquet. Venez, intrépides chasseurs ! La lune réclame du sang frais. Essayez de survivre à cette nuit de folles réjouissances !','¡Llegó la hora! Esta noche, tendremos un gran banquete. ¡Venid, cazadores! ¡La luna exige carne fresca! Haced lo posible para sobrevivir a esta noche de locura.','L''ora è giunta! Stanotte daremo un lauto banchetto! Venite avanti, impavidi cacciatori! La luna esige sangue fresco! Fate di tutto per sopravvivere a questa notte di baldoria e follia!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010134,'Raging Twins','Rasende Zwillinge','Jumeaux furibards','Gemelos enrabietados','Gemelli infuriati','','Hunt 2 Rajang','Erjage 2 Rajang.','Chasser 2 Rajang','Caza 2 Rajang','Caccia 2 Rajang','','Event','0','HR',0,0,7,9,50,3100,30300,1730,'None','–','Rien','Ninguno','Nessuno','','H-H-Hey! What are you doing? I absolutely, positively, WOULD NOT go in there, if I were you. There are not one, but TWO Rajang in there. Hey! Did you even here what I said? Two of them, you crazy bat!','Ha-Ha-Halt! Was machst du denn da? Ich würde auf KEINEN FALL da reingehen. Da drin ist nicht nur ein Rajang, sondern ZWEI! Hey, hast du überhaupt gehört, was ich gesagt habe? Zwei davon, du armer Irrer!','Hé ! Qu''est-ce que vous fabriquez ? À votre place, je ne m''aventurerais pas là-dedans... Deux Rajang vous y attendent ! Vous êtes sourd ou quoi ? Ils sont deux, je vous dis !','¡Eh! ¿Qué haces? Yo de ti no entraría ahí de ninguna manera. No hay uno, sino dos Rajang. ¡Eh! ¿Que no me oyes? ¡Hay dos Rajang, chalado!','E-E-Ehi! Che stai facendo? Se fossi in te, non entrerei lì dentro per niente al mondo! Ci sono non uno, ma due Rajang! Ehi! Hai sentito quello che ho detto! Ce ne sono due, incosciente!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010135,'All''s Hair in Glove & War','Die Prinzessin auf dem Handschuh','De mauvais poil','Guantes de pelo','Peli da trattare con i guanti','','Hunt a Rajang','Erjage einen Rajang.','Chasser 1 Rajang','Caza un Rajang','Caccia un Rajang','','Event','0','HR',0,0,7,13,50,2200,21600,980,'Break Rajang''s horns','Rajang-Hörner brechen','Briser les cornes du Rajang','Rompe los cuernos del Rajang','Spezza le corna del Rajang','','I am absolutely LIVID right now. I JUST ordered a set of gloves made from quality hair, when what do I see but even BETTER hair on a Hyper Rajang! I won''t stand for it! Get me that hair!','Ich bin völlig AUSSER mir. GERADE habe ich mir ein paar Handschuhe aus edelstem Haar bestellt, da sehe ich doch an einem Ferus-Rajang noch BESSERES Haar! Das ist untragbar! Hol mir das Haar!','C''est un SCANDALE ! J''ai commandé des gants en fourrure premier choix et que vois-je ? Les poils de cet hyper Rajang sont de bien meilleure qualité ! Rapportez-les-moi !','¡Estoy muy furiosa! Acabo de pedir unos guantes de pelo de calidad, y va y veo un Híper Rajang con un pelo mucho mejor. ¡No puede ser! ¡Quiero ese pelo!','Sono assolutamente furiosa! Ho ordinato un set di guanti fabbricati con pellicce di qualità... Poi ho visto lo splendido pelo del Rajang iper! È migliore! Non posso tollerarlo! Portamelo subito!','',2700,100);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010136,'Fire Emblem: Warding Despair','Fire Emblem: Verzweiflung','Fire Emblem : Fol espoir','Fire Emblem: desesperación','Fire Emblem: Disperazione','','Hunt a Silver Rathalos','Erjage einen Silbernen Rathalos.','Chasser 1 Rathalos d''argent','Caza un Rathalos Plateado','Caccia un Rathalos argentato','','Event','0','HR',0,0,7,1,50,2800,27300,1320,'Wound S.Rathalos''s head & wings','S.Rathalos-Kopf+Flügel verletzen','Blesser le Rath. d''arg. (tête & ailes)','Hiere R. Plateado cabeza/alas','Ferisci testa e ali Rathalos a.','','A silver wyvern is wreaking destruction. It''s horrible... It''s as though we were under the tyranny of the Shadow Dragon once more. Lend us your strength, Hunter, and as reward I will lend you mine!','Ein silberner Drache bringt Zerstörung. Es ist schrecklich! Als ob wir erneut unter der Tyrannei des Schattendrachens stehen würden. Leihe uns deine Kraft, Jäger, und als Belohnung werde ich dir meine leihen!','Une wyverne d''argent sème le chaos. C''est affreux... Comme si nous étions de nouveau sous le joug du Dragon d''ombre. Prêtez-nous main-forte, Chasseur, et je vous aiderai en retour !','Un Rathalos Plateado está sembrando la destrucción. ¡Es como si el Dragón de las Sombras hubiera impuesto de nuevo su tiranía! ¡Préstanos tu fuerza, cazador, y contarás con la mía como recompensa!','Un wyvern argentato sta seminando distruzione. Orribile! Come se il Drago Ombra fosse di nuovo tornato a imporre la sua tirannia... Prestaci la tua forza, cacciatore, e sarai ricompensato.','',2700,130);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010137,'A Golden Moon Rises','Ein goldener Mond geht auf','Le lever de la lune d''or','Bajo una luna dorada','Il sorgere della luna dorata','','Hunt a Gold Rathian','Erjage eine Goldene Rathian.','Chasser 1 Rathian d''or','Caza una Rathian Dorada','Caccia una Rathian dorata','','Event','0','HR',0,0,7,1,50,2800,27300,1320,'Wound G.Rathian''s head & wings','G.Rathian-Kopf+Flügel verletzen','Blesser la Rathian d''or (tête & ailes)','Hiere R. Dorada en cabeza/alas','Ferisci testa e ali Rathian d.','','Maah bah baaaah brah baaaaah bah bah baaaah. Meh meh... Ba-baaah baaaah bah... Braah braah brah bah bah♪ Mah Mah, brah bah bah♪ (Message by Carrier Moofah)','Määh bäh bääääh bräh bäääääh bäh bäh bääääh. Mäh mäh... Bä-bäääh bääääh bäh... Brääh brääh bräh bäh bäh♪ Mäh Mäh, bräh bäh bäh♪ (Nachricht per Träger-Muufah)','Mêêê bê bêêê brê bêêêeê bê bê bêêê. Mê mê... Bê-bêêê bêêêê bê... Brêê brêê brê bê bê♪ Mê mê, brê bê bê♪ (Message du Moofah voyageur)','Maah bah baaaah brah baaaaah bah bah baaaah. Meh meh... Ba-baaah baaaah bah... Braah braah brah bah bah♪ Mah Mah, brah bah bah♪ (Mensaje de Moofah mensajero)','Maaah bah baaah brah baaaaaah bah bah baaaaaah. Meh meh... Ba-baaah baaaaaah bah... Braaah braaah brah bah bah ♪ Mah mah, brah bah bah ♪ (Messaggio riportato da un corriere Moofah)','',2700,130);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010138,'Silver and Gold','Silber und Gold','Argent et or','Oro y plata','Argento e oro','','Hunt a Silver Rathalos and a Gold Rathian','Erjage einen Silbernen Rathalos und eine Goldene Rathian.','Chasser 1 Rathalos d''argent Chasser 1 Rathian d''or','Caza un Rathalos Plateado y una Rathian Dorada','Caccia un Rathalos argentato e una Rathian dorata','','Event','0','HR',0,0,7,17,50,3900,38400,2120,'Mount & topple monster 3 times','3x Monster besteigen + umwerfen','Monter & renverser un monstre 3 fois','Monta y derriba 3 veces al mons.','Cavalca e rovescia mostro x3','','Two Hyper Raths are roaming about in the Forlorn Arena. It is a thing of beauty to see them in their many-colored splendor. I do not think you could ask for a better stage to raise the curtain on this majestic tale of war.','Zwei Ferus-Raths suchen den Verbotenen Turm heim. Ihre Farbenpracht bietet ein wahrhaft ergötzliches Bild. Ich könnte mir keine bessere Bühne für diese hehre Erzählung vom heldenhaften Kampf denken.','Deux hyper Rath rôdent près de la Tour interdite. Vous devriez voir leurs couleurs chatoyantes ! Je doute que vous puissiez trouver mieux pour commencer votre épopée guerrière.','Dos Híper Rath merodean por Forlorn. Es precioso verlos en todo su esplendor multicolor. Creo que no hay mejor escenario en el que subir el telón para este combate majestuoso.','Due Rath iper si aggirano per l''arena desolata. I loro splendidi toni variopinti sono uno spettacolo. Non esiste scena migliore su cui aprire il sipario di una maestosa epopea guerresca.','',2700,210);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010139,'Okami: Most Wanted','Okami: Dringend gesucht','Okami : avis de recherche','Okami: se busca','Okami: Ricercato','','Hunt a Malfestio','Erjage einen Malfestio.','Chasser 1 Malfestio','Caza un Malfestio','Caccia un Malfestio','','Event','0','HR',0,0,7,6,50,1100,10200,580,'Wound Malfestio''s head & wings','Malfestio-Kopf+Flügel verletzen','Blesser le Malfestio (tête & ailes)','Hiere Malfestio en cabeza/alas','Ferisci testa e ali Malfestio','','Some monster that looks just like Nechku is causin'' a mess out there... Heh, what''re you sharpening your weapon for? You think you can take it, eh? Well then, show me what you got, Hunter!','Ein Monster, das aussieht wie Nechku, richtet da draußen mächtig Unordnung an... Hey, warum schärfst du deine Waffe? Glaubst, du kannst es mit ihm aufnehmen, was? Na, dann zeig mal, was du draufhast, Jäger!','Un monstre qui ressemble à à s''y méprendre à Nechku sème le chaos dans le coin. Pourquoi vous affûtez votre lame ? Vous vous croyez à la hauteur ? Ma foi, j''ai hâte de voir ça, Chasseur !','Un monstruo igualito que Nechku está causando un estropicio ahí fuera... ¿Y tú, qué haces afilando el arma? ¿Crees que podrás contra él? ¡Pues adelante, cazador, demuestra de qué estás hecho!','Un mostro simile a Nechku sta causando problemi lì fuori... Eheh, perché stai affilando la tua arma? Non vorrai mica affrontarlo? Va bene, fammi vedere di cosa sei capace, cacciatore!','',2700,60);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010140,'Secret Nibelsnarf Hunt','Geheime Nibelsnarf-Jagd','Chasse au Nibelsnarf','Caza secreta de Nibelsnarf','Caccia segreta al Nibelsnarf','','Hunt a Nibelsnarf','Erjage einen Nibelsnarf.','Chasser 1 Nibelsnarf','Caza un Nibelsnarf','Caccia un Nibelsnarf','','Event','0','HR',0,0,7,5,50,1000,9300,620,'Wound Nibelsnarf''s front legs','Nibelsnarf-Vorderbeine verletzen','Blesser les pattes avant du Nibelsnarf','Hiere Nibelsnarf en patas del.','Ferisci le zampe del Nibelsnarf','','Day in and day out, I dream of a glorious victory against a monster. Alas, father is not having any of it. *sigh* Well, don''t let that Hyper Nibelsnarf in the Dunes go to waste...','Tagein, tagaus träume ich von einem ruhmreichen Sieg über ein Monster. Aber Vater will nichts davon hören. *seufz* Sorge wenigstens du dafür, dass der Ferus-Nibelsnarf nicht umsonst die Dünen heimsucht...','Je rêve d''une glorieuse victoire face à un monstre. Hélas, mon père refuse d''en entendre parler. Je vous laisse donc l''honneur de chasser l''hyper Nibelsnarf des Dunes...','Cada día sueño con obtener una gloriosa victoria sobre un monstruo. Pero mi padre no me deja. Sea como sea, no dejes que se escape el Híper Nibelsnarf de las Dunas.','Un giorno dopo l''altro, sogno di sconfiggere con valore un mostro. Ma ahimè, mio padre non sente ragioni. *sigh* Beh, pensa tu al Nibelsnarf iper che s''aggira tra le dune...','',2700,60);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010141,'The Obvious Choice...','Die logische Wahl...','Un choix évident...','La elección obvia...','La scelta più ovvia','','Hunt a Daimyo Hermitaur','Erjage einen Daimyo Hermitaur.','Chasser 1 Hermitaur Daimyo','Caza un Daimyo Hermitaur','Caccia un Daimyo Hermitaur','','Event','0','HR',0,0,7,5,50,900,8700,500,'Break Daimyo''s claws & shell','Daimyo-Klauen+Panzer brechen','Briser le Daimyo (pinces & carapace)','Rompe garras/caparazón Daimyo','Rompi artigli e corazza Daimyo','','Shhh... Don''t make any noise! We want to observe it in its natural habitat undisturbed... Ah, what a unique specimen. I doubt we''ll ever find another one like it!','Pssst... Sei ganz leise! Wir wollen ihn ganz ungestört in seinem natürlichen Lebens- raum beobachten... Was für ein einzigartiges Exemplar! Ich bezweifle, dass wir noch einmal einen wie ihn finden!','Chut ! Ne faites pas de bruit ! Nous tenons à l''étudier dans son habitat naturel, sans la moindre perturbation... Ah, quel beau spécimen ! Il est unique en son genre !','¡Shhh! No hagas ruido... Queremos observarlo en su hábitat natural. ¡Oh, es un espécimen único! Dudo que encontremos otro igual.','Shhh... Non fare rumore! Dobbiamo osservarlo mentre vaga indisturbato nel suo habitat naturale... Dubito che troveremo un altro esemplare del genere!','',2700,50);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010142,'It Would Be a Shame If...','Es wäre eine Schande, wenn...','Quel dommage...','Sería una pena que...','Sarebbe un peccato se...','','Hunt a Shogun Ceanataur','Erjage einen Shogun Ceanataur.','Chasser 1 Ceanataur Shogun','Caza un Shogun Ceanataur','Caccia uno Shogun Ceanataur','','Event','0','HR',0,0,7,8,50,1100,10800,680,'Break Shogun''s claws & shell','Shogun-Klauen+Panzer brechen','Briser le Shogun (pinces & carapace)','Rompe garras/caparazón Shogun','Spezza artigli e corazza Shogun','','Would you just look at it... Take it all in... To think, a specimen such as this has gone THIS long without being hunted. Well, I can only think of one thing to do... Go get it Hunter!','Sieh ihn dir nur an... Diese Erscheinung... Wenn man sich vorstellt, dass ein solches Exemplar SO lange existiert hat, ohne gejagt zu werden. Dazu fällt mir nur eines ein... Hol ihn dir, Jäger!','Admirez un peu ça... Quand je pense que cela fait des années que personne n''a chassé ce fabuleux spécimen... Il n''y a qu''une chose à faire. Capturez-le, Chasseur !','Míralo... Fíjate bien... Y pensar que un espécimen así lleva un montón de tiempo sin ser cazado. Solo se me ocurre una forma de solucionarlo: ¡a por él, cazador!','Ammiralo! Contempla le sue meravigliose sembianze! E pensare che un esemplare del genere non viene cacciato da tempo immemore! Beh, una soluzione esiste: corri a sconfiggerlo tu!','',2700,70);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010143,'Not Stepping One Foot...','Keinen Schritt näher...','Pas un pas de plus','Ni un paso más...','Non muoverti di un passo','','Hunt a Brachydios','Erjage einen Brachydios.','Chasser 1 Brachydios','Caza un Brachydios','Caccia un Brachydios','','Event','0','HR',0,0,7,12,50,1900,18300,1070,'Wound Brachy''s head & front legs','Brachy-Kopf+Vorderbeine verletzen','Blesser le Brachy. (tête & pattes avt)','Hiere Brach cabeza/patas del.','Ferisci testa e zampe ant. Brachy','','What was THAT all about? Look, I know ol'' Brachy, and what I just saw was totally weird. Something had it all worked up, and I''m not even getting close enough to find out what it was. That''s what I''ve got YOU for!','Was war DAS denn? Weißt du, ich kenne den alten Brachy, aber das war echt abgedreht. Er war völlig aufgebracht! Ich geh da nicht nah genug ran, um herauszufinden, was los ist. Dafür hab ich ja DICH!','Bon sang, mais qu''est-ce qui se passe ? Je connais bien le vieux Brachy et il n''est pas dans son état normal. Approchez-vous de lui pour en savoir plus. Je compte sur vous !','¿Qué ha sido eso? Mira, sé cómo las gasta el Brachy, y lo que he visto es muy raro. Algo lo ha excitado de mala manera, pero no tengo ni idea de lo que puede ser. ¡Te toca averiguarlo!','Ma che succede? Io conosco i Brachydios, ma quello che ho visto è molto strano. Sembra agitatissimo, al punto che non sono neanche riuscita ad avvicinarmi per capire cosa aveva. Per questo servi TU!','',2700,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010144,'Picturesque Encounter','Malerische Begegnung','Rencontre pittoresque','Encuentro pintoresco','Incontro pittoresco','','Hunt a Zinogre','Erjage einen Zinogre.','Chasser 1 Zinogre','Caza un Zinogre','Caccia uno Zinogre','','Event','0','HR',0,0,7,14,50,1700,16500,850,'Wound Zinogre''s head & front legs','Zinogre-Kopf+Vorderbein verletzen','Blesser le Zinogre (tête & pattes avt)','Hiere Zinogre cabeza/patas del.','Ferisci testa e zampa ant. Zinogre','','Picture it: a Hyper Zinogre in the Frozen Seaway, locked in an epic struggle against a powerful hunter... Wouldn''t that make an amazing piece? Of course it would! Go make it happen, wouldja?','Mal dir das aus: Ein Ferus- Zinogre im Frostseeweg, im verzweifelten Kampf mit einem mächtigen Jäger... Was für ein Stoff für ein Gemälde! Würdest du bitte dafür sorgen, dass das tatsächlich stattfindet?','Imaginez un peu : un hyper Zinogre pris dans la Mer de glace dans un combat épique face à un maître chasseur ! Merveilleux, pas vrai ? Il ne tient qu''à vous de concrétiser ce rêve !','Imagina: un Híper Zinogre en el Canal Helado, sumido en una lucha épica contra un gran cazador. ¿No sería eso una buena historia? ¡Pues claro que sí! ¡Venga, a ponerla en práctica!','Immagina: uno Zinogre iper nel canale ghiacciato, teso in una strenua lotta con un possente cacciatore... Non sarebbe un''opera incredibile? Certo che sì! Vai e trasforma questo sogno in realtà!','',2700,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010145,'Absolute Power','Absolute Macht','Pouvoir absolu','Poder absoluto','Potere assoluto','','Hunt a Tigrex','Erjage einen Tigrex.','Chasser 1 Tigrex','Caza un Tigrex','Caccia un Tigrex','','Event','0','HR',0,0,7,103,50,1900,18300,880,'Wound Tigrex''s claw & tail','Tigrex-Klaue+Schwanz verletzen','Blesser le Tigrex (griffe & queue)','Hiere Tigrex en garra/cola','Ferisci artiglio e coda Tigrex','','As if those things could get any more aggressive, this Tigrex seems especially amped up. Our walls are useless against them. You must stop it before it lays waste to everything in its path!','Diese Dinger sind ja sowieso schon extrem aggressiv, aber dieser Tigrex scheint ganz besonders kräftig zu sein. Unsere Mauern sind dagegen wirkungslos. Halte ihn auf, bevor er hier alles verwüstet!','Comme si ces créatures n''étaient pas déjà assez agressives comme ça, ce Tigrex semble survolté. Nos murs n''y résisteront pas. Arrêtez-le avant qu''il ne sème la destruction.','Aunque parezca mentira, este Tigrex es más agresivo de lo normal. Nuestros muros son inútiles. ¡Acaba con ese bicho antes de que él acabe con todo a su paso!','Questo Tigrex sembra molto su di giri... Come se la ferocia di quelle bestiacce non fosse già sufficiente! Le nostre mura non reggeranno. Devi fermarlo prima che semini il caos!','',2700,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010146,'A Land Draped in Fantasy','Ein Land der Fantasie','Une contrée fabuleuse','Tierra cubierta de fantasía','Una terra velata di fantasia','','Slay a Kirin','Erlege einen Kirin.','Tuer le Kirin','Abate un Kirin','Uccidi un Kirin','','Event','0','HR',1,0,7,17,50,1800,17400,840,'None','–','Rien','Ninguno','Nessuno','','Is there anybody willing to face the majestic Kirin that has just manifested in the Forlorn Arena? Nothing has prepared you for the likes of this beast. If you wish to experience a land filled with dreams, please step forth.','Wer ist bereit, dem majestä- tischen Kirin entgegenzutreten, der beim Verbotenen Turm sein Unwesen treibt? Dieses Geschöpf entzieht sich jeder Erfahrung. Wer ein Land voller Träume erleben will, trete vor.','Qui osera se mesurer au majestueux Kirin qui vient d''apparaître à la Tour interdite ? Si vous voulez braver les dangers d''un monde onirique, faites un pas en avant.','¿Alguien quiere enfrentarse al majestuoso Kirin que merodea por Forlorn? Nada te ha preparado para algo así, de modo que si quieres sentir una tierra llena de sueños, da un paso adelante.','Qualcuno ha il coraggio di affrontare il maestoso Kirin comparso nell''arena desolata? È una bestia diversa da qualsiasi altra. Se volete immergervi in una terra di sogni, fatevi avanti.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010147,'The Formless One','Der Gestaltlose','L''insaisissable','Criatura sin forma','L''essere amorfo','','Slay a Chameleos','Erlege einen Chameleos.','Tuer 1 Chameleos','Abate un Chameleos','Uccidi un Chameleos','','Event','0','HR',1,0,7,17,50,2200,21600,1200,'None','–','Rien','Ninguno','Nessuno','','Numerous observers have found signs of a Chameleos around the Forlorn Arena. The Guild has also received reports of people being hit with poison from an unseen foe. Hunter, please investigate these strange incidents.','In der Gegend des Verbotenen Turms häufen sich die Anzeichen auf einen Chameleos. Zudem erhält die Gilde Berichte über Vergiftungen durch einen unsichtbaren Gegner. Bitte untersuche diese Vorkommnisse.','De nombreux observateurs ont repéré les traces d''un Chameleos près de la Tour interdite et la Guilde a eu vent d''attaques toxiques non identifiées. À vous de mener l''enquête, Chasseur.','Varios observadores indican que un Chameleos merodea por Forlorn. El gremio tiene informes de gente envenenada por un enemigo invisible. Cazador, investiga estos extraños incidentes.','Vari esploratori hanno scorto tracce di Chameleos vicino all''arena desolata. La Gilda ha saputo di persone avvelenate da un nemico invisibile. Ti prego, indaga su questi strani incidenti.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010148,'The White God Returns','Rückkehr des weißen Gottes','Le retour du dieu d''ivoire','Vuelve el dios blanco','Il ritorno del dio bianco','','Slay an Ukanlos','Erlege einen Ukanlos.','Tuer l''Ukanlos','Abate un Ukanlos','Uccidi un Ukanlos','','Event','0','HR',1,0,7,20,50,2400,23100,1200,'None','–','Rien','Ninguno','Nessuno','','Lately, in the deepest reaches of the polar region, the earth has been shaking tremendously. The people fear the very ground will open up and swallow them whole. Their fears may become reality if something is not done.','In jüngster Zeit erbebte in den Tiefen der Polarregion die Erde aufs Heftigste. Die Leute dort fürchten, der Boden werde sich auftun und sie verschlingen. Wenn nichts unternommen wird, könnte sich das bewahrheiten.','Dernièrement, la terre s''est mise à trembler dans les confins polaires et les gens craignent d''être engloutis dans les abîmes. Si on ne réagit pas, cela risque fort de finir ainsi.','En los rincones más ocultos de la región polar, la tierra se agita con fuerza. La gente teme que el suelo se abra y les engulla. Sus miedos pueden materializarse si nadie hace nada.','Nei recessi della regione polare, una tremenda scossa ha investito la terra. Temono tutti che si spalancherà una voragine. Se non facciamo qualcosa, le loro paure potrebbero diventare realtà.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010149,'Evil Descendeth Upon Us','Das Böse steigt herab','L''avènement des ténèbres','La llegada del mal','Il male incombe su di noi','','Slay an Amatsu','Erlege einen Amatsu.','Tuer 1 Amatsu','Abate un Amatsu','Uccidi un Amatsu','','Event','0','HR',1,0,7,18,50,2400,23100,1200,'None','–','Rien','Ninguno','Nessuno','','From the heavens descended a being bathed in light from a sacred mountain. With it, came a storm that plunged the world into calamity. Someone, deliver us from this evil, lest we exist in darkness forever.','Ein Wesen stieg vom Himmel herab, das in das Licht eines heiligen Berges gehüllt war. Mit ihm kam ein Sturm, der die Welt ins Verderben stürzte. Jemand befreie uns von diesem Übel und der ewigen Finsternis.','Des cieux descendit un être ceint d''un halo lumineux en provenance d''une montagne sacrée, amenant avec lui une tempête qui plongea le monde dans le chaos. Pitié, délivrez-nous du mal.','De los cielos bajó un ser iluminado por una montaña sagrada. Y consigo trajo una tormenta que sumió el mundo en el caos. Alguien debe librarnos de este mal o viviremos en la oscuridad.','Dai cieli è disceso un essere intriso della luce di una montagna sacra. Con esso sono giunte tempeste e rovina. Qualcuno ci liberi da questo male, o vivremo per sempre immersi nell''oscurità.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010150,'Where Gods Fear to Tread','Die Götter sind vorsichtig','Là où les dieux ne vont pas','Donde los dioses tiemblan','Il terrore degli dei','','Slay an Alatreon','Erlege einen Alatreon.','Tuer l''Alatreon','Abate un Alatreon','Uccidi un Alatreon','','Event','0','HR',1,0,7,19,50,2600,25200,1800,'None','–','Rien','Ninguno','Nessuno','','Long time, no see, hunters. I''ve prepared a test for you all. This day your opponent is an Alatreon. Which will emerge victorious in battle, you? Or the beast feared by the very heavens themselves!?','Lange nicht gesehen, liebe Jäger. Ich habe eine Prüfung für euch vorbereitet. Euer heu- tiger Gegner ist ein Alatreon. Wer wird am Ende siegreich sein? Ihr oder die Bestie, die selbst der Himmel fürchtet?','Cela faisait longtemps, Chasseurs. Je vous ai préparé un test au cours duquel vous devrez affronter un Alatreon. Parviendrez-vous à dompter le fléau des cieux ?','¡Cuánto tiempo, cazadores! Os he preparado una prueba. Vuestro rival hoy será un Alatreon. ¿Quién saldrá victorioso en la batalla? ¿Vosotros o esta bestia temida hasta en el cielo?','Ne è passato di tempo, cacciatori. Ho deciso di sottoporvi una prova. Oggi affronterete un Alatreon. Chi avrà la meglio? Voi? O l''orribile bestia temuta persino dagli dei?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010151,'Harbinger of Death','Vorbote des Todes','Émissaire funeste','Presagio de muerte','Araldo di morte','','Slay Nakarkos','Erlege einen Nakarkos.','Tuer le Nakarkos','Abate al Nakarkos','Uccidi un Nakarkos','','Event','0','HR',1,0,7,21,50,2600,25200,1440,'None','–','Rien','Ninguno','Nessuno','','I''ve received an urgent request from the Wycademy. It seems that another Nakarkos has been spotted in Wyvern''s End. I''d like to avoid a situation like before. Hunter you must face and defeat that Nakarkos!','Ich habe eine dringende Anfrage der Wykademie erhalten. Offen- bar wurde in Wyvernsend ein weiterer Nakarkos gesichtet. Ich will nicht noch mal so eine Situation wie kürzlich. Du musst den Nakarkos ausschalten!','J''ai reçu une demande urgente de la Wycadémie. Un autre Nakarkos a été aperçu au Cimetière des wyvernes. Étouffez le problème dans l''œuf en terrassant cette créature !','Tengo una petición urgente de la Wycademia. Al parecer se ha visto otro Nakarkos en la Fosa Wyvern. Quiero evitar una situación como la anterior, así que debes acabar con ese Nakarkos.','Ho ricevuto una richiesta urgente dalla Wyccademia. A quanto pare, nella terra dei wyvern è comparso un nuovo Nakarkos. Dobbiamo impedire che si verifichi un altro pasticcio. Va'' e uccidilo!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010152,'No Party Poopers!','Nichts für Partymuffel!','Dehors, les rabat-joie !','¡Nada de aguafiestas!','Niente guastafeste!','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,2400,23400,1240,'None','–','Rien','Ninguno','Nessuno','','I''m throwing a party featuring some of my very favorite pets. I''m bringing a Kecha Wacha, Blangonga, and a Rajang to the fray! Don''t come to this throwdown if you ain''t gonna throw down!','Ich schmeiße eine Party mit einigen meiner Lieblinge. Ich bringe einen Kecha Wacha, einen Blangonga und einen Rajang mit! Lass dich nur blicken, wenn du mit ihnen auf Augenhöhe bist!','J''organise une soirée avec certains de mes animaux préférés. Un Kecha Wacha, un Blangonga et un Rajang ! Inutile de venir si vous n''êtes pas prêt à en découdre !','Voy a dar una fiesta con algunos de mis animales preferidos: un Kecha Wacha, un Blangonga y un Rajang. ¡No vengas si no tienes intención de pelear!','Sto organizzando una festa con alcuni dei miei mostri preferiti. Lancerò nella mischia un Kecha Wacha, un Blangonga e un Rajang! Se vuoi essere dei nostri, devi assolutamente combattere!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010153,'The Five Kings of Destruction','Die fünf Könige der Zerstörung','Les rois de la destruction','5 reyes de la destrucción','I cinque re della rovina','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,17,50,6400,63600,3230,'None','–','Rien','Ninguno','Nessuno','','And so it begins! Having honed their fangs over the years, five masters of disaster have just appeared near the Forlorn Arena. Will they prove to be the end of you, or will you overcome them and be the ultimate hunter!?','Und so beginnt es. Nach Jahren, in denen sie ihre Reißzähne schärften, sind fünf Meister der Verwüstung beim Verbotenen Turm erschienen. Sind sie dein Ende oder bezwingst du sie und wirst zum ultimativen Jäger?','Alors voilà... Après avoir affûté leurs crocs pendant des années, 5 maîtres du chaos ont débarqué à la Tour interdite. Réussirez- vous à les vaincre ou auront-ils raison de vous ?','¡Llegó el día! Tras afilar los colmillos durante años, 5 maestros del desastre han aparecido cerca de Forlorn. ¿Será este tu final o te coronarás como el cazador definitivo?','L''ora è giunta! Dopo aver affilato le zanne per anni, cinque sovrani della distruzione sono comparsi nei pressi dell''arena desolata. Sarà la tua fine, o riuscirai a diventare una leggenda?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010154,'How It''s Done','So wird''s gemacht','L''art et la manière','Cómo se hace','Pronti alla lezione','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,8,50,3200,31800,1830,'None','–','Rien','Ninguno','Nessuno','','My home''s constantly in danger, meaning lot''s of kids grow up to be hunters. Unfortunately, there aren''t any master hunters around to train the newcomers how to hunt Hyper Monsters. Care to show them the ropes?','Meine Heimat ist ständig in Gefahr. Deswegen werden viele der Kinder Jäger. Leider gibt es bei uns keine Meisterjäger, die den Neulingen zeigen können, wie man Ferus-Monster jagt. Würdest du vielleicht?','Le danger est omniprésent dans la région et seuls les maîtres chasseurs savent s''y prendre avec les hyper monstres. Enseignez les ficelles du métier à nos apprentis chasseurs !','Mi aldea está siempre en peligro, así que muchos niños se hacen cazadores. Eso sí, faltan maestros que enseñen a cazar Híper monstruos. ¿Te importaría enseñarles lo básico?','La mia patria è costantemente in pericolo e molti ragazzi scelgono di diventare cacciatori, ma abbiamo pochi maestri in grado di istruire le reclute sui mostri iper. Puoi darci una dimostrazione?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010155,'Unbridled Rage','Ungezügelte Wut','Rage incontrôlée','Rabia desenfrenada','Ira sfrenata','','Hunt 2 Deviljho','Erjage 2 Dämonjho.','Chasser 2 Deviljho','Caza 2 Deviljho','Caccia 2 Deviljho','','Event','0','HR',0,0,7,9,50,3100,30300,1730,'None','–','Rien','Ninguno','Nessuno','','As a hunter, you gotta know your limit. For me, ol'' Deviljho is mine. That crazy pickle just about did me in on more than one occasion. I heard some nutty hunter is gonna take on two of ''em today.','Ein Jäger muss seine Grenzen kennen. Bei mir ist beim Dämonjho Schluss. So einer hätte mich schon ein paar mal beinahe erledigt. Angeblich will es irgendein irrer Jäger heute mit zwei davon aufnehmen.','Tout chasseur doit avoir conscience de ses limites. La mienne, c''est le Deviljho. Il a bien failli m''avoir plus d''une fois. On raconte qu''un forcené va en affronter deux à la fois.','Como cazador, debes conocer tu límite. El mío es el Deviljho. Ese bicho loco casi acaba conmigo en más de una ocasión. Al parecer, un chiflado va a intentar cargarse a dos hoy.','Un vero cacciatore deve conoscere i propri limiti. Le mie bestie nere sono i folli Deviljho. Quei furfanti mi hanno già battuto più volte, ma pare che qualche svitato voglia cacciarne due!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010156,'Insanity and Destruction','Irrsinn und Zerstörung','Folie destructrice','Locura y destrucción','Follia e distruzione','','Hunt a Deviljho and a Rajang','Erjage einen Dämonjho und einen Rajang.','Chasser 1 Deviljho Chasser 1 Rajang','Caza un Deviljho y un Rajang','Caccia un Deviljho e un Rajang','','Event','0','HR',0,0,7,11,50,3100,30300,1730,'None','–','Rien','Ninguno','Nessuno','','Recently, I''ve been wondering about something... You hunters always carry out my requests without hesitation, but I am curious as to the extent of your strength. I''ve prepared a little test to see what you can do!','Ich hab vor Kurzem über etwas nachgedacht... Ihr Jäger führt meine Aufträge ja immer ohne Zögern aus, aber ich wüsste zu gern, wie stark ihr wirklich seid. Deshalb habe ich einen kleinen Test vorbereitet!','Quelque chose me turlupine. Vous, les chasseurs, menez toujours vos quêtes à bien, mais je m''interroge sur les limites de vos capacités. J''ai donc préparé un test pour éprouver vos talents !','Hace poco se me ocurrió algo: los cazadores siempre aceptáis mis peticiones sin dudar, pero me gustaría saber la magnitud de vuestra fuerza. He hecho una prueba para ver qué sabes hacer.','Stavo pensando... Voi cacciatori mi obbedite sempre senza esitazione, ma io voglio capire fin dove si spinge la vostra forza. Ho preparato un test per vedere cosa sai fare!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010157,'Volcano Item Delivery','Lieferung Vulkan-Objekt','Livraison volcanique','Entrega de objeto del Volcán','Consegna vulcanica','','Obtain 7,000 Wycademy Points','Sammle 7.000 Wykademie-Punkte.','Obtenir 7 000 pts Wycadémie','Obtén 7.000 puntos de la Wycademia','Ottieni 7000 punti Wyccademia','','Event','0','HR',3,0,7,8,50,1800,18000,1000,'None','–','Rien','Ninguno','Nessuno','','Hey, you there! Yo know what they say about idle hands... Don''t just sit on ''em, go to the Volcano and gather us some Account Items. Don''t matter what, just make sure you put ''em in the Delivery Box!','Du da! Du weißt ja, was man über Müßiggang sagt... Also sitz hier nicht rum, sondern geh zum Vulkan und sammle ein paar Konto-Objekte für uns. Ganz egal, was. Hauptsache, du packst sie in die Lieferkiste!','Vous, là-bas ! Le travail, c''est la santé. Filez au Volcan et rapportez-nous des objets bonus. Peu importe lesquels, déposez-les simplement dans la boîte de livraison !','¡Eh, tú! Pareces aburrido. No te quedes sentado y ve al Volcán a buscar objetos de cuenta. Pase lo que pase, ponlos en la caja de entregas.','Ehi, tu! Mai restare con le mani in mano... Datti da fare, corri al vulcano e procuraci degli oggetti punti. Non importa quali. È sufficienti che li porti alla cassa consegne!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010158,'Anything For a Buck','Geschäft ist Geschäft','La soif de l''or','Todo por la pasta','In nome del denaro','','Hunt a Great Maccao','Erjage einen Groß-Maccao.','Chasser 1 Grand Maccao','Caza un Gran Maccao','Caccia un Gran Maccao','','Event','0','HR',0,0,6,101,50,500,4800,250,'None','–','Rien','Ninguno','Nessuno','','One of my biggest clients came to me with a request. She saw a Great Maccao and just HAD to have its hide as a throw rug... Weird, but business is business. Help a guy out?','Eine meiner besten Kundinnen kam mit einer Bitte zu mir. Sie hat einen Groß-Maccao gesehen und MUSS nun einfach sein Fell als Überwurf haben! Eigenartig, aber was will man machen? Hilfst du mir?','Une de mes plus grosses clientes m''a demandé de lui procurer la peau d''un Grand Maccao. Drôle d''idée, mais les affaires sont les affaires. Je peux compter sur votre aide ?','Una de mis mejores clientas me ha hecho una petición. Ha visto un Gran Maccao y quiere una alfombra con su pelo. Es raro, pero así es el negocio. ¿Te importaría ayudarme?','Una delle mie migliori clienti se n''è uscita con una richiesta. Ha visto un Gran Maccao e pretende la sua pelle per farne un tappeto. Strano, ma gli affari sono affari. Mi dai una mano tu?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010159,'A Mammoth Mistake','Ein gigantischer Fehler','Une erreur colossale','Un fallo enorme','Un mastodontico errore','','Hunt a Gammoth','Erjage einen Gammoth.','Chasser 1 Gammoth','Caza un Gammoth','Caccia un Gammoth','','Event','0','HR',0,0,7,14,50,1500,15000,800,'None','–','Rien','Ninguno','Nessuno','','Gammoths are so cute and cuddly! Least I thought so ''til I tried to hug one. It attacked me and now I can''t even move my arms. I just wanted some wooley lovin''! Why''s it gotta be so cold?','Gammoths sind so süß und knuddelig! Dachte ich jeden- falls, bis ich einen umarmen wollte. Er griff an, und jetzt kann ich noch nicht mal mehr die Arme bewegen! Und dabei wollte ich doch nur kuscheln!','Le Gammoth est trop chou ! Enfin, c''est ce que je croyais jusqu''à ce qu''il m''attaque alors que je voulais juste lui faire un gros câlin pour me réchauffer !','Los Gammoth son preciosos. O eso pensaba yo hasta que intenté abrazar uno. Me atacó y ya no puedo mover los brazos. ¡Yo solo quería un poco de amor esponjoso! ¿Por qué hace tanto frío?','Credevo che i Gammoth fossero dolci e teneri... Poi ho provato ad abbracciarne uno. Lui mi ha attaccato e ora non riesco più muovere le braccia. Io volevo solo un po'' d''affetto! Che crudeltà!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010161,'The Untamed Mountains','Die ungezähmten Berge','Les Montagnes indomptées','Las montañas indomables','Le montagne inesplorate','','Hunt a Duramboros and a Gammoth','Erjage einen Duramboros und einen Gammoth.','Chasser 1 Duramboros et 1 Gammoth','Caza un Duramboros y un Gammoth','Caccia un Duramboros e un Gammoth','','Event','0','HR',0,0,7,9,50,2100,20700,1190,'Mount & topple monster 3 times','3x Monster besteigen + umwerfen','Monter & renverser un monstre 3 fois','Monta y derriba 3 veces al mons.','Cavalca e rovescia mostro x3','','One is a great peak covered in moss and spins like a tornado, while the other is covered in snow and buffeted by gusts of wind. Conquer these two peaks and you will never fear another mountain again.','Der eine ist groß, von Moos bedeckt und umgeben von Wirbeln wie ein Tornado, der andere ist schneebedeckt und vom Wind gepeitscht. Wer diese beiden bezwingt, wird keinen Berg mehr fürchten.','Le premier pic est couvert de mousse et vertigineux. Le second est enneigé et battu par les vents. Prenez d''assaut ces deux sommets et vous pourrez soulever des montagnes.','Un pico está cubierto de musgo y gira como un tornado; el otro está nevado y es sacudido por rachas de viento. Conquista ambos picos y ya no temerás a ninguna montaña.','Una vetta è ricoperta di muschio e vortica come un tornado, l''altra dorme sotto un manto di neve ed è battuta da raffiche di vento. Scala queste due cime e non temerai più alcuna montagna.','',2400,120);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010162,'Ghosts ''n Goblins: Seeing Red','Ghosts ''n Goblins: Rot vor Augen','Ghosts ''n Goblins : alerte rouge','Ghosts ''n Goblins: rojo','Ghosts ''n Goblins: Vedo rosso!','','Hunt a Rathalos','Erjage einen Rathalos.','Chasser 1 Rathalos','Caza un Rathalos','Caccia un Rathalos','','Event','0','HR',0,0,7,8,50,1600,15300,850,'Mount and topple monster twice','2x Monster besteigen + umwerfen','Monter & renverser le monstre 2 fois','Monta y derriba 2 veces al mons.','Cavalca e rovescia mostro x2','','I have to save the princess, but that RED demon is in my way again... If only I weren''t turned into a frog, I''d spear him in a second! If anyone out there can help me out, I''ll make sure you''re rewarded!','Ich muss die Prinzessin retten, aber dieser ROTE Dämon steht mir schon wieder im Weg... Wäre ich nicht in einen Frosch verwandelt worden, könnte ich ihn im Nu erledigen. Wer auch immer mir hilft, wird belohnt!','Je dois sauver la princesse, mais ce monstre ROUGE me barre la route. Si on ne m''avait pas changé en crapaud, je le pourfendrais sans coup férir. Aidez-moi, vous ne le regretterez pas.','Debo rescatar a la princesa, pero ese demonio rojo vuelve a interponerse en mi camino... ¡Si no me hubieran transmutado en rana lo alancearía presto! ¡Si alguien puede ayudarme, juro que será recompensado!','Devo salvare la principessa, ma quel demone rosso è di nuovo tra i piedi. Se non mi avessero trasformato in una rana, lo trafiggerei con la mia lancia in un secondo! Aiutami e otterrai un premio!','',2700,90);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010163,'The Best of the Best','Der Beste der Besten','La crème de la crème','Lo mejor de lo mejor','Il meglio del meglio','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,5900,58800,2970,'None','–','Rien','Ninguno','Nessuno','','There is nothing left to say. If you wish to claim the title of the strongest hunter, you must go forth and forge your own path to glory. Destiny waits for no one, Hunter!','Es gibt nichts mehr zu sagen. Willst du dir den Titel als stärkster Jäger erwerben, musst du dir deinen eigenen Pfad zu Ruhm und Ehre bahnen. Das Schicksal wartet auf niemanden, Jäger!','Tout est dit, il n''y a rien à ajouter. Si vous voulez décrocher le titre de Chasseur suprême, vous devrez vous faire un nom. Chacun forge son destin, Chasseur !','No hay nada más que decir. Si quieres el título de cazador más fuerte, debes forjarte tu propio camino a la gloria. ¡El destino no espera a nadie, cazador!','Non c''è nient''altro da dire. Se vuoi assurgere all''olimpo dei cacciatori, devi andare avanti e forgiare il tuo cammino verso la gloria. Il destino non aspetta nessuno!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010164,'The Greatest Hunter','Der beste Jäger','Roi des chasseurs','El gran cazador','Il cacciatore migliore','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,5400,53100,2860,'None','–','Rien','Ninguno','Nessuno','','Want to be a part of hunting history? Think you got what it takes to face all of the most popular monsters in a row? Prove yourself and your name will echo within the hall of legends!','Möchtest du in die Jagd- geschichte eingehen? Hast du das Zeug dazu, dich nach- einander den beliebtesten Monstern zu stellen? Bewähre dich, und dein Name wird ewig in der Ruhmeshalle erklingen!','Vous voulez entrer dans la légende de la chasse ? Vous pensez être capable de terrasser les monstres les plus populaires ? Faites vos preuves et votre nom marquera les mémoires !','¿Quieres pasar a la historia de la caza? ¿Te atreverías a luchar con los monstruos más famosos uno tras otro? Demuestra lo que vales y tu nombre será recordado en múltiples leyendas.','Vuoi lasciare un segno nella storia? Credi di avere le carte in regola per cacciare uno dopo l''altro tutti i mostri più celebri? Dai prova della tua forza e il tuo nome diverrà materia di leggenda!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010165,'Retreat Run-in','Zerschlagene Zuflucht','Havre infesté','Ensayo de retirada','Ritirata dal ritiro','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,15,50,2700,26400,1520,'None','–','Rien','Ninguno','Nessuno','','I set up this place to celebrate the health and happiness of my beloved children, but it''s been overrun by monsters! I need you to go out there and strike them down with the ferocity of a dragon!','Ich habe diesen Ort errichtet, um die Gesundheit und das Glück meiner geliebten Kinder zu feiern, aber Monster haben ihn überrannt! Du musst sie mit der Unbarmherzigkeit eines Drachen bestrafen!','J''ai aménagé ce sanctuaire en l''honneur de ma noble progéniture, mais il est infesté de monstres ! Je compte sur vous pour les pourfendre avec la férocité d''un auguste dragon !','Creé este lugar para honrar la salud y felicidad de mis hijos, pero está plagado de monstruos. ¡Quiero que vayas hasta allí y les golpees con la ferocidad de un dragón!','Ho organizzato una festa per celebrare la buona salute e la felicità dei miei adorati figli, ma il luogo prescelto è stato invaso dai mostri! Devi andare là e sconfiggerli con la ferocia di un drago!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010166,'Overcome with Awe','Vor Ehrfurcht erstarrt','La grande classe','Abrumado','Trionfo e soggezione','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,2600,25200,1400,'None','–','Rien','Ninguno','Nessuno','','Hunter, I don''t think I''ve ever seen anybody hunt with as much style and grace as you! I am hoping that you will put your superior skills on display in the Arena! It truly is an honor to meet you!','Oh, Jäger, niemals sah ich jemanden so stilvoll und anmutig jagen wie dich! Ich hoffe, du wirst deine Fähig- keiten in der Arena unter Beweis stellen. Es ist eine Ehre, dich kennenzulernen!','Chasseur, je n''ai jamais vu personne chasser avec autant d''élégance ! Je compte sur vous pour assurer le spectacle dans l''Arène. C''est un honneur de vous rencontrer !','Cazador, creo que nunca he visto a nadie cazar con tanto estilo como tú. Espero que quieras mostrar tus habilidades superiores en la arena. ¡Ha sido un honor conocerte!','Oh, prodigio della caccia, non ho mai visto nessuno combattere con tale grazia! Spero che metterai le tue formidabili capacità al servizio dell''Arena! È un vero onore parlare con te!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1010167,'The Hammer and the Pike','Hammer und Dorn','La pique et le marteau','El martillo y la pica','Il martello e la picca','','Hunt an Uragaan and an Agnaktor','Erjage einen Uragaan and einen Agnaktor.','Chasser 1 Uragaan et 1 Agnaktor','Caza un Uragaan y un Agnaktor','Caccia un Uragaan e un Agnaktor','','Event','0','HR',0,0,7,10,50,1800,17700,1070,'Mount & topple monster 3 times','3x Monster besteigen + umwerfen','Monter & renverser un monstre 3 fois','Monta y derriba 3 veces al mons.','Cavalca e rovescia mostro x3','','The Princess just set out for the Arena, saying something about becoming a hunter! The palace guards went after her, but they''re no match for any monster! Please hunter, get them before they get her!','Die Prinzessin ist zur Arena gegangen. Sie sagt, dass sie Jägerin werden will! Die Palastwache ist ihr nach, aber gegen Monster kann sie nichts ausrichten! Bitte hol dir die Monster, bevor es zu spät ist!','La princesse est partie pour l''Arène dans l''idée de devenir chasseuse ! Les gardes l''ont suivie, mais les monstres sont trop forts pour eux ! De grâce, sauvez-la !','La princesa ha salido diciendo que quería ser cazadora. Los guardias del palacio han ido por ella, pero los monstruos han acabado con ellos. Mátalos antes de que la encuentren.','La principessa è andata all''Arena per diventare una cacciatrice! Le guardie del palazzo si sono messe sulle sue tracce, ma non hanno speranze contro i mostri! Devi raggiungerle in tempo!','',2400,110);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020001,'Event: Slay a Mizutsune','Event: Erlege einen Mizutsune','Évt : Tuer 1 Mizutsune','Evento: Batida Mizutsune','Evento: Uccidi un Mizutsune','','Slay a Mizutsune','Erlege einen Mizutsune.','Tuer 1 Mizutsune','Abate un Mizutsune','Uccidi un Mizutsune','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Today you face a Mizutsune. Don''t let its foam and bubbles fool you. That thing will rip your face right off. Weapons: Long Sword, Hunting Horn, Charge Blade, Lance, Light Bowgun','Heute kämpfst du mit einem Mizutsune. Lass dich von Schaum und Blasen nicht täuschen, er ist brutal! Waffen: Langschwert, Jagd- horn, Energieklinge, Lanze, Leichtes Bogengewehr','Aujourd''hui, vous serez aux prises avec un Mizutsune. Ne vous laissez pas berner par ce monstre sanguinaire. Armes : épée longue, corne de chasse, volto-hache, lance, fusarbalète léger','Hoy te toca un Mizutsune. No te dejes engañar por su espuma y burbujas o ese bicho te arrancará la cabeza. Armas: espada larga, cornamusa, hacha cargada, lanza, ballesta ligera.','Oggi affronterai un Mizutsune. Le sue bolle e la sua schiuma sono letali: non lasciarti ingannare! Armi: spada lunga, corno da caccia, spad-ascia caricata, lancia, balestra leggera','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020002,'Event: Slay a Glavenus','Event: Erlege einen Glavenus','Évt : Tuer 1 Glavenus','Evento: Batida Glavenus','Evento: Uccidi un Glavenus','','Slay a Glavenus','Erlege einen Glavenus.','Tuer 1 Glavenus','Abate un Glavenus','Uccidi un Glavenus','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','I have trained you to become a hardened warrior. Today you prove yourself against the mighty Glavenus! Weapons: Great Sword, Insect Glaive, Hammer, Switch Axe, Bow','Ich habe dich zum abgehär- teten Krieger ausgebildet. Beweis dich heute im Kampf mit dem mächtigen Glavenus! Waffen: Bogen Großschwert, Morph-Axt, Insektenglefe, Hammer','Je vous ai formé à l''art du combat. Prouvez-moi que je n''ai pas perdu mon temps en tuant le Glavenus ! Armes : grande épée, insectoglaive, marteau, morpho-hache, arc','Te he entrenado para que seas más duro. Demuestra de lo que eres capaz ante el temible Glavenus. Armas: gran espada, glaive insecto, martillo, hacha espada, arco.','Il mio addestramento ti ha trasformato in una leggenda, ma oggi dovrai misurarti con il possente Glavenus! Armi: spadone, falcione insetto, spad-ascia, martello, arco','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020003,'Event: Slay a Gammoth','Event: Erlege einen Gammoth','Évt : Tuer 1 Gammoth','Evento: Batida Gammoth','Evento: Uccidi un Gammoth','','Slay a Gammoth','Erlege einen Gammoth.','Tuer 1 Gammoth','Abate un Gammoth','Uccidi un Gammoth','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Today, you will face a Gammoth. It will stomp you into next week if you''re not careful. I will too if you don''t defeat it! Weapons: Long Sword, Hunting Horn, Switch Axe, Gunlance, Heavy Bowgun','Heute ist ein Gammoth dein Gegner. Er macht dich platt, wenn du nicht aufpasst. Ich aber auch, wenn du versagst! Waffen: Langschwert, Morph- Axt, Jagdhorn, Gewehrlanze, Schweres Bogengewehr','Aujourd''hui, vous allez affronter un Gammoth. Gare à vous ou il vous piétinera sans hésiter. Armes : épée longue, corne de chasse, morpho-hache, lancecanon, fusarbalète lourd','Hoy te enfrentarás a un Gammoth. Te chafará como si nada si no vas con ojo. ¡Y yo también, si no ganas! Armas: espada larga, cornamusa, hacha espada, lanza pist., ballesta pesada.','Oggi affronterai un Gammoth. Prudenza, o ti prenderà a calci per mesi! E io farò lo stesso, se non lo sconfiggi! Armi: spada lunga, corno da caccia, spad-ascia, balestra pesante, lancia-fucile','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020004,'Event: Slay an Astalos','Event: Erlege einen Astalos','Évt : Tuer 1 Astalos','Evento: Batida Astalos','Evento: Uccidi un Astalos','','Slay an Astalos','Erlege einen Astalos.','Tuer 1 Astalos','Abate un Astalos','Uccidi un Astalos','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','The Astalos is a formidable opponent. It will stop at nothing to achieve its goal of ripping you a new one! Weapons: Sword & Shield, Lance, Charge Blade, Insect Glaive, Bow','Der Astalos ist ein starker Gegner. Nichts kann ihn davon losreißen, etwas von dir loszureißen! Waffen: Schwert & Schild, Lanze, Energieklinge, Insektenglefe, Bogen','L''Astalos est un redoutable adversaire. Il ne reculera devant rien pour vous tailler en pièces ! Armes : épée & bouclier, lance, volto-hache, insectoglaive, arc','El Astalos es un adversario formidable. ¡No se detendrá ante nada hasta alcanzar su meta de destrozarte! Armas: espada y escudo, lanza, hacha cargada, glaive insecto, arco.','L''Astalos è un avversario formidabile. Non si ferma di fronte a nulla pur di fare a pezzi il nemico... Cioè te! Armi: spada e scudo, lancia, spad-ascia caricata, falcione insetto, arco','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020005,'Event: Slay a Lagiacrus','Event: Erlege einen Lagiacrus','Évt : Tuer 1 Lagiacrus','Evento: Batida Lagiacrus','Evento: Uccidi un Lagiacrus','','Slay a Lagiacrus','Erlege einen Lagiacrus.','Tuer 1 Lagiacrus','Abate un Lagiacrus','Uccidi un Lagiacrus','','Event','0','HR',1,0,6,15,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Did you get lost on the way to daycare? Am I here to take care of you? No? GOOD! Now go take out that Lagi! Weapons: Long Sword, Dual Blades, Hammer, Gunlance, Light Bowgun','Hast du dich verlaufen? Muss ich auf dich aufpassen? Nein? GUT! Jetzt kümmer dich um den Lagi! Waffen: Hammer, Doppelklingen, Langschwert, Gewehrlanze, Leichtes Bogengewehr','On a perdu sa nounou ? Ne comptez pas sur moi pour la remplacer ! Tuez-moi ce Lagi, et que ça saute ! Armes : épée longue, lames doubles, marteau, lance- canon, fusarbalète léger','¿Te has perdido de camino al asilo? ¿Estoy aquí para cuidarte? ¿A que no? ¡Bien! ¡Pues a por ese Lagi! Armas: espada larga, espadas dobles, martillo, lanza pistola, ballesta ligera.','Non trovi più la strada per l''asilo? Vuoi che ti cambi il pannolino? No? BENE! Ora togli di mezzo il Lagiacrus! Armi: spada lunga, doppie lame, martello, balestra leggera, lancia-fucile','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020006,'Event: Endurance Quest 1','Event: Ausdauer-Quest 1','Évt : Quête Endurance 1','Evento: Misión resistencia 1','Evento: Missione resistenza 1','','Slay all large monsters','Erlege alle großen Monster.','Tuer tous les grands monstres','Abate a todos los monstruos grandes','Uccidi tutti i mostri grandi','','Event','0','HR',5,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Your path to glory begins with a Tigrex, a Zinogre, and a Nargacuga. Honor our beloved Arena with your victory! Weapons: Great Sword, Lance, Charge Blade, Insect Glaive, Light Bowgun','Dein Weg zum Ruhm führt an einem Tigrex, einem Zinogre und einem Nargacuga vorbei. Zeige es ihnen in der Arena! Waffen: Großschwert, Lanze, Energieklinge, Insekten- glefe, Leichtes Bogengewehr','Un Tigrex, un Zinogre et un Nargacuga se dressent entre vous et la gloire. Faites honneur à notre Arène ! Armes : grande épée, lance, volto-hache, insectoglaive, fusarbalète léger','Tu camino a la gloria empieza con un Tigrex, un Zinogre y un Nargacuga. ¡Honra a la arena con tu victoria! Armas: gran espada, lanza, hacha cargada, glaive insecto, ballesta ligera.','Il tuo cammino verso la gloria inizia con un Tigrex, uno Zinogre e un Nargacuga. Onora l''Arena con il trionfo! Armi: spadone, lancia, falcione insetto, spad-ascia caricata, balestra leggera','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020007,'Event: Endurance Quest 2','Event: Ausdauer-Quest 2','Évt : Quête Endurance 2','Evento: Misión resistencia 2','Evento: Missione resistenza 2','','Slay all large monsters','Erlege alle großen Monster.','Tuer tous les grands monstres','Abate a todos los monstruos grandes','Uccidi tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Today, you will fight a Rajang and a Deviljho. You may find Glory, or you may draw your last breath. Weapons: Long Sword, Sword & Shield, Hunting Horn, Switch Axe, Bow','Heute findest du im Kampf gegen einen Rajang und einen Dämonjho Ruhm und Ehre – oder dein Ende. Waffen: Langschwert, Schwert & Schild, Bogen, Jagdhorn, Morph-Axt','Aujourd''hui, vous allez affronter un Rajang et un Deviljho. À la clé : gloire... ou funeste sort. Armes : épée longue, épée & bouclier, corne de chasse, morpho-hache, arc','Hoy lucharás con un Rajang y un Deviljho. O te llevas la gloria, o puede que no vuelvas a ver el sol. Armas: espada larga, espada y escudo, cornamusa, hacha espada, arco.','Oggi affronterai un Rajang e un Deviljho. Puoi conquistare la gloria, o esalare il tuo ultimo respiro... Armi: spada lunga, spada e scudo, corno da caccia, arco, spad-ascia','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020008,'Event: Endurance Quest 3','Event: Ausdauer-Quest 3','Évt : Quête Endurance 3','Evento: Misión resistencia 3','Evento: Missione resistenza 3','','Slay all large monsters','Erlege alle großen Monster.','Tuer tous les grands monstres','Abate a todos los monstruos grandes','Uccidi tutti i mostri grandi','','Event','0','HR',5,0,7,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Glory is at hand, Greenhorn! It is yours if you slay a Gammoth, an Astalos, a Mizutsune, and a Glavenus! Weapons: Great Sword, Dual Blades, Gunlance, Charge Blade, Heavy Bowgun','Erlege einen Gammoth, einen Astalos, einen Mizutsune und einen Glavenus und erlange großen Ruhm, Grünschnabel! Waffen: Großschwert, Doppel- klingen, Gewehrlanze, Energie- klinge, Schweres Bogengewehr','Blanc-bec ! Couvrez-vous de gloire en tuant un Gammoth, un Astalos, un Mizutsune et un Glavenus ! Armes : grande épée, lames doubles, lancecanon, volto- hache, fusarbalète lourd','¡A por la gloria, inexperto! Será tuya si abates un Gammoth, un Astalos, un Mizutsune y un Glavenus. Armas: gran espada, espadas dobles, lanza pistola, hacha cargada, ballesta pesada.','La gloria ti attende, incapace! Sarà tua se uccidi un Gammoth, un Astalos, un Mizutsune e un Glavenus! Armi: spadone, doppie lame, lancia-fucile, spad-ascia caricata, balestra pesante','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020009,'Event: Slay a Zinogre','Event: Erlege einen Zinogre','Évt : Tuer 1 Zinogre','Evento: Batida Zinogre','Evento: Uccidi uno Zinogre','','Slay a Zinogre','Erlege einen Zinogre.','Tuer 1 Zinogre','Abate un Zinogre','Uccidi uno Zinogre','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','This Challenge Quest requires you to slay a Zinogre. Do not underestimate this handsome beast''s nimble movements. Weapons: Long Sword, Lance, Hammer, Insect Glaive, Light Bowgun','Bei dieser Herausforderungs- Quest musst du einen Zinogre erlegen. Unterschätze die Agilität der schönen Bestie nicht. Waffen: Langschwert, Lanze, Hammer, Insekten- glefe, Leichtes Bogengewehr','Cette Quête Défi consiste à tuer un Zinogre. Ne sous- estimez pas l''agilité de cette élégante créature. Armes : épée longue, lance, marteau, insectoglaive, fusarbalète léger','En esta misión de desafío debes abatir un Zinogre. No subestimes los gráciles movimientos de esta bestia. Armas: espada larga, lanza, martillo, glaive insecto, ballesta ligera.','Questa missione sfida prevede di uccidere uno Zinogre. Non sottovalutare le agili movenze di questa bestia. Armi: spada lunga, lancia, martello, falcione insetto, balestra leggera','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020010,'Event: Slay a Gore Magala','Event: Erlege einen G. Magala','Évt : Tuer 1 Gore Magala','Evento: Batida Gore Magala','Evento: Uccidi un G.Magala','','Slay Gore Magala','Erlege den Gore Magala.','Tuer 1 Gore Magala','Abate un Gore Magala','Uccidi il Gore Magala','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','The Gore Magala''s Frenzy virus spreads like the perfume of a beautiful woman. Weapons: Sword & Shield, Hammer, Insect Glaive, Charge Blade, Light Bowgun','Das Raserei-Virus des Gore Magala verbreitet sich wie das Parfüm einer schönen Dame. Waffen: Schwert & Schild, Hammer, Insekten- glefe, Energieklinge, Leichtes Bogengewehr','La furie du Gore Magala se propage comme le parfum d''une jolie femme. Armes : épée & bouclier, marteau, insectoglaive, volto-hache, fusarbalète léger','El virus de la cólera del Gore Magala se propaga como el perfume de una mujer bonita. Armas: espada y escudo, martillo, glaive insecto, hacha cargada, ball. ligera.','Il virus della frenesia diffuso dal Gore Magala si spande come il profumo di una splendida donna. Armi: spada e scudo, balestra leggera, martello, falcione insetto, spad-ascia caricata','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020011,'Event: Slay a Tigrex','Event: Erlege einen Tigrex','Évt : Tuer 1 Tigrex','Evento: Batida Tigrex','Evento: Uccidi un Tigrex','','Slay a Tigrex','Erlege einen Tigrex.','Tuer 1 Tigrex','Abate un Tigrex','Uccidi un Tigrex','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','There is a certain grace to the Tigrex''s brutality. Try not to get caught up in its majesty. Weapons: Long Sword, Dual Blades, Hunting Horn, Bow, Gunlance','Der Tigrex ist bei aller Brutalität auch anmutig. Lass dich davon nicht einwickeln! Waffen: Langschwert, Jagdhorn, Doppelklingen, Bogen, Gewehrlanze','Le Tigrex est tout en grâce et en brutalité. Ne vous laissez pas hypnotiser par sa splendeur. Armes : épée longue, lames doubles, corne de chasse, arc, lancecanon','La brutalidad del Tigrex tiene cierto encanto. Ojo que no te pille con su esplendor. Armas: espada larga, espadas dobles, arco, cornamusa, lanza pistola.','C''è una mirabile grazia nella ferocia del Tigrex... Ma vedi di non lasciarti travolgere dalla sua maestosità! Armi: spada lunga, doppie lame, corno da caccia, arco, lancia-fucile','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020012,'Event: Slay a Rathalos','Event: Erlege einen Rathalos','Évt : Tuer 1 Rathalos','Evento: Batida Rathalos','Evento: Uccidi un Rathalos','','Slay a Rathalos','Erlege einen Rathalos.','Tuer 1 Rathalos','Abate un Rathalos','Uccidi un Rathalos','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','A sight to behold to witness a Rathalos dance across the sky... In this Challenge Quest, I hope your form can compete. Weapons: Great Sword, Charge Blade, Switch Axe, Hunting Horn, Heavy Bowgun','Ein Rathalos am Himmel ist ein wahrer Augenschmaus! Du bei dieser Herausforderungs- Quest hoffentlich auch. Waffen: Großschwert, Energie- klinge, Morph-Axt, Jagdhorn, Schweres Bogengewehr','Le Rathalos est le maître incontesté des cieux. Cette Quête Défi vous mettra à rude épreuve. Armes : grande épée, volto-hache, morpho-hache, corne de chasse, fusarbalère lourd','Ver un Rathalos bailar por el cielo es memorable. En esta misión de desafío, espero que estés a la altura. Armas: gran espada, hacha cargada, hacha espada, cornamusa, ballesta pesada.','È uno spettacolo ammirare un Rathalos che danza nel cielo. Spero che tu sia all''altezza in questa missione sfida. Armi: spadone, spad-ascia caricata, spad-ascia, corno da caccia, balestra pesante','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020013,'Event: Slay a Nargacuga','Event: Erlege einen Nargacuga','Évt : Tuer 1 Nargacuga','Evento: Batida Nargacuga','Evento: Uccidi un Nargacuga','','Slay a Nargacuga','Erlege einen Nargacuga.','Tuer 1 Nargacuga','Abate un Nargacuga','Uccidi un Nargacuga','','Event','0','HR',1,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','This is a Challenge Quest to slay a Nargacuga. Watch its movements and strike with conviction, or this will be our last conversation. Weapons: Long Sword, Sword & Shield, Hammer, Lance, Bow','Bei dieser Herausforderungs- Quest musst du einen Nargacu- ga genau beobachten und ihn gezielt erlegen, sonst hast du erst gar keine Chance. Waf- fen: Langschwert, Schwert & Schild, Hammer, Lanze, Bogen','Cette Quête Défi consiste à tuer un Nargacuga. Suivez bien ses mouvements et frappez fort ou vous êtes cuit. Armes : épée longue, épée & bouclier, marteau, lance, arc','En esta misión de desafío debes abatir un Nargacuga. Ojo a sus movimientos, y golpea con convicción o no lo contarás. Armas: esp. larga, espada y escudo, martillo, lanza, arco.','Questa missione sfida prevede di uccidere un Nargacuga. Se vuoi salvare la pelle, tieni d''occhio i suoi movimenti e colpisci senza esitazione. Armi: spada lunga, spada e scudo, martello, lancia, arco','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1020014,'Event: Endurance Quest 4','Event: Ausdauer-Quest 4','Évt : Quête Endurance 4','Evento: Misión resistencia 4','Evento: Missione resistenza 4','','Slay all large monsters','Erlege alle großen Monster.','Tuer tous les grands monstres','Abate a todos los monstruos grandes','Uccidi tutti i mostri grandi','','Event','0','HR',5,0,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','Welcome to the Arena. We''ve prepared four monsters for you today, with the elements of Ice, Water, Fire, & Thunder. Weapons: Great Sword, Dual Blades, Hunting Horn, Gunlance, Light Bowgun','Willkommen in der Arena. Wir haben heute vier Monster für dich, mit den Elementen Eis, Wasser, Feuer und Donner. Waffen: Großschwert, Doppel- klingen, Jagdhorn, Gewehr- lanze, Leichtes Bogengewehr','Aujourd''hui dans l''arène, 4 monstres incarnant les éléments glace, eau, feu et foudre. Armes : grande épée, lames doubles, corne de chasse, lancecanon, fusarbalète léger','Bienvenido a la arena. Hemos preparado 4 monstruos para ti, con los elementos de hielo, agua, fuego y trueno. Armas: gran espada, espadas dobles, cornamusa, lanza pistola, ballesta ligera.','Ehi, che piacere averti qui! Oggi l''Arena presenta quattro mostri, legati a ghiaccio, acqua, fuoco e tuono. Armi: spadone, doppie lame, corno da caccia, balestra leggera, lancia-fucile','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110001,'CoroCoro: Lost in the Swamp','CoroCoro: Verirrt im Sumpf','CoroCoro : Dans les marais','CoroCoro: en la ciénaga','CoroCoro: Perso nella palude','','Slay a total of 20 Ceanataurs and Iopreys','Erlege insgesamt 20 Ceanatauren und Ioprey.','Tuer un total de 20 Ceanataur et Ioprey','Abate un total de 20 Ceanataur e Ioprey','Uccidi 20 Ceanataur e Ioprey in totale','','Event','0','LR',1,1,2,7,50,300,2700,120,'None','–','Rien','Ninguno','Nessuno','','I came here with Zombie to try and collect some insects! Wow! This place is huge! There''re probably all kinds of bugs to find! ...Zombie? Where''d you go? I-It''s kinda scary all alone... Help!','Ich bin mit Zombie hierher gekommen, um Insekten zu sammeln! Wow! Dieser Ort ist riesig! Hier gibt es sicher Insekten aller Art! ... Zombie? Wo bist du? A-Alleine fürchte ich mich... Hilfe!','Avec Zombie, on est venus pour capturer des insectes. Avec autant d''espace, il doit y en avoir des tonnes. Zombie ? T''es où ? Me laisse pas tout seul, j''ai peur... Au secours !','He venido con Zombie para recoger insectos. ¡Uf, este sitio es enorme! Debe de haber todo tipo de bichos. ¿Zombie? ¿Dónde estás? Uy, solo me da miedo. ¡Ayuda!','Sono venuto qui con Zombie per catturare alcuni insetti! Caspita! Questo posto è enorme! Scommetto che troverò bacarozzi di ogni genere! Ma... Zombie? Dove sei? D-Da solo ho paura... Aiuto!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110002,'The King of Lynians!','König der Lynian!','Le Roi des Lynians !','¡El rey de los Lynian!','Il re dei Lynian!','','Defeat a total of 20 Felynes and Melynxes','Besiege insgesamt 20 Felynen und Melynxe.','Battre un total de 20 Felynes et Melynx','Acaba con un total de 20 Felyne y Melynx','Sconfiggi 20 Felyne e Melynx in totale','','Event','0','LR',1,1,3,15,50,300,2100,200,'None','–','Rien','Ninguno','Nessuno','','I''ve prepared a tournament to decide who''s the REAL King of Lynians! Knock out the other Felyne and Melynx contenders to claim the title of champ, AND an item that''ll let you make some special Palico gear!','Ein Wettbewerb, um zu ent- scheiden, wer der wahre König der Lynian ist! Schalte die an- deren Felynen und Melynxe aus, um ein Objekt zu erhalten, mit dem du besondere Kameraden- ausrüstung schmieden kannst.','J''ai organisé un tournoi pour désigner le véritable Roi des Lynians ! Mettez KO les autres Felynes et Melynx pour remporter le titre de champion et un objet pour forger un équipement Palico spécial !','¡He preparado un torneo para decidir quién es el VERDADERO rey de los Lynian! ¡Si derrotas a todos los Felyne y Melynx, serás el campeón y ganarás un objeto con el que crear equipo único de camarada!','Ho qui per te un torneo per decidere chi è il VERO re dei Lynian. Sconfiggi gli altri Felyne e Melynx e ottieni il titolo di campione, più un oggetto per forgiare equipaggiamento compagni!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110003,'Jump: Crimson Strongmen','Jump: Purpur-Kraftprotze','Jump : Colosses rouges','Jump: Forzudos carmesí','Jump: Forzuti scarlatti','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','LR',5,1,3,8,50,500,4800,230,'None','–','Rien','Ninguno','Nessuno','','I know about powerful people in small packages. I would like to ask your help hunting two red monsters running wild near the Volcano. In return, I''ve prepared an item that will let you make some nice pirate gear.','Ich weiß alles über starke Leute mit unscheinbarem Aussehen. Ich bräuchte deine Hilfe bei der Jagd auf zwei rote Monster beim Vulkan. Als Dank gibt es ein Objekt, mit dem du Piraten- ausrüstung herstellen kannst.','Les vrais héros sont rares, ici-bas. Aidez-moi en chassant 2 monstres rouges en liberté près du Volcan. En retour, je vous donnerai un objet pour fabriquer de l''équipement de pirate.','Conozco a gente muy poderosa. Me gustaría que me ayudaras con dos monstruos rojos que merodean cerca del Volcán. A cambio, te he preparado un objeto con el que podrás hacerte un equipo de pirata.','Mai sottovalutare la potenza dei piccoletti! Ho bisogno di aiuto per cacciare due mostri rossi vicino al vulcano. In cambio, ti darò un oggetto che permette di creare equipaggiamento da pirati.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110004,'Initiate: Operation Moofah','Initiiere: Operation Muufah','Activation : Opération Moofah','Inicia: operación Moofah','Eseguire: operazione Moofah','','Deliver 20 pieces of Moofah Fur','Liefere 20 Einheiten Muufah-Fell ab.','Livrer 20 fourrures Moofah','Entrega 20 mechones de pelo de Moofah','Consegna 20 pezzi di pelliccia di Moofah','','Event','0','LR',3,1,2,6,50,300,3000,210,'None','–','Rien','Ninguno','Nessuno','','ENERGY LEVELS...FALLING. WITHOUT...MONEY...PARTS ARE INOPERABLE. COLLECT MOOFAH FUR AND I COULD... SELL IT FOR LOTS OF MONEY. HELP ME, PROWLER. YOU ARE MY ONLY HOPE.','ENERGIESTUFEN... SINKEN. OHNE... GELD... KANN... NICHT BEWEGEN. SAMMLE MUUFAH-FELL... ICH KANN ES... ES FÜR VIEL GELD VERKAUFEN. HELFT MIR, PIRSCHER. IHR SEID MEINE LETZTE HOFFNUNG.','NIVEAU D''ÉNERGIE... CRITIQUE. BESOIN DE SOUSOUS... POUR M''ACTIVER... OBTENEZ DES FOURRURES MOOFAH... ET JE POURRAI RÉCOLTER... DES SOUSOUS... AU SECOURS...','NIVEL DE ENERGÍA... BAJO. SIN... DINERO... PARTES INOPERATIVAS. CONSIGUE PELO DE MOOFAH Y PODRÍA... VENDERLO POR MONTONES DE DINERO. GATADOR, ERES MI ÚNICA ESPERANZA.','LIVELLI ENERGIA CRITICI. SENZA... DENARO... PARTI NON FUNZIONANTI. CONSEGNARE PELLICCIA DI MOOFAH, SARÀ VENDUTA PER MOLTO DENARO. AIUTAMI, CACCIAMIAO.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110005,'The Uniqlo Avant-garde','Die Uniqlo-Avantgarde','L''avant-garde Uniqlo','Vanguardia Uniqlo','Avanguardia Uniqlo','','Slay a total of 20 Vespoids and Bnahabras','Erlege insgesamt 20 Vespoiden und Bnahabra.','Tuer un total de 20 Vespoid et Bnahabra','Abate un total de 20 Vespoid o Bnahabra.','Uccidi 20 Vespoid e Bnahabra in totale','','Event','0','LR',1,1,2,5,50,300,3000,200,'None','–','Rien','Ninguno','Nessuno','','What Felynes need is a great look FOR Felynes, BY Felynes, and I''ve got just the thing! I''ll need your help gathering the materials, but just think how snazzy you''ll look in my design!','Was Felynen brauchen, ist großartige Mode FÜR Felynen VON Felynen – und ich habe da etwas! Du musst mir beim Sammeln von Materialien helfen. Aber überleg nur, wie schick du bald aussehen wirst!','Les Felynes ont besoin d''un look conçu pour les Felynes, et par les Felynes ! J''ai eu une idée de génie, mais vous devez m''aider à obtenir les matériaux. Pensez à votre futur grand méchant look !','Lo que los Felyne necesitan es un buen look hecho POR Felyne PARA Felyne. ¡Y eso es lo que tengo! Necesito tu ayuda para conseguir los materiales, ¡pero piensa en lo elegante que estarás!','I Felyne hanno bisogno di un look per Felyne, creato da Felyne! E io ho proprio quello che serve! Avrò soltanto bisogno del tuo aiuto per trovare i materiali adatti!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110101,'CoroCoro: Big Bad Bugs','CoroCoro: Große böse Käfer','CoroCoro : Insecte infect','CoroCoro: bichos malos','CoroCoro: Brutti insettacci','','Slay 12 Konchus','Erlege 12 Konchu.','Tuer 12 Konchu','Abate a 12 Konchu','Uccidi 12 Konchu','','Event','0','HR',1,1,4,9,50,300,3000,140,'None','–','Rien','Ninguno','Nessuno','','I can teach you how to protect yourself from dangerous bugs. Let''s practice using those big ones over there! Just use your death ray to take ''em out! Wha!? You Prowlers don''t have a death ray!?','Ich kann dir zeigen, wie du dich vor gefährlichen Käfern schützt. Komm, wir üben mit den großen da drüben! Blas sie einfach mit deinem Todesstrahl weg! Wie, ihr Pirscher habt keinen Todesstrahl?','Je vais vous apprendre à vous défendre face aux insectes nuisibles. Liquidez ceux-ci avec votre rayon mortel. Comment ça, les Miaroudeurs n''ont pas de rayon mortel !?','Puedo enseñarte a protegerte de los bichos peligrosos. ¡Practiquemos con aquellos de allí! Usa tu rayo de la muerte para eliminarlos. ¿Cómo? ¿Que los gatadores no tenéis rayo de la muerte?','Posso insegnarti come ci si difende dagli insetti più pericolosi. Proviamo con quelli laggiù! Usa il tuo raggio mortale per spazzarli via! Cosa?! Voi cacciamiao non sparate raggi mortali?!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110102,'A Hero''s Return','Heldenrückkehr','Le retour d''un héros','El retorno de un héroe','Il ritorno dell''eroe','','Hunt a Bulldrome','Erjage einen Bulldrome.','Chasser 1 Bulldrome','Caza un Bulldrome','Caccia un Bulldrome','','Event','0','HR',0,1,4,9,50,500,4200,220,'None','–','Rien','Ninguno','Nessuno','','There''s a monster out there running wild and keeping me from getting anything done! I want you to go and smash its fangs in! If you help me out, I''ll give you an item to make some great new Palico gear!','Ich kriege gar nichts auf die Reihe, weil sich da draußen ein Monster herumtreibt! Ich will, dass du ihm die Reiß- zähne ziehst! Dafür gebe ich dir auch ein Objekt für tolle neue Kameradenausrüstung!','Il y a un monstre en cavale qui me met sans cesse des bâtons dans les roues ! Rectifiez-le et je vous donnerai un objet génial pour fabriquer un nouvel équipement Palico.','¡Hay un monstruo suelto por ahí que no me deja hacer nada! Quiero que acabes con él. Si me ayudas, te daré un objeto para hacer un nuevo equipo de camarada.','Aiuto! C''è un mostro furioso che m''impedisce di fare qualsiasi cosa! Vai lì e spaccagli le zanne! Se mi aiuti, ti regalerò un oggetto che permette di creare nuovo equipaggiamento per compagni!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110103,'Mario: The White Donkey Kong?','Mario: Der weiße Donkey Kong?','Mario : Sosie de Donkey Kong','Mario: ¿Donkey Kong blanco?','Mario: Donkey Kong bianco?','','Slay 20 Blangos','Erlege 20 Blangos.','Tuer 20 Blango','Abate a 20 Blango','Uccidi 20 Blango','','Event','0','HR',1,1,4,3,50,400,3900,160,'None','–','Rien','Ninguno','Nessuno','','We''ve got trouble! There''s a Donkey Kong doppelganger on the loose and it just stole my precious mushroom! If you help me out, I''ll reward you with an item that''ll enable you to make the perfect gear!','Wir haben ein Problem! Ein Donkey-Kong-Doppelgänger hat meinen kostbaren Pilz gestohlen! Wenn du mir hilfst, gebe ich dir ein Objekt, mit dem du die perfekte Ausrüstung herstellen kannst!','C''est la cata ! Un sosie de Donkey Kong vient de me voler mon champignon ! Aidez-moi et je vous offrirai un objet pour fabriquer de l''équipement de premier choix !','¡Tenemos problemas! Hay un doble de Donkey Kong suelto y me acaba de robar mi querido champiñón. Si me ayudas, te daré un objeto que te permitirá crear el equipo perfecto.','Siamo nei guai! C''è un sosia di Donkey Kong a piede libero e ha rubato il mio prezioso fungo! Se mi aiuti, ti regalerò un oggetto che ti permetterà di creare l''equipaggiamento perfetto!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110104,'Animal Crossing: Picnic Day','Animal Crossing: Picknick','Animal Crossing : Rations','Animal Crossing: merienda','Animal Crossing: Picnic','','Obtain 3,000 Wycademy Points','Sammle 3.000 Wykademie-Punkte.','Obtenir 3 000 pts Wycadémie','Obtén 3.000 puntos de la Wycademia','Ottieni 3000 punti Wyccademia','','Event','0','HR',3,1,5,1,50,700,6300,420,'None','–','Rien','Ninguno','Nessuno','','It''s a great day for a picnic! Go and visit a bunch of places to get us some goodies! When you get back, I''ll reward you with an item that can be used to make special Palico gear.','Ein toller Tag zum Picknicken! Besorge uns doch an einer Reihe von Orten ein paar feine Sachen! Wenn du wieder da bist, gebe ich dir ein Objekt zur Herstellung besonderer Kameradenausrüstung!','Le temps est idéal pour un pique-nique ! Allez nous chercher de quoi manger. En retour, vous aurez droit à un objet permettant de fabriquer de l''équipement Palico spécial.','¡Buen día para ir de merienda! Ve por ahí y tráeme cosas buenas. Cuando vuelvas, te recompensaré con un objeto que puede usarse para hacer equipo de camarada.','È la giornata ideale per un picnic! Mettiti in viaggio e portaci dei regali dai posti che visiti! Ti ricompenserò con un oggetto che permette di creare degli speciali equipaggiamenti per compagni.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110105,'SF: Ultimate Entrance Exam','SF: Ultimative Aufnahmeprüfung','SF : Examen final','SF: examen de entrada final','SF: Ultra esame d''ingresso','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,1,7,9,50,800,7800,360,'None','–','Rien','Ninguno','Nessuno','','Hm... Are you here to join us? In that case, let''s get the exam started! You must take on my most dedicated disciples! If you can beat them, I just might let you become my newest student!','Hm... Du willst bei uns mit- machen? Dann fangen wir mal mit der Prüfung an! Du musst es mit meinen besten Schülern aufnehmen. Wenn du sie schlägst, darfst du vielleicht bei mir lernen!','Hmm... Vous êtes là pour nous rejoindre ? Bien, que l''épreuve commence ! Si vous arrivez à battre mes meilleurs disciples, je vous prendrai sous mon aile !','¿Has venido a apuntarte? En tal caso, ¡que empiece el examen! Debes luchar con mis discípulos más entregados. Si les ganas, te dejaré que estudies conmigo.','Uhm... Sei qui per unirti a noi? In questo caso, diamo inizio all''esame! Preparati ad affrontare i miei allievi più zelanti! Se riuscirai a batterli, ti accoglierò tra i miei studenti!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110106,'Fan Club: Arena Action','Fanclub: Arena-Action','Fan Club : Dans l''Arène','Club de fans: ¡en la arena!','Fan club: Arena e caos','','Hunt all large monsters','Erjage alle großen Monster.','Chasser tous les grands monstres','Caza a todos los monstruos grandes','Caccia tutti i mostri grandi','','Event','0','HR',5,1,7,9,50,1200,12000,610,'None','–','Rien','Ninguno','Nessuno','','Oh my gorsh! I''m finally a MH Fan Club member! I''m schuper pumped! I can''t believe I get to fight four Fanged Beasts right off the bat! If we beat ''em we''ll be able to make some schuper cute Palico gear!','Endlisch bin isch Mitglied im MH-Fanclub! Isch bin ja scho aufgerescht! Und isch darf gleisch mit vier Reischschahn- Beschtien kämpfen! Wenn dasch klappt, können wir schuper Kameradenschachen machen!','Génial ! J''ai enfin rejoint le Fan Club de MH ! Quand je pense que je vais devoir affronter quatre bêtes à crocs... Si on les bat, à nous les super équipements Palico !','¡Ay, madre! ¡Por fin soy miembro del club de fans de MH! ¡Es increíble! Ya puedo luchar contra 4 bestias de colmillos de golpe. Si ganamos, podremos crear un nuevo equipo de camarada.','Cashpita! Finalmente faccio parte del fan club di MH! Sciono shuper felice! E shto per combattere contro quattro bestie zannute! Se vinciamo, potremo creare dei shuper equipaggiamenti per compagni!','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110108,'Making the Cut','Ein sauberer Schnitt','Taillé pour lutter','Cortar por lo sano','La pratica del taglio','','Sever Rathian''s tail','Trenne den Schwanz der Rathian ab.','Couper la queue de la Rathian','Corta la cola de la Rathian','Mozza la coda della Rathian','','Event','0','HR',0,1,6,2,50,300,3000,260,'None','–','Rien','Ninguno','Nessuno','','Argh! Every time I fight a Rathian, I can''t cut off her tail! She''s always doing that flippy thing and then I get poisoned. Can you help a girl out and show me how it''s done?','Oh Mann! Wenn ich mit einer Rathian kämpfe, schaffe ich es nie, den Schwanz abzutrennen! Die drehen sich immer so rum, und dann werd ich vergiftet. Kannst du mir zeigen, wie das geht?','Argh ! Je n''arrive jamais à couper la queue de la Rathian ! Elle finit toujours par m''empoisonner. Vous voulez bien me montrer comment on fait ?','¡Ah! Cuando lucho con un Rathian, no puedo cortarle la cola. Siempre se voltea y acabo envenenada. ¿Podrías enseñar a esta pobre chica cómo se hace?','Argh! Quando combatto con le Rathian non riesco mai a mozzare le loro code! Mi confondono con quella specie di capriola e io finisco sempre avvelenata. Perché non mi mostri tu come si fa?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110109,'Catch a Rathalos by the Tail','Ausgewedelt, Rathalos!','La queue du Rathalos','Coge un Rathalos por la cola','Rathalos per la coda','','Sever Rathalos''s tail','Trenne den Schwanz des Rathalos ab.','Couper la queue du Rathalos','Corta la cola del Rathalos','Mozza la coda del Rathalos','','Event','0','HR',0,1,7,8,50,500,4200,320,'None','–','Rien','Ninguno','Nessuno','','Yo, what up, brah! Still bringin'' the pain to the monster game? You know I have, but my lil'' breaker-in-trainin'' can''t seem to get the hang of cuttin'' off tails. Drops some science on her, wouldja?','Hey, was läuft, Kumpel? Immer noch hinter Monstern her? Ich natürlich auch, aber meine kleine Brecher-Auszubildende schafft es einfach nicht, Schwänze abzutrennen. Bring ihr das mal bei, ja?','Salut ! Toujours à l''affût des monstres ? Moi aussi, mais mon apprentie n''arrive toujours pas à leur couper la queue. Montrez-lui comme on fait, d''accord ?','¿Qué pasa, colega? ¿Sigues liado con los monstruos? Ya sabes que yo sí, pero mi ayudante no tiene ni idea de cortar colas. ¿Por qué no le das una lección?','Bella, oh! Come ti butta? Sei ancora in giro a mazzolare mostri? Anch''io, certo, ma quella sbarbatella della mia apprendista non ha ancora capito come si mozzano le code. Glielo insegni tu?','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1110111,'Too Hot to Handle','Eine heiße Sache','Chaud devant','Demasiado ardiente','Bollori insopportabili','','Deliver 2 Powderstones','Liefere 2 Pulversteine ab.','Livrer 2 pierres-poudre','Entrega 2 Piedras polvo','Consegna 2 Pietre-polvere','','Event','0','HR',3,1,6,12,50,700,6900,350,'None','–','Rien','Ninguno','Nessuno','','I have a lil'' purroblem, Prowler. This Powderstone is too hot for me to get my paws on. I could really use a Palico with heat- resistant Palico Skills, or one who can quickly recover after taking damage.','Ich habe eines katzekleines Problem, Pirscher. Dieser Pulverstein ist zu heiß für mich. Ein Kamerad mit hitzebeständigen Kameraden- fähigkeiten oder schneller Heilung könnte mir helfen.','J''ai un souci, Miaroudeur. Cette pierre explosive me brûle les pattes. Il me faut un Palico résistant à la chaleur ou capable de récupérer rapidement après avoir subi des dégâts.','Tengo un problemilla, gatador. Esta piedra de pólvora está demasiado caliente y necesito un camarada con capacidad para resistir el calor o que pueda recuperarse pronto de los daños sufridos.','Ho un prrroblema, cacciamiao. Questa pietra-polvere è così calda che non riesco a metterci le zampe. Mi serve un compagno resistente al calore o con un''alta capacità di recupero dai danni.','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1120001,'Event: Slay a Volvidon','Event: Erlege einen Volvidon','Évt : Tuer 1 Volvidon','Evento: Batida Volvidon','Evento: Uccidi un Volvidon','','Slay a Volvidon','Erlege einen Volvidon.','Tuer 1 Volvidon','Abate un Volvidon','Uccidi un Volvidon','','Event','0','HR',1,1,4,9,50,0,1800,0,'None','–','Rien','Ninguno','Nessuno','','Today, Prowler, you''re up against a Volvidon. Come back victorious or don''t come back! That''s an order! Support: Charisma, Protection Healing, Bombing, Gathering','Pirscher, komm heute als Sieger über diesen Volvidon zurück... oder gar nicht! Das ist ein Befehl! Unterstützung: Charisma, Schutz, Heilen, Sammeln, Bombardieren','Aujourd''hui, Miaroudeur, vous allez affronter un Volvidon. Terrassez-le ou vous aurez affaire à moi ! Classe : Leader, Protecteur, Soigneur, Bombardeur, Chapardeur','Gatador, hoy te enfrentas a un Volvidon. Véncele o no vengas más por aquí. ¡Es una orden! Soporte: Carisma, Protec., Curación, Bombas, Recogida.','Oggi, cacciamiao, dovrai affrontare un Volvidon. Torna trionfante o non tornare affatto! È un ordine! Supporto: Comando, Protezione, Cura, Bombardiere, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1120002,'Event: Slay a Royal Ludroth','Event: Erlege einen K.Ludroth','Évt : Tuer 1 Ludroth royal','Evento: Batida Ludroth Real','Evento: Uccidi un Ludroth r.','','Slay a Royal Ludroth','Erlege einen Königs-Ludroth.','Tuer 1 Ludroth royal','Abate un Ludroth Real','Uccidi un Ludroth reale','','Event','0','HR',1,1,4,15,50,0,1800,0,'None','–','Rien','Ninguno','Nessuno','','The Royal Ludroth is a vicious creature with a sponge-like mane. Be careful or I''ll need it to mop up what''s left of you. Support: Charisma, Fighting, Protection, Healing, Bombing','Der Königs-Ludroth ist ein brutales Wesen mit schwamm- artiger Mähne. Gib gut acht oder ich wische damit deine Überreste auf. Unterstüt- zung: Charisma, Kämpfen, Schutz, Heilen, Bombardieren','Le Ludroth royal est une créature sournoise. Gare à vous, je ne tiens pas à vous ramasser à la petite cuillère ! Classe : Leader, Soldat, Protecteur, Soigneur, Chapardeur','El Ludroth Real es un bicho sanguinario de larga melena. Cuidado o tendré que usarla para barrer tus restos. Soporte: Carisma, Combate, Protección, Curación, Bombas.','Il feroce Ludroth reale ha una criniera a spugna. Fai attenzione o dovrò usarla per pulire ciò che resta di te. Supporto: Comando, Combattimento, Protezione, Cura, Bombardiere','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1120003,'Event: Slay a Lagombi','Event: Erlege einen Lagombi','Évt : Tuer 1 Lagombi','Evento: Batida Lagombi','Evento: Uccidi un Lagombi','','Slay a Lagombi','Erlege einen Lagombi.','Tuer 1 Lagombi','Abate un Lagombi','Uccidi un Lagombi','','Event','0','HR',1,1,4,9,50,0,1800,0,'None','–','Rien','Ninguno','Nessuno','','What''ll it be today, Prowler? Glory for our beloved Arena or monster chow? Get out there and slay that Lagombi! That''s an order! Support: Protection, Bombing, Charisma, Fighting, Gathering','Wie geht es heute aus, Pir- scher? Ruhm und Ehre in der Arena oder Monsterfutter? Erlege den Lagombi! Das ist ein Befehl! Unterstützung: Charisma, Bombardieren, Schutz, Kämpfen, Sammeln','Que vous réserve ce combat, Miaroudeur ? La gloire ou la fin des haricots ? Trucidez ce Lagombi, c''est un ordre ! Classe : Protecteur, Bombardeur, Leader, Soldat, Chapardeur','¿Qué toca hoy, gatador? ¿Gloria para la arena o picnic de monstruos? ¡Sal ahí y abate al Lagombi! ¡Es una orden! Soporte: Protección, Bombas, Carisma, Combate, Recogida.','Cosa ti aspetta, cacciamiao? La gloria dell''Arena o le fauci di un mostro? Uccidi quel Lagombi! È un ordine! Supporto: Protezione, Bombardiere, Comando, Combattimento, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1120004,'Event: Slay a Yian Kut-Ku','Event: Erlege einen YianKutKu','Évt : Tuer 1 Yian Kut-Ku','Evento: Batida Yian Kut-Ku','Evento: Uccidi uno Y.Kut-Ku','','Slay a Yian Kut-Ku','Erlege einen Yian Kut-Ku.','Tuer 1 Yian Kut-Ku','Abate un Yian Kut-Ku','Uccidi uno Yian Kut-Ku','','Event','0','HR',1,1,4,9,50,0,1800,0,'None','–','Rien','Ninguno','Nessuno','','Prowler! Your chance at glory is at hand. Make our beloved Arena proud by slaying a Yian Kut-Ku. Hooooah! Support: Fighting, Protection, Healing, Bombing, Gathering','Pirscher! Heute kannst du in der Arena zu Ehre kommen, indem du einen Yian Kut-Ku besiegst. Unterstützung: Kämpfen, Schutz, Heilen, Bombardieren, Sammeln','Miaroudeur ! Voici une occasion de vous illustrer. Faites la fierté de notre Arène en tuant un Yian Kut-Ku ! Classe : Soldat, Protecteur, Soigneur, Bombardeur, Chapardeur','¡Gatador! Tienes la gloria al alcance. Salta a la arena y cárgate a un Yian Kut-Ku. ¡Vamos! Soporte: Combate, Protección, Curación, Bombas, Recogida','Cacciamiao! La gloria è a un passo! Uccidi uno Yian Kut-Ku e rendi fieri di te i membri della nostra beneamata Arena! Supporto: Combattimento, Protezione, Cura, Bombardiere, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1120005,'Event: Slay an Arzuros','Event: Erlege einen Arzuros','Évt : Tuer 1 Arzuros','Evento: Batida Arzuros','Evento: Uccidi un Arzuros','','Slay an Arzuros','Erlege einen Arzuros.','Tuer 1 Arzuros','Abate un Arzuros','Uccidi un Arzuros','','Event','0','HR',1,1,4,9,50,0,1800,0,'None','–','Rien','Ninguno','Nessuno','','Ten-hut, Prowler! Today, you''ll be facing an Arzuros. Under- estimate it and you''ll be in a world of hurt! Support: Charisma, Fighting, Protection, Bombing, Gathering','Aaach-tung, Pirscher! Heute triffst du auf einen Arzuros. Ihn zu unterschätzen, bedeutet viel Schmerz! Unterstützung: Charisma, Kämpfen, Sammeln, Bombardieren, Schutz','Garde-à-vous, Miaroudeur ! Vous allez affronter un Arzuros. Ne le sous-estimez pas ou vous irez au casse-pipe ! Classe : Leader, Soldat, Protecteur, Bombardeur, Chapardeur','¡Atención, gatador! Hoy te enfrentarás a un Arzuros. Si lo menosprecias, ¡acabarás muy mal! Soporte: Carisma, Combate, Protección, Bombas, Recogida.','At-tenti, cacciamiao! Oggi affronterai un Arzuros. Non sottovalutarlo o finirai in un mare di guai! Supporto: Comando, Combattimento, Protezione, Bombardiere, Raccolta','',0,0);
INSERT INTO `quests` (_id,name,name_de,name_fr,name_es,name_it,name_ja,goal,goal_de,goal_fr,goal_es,goal_it,goal_ja,hub,type,rank,goal_type,hunter_type,stars,location_id,time_limit,fee,reward,hrp,sub_goal,sub_goal_de,sub_goal_fr,sub_goal_es,sub_goal_it,sub_goal_ja,flavor,flavor_de,flavor_fr,flavor_es,flavor_it,flavor_ja,sub_reward,sub_hrp) VALUES (1120006,'Event: Endurance Quest 5','Event: Ausdauer-Quest 5','Évt : Quête Endurance 5','Evento: Misión resistencia 5','Evento: Missione resistenza 5','','Slay all large monsters','Erlege alle großen Monster.','Tuer tous les grands monstres','Abate a todos los monstruos grandes','Uccidi tutti i mostri grandi','','Event','0','HR',5,1,6,9,50,0,2700,0,'None','–','Rien','Ninguno','Nessuno','','True Prowlers test themselves in an Endurance Quest! This time, you''ll face a Yian Kut-Ku, a Velocidrome, and an Arzuros! Make us proud! Support: Charisma, Fighting, Protection, Healing, Gathering','Echte Pirscher beweisen sich in einer Ausdauer-Quest! Du triffst auf einen Yian Kut-Ku, einen Velocidrome und einen Arzuros! Zeig''s ihnen! Unter- stützung: Charisma, Kämpfen, Schutz, Heilen, Sammeln','Un Miaroudeur ne recule jamais devant une Quête Endurance ! Au programme : Yian Kut-Ku, Velocidrome et Arzuros ! Classe : Leader, Soldat, Protecteur, Soigneur, Chapardeur','¡Los auténticos gatadores deben hacer una misión de resistencia! Te enfrentarás a un Yian Kut-Ku, un Velocidrome y un Arzuros. Soporte: Carisma, Combate, Protec., Curación, Recogida.','I veri cacciamiao si vedono nelle missioni di resistenza! Affronterai uno Yian Kut-Ku, un Velocidrome e un Arzuros! Supporto: Comando, Combattimento, Protezione, Cura, Raccolta','',0,0);
